INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (167, 26, 374, 'ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ', 'قَالُوا۟ لَئِن لَّمْ تَنتَهِ يَـٰلُوطُ لَتَكُونَنَّ مِنَ ٱلْمُخْرَجِينَ ١٦٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (168, 26, 374, 'ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ', 'قَالَ إِنِّى لِعَمَلِكُم مِّنَ ٱلْقَالِينَ ١٦٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (169, 26, 374, 'ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ', 'رَبِّ نَجِّنِى وَأَهْلِى مِمَّا يَعْمَلُونَ ١٦٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (170, 26, 374, 'ﲋ ﲌ ﲍ ﲎ', 'فَنَجَّيْنَـٰهُ وَأَهْلَهُۥٓ أَجْمَعِينَ ١٧٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (171, 26, 374, 'ﲏ ﲐ ﲑ ﲒ ﲓ', 'إِلَّا عَجُوزًۭا فِى ٱلْغَـٰبِرِينَ ١٧١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (172, 26, 374, 'ﲔ ﲕ ﲖ ﲗ', 'ثُمَّ دَمَّرْنَا ٱلْـَٔاخَرِينَ ١٧٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (173, 26, 374, 'ﲘ ﲙ ﲚﲛ ﲜ ﲝ ﲞ ﲟ', 'وَأَمْطَرْنَا عَلَيْهِم مَّطَرًۭا ۖ فَسَآءَ مَطَرُ ٱلْمُنذَرِينَ ١٧٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (174, 26, 374, 'ﲠ ﲡ ﲢ ﲣﲤ ﲥ ﲦ ﲧ ﲨ ﲩ', 'إِنَّ فِى ذَٰلِكَ لَـَٔايَةًۭ ۖ وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ ١٧٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (175, 26, 374, 'ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ', 'وَإِنَّ رَبَّكَ لَهُوَ ٱلْعَزِيزُ ٱلرَّحِيمُ ١٧٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (176, 26, 374, 'ﲰ ﲱ ﲲ ﲳ ﲴ', 'كَذَّبَ أَصْحَـٰبُ لْـَٔيْكَةِ ٱلْمُرْسَلِينَ ١٧٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (177, 26, 374, 'ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ', 'إِذْ قَالَ لَهُمْ شُعَيْبٌ أَلَا تَتَّقُونَ ١٧٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (178, 26, 374, 'ﲼ ﲽ ﲾ ﲿ ﳀ', 'إِنِّى لَكُمْ رَسُولٌ أَمِينٌۭ ١٧٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (179, 26, 374, 'ﳁ ﳂ ﳃ ﳄ', 'فَٱتَّقُوا۟ ٱللَّهَ وَأَطِيعُونِ ١٧٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (180, 26, 374, 'ﳅ ﳆ ﳇ ﳈ ﳉﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ', 'وَمَآ أَسْـَٔلُكُمْ عَلَيْهِ مِنْ أَجْرٍ ۖ إِنْ أَجْرِىَ إِلَّا عَلَىٰ رَبِّ ٱلْعَـٰلَمِينَ ١٨٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (181, 26, 374, 'ﳒﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ', '۞ أَوْفُوا۟ ٱلْكَيْلَ وَلَا تَكُونُوا۟ مِنَ ٱلْمُخْسِرِينَ ١٨١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (182, 26, 374, 'ﳚ ﳛ ﳜ ﳝ', 'وَزِنُوا۟ بِٱلْقِسْطَاسِ ٱلْمُسْتَقِيمِ ١٨٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (183, 26, 374, 'ﳞ ﳟ ﳠ ﳡ ﳢ ﳣ ﳤ ﳥ ﳦ ﳧ', 'وَلَا تَبْخَسُوا۟ ٱلنَّاسَ أَشْيَآءَهُمْ وَلَا تَعْثَوْا۟ فِى ٱلْأَرْضِ مُفْسِدِينَ ١٨٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (184, 26, 375, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ', 'وَٱتَّقُوا۟ ٱلَّذِى خَلَقَكُمْ وَٱلْجِبِلَّةَ ٱلْأَوَّلِينَ ١٨٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (185, 26, 375, 'ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ', 'قَالُوٓا۟ إِنَّمَآ أَنتَ مِنَ ٱلْمُسَحَّرِينَ ١٨٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (186, 26, 375, 'ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ', 'وَمَآ أَنتَ إِلَّا بَشَرٌۭ مِّثْلُنَا وَإِن نَّظُنُّكَ لَمِنَ ٱلْكَـٰذِبِينَ ١٨٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (187, 26, 375, 'ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ', 'فَأَسْقِطْ عَلَيْنَا كِسَفًۭا مِّنَ ٱلسَّمَآءِ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ ١٨٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (188, 26, 375, 'ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ', 'قَالَ رَبِّىٓ أَعْلَمُ بِمَا تَعْمَلُونَ ١٨٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (189, 26, 375, 'ﱧ ﱨ ﱩ ﱪ ﱫﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ', 'فَكَذَّبُوهُ فَأَخَذَهُمْ عَذَابُ يَوْمِ ٱلظُّلَّةِ ۚ إِنَّهُۥ كَانَ عَذَابَ يَوْمٍ عَظِيمٍ ١٨٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (190, 26, 375, 'ﱳ ﱴ ﱵ ﱶﱷ ﱸ ﱹ ﱺ ﱻ ﱼ', 'إِنَّ فِى ذَٰلِكَ لَـَٔايَةًۭ ۖ وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ ١٩٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (191, 26, 375, 'ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ', 'وَإِنَّ رَبَّكَ لَهُوَ ٱلْعَزِيزُ ٱلرَّحِيمُ ١٩١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (192, 26, 375, 'ﲃ ﲄ ﲅ ﲆ ﲇ', 'وَإِنَّهُۥ لَتَنزِيلُ رَبِّ ٱلْعَـٰلَمِينَ ١٩٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (193, 26, 375, 'ﲈ ﲉ ﲊ ﲋ ﲌ', 'نَزَلَ بِهِ ٱلرُّوحُ ٱلْأَمِينُ ١٩٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (194, 26, 375, 'ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ', 'عَلَىٰ قَلْبِكَ لِتَكُونَ مِنَ ٱلْمُنذِرِينَ ١٩٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (195, 26, 375, 'ﲓ ﲔ ﲕ ﲖ', 'بِلِسَانٍ عَرَبِىٍّۢ مُّبِينٍۢ ١٩٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (196, 26, 375, 'ﲗ ﲘ ﲙ ﲚ ﲛ', 'وَإِنَّهُۥ لَفِى زُبُرِ ٱلْأَوَّلِينَ ١٩٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (197, 26, 375, 'ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ', 'أَوَلَمْ يَكُن لَّهُمْ ءَايَةً أَن يَعْلَمَهُۥ عُلَمَـٰٓؤُا۟ بَنِىٓ إِسْرَٰٓءِيلَ ١٩٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (198, 26, 375, 'ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ', 'وَلَوْ نَزَّلْنَـٰهُ عَلَىٰ بَعْضِ ٱلْأَعْجَمِينَ ١٩٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (199, 26, 375, 'ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ', 'فَقَرَأَهُۥ عَلَيْهِم مَّا كَانُوا۟ بِهِۦ مُؤْمِنِينَ ١٩٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (200, 26, 375, 'ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ', 'كَذَٰلِكَ سَلَكْنَـٰهُ فِى قُلُوبِ ٱلْمُجْرِمِينَ ٢٠٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (201, 26, 375, 'ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ', 'لَا يُؤْمِنُونَ بِهِۦ حَتَّىٰ يَرَوُا۟ ٱلْعَذَابَ ٱلْأَلِيمَ ٢٠١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (202, 26, 375, 'ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ', 'فَيَأْتِيَهُم بَغْتَةًۭ وَهُمْ لَا يَشْعُرُونَ ٢٠٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (203, 26, 375, 'ﳇ ﳈ ﳉ ﳊ ﳋ', 'فَيَقُولُوا۟ هَلْ نَحْنُ مُنظَرُونَ ٢٠٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (204, 26, 375, 'ﳌ ﳍ ﳎ', 'أَفَبِعَذَابِنَا يَسْتَعْجِلُونَ ٢٠٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (205, 26, 375, 'ﳏ ﳐ ﳑ ﳒ ﳓ', 'أَفَرَءَيْتَ إِن مَّتَّعْنَـٰهُمْ سِنِينَ ٢٠٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (206, 26, 375, 'ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ', 'ثُمَّ جَآءَهُم مَّا كَانُوا۟ يُوعَدُونَ ٢٠٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (207, 26, 376, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ', 'مَآ أَغْنَىٰ عَنْهُم مَّا كَانُوا۟ يُمَتَّعُونَ ٢٠٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (208, 26, 376, 'ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ', 'وَمَآ أَهْلَكْنَا مِن قَرْيَةٍ إِلَّا لَهَا مُنذِرُونَ ٢٠٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (209, 26, 376, 'ﱐ ﱑ ﱒ ﱓ ﱔ', 'ذِكْرَىٰ وَمَا كُنَّا ظَـٰلِمِينَ ٢٠٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (210, 26, 376, 'ﱕ ﱖ ﱗ ﱘ ﱙ', 'وَمَا تَنَزَّلَتْ بِهِ ٱلشَّيَـٰطِينُ ٢١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (211, 26, 376, 'ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ', 'وَمَا يَنۢبَغِى لَهُمْ وَمَا يَسْتَطِيعُونَ ٢١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (212, 26, 376, 'ﱠ ﱡ ﱢ ﱣ ﱤ', 'إِنَّهُمْ عَنِ ٱلسَّمْعِ لَمَعْزُولُونَ ٢١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (213, 26, 376, 'ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ', 'فَلَا تَدْعُ مَعَ ٱللَّهِ إِلَـٰهًا ءَاخَرَ فَتَكُونَ مِنَ ٱلْمُعَذَّبِينَ ٢١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (214, 26, 376, 'ﱯ ﱰ ﱱ ﱲ', 'وَأَنذِرْ عَشِيرَتَكَ ٱلْأَقْرَبِينَ ٢١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (215, 26, 376, 'ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ', 'وَٱخْفِضْ جَنَاحَكَ لِمَنِ ٱتَّبَعَكَ مِنَ ٱلْمُؤْمِنِينَ ٢١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (216, 26, 376, 'ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ', 'فَإِنْ عَصَوْكَ فَقُلْ إِنِّى بَرِىٓءٌۭ مِّمَّا تَعْمَلُونَ ٢١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (217, 26, 376, 'ﲂ ﲃ ﲄ ﲅ ﲆ', 'وَتَوَكَّلْ عَلَى ٱلْعَزِيزِ ٱلرَّحِيمِ ٢١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (218, 26, 376, 'ﲇ ﲈ ﲉ ﲊ ﲋ', 'ٱلَّذِى يَرَىٰكَ حِينَ تَقُومُ ٢١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (219, 26, 376, 'ﲌ ﲍ ﲎ ﲏ', 'وَتَقَلُّبَكَ فِى ٱلسَّـٰجِدِينَ ٢١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (220, 26, 376, 'ﲐ ﲑ ﲒ ﲓ ﲔ', 'إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلْعَلِيمُ ٢٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (221, 26, 376, 'ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ', 'هَلْ أُنَبِّئُكُمْ عَلَىٰ مَن تَنَزَّلُ ٱلشَّيَـٰطِينُ ٢٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (222, 26, 376, 'ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ', 'تَنَزَّلُ عَلَىٰ كُلِّ أَفَّاكٍ أَثِيمٍۢ ٢٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (223, 26, 376, 'ﲢ ﲣ ﲤ ﲥ ﲦ', 'يُلْقُونَ ٱلسَّمْعَ وَأَكْثَرُهُمْ كَـٰذِبُونَ ٢٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (224, 26, 376, 'ﲧ ﲨ ﲩ ﲪ', 'وَٱلشُّعَرَآءُ يَتَّبِعُهُمُ ٱلْغَاوُۥنَ ٢٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (225, 26, 376, 'ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ', 'أَلَمْ تَرَ أَنَّهُمْ فِى كُلِّ وَادٍۢ يَهِيمُونَ ٢٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (226, 26, 376, 'ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ', 'وَأَنَّهُمْ يَقُولُونَ مَا لَا يَفْعَلُونَ ٢٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (227, 26, 376, 'ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ', 'إِلَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ وَذَكَرُوا۟ ٱللَّهَ كَثِيرًۭا وَٱنتَصَرُوا۟ مِنۢ بَعْدِ مَا ظُلِمُوا۟ ۗ وَسَيَعْلَمُ ٱلَّذِينَ ظَلَمُوٓا۟ أَىَّ مُنقَلَبٍۢ يَنقَلِبُونَ ٢٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 27, 377, 'ﱁﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ', 'طسٓ ۚ تِلْكَ ءَايَـٰتُ ٱلْقُرْءَانِ وَكِتَابٍۢ مُّبِينٍ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 27, 377, 'ﱉ ﱊ ﱋ ﱌ', 'هُدًۭى وَبُشْرَىٰ لِلْمُؤْمِنِينَ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 27, 377, 'ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ', 'ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤْتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلْـَٔاخِرَةِ هُمْ يُوقِنُونَ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 27, 377, 'ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ', 'إِنَّ ٱلَّذِينَ لَا يُؤْمِنُونَ بِٱلْـَٔاخِرَةِ زَيَّنَّا لَهُمْ أَعْمَـٰلَهُمْ فَهُمْ يَعْمَهُونَ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 27, 377, 'ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ', 'أُو۟لَـٰٓئِكَ ٱلَّذِينَ لَهُمْ سُوٓءُ ٱلْعَذَابِ وَهُمْ فِى ٱلْـَٔاخِرَةِ هُمُ ٱلْأَخْسَرُونَ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 27, 377, 'ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ', 'وَإِنَّكَ لَتُلَقَّى ٱلْقُرْءَانَ مِن لَّدُنْ حَكِيمٍ عَلِيمٍ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 27, 377, 'ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ', 'إِذْ قَالَ مُوسَىٰ لِأَهْلِهِۦٓ إِنِّىٓ ءَانَسْتُ نَارًۭا سَـَٔاتِيكُم مِّنْهَا بِخَبَرٍ أَوْ ءَاتِيكُم بِشِهَابٍۢ قَبَسٍۢ لَّعَلَّكُمْ تَصْطَلُونَ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 27, 377, 'ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ', 'فَلَمَّا جَآءَهَا نُودِىَ أَنۢ بُورِكَ مَن فِى ٱلنَّارِ وَمَنْ حَوْلَهَا وَسُبْحَـٰنَ ٱللَّهِ رَبِّ ٱلْعَـٰلَمِينَ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 27, 377, 'ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ', 'يَـٰمُوسَىٰٓ إِنَّهُۥٓ أَنَا ٱللَّهُ ٱلْعَزِيزُ ٱلْحَكِيمُ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 27, 377, 'ﲜ ﲝﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ', 'وَأَلْقِ عَصَاكَ ۚ فَلَمَّا رَءَاهَا تَهْتَزُّ كَأَنَّهَا جَآنٌّۭ وَلَّىٰ مُدْبِرًۭا وَلَمْ يُعَقِّبْ ۚ يَـٰمُوسَىٰ لَا تَخَفْ إِنِّى لَا يَخَافُ لَدَىَّ ٱلْمُرْسَلُونَ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 27, 377, 'ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ', 'إِلَّا مَن ظَلَمَ ثُمَّ بَدَّلَ حُسْنًۢا بَعْدَ سُوٓءٍۢ فَإِنِّى غَفُورٌۭ رَّحِيمٌۭ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 27, 377, 'ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍﳎ ﳏ ﳐ ﳑ ﳒ ﳓ', 'وَأَدْخِلْ يَدَكَ فِى جَيْبِكَ تَخْرُجْ بَيْضَآءَ مِنْ غَيْرِ سُوٓءٍۢ ۖ فِى تِسْعِ ءَايَـٰتٍ إِلَىٰ فِرْعَوْنَ وَقَوْمِهِۦٓ ۚ إِنَّهُمْ كَانُوا۟ قَوْمًۭا فَـٰسِقِينَ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 27, 377, 'ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ', 'فَلَمَّا جَآءَتْهُمْ ءَايَـٰتُنَا مُبْصِرَةًۭ قَالُوا۟ هَـٰذَا سِحْرٌۭ مُّبِينٌۭ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 27, 378, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ', 'وَجَحَدُوا۟ بِهَا وَٱسْتَيْقَنَتْهَآ أَنفُسُهُمْ ظُلْمًۭا وَعُلُوًّۭا ۚ فَٱنظُرْ كَيْفَ كَانَ عَـٰقِبَةُ ٱلْمُفْسِدِينَ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 27, 378, 'ﱎ ﱏ ﱐ ﱑ ﱒﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ', 'وَلَقَدْ ءَاتَيْنَا دَاوُۥدَ وَسُلَيْمَـٰنَ عِلْمًۭا ۖ وَقَالَا ٱلْحَمْدُ لِلَّهِ ٱلَّذِى فَضَّلَنَا عَلَىٰ كَثِيرٍۢ مِّنْ عِبَادِهِ ٱلْمُؤْمِنِينَ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 27, 378, 'ﱟ ﱠ ﱡﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ', 'وَوَرِثَ سُلَيْمَـٰنُ دَاوُۥدَ ۖ وَقَالَ يَـٰٓأَيُّهَا ٱلنَّاسُ عُلِّمْنَا مَنطِقَ ٱلطَّيْرِ وَأُوتِينَا مِن كُلِّ شَىْءٍ ۖ إِنَّ هَـٰذَا لَهُوَ ٱلْفَضْلُ ٱلْمُبِينُ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 27, 378, 'ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ', 'وَحُشِرَ لِسُلَيْمَـٰنَ جُنُودُهُۥ مِنَ ٱلْجِنِّ وَٱلْإِنسِ وَٱلطَّيْرِ فَهُمْ يُوزَعُونَ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 27, 378, 'ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ', 'حَتَّىٰٓ إِذَآ أَتَوْا۟ عَلَىٰ وَادِ ٱلنَّمْلِ قَالَتْ نَمْلَةٌۭ يَـٰٓأَيُّهَا ٱلنَّمْلُ ٱدْخُلُوا۟ مَسَـٰكِنَكُمْ لَا يَحْطِمَنَّكُمْ سُلَيْمَـٰنُ وَجُنُودُهُۥ وَهُمْ لَا يَشْعُرُونَ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 27, 378, 'ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ', 'فَتَبَسَّمَ ضَاحِكًۭا مِّن قَوْلِهَا وَقَالَ رَبِّ أَوْزِعْنِىٓ أَنْ أَشْكُرَ نِعْمَتَكَ ٱلَّتِىٓ أَنْعَمْتَ عَلَىَّ وَعَلَىٰ وَٰلِدَىَّ وَأَنْ أَعْمَلَ صَـٰلِحًۭا تَرْضَىٰهُ وَأَدْخِلْنِى بِرَحْمَتِكَ فِى عِبَادِكَ ٱلصَّـٰلِحِينَ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 27, 378, 'ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ', 'وَتَفَقَّدَ ٱلطَّيْرَ فَقَالَ مَا لِىَ لَآ أَرَى ٱلْهُدْهُدَ أَمْ كَانَ مِنَ ٱلْغَآئِبِينَ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 27, 378, 'ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ', 'لَأُعَذِّبَنَّهُۥ عَذَابًۭا شَدِيدًا أَوْ لَأَا۟ذْبَحَنَّهُۥٓ أَوْ لَيَأْتِيَنِّى بِسُلْطَـٰنٍۢ مُّبِينٍۢ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 27, 378, 'ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ', 'فَمَكَثَ غَيْرَ بَعِيدٍۢ فَقَالَ أَحَطتُ بِمَا لَمْ تُحِطْ بِهِۦ وَجِئْتُكَ مِن سَبَإٍۭ بِنَبَإٍۢ يَقِينٍ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 27, 379, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ', 'إِنِّى وَجَدتُّ ٱمْرَأَةًۭ تَمْلِكُهُمْ وَأُوتِيَتْ مِن كُلِّ شَىْءٍۢ وَلَهَا عَرْشٌ عَظِيمٌۭ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 27, 379, 'ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ', 'وَجَدتُّهَا وَقَوْمَهَا يَسْجُدُونَ لِلشَّمْسِ مِن دُونِ ٱللَّهِ وَزَيَّنَ لَهُمُ ٱلشَّيْطَـٰنُ أَعْمَـٰلَهُمْ فَصَدَّهُمْ عَنِ ٱلسَّبِيلِ فَهُمْ لَا يَهْتَدُونَ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 27, 379, 'ﱟﱠ ﱡﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ', 'أَلَّا يَسْجُدُوا۟ لِلَّهِ ٱلَّذِى يُخْرِجُ ٱلْخَبْءَ فِى ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ وَيَعْلَمُ مَا تُخْفُونَ وَمَا تُعْلِنُونَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 27, 379, 'ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷﱸ ﱹ', 'ٱللَّهُ لَآ إِلَـٰهَ إِلَّا هُوَ رَبُّ ٱلْعَرْشِ ٱلْعَظِيمِ ۩‏ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 27, 379, 'ﱺﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ', '۞ قَالَ سَنَنظُرُ أَصَدَقْتَ أَمْ كُنتَ مِنَ ٱلْكَـٰذِبِينَ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 27, 379, 'ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ', 'ٱذْهَب بِّكِتَـٰبِى هَـٰذَا فَأَلْقِهْ إِلَيْهِمْ ثُمَّ تَوَلَّ عَنْهُمْ فَٱنظُرْ مَاذَا يَرْجِعُونَ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 27, 379, 'ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ', 'قَالَتْ يَـٰٓأَيُّهَا ٱلْمَلَؤُا۟ إِنِّىٓ أُلْقِىَ إِلَىَّ كِتَـٰبٌۭ كَرِيمٌ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 27, 379, 'ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ', 'إِنَّهُۥ مِن سُلَيْمَـٰنَ وَإِنَّهُۥ بِسْمِ ٱللَّهِ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 27, 379, 'ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ', 'أَلَّا تَعْلُوا۟ عَلَىَّ وَأْتُونِى مُسْلِمِينَ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 27, 379, 'ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ', 'قَالَتْ يَـٰٓأَيُّهَا ٱلْمَلَؤُا۟ أَفْتُونِى فِىٓ أَمْرِى مَا كُنتُ قَاطِعَةً أَمْرًا حَتَّىٰ تَشْهَدُونِ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 27, 379, 'ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ', 'قَالُوا۟ نَحْنُ أُو۟لُوا۟ قُوَّةٍۢ وَأُو۟لُوا۟ بَأْسٍۢ شَدِيدٍۢ وَٱلْأَمْرُ إِلَيْكِ فَٱنظُرِى مَاذَا تَأْمُرِينَ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 27, 379, 'ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋﳌ ﳍ ﳎ ﳏ', 'قَالَتْ إِنَّ ٱلْمُلُوكَ إِذَا دَخَلُوا۟ قَرْيَةً أَفْسَدُوهَا وَجَعَلُوٓا۟ أَعِزَّةَ أَهْلِهَآ أَذِلَّةًۭ ۖ وَكَذَٰلِكَ يَفْعَلُونَ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 27, 379, 'ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ', 'وَإِنِّى مُرْسِلَةٌ إِلَيْهِم بِهَدِيَّةٍۢ فَنَاظِرَةٌۢ بِمَ يَرْجِعُ ٱلْمُرْسَلُونَ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 27, 380, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌﱍ ﱎ ﱏ ﱐ ﱑ ﱒ', 'فَلَمَّا جَآءَ سُلَيْمَـٰنَ قَالَ أَتُمِدُّونَنِ بِمَالٍۢ فَمَآ ءَاتَىٰنِۦَ ٱللَّهُ خَيْرٌۭ مِّمَّآ ءَاتَىٰكُم بَلْ أَنتُم بِهَدِيَّتِكُمْ تَفْرَحُونَ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 27, 380, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ', 'ٱرْجِعْ إِلَيْهِمْ فَلَنَأْتِيَنَّهُم بِجُنُودٍۢ لَّا قِبَلَ لَهُم بِهَا وَلَنُخْرِجَنَّهُم مِّنْهَآ أَذِلَّةًۭ وَهُمْ صَـٰغِرُونَ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 27, 380, 'ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ', 'قَالَ يَـٰٓأَيُّهَا ٱلْمَلَؤُا۟ أَيُّكُمْ يَأْتِينِى بِعَرْشِهَا قَبْلَ أَن يَأْتُونِى مُسْلِمِينَ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 27, 380, 'ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷﱸ ﱹ ﱺ ﱻ ﱼ ﱽ', 'قَالَ عِفْرِيتٌۭ مِّنَ ٱلْجِنِّ أَنَا۠ ءَاتِيكَ بِهِۦ قَبْلَ أَن تَقُومَ مِن مَّقَامِكَ ۖ وَإِنِّى عَلَيْهِ لَقَوِىٌّ أَمِينٌۭ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 27, 380, 'ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙﲚ ﲛ ﲜ ﲝ ﲞ ﲟﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ', 'قَالَ ٱلَّذِى عِندَهُۥ عِلْمٌۭ مِّنَ ٱلْكِتَـٰبِ أَنَا۠ ءَاتِيكَ بِهِۦ قَبْلَ أَن يَرْتَدَّ إِلَيْكَ طَرْفُكَ ۚ فَلَمَّا رَءَاهُ مُسْتَقِرًّا عِندَهُۥ قَالَ هَـٰذَا مِن فَضْلِ رَبِّى لِيَبْلُوَنِىٓ ءَأَشْكُرُ أَمْ أَكْفُرُ ۖ وَمَن شَكَرَ فَإِنَّمَا يَشْكُرُ لِنَفْسِهِۦ ۖ وَمَن كَفَرَ فَإِنَّ رَبِّى غَنِىٌّۭ كَرِيمٌۭ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 27, 380, 'ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ', 'قَالَ نَكِّرُوا۟ لَهَا عَرْشَهَا نَنظُرْ أَتَهْتَدِىٓ أَمْ تَكُونُ مِنَ ٱلَّذِينَ لَا يَهْتَدُونَ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 27, 380, 'ﲵ ﲶ ﲷ ﲸ ﲹﲺ ﲻ ﲼ ﲽﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ', 'فَلَمَّا جَآءَتْ قِيلَ أَهَـٰكَذَا عَرْشُكِ ۖ قَالَتْ كَأَنَّهُۥ هُوَ ۚ وَأُوتِينَا ٱلْعِلْمَ مِن قَبْلِهَا وَكُنَّا مُسْلِمِينَ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 27, 380, 'ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ', 'وَصَدَّهَا مَا كَانَت تَّعْبُدُ مِن دُونِ ٱللَّهِ ۖ إِنَّهَا كَانَتْ مِن قَوْمٍۢ كَـٰفِرِينَ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 27, 380, 'ﳔ ﳕ ﳖ ﳗﳘ ﳙ ﳚ ﳛ ﳜ ﳝ ﳞ ﳟﳠ ﳡ ﳢ ﳣ ﳤ ﳥ ﳦﳧ ﳨ ﳩ ﳪ ﳫ ﳬ ﳭ ﳮ ﳯ ﳰ ﳱ ﳲ ﳳ', 'قِيلَ لَهَا ٱدْخُلِى ٱلصَّرْحَ ۖ فَلَمَّا رَأَتْهُ حَسِبَتْهُ لُجَّةًۭ وَكَشَفَتْ عَن سَاقَيْهَا ۚ قَالَ إِنَّهُۥ صَرْحٌۭ مُّمَرَّدٌۭ مِّن قَوَارِيرَ ۗ قَالَتْ رَبِّ إِنِّى ظَلَمْتُ نَفْسِى وَأَسْلَمْتُ مَعَ سُلَيْمَـٰنَ لِلَّهِ رَبِّ ٱلْعَـٰلَمِينَ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 27, 381, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ', 'وَلَقَدْ أَرْسَلْنَآ إِلَىٰ ثَمُودَ أَخَاهُمْ صَـٰلِحًا أَنِ ٱعْبُدُوا۟ ٱللَّهَ فَإِذَا هُمْ فَرِيقَانِ يَخْتَصِمُونَ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 27, 381, 'ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ', 'قَالَ يَـٰقَوْمِ لِمَ تَسْتَعْجِلُونَ بِٱلسَّيِّئَةِ قَبْلَ ٱلْحَسَنَةِ ۖ لَوْلَا تَسْتَغْفِرُونَ ٱللَّهَ لَعَلَّكُمْ تُرْحَمُونَ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 27, 381, 'ﱝ ﱞ ﱟ ﱠ ﱡﱢ ﱣ ﱤ ﱥ ﱦﱧ ﱨ ﱩ ﱪ ﱫ ﱬ', 'قَالُوا۟ ٱطَّيَّرْنَا بِكَ وَبِمَن مَّعَكَ ۚ قَالَ طَـٰٓئِرُكُمْ عِندَ ٱللَّهِ ۖ بَلْ أَنتُمْ قَوْمٌۭ تُفْتَنُونَ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 27, 381, 'ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ', 'وَكَانَ فِى ٱلْمَدِينَةِ تِسْعَةُ رَهْطٍۢ يُفْسِدُونَ فِى ٱلْأَرْضِ وَلَا يُصْلِحُونَ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 27, 381, 'ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ', 'قَالُوا۟ تَقَاسَمُوا۟ بِٱللَّهِ لَنُبَيِّتَنَّهُۥ وَأَهْلَهُۥ ثُمَّ لَنَقُولَنَّ لِوَلِيِّهِۦ مَا شَهِدْنَا مَهْلِكَ أَهْلِهِۦ وَإِنَّا لَصَـٰدِقُونَ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 27, 381, 'ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ', 'وَمَكَرُوا۟ مَكْرًۭا وَمَكَرْنَا مَكْرًۭا وَهُمْ لَا يَشْعُرُونَ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 27, 381, 'ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ', 'فَٱنظُرْ كَيْفَ كَانَ عَـٰقِبَةُ مَكْرِهِمْ أَنَّا دَمَّرْنَـٰهُمْ وَقَوْمَهُمْ أَجْمَعِينَ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 27, 381, 'ﲙ ﲚ ﲛ ﲜ ﲝﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ', 'فَتِلْكَ بُيُوتُهُمْ خَاوِيَةًۢ بِمَا ظَلَمُوٓا۟ ۗ إِنَّ فِى ذَٰلِكَ لَـَٔايَةًۭ لِّقَوْمٍۢ يَعْلَمُونَ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 27, 381, 'ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ', 'وَأَنجَيْنَا ٱلَّذِينَ ءَامَنُوا۟ وَكَانُوا۟ يَتَّقُونَ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 27, 381, 'ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ', 'وَلُوطًا إِذْ قَالَ لِقَوْمِهِۦٓ أَتَأْتُونَ ٱلْفَـٰحِشَةَ وَأَنتُمْ تُبْصِرُونَ ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (55, 27, 381, 'ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻﲼ ﲽ ﲾ ﲿ ﳀ ﳁ', 'أَئِنَّكُمْ لَتَأْتُونَ ٱلرِّجَالَ شَهْوَةًۭ مِّن دُونِ ٱلنِّسَآءِ ۚ بَلْ أَنتُمْ قَوْمٌۭ تَجْهَلُونَ ٥٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (56, 27, 382, 'ﱁﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍﱎ ﱏ ﱐ ﱑ ﱒ', '۞ فَمَا كَانَ جَوَابَ قَوْمِهِۦٓ إِلَّآ أَن قَالُوٓا۟ أَخْرِجُوٓا۟ ءَالَ لُوطٍۢ مِّن قَرْيَتِكُمْ ۖ إِنَّهُمْ أُنَاسٌۭ يَتَطَهَّرُونَ ٥٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (57, 27, 382, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ', 'فَأَنجَيْنَـٰهُ وَأَهْلَهُۥٓ إِلَّا ٱمْرَأَتَهُۥ قَدَّرْنَـٰهَا مِنَ ٱلْغَـٰبِرِينَ ٥٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (58, 27, 382, 'ﱛ ﱜ ﱝﱞ ﱟ ﱠ ﱡ ﱢ', 'وَأَمْطَرْنَا عَلَيْهِم مَّطَرًۭا ۖ فَسَآءَ مَطَرُ ٱلْمُنذَرِينَ ٥٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (59, 27, 382, 'ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪﱫ ﱬ ﱭ ﱮ ﱯ ﱰ', 'قُلِ ٱلْحَمْدُ لِلَّهِ وَسَلَـٰمٌ عَلَىٰ عِبَادِهِ ٱلَّذِينَ ٱصْطَفَىٰٓ ۗ ءَآللَّهُ خَيْرٌ أَمَّا يُشْرِكُونَ ٥٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (60, 27, 382, 'ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄﲅ ﲆ ﲇ ﲈﲉ ﲊ ﲋ ﲌ ﲍ ﲎ', 'أَمَّنْ خَلَقَ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ وَأَنزَلَ لَكُم مِّنَ ٱلسَّمَآءِ مَآءًۭ فَأَنۢبَتْنَا بِهِۦ حَدَآئِقَ ذَاتَ بَهْجَةٍۢ مَّا كَانَ لَكُمْ أَن تُنۢبِتُوا۟ شَجَرَهَآ ۗ أَءِلَـٰهٌۭ مَّعَ ٱللَّهِ ۚ بَلْ هُمْ قَوْمٌۭ يَعْدِلُونَ ٦٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (61, 27, 382, 'ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜﲝ ﲞ ﲟ ﲠﲡ ﲢ ﲣ ﲤ ﲥ ﲦ', 'أَمَّن جَعَلَ ٱلْأَرْضَ قَرَارًۭا وَجَعَلَ خِلَـٰلَهَآ أَنْهَـٰرًۭا وَجَعَلَ لَهَا رَوَٰسِىَ وَجَعَلَ بَيْنَ ٱلْبَحْرَيْنِ حَاجِزًا ۗ أَءِلَـٰهٌۭ مَّعَ ٱللَّهِ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ ٦١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (62, 27, 382, 'ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰﲱ ﲲ ﲳ ﲴﲵ ﲶ ﲷ ﲸ ﲹ', 'أَمَّن يُجِيبُ ٱلْمُضْطَرَّ إِذَا دَعَاهُ وَيَكْشِفُ ٱلسُّوٓءَ وَيَجْعَلُكُمْ خُلَفَآءَ ٱلْأَرْضِ ۗ أَءِلَـٰهٌۭ مَّعَ ٱللَّهِ ۚ قَلِيلًۭا مَّا تَذَكَّرُونَ ٦٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (63, 27, 382, 'ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆﳇ ﳈ ﳉ ﳊﳋ ﳌ ﳍ ﳎ ﳏ ﳐ', 'أَمَّن يَهْدِيكُمْ فِى ظُلُمَـٰتِ ٱلْبَرِّ وَٱلْبَحْرِ وَمَن يُرْسِلُ ٱلرِّيَـٰحَ بُشْرًۢا بَيْنَ يَدَىْ رَحْمَتِهِۦٓ ۗ أَءِلَـٰهٌۭ مَّعَ ٱللَّهِ ۚ تَعَـٰلَى ٱللَّهُ عَمَّا يُشْرِكُونَ ٦٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (64, 27, 383, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊﱋ ﱌ ﱍ ﱎﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ', 'أَمَّن يَبْدَؤُا۟ ٱلْخَلْقَ ثُمَّ يُعِيدُهُۥ وَمَن يَرْزُقُكُم مِّنَ ٱلسَّمَآءِ وَٱلْأَرْضِ ۗ أَءِلَـٰهٌۭ مَّعَ ٱللَّهِ ۚ قُلْ هَاتُوا۟ بُرْهَـٰنَكُمْ إِن كُنتُمْ صَـٰدِقِينَ ٦٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (65, 27, 383, 'ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠﱡ ﱢ ﱣ ﱤ ﱥ ﱦ', 'قُل لَّا يَعْلَمُ مَن فِى ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ٱلْغَيْبَ إِلَّا ٱللَّهُ ۚ وَمَا يَشْعُرُونَ أَيَّانَ يُبْعَثُونَ ٦٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (66, 27, 383, 'ﱧ ﱨ ﱩ ﱪ ﱫﱬ ﱭ ﱮ ﱯ ﱰ ﱱﱲ ﱳ ﱴ ﱵ ﱶ ﱷ', 'بَلِ ٱدَّٰرَكَ عِلْمُهُمْ فِى ٱلْـَٔاخِرَةِ ۚ بَلْ هُمْ فِى شَكٍّۢ مِّنْهَا ۖ بَلْ هُم مِّنْهَا عَمُونَ ٦٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (67, 27, 383, 'ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ', 'وَقَالَ ٱلَّذِينَ كَفَرُوٓا۟ أَءِذَا كُنَّا تُرَٰبًۭا وَءَابَآؤُنَآ أَئِنَّا لَمُخْرَجُونَ ٦٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (68, 27, 383, 'ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ', 'لَقَدْ وُعِدْنَا هَـٰذَا نَحْنُ وَءَابَآؤُنَا مِن قَبْلُ إِنْ هَـٰذَآ إِلَّآ أَسَـٰطِيرُ ٱلْأَوَّلِينَ ٦٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (69, 27, 383, 'ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ', 'قُلْ سِيرُوا۟ فِى ٱلْأَرْضِ فَٱنظُرُوا۟ كَيْفَ كَانَ عَـٰقِبَةُ ٱلْمُجْرِمِينَ ٦٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (70, 27, 383, 'ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ', 'وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُن فِى ضَيْقٍۢ مِّمَّا يَمْكُرُونَ ٧٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (71, 27, 383, 'ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ', 'وَيَقُولُونَ مَتَىٰ هَـٰذَا ٱلْوَعْدُ إِن كُنتُمْ صَـٰدِقِينَ ٧١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (72, 27, 383, 'ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ', 'قُلْ عَسَىٰٓ أَن يَكُونَ رَدِفَ لَكُم بَعْضُ ٱلَّذِى تَسْتَعْجِلُونَ ٧٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (73, 27, 383, 'ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ', 'وَإِنَّ رَبَّكَ لَذُو فَضْلٍ عَلَى ٱلنَّاسِ وَلَـٰكِنَّ أَكْثَرَهُمْ لَا يَشْكُرُونَ ٧٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (74, 27, 383, 'ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ', 'وَإِنَّ رَبَّكَ لَيَعْلَمُ مَا تُكِنُّ صُدُورُهُمْ وَمَا يُعْلِنُونَ ٧٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (75, 27, 383, 'ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ', 'وَمَا مِنْ غَآئِبَةٍۢ فِى ٱلسَّمَآءِ وَٱلْأَرْضِ إِلَّا فِى كِتَـٰبٍۢ مُّبِينٍ ٧٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (76, 27, 383, 'ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ', 'إِنَّ هَـٰذَا ٱلْقُرْءَانَ يَقُصُّ عَلَىٰ بَنِىٓ إِسْرَٰٓءِيلَ أَكْثَرَ ٱلَّذِى هُمْ فِيهِ يَخْتَلِفُونَ ٧٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (77, 27, 384, 'ﱁ ﱂ ﱃ ﱄ ﱅ', 'وَإِنَّهُۥ لَهُدًۭى وَرَحْمَةٌۭ لِّلْمُؤْمِنِينَ ٧٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (78, 27, 384, 'ﱆ ﱇ ﱈ ﱉ ﱊﱋ ﱌ ﱍ ﱎ ﱏ', 'إِنَّ رَبَّكَ يَقْضِى بَيْنَهُم بِحُكْمِهِۦ ۚ وَهُوَ ٱلْعَزِيزُ ٱلْعَلِيمُ ٧٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (79, 27, 384, 'ﱐ ﱑ ﱒﱓ ﱔ ﱕ ﱖ ﱗ ﱘ', 'فَتَوَكَّلْ عَلَى ٱللَّهِ ۖ إِنَّكَ عَلَى ٱلْحَقِّ ٱلْمُبِينِ ٧٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (80, 27, 384, 'ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ', 'إِنَّكَ لَا تُسْمِعُ ٱلْمَوْتَىٰ وَلَا تُسْمِعُ ٱلصُّمَّ ٱلدُّعَآءَ إِذَا وَلَّوْا۟ مُدْبِرِينَ ٨٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (81, 27, 384, 'ﱥ ﱦ ﱧ ﱨ ﱩ ﱪﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ', 'وَمَآ أَنتَ بِهَـٰدِى ٱلْعُمْىِ عَن ضَلَـٰلَتِهِمْ ۖ إِن تُسْمِعُ إِلَّا مَن يُؤْمِنُ بِـَٔايَـٰتِنَا فَهُم مُّسْلِمُونَ ٨١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (82, 27, 384, 'ﱵﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ', '۞ وَإِذَا وَقَعَ ٱلْقَوْلُ عَلَيْهِمْ أَخْرَجْنَا لَهُمْ دَآبَّةًۭ مِّنَ ٱلْأَرْضِ تُكَلِّمُهُمْ أَنَّ ٱلنَّاسَ كَانُوا۟ بِـَٔايَـٰتِنَا لَا يُوقِنُونَ ٨٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (83, 27, 384, 'ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ', 'وَيَوْمَ نَحْشُرُ مِن كُلِّ أُمَّةٍۢ فَوْجًۭا مِّمَّن يُكَذِّبُ بِـَٔايَـٰتِنَا فَهُمْ يُوزَعُونَ ٨٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (84, 27, 384, 'ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ', 'حَتَّىٰٓ إِذَا جَآءُو قَالَ أَكَذَّبْتُم بِـَٔايَـٰتِى وَلَمْ تُحِيطُوا۟ بِهَا عِلْمًا أَمَّاذَا كُنتُمْ تَعْمَلُونَ ٨٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (85, 27, 384, 'ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ', 'وَوَقَعَ ٱلْقَوْلُ عَلَيْهِم بِمَا ظَلَمُوا۟ فَهُمْ لَا يَنطِقُونَ ٨٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (86, 27, 384, 'ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ', 'أَلَمْ يَرَوْا۟ أَنَّا جَعَلْنَا ٱلَّيْلَ لِيَسْكُنُوا۟ فِيهِ وَٱلنَّهَارَ مُبْصِرًا ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَـٰتٍۢ لِّقَوْمٍۢ يُؤْمِنُونَ ٨٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (87, 27, 384, 'ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉﳊ ﳋ ﳌ ﳍ ﳎ', 'وَيَوْمَ يُنفَخُ فِى ٱلصُّورِ فَفَزِعَ مَن فِى ٱلسَّمَـٰوَٰتِ وَمَن فِى ٱلْأَرْضِ إِلَّا مَن شَآءَ ٱللَّهُ ۚ وَكُلٌّ أَتَوْهُ دَٰخِرِينَ ٨٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (88, 27, 384, 'ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖﳗ ﳘ ﳙ ﳚ ﳛ ﳜ ﳝﳞ ﳟ ﳠ ﳡ ﳢ ﳣ', 'وَتَرَى ٱلْجِبَالَ تَحْسَبُهَا جَامِدَةًۭ وَهِىَ تَمُرُّ مَرَّ ٱلسَّحَابِ ۚ صُنْعَ ٱللَّهِ ٱلَّذِىٓ أَتْقَنَ كُلَّ شَىْءٍ ۚ إِنَّهُۥ خَبِيرٌۢ بِمَا تَفْعَلُونَ ٨٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (89, 27, 385, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ', 'مَن جَآءَ بِٱلْحَسَنَةِ فَلَهُۥ خَيْرٌۭ مِّنْهَا وَهُم مِّن فَزَعٍۢ يَوْمَئِذٍ ءَامِنُونَ ٨٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (90, 27, 385, 'ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ', 'وَمَن جَآءَ بِٱلسَّيِّئَةِ فَكُبَّتْ وُجُوهُهُمْ فِى ٱلنَّارِ هَلْ تُجْزَوْنَ إِلَّا مَا كُنتُمْ تَعْمَلُونَ ٩٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (91, 27, 385, 'ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ', 'إِنَّمَآ أُمِرْتُ أَنْ أَعْبُدَ رَبَّ هَـٰذِهِ ٱلْبَلْدَةِ ٱلَّذِى حَرَّمَهَا وَلَهُۥ كُلُّ شَىْءٍۢ ۖ وَأُمِرْتُ أَنْ أَكُونَ مِنَ ٱلْمُسْلِمِينَ ٩١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (92, 27, 385, 'ﱮ ﱯ ﱰﱱ ﱲ ﱳ ﱴ ﱵ ﱶﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ', 'وَأَنْ أَتْلُوَا۟ ٱلْقُرْءَانَ ۖ فَمَنِ ٱهْتَدَىٰ فَإِنَّمَا يَهْتَدِى لِنَفْسِهِۦ ۖ وَمَن ضَلَّ فَقُلْ إِنَّمَآ أَنَا۠ مِنَ ٱلْمُنذِرِينَ ٩٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (93, 27, 385, 'ﲀ ﲁ ﲂ ﲃ ﲄ ﲅﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ', 'وَقُلِ ٱلْحَمْدُ لِلَّهِ سَيُرِيكُمْ ءَايَـٰتِهِۦ فَتَعْرِفُونَهَا ۚ وَمَا رَبُّكَ بِغَـٰفِلٍ عَمَّا تَعْمَلُونَ ٩٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 28, 385, 'ﲍ ﲎ', 'طسٓمٓ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 28, 385, 'ﲏ ﲐ ﲑ ﲒ ﲓ', 'تِلْكَ ءَايَـٰتُ ٱلْكِتَـٰبِ ٱلْمُبِينِ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 28, 385, 'ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ', 'نَتْلُوا۟ عَلَيْكَ مِن نَّبَإِ مُوسَىٰ وَفِرْعَوْنَ بِٱلْحَقِّ لِقَوْمٍۢ يُؤْمِنُونَ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 28, 385, 'ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬﲭ ﲮ ﲯ ﲰ ﲱ ﲲ', 'إِنَّ فِرْعَوْنَ عَلَا فِى ٱلْأَرْضِ وَجَعَلَ أَهْلَهَا شِيَعًۭا يَسْتَضْعِفُ طَآئِفَةًۭ مِّنْهُمْ يُذَبِّحُ أَبْنَآءَهُمْ وَيَسْتَحْىِۦ نِسَآءَهُمْ ۚ إِنَّهُۥ كَانَ مِنَ ٱلْمُفْسِدِينَ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 28, 385, 'ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ', 'وَنُرِيدُ أَن نَّمُنَّ عَلَى ٱلَّذِينَ ٱسْتُضْعِفُوا۟ فِى ٱلْأَرْضِ وَنَجْعَلَهُمْ أَئِمَّةًۭ وَنَجْعَلَهُمُ ٱلْوَٰرِثِينَ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 28, 386, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ', 'وَنُمَكِّنَ لَهُمْ فِى ٱلْأَرْضِ وَنُرِىَ فِرْعَوْنَ وَهَـٰمَـٰنَ وَجُنُودَهُمَا مِنْهُم مَّا كَانُوا۟ يَحْذَرُونَ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 28, 386, 'ﱎ ﱏ ﱐ ﱑ ﱒ ﱓﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ', 'وَأَوْحَيْنَآ إِلَىٰٓ أُمِّ مُوسَىٰٓ أَنْ أَرْضِعِيهِ ۖ فَإِذَا خِفْتِ عَلَيْهِ فَأَلْقِيهِ فِى ٱلْيَمِّ وَلَا تَخَافِى وَلَا تَحْزَنِىٓ ۖ إِنَّا رَآدُّوهُ إِلَيْكِ وَجَاعِلُوهُ مِنَ ٱلْمُرْسَلِينَ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 28, 386, 'ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ', 'فَٱلْتَقَطَهُۥٓ ءَالُ فِرْعَوْنَ لِيَكُونَ لَهُمْ عَدُوًّۭا وَحَزَنًا ۗ إِنَّ فِرْعَوْنَ وَهَـٰمَـٰنَ وَجُنُودَهُمَا كَانُوا۟ خَـٰطِـِٔينَ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 28, 386, 'ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ', 'وَقَالَتِ ٱمْرَأَتُ فِرْعَوْنَ قُرَّتُ عَيْنٍۢ لِّى وَلَكَ ۖ لَا تَقْتُلُوهُ عَسَىٰٓ أَن يَنفَعَنَآ أَوْ نَتَّخِذَهُۥ وَلَدًۭا وَهُمْ لَا يَشْعُرُونَ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 28, 386, 'ﲊ ﲋ ﲌ ﲍ ﲎﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ', 'وَأَصْبَحَ فُؤَادُ أُمِّ مُوسَىٰ فَـٰرِغًا ۖ إِن كَادَتْ لَتُبْدِى بِهِۦ لَوْلَآ أَن رَّبَطْنَا عَلَىٰ قَلْبِهَا لِتَكُونَ مِنَ ٱلْمُؤْمِنِينَ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 28, 386, 'ﲝ ﲞ ﲟﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ', 'وَقَالَتْ لِأُخْتِهِۦ قُصِّيهِ ۖ فَبَصُرَتْ بِهِۦ عَن جُنُبٍۢ وَهُمْ لَا يَشْعُرُونَ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 28, 386, 'ﲩﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ', '۞ وَحَرَّمْنَا عَلَيْهِ ٱلْمَرَاضِعَ مِن قَبْلُ فَقَالَتْ هَلْ أَدُلُّكُمْ عَلَىٰٓ أَهْلِ بَيْتٍۢ يَكْفُلُونَهُۥ لَكُمْ وَهُمْ لَهُۥ نَـٰصِحُونَ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 28, 386, 'ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ', 'فَرَدَدْنَـٰهُ إِلَىٰٓ أُمِّهِۦ كَىْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ وَلِتَعْلَمَ أَنَّ وَعْدَ ٱللَّهِ حَقٌّۭ وَلَـٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 28, 387, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇﱈ ﱉ ﱊ ﱋ ﱌ', 'وَلَمَّا بَلَغَ أَشُدَّهُۥ وَٱسْتَوَىٰٓ ءَاتَيْنَـٰهُ حُكْمًۭا وَعِلْمًۭا ۚ وَكَذَٰلِكَ نَجْزِى ٱلْمُحْسِنِينَ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 28, 387, 'ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪﱫ ﱬ ﱭ ﱮ ﱯ ﱰﱱ ﱲ ﱳ ﱴ ﱵ ﱶ', 'وَدَخَلَ ٱلْمَدِينَةَ عَلَىٰ حِينِ غَفْلَةٍۢ مِّنْ أَهْلِهَا فَوَجَدَ فِيهَا رَجُلَيْنِ يَقْتَتِلَانِ هَـٰذَا مِن شِيعَتِهِۦ وَهَـٰذَا مِنْ عَدُوِّهِۦ ۖ فَٱسْتَغَـٰثَهُ ٱلَّذِى مِن شِيعَتِهِۦ عَلَى ٱلَّذِى مِنْ عَدُوِّهِۦ فَوَكَزَهُۥ مُوسَىٰ فَقَضَىٰ عَلَيْهِ ۖ قَالَ هَـٰذَا مِنْ عَمَلِ ٱلشَّيْطَـٰنِ ۖ إِنَّهُۥ عَدُوٌّۭ مُّضِلٌّۭ مُّبِينٌۭ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 28, 387, 'ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿﲀ ﲁ ﲂ ﲃ ﲄ ﲅ', 'قَالَ رَبِّ إِنِّى ظَلَمْتُ نَفْسِى فَٱغْفِرْ لِى فَغَفَرَ لَهُۥٓ ۚ إِنَّهُۥ هُوَ ٱلْغَفُورُ ٱلرَّحِيمُ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 28, 387, 'ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ', 'قَالَ رَبِّ بِمَآ أَنْعَمْتَ عَلَىَّ فَلَنْ أَكُونَ ظَهِيرًۭا لِّلْمُجْرِمِينَ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 28, 387, 'ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ', 'فَأَصْبَحَ فِى ٱلْمَدِينَةِ خَآئِفًۭا يَتَرَقَّبُ فَإِذَا ٱلَّذِى ٱسْتَنصَرَهُۥ بِٱلْأَمْسِ يَسْتَصْرِخُهُۥ ۚ قَالَ لَهُۥ مُوسَىٰٓ إِنَّكَ لَغَوِىٌّۭ مُّبِينٌۭ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 28, 387, 'ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ', 'فَلَمَّآ أَنْ أَرَادَ أَن يَبْطِشَ بِٱلَّذِى هُوَ عَدُوٌّۭ لَّهُمَا قَالَ يَـٰمُوسَىٰٓ أَتُرِيدُ أَن تَقْتُلَنِى كَمَا قَتَلْتَ نَفْسًۢا بِٱلْأَمْسِ ۖ إِن تُرِيدُ إِلَّآ أَن تَكُونَ جَبَّارًۭا فِى ٱلْأَرْضِ وَمَا تُرِيدُ أَن تَكُونَ مِنَ ٱلْمُصْلِحِينَ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 28, 387, 'ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ', 'وَجَآءَ رَجُلٌۭ مِّنْ أَقْصَا ٱلْمَدِينَةِ يَسْعَىٰ قَالَ يَـٰمُوسَىٰٓ إِنَّ ٱلْمَلَأَ يَأْتَمِرُونَ بِكَ لِيَقْتُلُوكَ فَٱخْرُجْ إِنِّى لَكَ مِنَ ٱلنَّـٰصِحِينَ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 28, 387, 'ﳗ ﳘ ﳙ ﳚﳛ ﳜ ﳝ ﳞ ﳟ ﳠ ﳡ ﳢ', 'فَخَرَجَ مِنْهَا خَآئِفًۭا يَتَرَقَّبُ ۖ قَالَ رَبِّ نَجِّنِى مِنَ ٱلْقَوْمِ ٱلظَّـٰلِمِينَ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 28, 388, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ', 'وَلَمَّا تَوَجَّهَ تِلْقَآءَ مَدْيَنَ قَالَ عَسَىٰ رَبِّىٓ أَن يَهْدِيَنِى سَوَآءَ ٱلسَّبِيلِ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 28, 388, 'ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛﱜ ﱝ ﱞ ﱟﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦﱧ ﱨ ﱩ ﱪ ﱫ', 'وَلَمَّا وَرَدَ مَآءَ مَدْيَنَ وَجَدَ عَلَيْهِ أُمَّةًۭ مِّنَ ٱلنَّاسِ يَسْقُونَ وَوَجَدَ مِن دُونِهِمُ ٱمْرَأَتَيْنِ تَذُودَانِ ۖ قَالَ مَا خَطْبُكُمَا ۖ قَالَتَا لَا نَسْقِى حَتَّىٰ يُصْدِرَ ٱلرِّعَآءُ ۖ وَأَبُونَا شَيْخٌۭ كَبِيرٌۭ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 28, 388, 'ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ', 'فَسَقَىٰ لَهُمَا ثُمَّ تَوَلَّىٰٓ إِلَى ٱلظِّلِّ فَقَالَ رَبِّ إِنِّى لِمَآ أَنزَلْتَ إِلَىَّ مِنْ خَيْرٍۢ فَقِيرٌۭ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 28, 388, 'ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒﲓ ﲔ ﲕ ﲖ ﲗ ﲘ', 'فَجَآءَتْهُ إِحْدَىٰهُمَا تَمْشِى عَلَى ٱسْتِحْيَآءٍۢ قَالَتْ إِنَّ أَبِى يَدْعُوكَ لِيَجْزِيَكَ أَجْرَ مَا سَقَيْتَ لَنَا ۚ فَلَمَّا جَآءَهُۥ وَقَصَّ عَلَيْهِ ٱلْقَصَصَ قَالَ لَا تَخَفْ ۖ نَجَوْتَ مِنَ ٱلْقَوْمِ ٱلظَّـٰلِمِينَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 28, 388, 'ﲙ ﲚ ﲛ ﲜﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ', 'قَالَتْ إِحْدَىٰهُمَا يَـٰٓأَبَتِ ٱسْتَـْٔجِرْهُ ۖ إِنَّ خَيْرَ مَنِ ٱسْتَـْٔجَرْتَ ٱلْقَوِىُّ ٱلْأَمِينُ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 28, 388, 'ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱﲲ ﲳ ﲴ ﲵ ﲶ ﲷﲸ ﲹ ﲺ ﲻ ﲼ ﲽﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ', 'قَالَ إِنِّىٓ أُرِيدُ أَنْ أُنكِحَكَ إِحْدَى ٱبْنَتَىَّ هَـٰتَيْنِ عَلَىٰٓ أَن تَأْجُرَنِى ثَمَـٰنِىَ حِجَجٍۢ ۖ فَإِنْ أَتْمَمْتَ عَشْرًۭا فَمِنْ عِندِكَ ۖ وَمَآ أُرِيدُ أَنْ أَشُقَّ عَلَيْكَ ۚ سَتَجِدُنِىٓ إِن شَآءَ ٱللَّهُ مِنَ ٱلصَّـٰلِحِينَ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 28, 388, 'ﳆ ﳇ ﳈ ﳉﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ', 'قَالَ ذَٰلِكَ بَيْنِى وَبَيْنَكَ ۖ أَيَّمَا ٱلْأَجَلَيْنِ قَضَيْتُ فَلَا عُدْوَٰنَ عَلَىَّ ۖ وَٱللَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٌۭ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 28, 389, 'ﱁﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ', '۞ فَلَمَّا قَضَىٰ مُوسَى ٱلْأَجَلَ وَسَارَ بِأَهْلِهِۦٓ ءَانَسَ مِن جَانِبِ ٱلطُّورِ نَارًۭا قَالَ لِأَهْلِهِ ٱمْكُثُوٓا۟ إِنِّىٓ ءَانَسْتُ نَارًۭا لَّعَلِّىٓ ءَاتِيكُم مِّنْهَا بِخَبَرٍ أَوْ جَذْوَةٍۢ مِّنَ ٱلنَّارِ لَعَلَّكُمْ تَصْطَلُونَ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 28, 389, 'ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ', 'فَلَمَّآ أَتَىٰهَا نُودِىَ مِن شَـٰطِئِ ٱلْوَادِ ٱلْأَيْمَنِ فِى ٱلْبُقْعَةِ ٱلْمُبَـٰرَكَةِ مِنَ ٱلشَّجَرَةِ أَن يَـٰمُوسَىٰٓ إِنِّىٓ أَنَا ٱللَّهُ رَبُّ ٱلْعَـٰلَمِينَ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 28, 389, 'ﱳ ﱴ ﱵﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿﲀ ﲁ ﲂ ﲃ ﲄﲅ ﲆ ﲇ ﲈ ﲉ', 'وَأَنْ أَلْقِ عَصَاكَ ۖ فَلَمَّا رَءَاهَا تَهْتَزُّ كَأَنَّهَا جَآنٌّۭ وَلَّىٰ مُدْبِرًۭا وَلَمْ يُعَقِّبْ ۚ يَـٰمُوسَىٰٓ أَقْبِلْ وَلَا تَخَفْ ۖ إِنَّكَ مِنَ ٱلْـَٔامِنِينَ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 28, 389, 'ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟﲠ ﲡ ﲢ ﲣ ﲤ ﲥ', 'ٱسْلُكْ يَدَكَ فِى جَيْبِكَ تَخْرُجْ بَيْضَآءَ مِنْ غَيْرِ سُوٓءٍۢ وَٱضْمُمْ إِلَيْكَ جَنَاحَكَ مِنَ ٱلرَّهْبِ ۖ فَذَٰنِكَ بُرْهَـٰنَانِ مِن رَّبِّكَ إِلَىٰ فِرْعَوْنَ وَمَلَإِي۟هِۦٓ ۚ إِنَّهُمْ كَانُوا۟ قَوْمًۭا فَـٰسِقِينَ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 28, 389, 'ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ', 'قَالَ رَبِّ إِنِّى قَتَلْتُ مِنْهُمْ نَفْسًۭا فَأَخَافُ أَن يَقْتُلُونِ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 28, 389, 'ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹﲺ ﲻ ﲼ ﲽ ﲾ ﲿ', 'وَأَخِى هَـٰرُونُ هُوَ أَفْصَحُ مِنِّى لِسَانًۭا فَأَرْسِلْهُ مَعِىَ رِدْءًۭا يُصَدِّقُنِىٓ ۖ إِنِّىٓ أَخَافُ أَن يُكَذِّبُونِ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 28, 389, 'ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊﳋ ﳌ ﳍ ﳎ ﳏ ﳐ', 'قَالَ سَنَشُدُّ عَضُدَكَ بِأَخِيكَ وَنَجْعَلُ لَكُمَا سُلْطَـٰنًۭا فَلَا يَصِلُونَ إِلَيْكُمَا ۚ بِـَٔايَـٰتِنَآ أَنتُمَا وَمَنِ ٱتَّبَعَكُمَا ٱلْغَـٰلِبُونَ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 28, 390, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ', 'فَلَمَّا جَآءَهُم مُّوسَىٰ بِـَٔايَـٰتِنَا بَيِّنَـٰتٍۢ قَالُوا۟ مَا هَـٰذَآ إِلَّا سِحْرٌۭ مُّفْتَرًۭى وَمَا سَمِعْنَا بِهَـٰذَا فِىٓ ءَابَآئِنَا ٱلْأَوَّلِينَ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 28, 390, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠﱡ ﱢ ﱣ ﱤ ﱥ ﱦ', 'وَقَالَ مُوسَىٰ رَبِّىٓ أَعْلَمُ بِمَن جَآءَ بِٱلْهُدَىٰ مِنْ عِندِهِۦ وَمَن تَكُونُ لَهُۥ عَـٰقِبَةُ ٱلدَّارِ ۖ إِنَّهُۥ لَا يُفْلِحُ ٱلظَّـٰلِمُونَ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 28, 390, 'ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ', 'وَقَالَ فِرْعَوْنُ يَـٰٓأَيُّهَا ٱلْمَلَأُ مَا عَلِمْتُ لَكُم مِّنْ إِلَـٰهٍ غَيْرِى فَأَوْقِدْ لِى يَـٰهَـٰمَـٰنُ عَلَى ٱلطِّينِ فَٱجْعَل لِّى صَرْحًۭا لَّعَلِّىٓ أَطَّلِعُ إِلَىٰٓ إِلَـٰهِ مُوسَىٰ وَإِنِّى لَأَظُنُّهُۥ مِنَ ٱلْكَـٰذِبِينَ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 28, 390, 'ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ', 'وَٱسْتَكْبَرَ هُوَ وَجُنُودُهُۥ فِى ٱلْأَرْضِ بِغَيْرِ ٱلْحَقِّ وَظَنُّوٓا۟ أَنَّهُمْ إِلَيْنَا لَا يُرْجَعُونَ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 28, 390, 'ﲐ ﲑ ﲒ ﲓ ﲔﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ', 'فَأَخَذْنَـٰهُ وَجُنُودَهُۥ فَنَبَذْنَـٰهُمْ فِى ٱلْيَمِّ ۖ فَٱنظُرْ كَيْفَ كَانَ عَـٰقِبَةُ ٱلظَّـٰلِمِينَ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 28, 390, 'ﲜ ﲝ ﲞ ﲟ ﲠﲡ ﲢ ﲣ ﲤ ﲥ ﲦ', 'وَجَعَلْنَـٰهُمْ أَئِمَّةًۭ يَدْعُونَ إِلَى ٱلنَّارِ ۖ وَيَوْمَ ٱلْقِيَـٰمَةِ لَا يُنصَرُونَ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 28, 390, 'ﲧ ﲨ ﲩ ﲪ ﲫﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ', 'وَأَتْبَعْنَـٰهُمْ فِى هَـٰذِهِ ٱلدُّنْيَا لَعْنَةًۭ ۖ وَيَوْمَ ٱلْقِيَـٰمَةِ هُم مِّنَ ٱلْمَقْبُوحِينَ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 28, 390, 'ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ', 'وَلَقَدْ ءَاتَيْنَا مُوسَى ٱلْكِتَـٰبَ مِنۢ بَعْدِ مَآ أَهْلَكْنَا ٱلْقُرُونَ ٱلْأُولَىٰ بَصَآئِرَ لِلنَّاسِ وَهُدًۭى وَرَحْمَةًۭ لَّعَلَّهُمْ يَتَذَكَّرُونَ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 28, 391, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ', 'وَمَا كُنتَ بِجَانِبِ ٱلْغَرْبِىِّ إِذْ قَضَيْنَآ إِلَىٰ مُوسَى ٱلْأَمْرَ وَمَا كُنتَ مِنَ ٱلشَّـٰهِدِينَ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 28, 391, 'ﱏ ﱐ ﱑ ﱒ ﱓ ﱔﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ', 'وَلَـٰكِنَّآ أَنشَأْنَا قُرُونًۭا فَتَطَاوَلَ عَلَيْهِمُ ٱلْعُمُرُ ۚ وَمَا كُنتَ ثَاوِيًۭا فِىٓ أَهْلِ مَدْيَنَ تَتْلُوا۟ عَلَيْهِمْ ءَايَـٰتِنَا وَلَـٰكِنَّا كُنَّا مُرْسِلِينَ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 28, 391, 'ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ', 'وَمَا كُنتَ بِجَانِبِ ٱلطُّورِ إِذْ نَادَيْنَا وَلَـٰكِن رَّحْمَةًۭ مِّن رَّبِّكَ لِتُنذِرَ قَوْمًۭا مَّآ أَتَىٰهُم مِّن نَّذِيرٍۢ مِّن قَبْلِكَ لَعَلَّهُمْ يَتَذَكَّرُونَ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 28, 391, 'ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ', 'وَلَوْلَآ أَن تُصِيبَهُم مُّصِيبَةٌۢ بِمَا قَدَّمَتْ أَيْدِيهِمْ فَيَقُولُوا۟ رَبَّنَا لَوْلَآ أَرْسَلْتَ إِلَيْنَا رَسُولًۭا فَنَتَّبِعَ ءَايَـٰتِكَ وَنَكُونَ مِنَ ٱلْمُؤْمِنِينَ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 28, 391, 'ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ', 'فَلَمَّا جَآءَهُمُ ٱلْحَقُّ مِنْ عِندِنَا قَالُوا۟ لَوْلَآ أُوتِىَ مِثْلَ مَآ أُوتِىَ مُوسَىٰٓ ۚ أَوَلَمْ يَكْفُرُوا۟ بِمَآ أُوتِىَ مُوسَىٰ مِن قَبْلُ ۖ قَالُوا۟ سِحْرَانِ تَظَـٰهَرَا وَقَالُوٓا۟ إِنَّا بِكُلٍّۢ كَـٰفِرُونَ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 28, 391, 'ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ', 'قُلْ فَأْتُوا۟ بِكِتَـٰبٍۢ مِّنْ عِندِ ٱللَّهِ هُوَ أَهْدَىٰ مِنْهُمَآ أَتَّبِعْهُ إِن كُنتُمْ صَـٰدِقِينَ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 28, 391, 'ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ', 'فَإِن لَّمْ يَسْتَجِيبُوا۟ لَكَ فَٱعْلَمْ أَنَّمَا يَتَّبِعُونَ أَهْوَآءَهُمْ ۚ وَمَنْ أَضَلُّ مِمَّنِ ٱتَّبَعَ هَوَىٰهُ بِغَيْرِ هُدًۭى مِّنَ ٱللَّهِ ۚ إِنَّ ٱللَّهَ لَا يَهْدِى ٱلْقَوْمَ ٱلظَّـٰلِمِينَ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 28, 392, 'ﱁﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ', '۞ وَلَقَدْ وَصَّلْنَا لَهُمُ ٱلْقَوْلَ لَعَلَّهُمْ يَتَذَكَّرُونَ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 28, 392, 'ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ', 'ٱلَّذِينَ ءَاتَيْنَـٰهُمُ ٱلْكِتَـٰبَ مِن قَبْلِهِۦ هُم بِهِۦ يُؤْمِنُونَ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 28, 392, 'ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ', 'وَإِذَا يُتْلَىٰ عَلَيْهِمْ قَالُوٓا۟ ءَامَنَّا بِهِۦٓ إِنَّهُ ٱلْحَقُّ مِن رَّبِّنَآ إِنَّا كُنَّا مِن قَبْلِهِۦ مُسْلِمِينَ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 28, 392, 'ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ', 'أُو۟لَـٰٓئِكَ يُؤْتَوْنَ أَجْرَهُم مَّرَّتَيْنِ بِمَا صَبَرُوا۟ وَيَدْرَءُونَ بِٱلْحَسَنَةِ ٱلسَّيِّئَةَ وَمِمَّا رَزَقْنَـٰهُمْ يُنفِقُونَ ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (55, 28, 392, 'ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ', 'وَإِذَا سَمِعُوا۟ ٱللَّغْوَ أَعْرَضُوا۟ عَنْهُ وَقَالُوا۟ لَنَآ أَعْمَـٰلُنَا وَلَكُمْ أَعْمَـٰلُكُمْ سَلَـٰمٌ عَلَيْكُمْ لَا نَبْتَغِى ٱلْجَـٰهِلِينَ ٥٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (56, 28, 392, 'ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈﲉ ﲊ ﲋ ﲌ ﲍ', 'إِنَّكَ لَا تَهْدِى مَنْ أَحْبَبْتَ وَلَـٰكِنَّ ٱللَّهَ يَهْدِى مَن يَشَآءُ ۚ وَهُوَ أَعْلَمُ بِٱلْمُهْتَدِينَ ٥٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (57, 28, 392, 'ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ', 'وَقَالُوٓا۟ إِن نَّتَّبِعِ ٱلْهُدَىٰ مَعَكَ نُتَخَطَّفْ مِنْ أَرْضِنَآ ۚ أَوَلَمْ نُمَكِّن لَّهُمْ حَرَمًا ءَامِنًۭا يُجْبَىٰٓ إِلَيْهِ ثَمَرَٰتُ كُلِّ شَىْءٍۢ رِّزْقًۭا مِّن لَّدُنَّا وَلَـٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ ٥٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (58, 28, 392, 'ﲩ ﲪ ﲫ ﲬ ﲭ ﲮﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷﲸ ﲹ ﲺ ﲻ ﲼ', 'وَكَمْ أَهْلَكْنَا مِن قَرْيَةٍۭ بَطِرَتْ مَعِيشَتَهَا ۖ فَتِلْكَ مَسَـٰكِنُهُمْ لَمْ تُسْكَن مِّنۢ بَعْدِهِمْ إِلَّا قَلِيلًۭا ۖ وَكُنَّا نَحْنُ ٱلْوَٰرِثِينَ ٥٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (59, 28, 392, 'ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ', 'وَمَا كَانَ رَبُّكَ مُهْلِكَ ٱلْقُرَىٰ حَتَّىٰ يَبْعَثَ فِىٓ أُمِّهَا رَسُولًۭا يَتْلُوا۟ عَلَيْهِمْ ءَايَـٰتِنَا ۚ وَمَا كُنَّا مُهْلِكِى ٱلْقُرَىٰٓ إِلَّا وَأَهْلُهَا ظَـٰلِمُونَ ٥٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (60, 28, 393, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈﱉ ﱊ ﱋ ﱌ ﱍ ﱎﱏ ﱐ ﱑ ﱒ', 'وَمَآ أُوتِيتُم مِّن شَىْءٍۢ فَمَتَـٰعُ ٱلْحَيَوٰةِ ٱلدُّنْيَا وَزِينَتُهَا ۚ وَمَا عِندَ ٱللَّهِ خَيْرٌۭ وَأَبْقَىٰٓ ۚ أَفَلَا تَعْقِلُونَ ٦٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (61, 28, 393, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ', 'أَفَمَن وَعَدْنَـٰهُ وَعْدًا حَسَنًۭا فَهُوَ لَـٰقِيهِ كَمَن مَّتَّعْنَـٰهُ مَتَـٰعَ ٱلْحَيَوٰةِ ٱلدُّنْيَا ثُمَّ هُوَ يَوْمَ ٱلْقِيَـٰمَةِ مِنَ ٱلْمُحْضَرِينَ ٦١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (62, 28, 393, 'ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ', 'وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ أَيْنَ شُرَكَآءِىَ ٱلَّذِينَ كُنتُمْ تَزْعُمُونَ ٦٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (63, 28, 393, 'ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹﱺ ﱻ ﱼﱽ ﱾ ﱿ ﲀ ﲁ ﲂ', 'قَالَ ٱلَّذِينَ حَقَّ عَلَيْهِمُ ٱلْقَوْلُ رَبَّنَا هَـٰٓؤُلَآءِ ٱلَّذِينَ أَغْوَيْنَآ أَغْوَيْنَـٰهُمْ كَمَا غَوَيْنَا ۖ تَبَرَّأْنَآ إِلَيْكَ ۖ مَا كَانُوٓا۟ إِيَّانَا يَعْبُدُونَ ٦٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (64, 28, 393, 'ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋﲌ ﲍ ﲎ ﲏ ﲐ ﲑ', 'وَقِيلَ ٱدْعُوا۟ شُرَكَآءَكُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيبُوا۟ لَهُمْ وَرَأَوُا۟ ٱلْعَذَابَ ۚ لَوْ أَنَّهُمْ كَانُوا۟ يَهْتَدُونَ ٦٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (65, 28, 393, 'ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ', 'وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ مَاذَآ أَجَبْتُمُ ٱلْمُرْسَلِينَ ٦٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (66, 28, 393, 'ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ', 'فَعَمِيَتْ عَلَيْهِمُ ٱلْأَنۢبَآءُ يَوْمَئِذٍۢ فَهُمْ لَا يَتَسَآءَلُونَ ٦٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (67, 28, 393, 'ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ', 'فَأَمَّا مَن تَابَ وَءَامَنَ وَعَمِلَ صَـٰلِحًۭا فَعَسَىٰٓ أَن يَكُونَ مِنَ ٱلْمُفْلِحِينَ ٦٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (68, 28, 393, 'ﲭ ﲮ ﲯ ﲰ ﲱﲲ ﲳ ﲴ ﲵ ﲶﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ', 'وَرَبُّكَ يَخْلُقُ مَا يَشَآءُ وَيَخْتَارُ ۗ مَا كَانَ لَهُمُ ٱلْخِيَرَةُ ۚ سُبْحَـٰنَ ٱللَّهِ وَتَعَـٰلَىٰ عَمَّا يُشْرِكُونَ ٦٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (69, 28, 393, 'ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ', 'وَرَبُّكَ يَعْلَمُ مَا تُكِنُّ صُدُورُهُمْ وَمَا يُعْلِنُونَ ٦٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (70, 28, 393, 'ﳆ ﳇ ﳈ ﳉ ﳊ ﳋﳌ ﳍ ﳎ ﳏ ﳐ ﳑﳒ ﳓ ﳔ ﳕ ﳖ ﳗ', 'وَهُوَ ٱللَّهُ لَآ إِلَـٰهَ إِلَّا هُوَ ۖ لَهُ ٱلْحَمْدُ فِى ٱلْأُولَىٰ وَٱلْـَٔاخِرَةِ ۖ وَلَهُ ٱلْحُكْمُ وَإِلَيْهِ تُرْجَعُونَ ٧٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (71, 28, 394, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑﱒ ﱓ ﱔ ﱕ', 'قُلْ أَرَءَيْتُمْ إِن جَعَلَ ٱللَّهُ عَلَيْكُمُ ٱلَّيْلَ سَرْمَدًا إِلَىٰ يَوْمِ ٱلْقِيَـٰمَةِ مَنْ إِلَـٰهٌ غَيْرُ ٱللَّهِ يَأْتِيكُم بِضِيَآءٍ ۖ أَفَلَا تَسْمَعُونَ ٧١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (72, 28, 394, 'ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨﱩ ﱪ ﱫ ﱬ', 'قُلْ أَرَءَيْتُمْ إِن جَعَلَ ٱللَّهُ عَلَيْكُمُ ٱلنَّهَارَ سَرْمَدًا إِلَىٰ يَوْمِ ٱلْقِيَـٰمَةِ مَنْ إِلَـٰهٌ غَيْرُ ٱللَّهِ يَأْتِيكُم بِلَيْلٍۢ تَسْكُنُونَ فِيهِ ۖ أَفَلَا تُبْصِرُونَ ٧٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (73, 28, 394, 'ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ', 'وَمِن رَّحْمَتِهِۦ جَعَلَ لَكُمُ ٱلَّيْلَ وَٱلنَّهَارَ لِتَسْكُنُوا۟ فِيهِ وَلِتَبْتَغُوا۟ مِن فَضْلِهِۦ وَلَعَلَّكُمْ تَشْكُرُونَ ٧٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (74, 28, 394, 'ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ', 'وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ أَيْنَ شُرَكَآءِىَ ٱلَّذِينَ كُنتُمْ تَزْعُمُونَ ٧٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (75, 28, 394, 'ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ', 'وَنَزَعْنَا مِن كُلِّ أُمَّةٍۢ شَهِيدًۭا فَقُلْنَا هَاتُوا۟ بُرْهَـٰنَكُمْ فَعَلِمُوٓا۟ أَنَّ ٱلْحَقَّ لِلَّهِ وَضَلَّ عَنْهُم مَّا كَانُوا۟ يَفْتَرُونَ ٧٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (76, 28, 394, 'ﲖﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ', '۞ إِنَّ قَـٰرُونَ كَانَ مِن قَوْمِ مُوسَىٰ فَبَغَىٰ عَلَيْهِمْ ۖ وَءَاتَيْنَـٰهُ مِنَ ٱلْكُنُوزِ مَآ إِنَّ مَفَاتِحَهُۥ لَتَنُوٓأُ بِٱلْعُصْبَةِ أُو۟لِى ٱلْقُوَّةِ إِذْ قَالَ لَهُۥ قَوْمُهُۥ لَا تَفْرَحْ ۖ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلْفَرِحِينَ ٧٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (77, 28, 394, 'ﲷ ﲸ ﲹ ﲺ ﲻ ﲼﲽ ﲾ ﲿ ﳀ ﳁ ﳂﳃ ﳄ ﳅ ﳆ ﳇ ﳈﳉ ﳊ ﳋ ﳌ ﳍ ﳎﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ', 'وَٱبْتَغِ فِيمَآ ءَاتَىٰكَ ٱللَّهُ ٱلدَّارَ ٱلْـَٔاخِرَةَ ۖ وَلَا تَنسَ نَصِيبَكَ مِنَ ٱلدُّنْيَا ۖ وَأَحْسِن كَمَآ أَحْسَنَ ٱللَّهُ إِلَيْكَ ۖ وَلَا تَبْغِ ٱلْفَسَادَ فِى ٱلْأَرْضِ ۖ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلْمُفْسِدِينَ ٧٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (78, 28, 395, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ', 'قَالَ إِنَّمَآ أُوتِيتُهُۥ عَلَىٰ عِلْمٍ عِندِىٓ ۚ أَوَلَمْ يَعْلَمْ أَنَّ ٱللَّهَ قَدْ أَهْلَكَ مِن قَبْلِهِۦ مِنَ ٱلْقُرُونِ مَنْ هُوَ أَشَدُّ مِنْهُ قُوَّةًۭ وَأَكْثَرُ جَمْعًۭا ۚ وَلَا يُسْـَٔلُ عَن ذُنُوبِهِمُ ٱلْمُجْرِمُونَ ٧٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (79, 28, 395, 'ﱠ ﱡ ﱢ ﱣ ﱤﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ', 'فَخَرَجَ عَلَىٰ قَوْمِهِۦ فِى زِينَتِهِۦ ۖ قَالَ ٱلَّذِينَ يُرِيدُونَ ٱلْحَيَوٰةَ ٱلدُّنْيَا يَـٰلَيْتَ لَنَا مِثْلَ مَآ أُوتِىَ قَـٰرُونُ إِنَّهُۥ لَذُو حَظٍّ عَظِيمٍۢ ٧٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (80, 28, 395, 'ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁﲂ ﲃ ﲄ ﲅ ﲆ ﲇ', 'وَقَالَ ٱلَّذِينَ أُوتُوا۟ ٱلْعِلْمَ وَيْلَكُمْ ثَوَابُ ٱللَّهِ خَيْرٌۭ لِّمَنْ ءَامَنَ وَعَمِلَ صَـٰلِحًۭا وَلَا يُلَقَّىٰهَآ إِلَّا ٱلصَّـٰبِرُونَ ٨٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (81, 28, 395, 'ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ', 'فَخَسَفْنَا بِهِۦ وَبِدَارِهِ ٱلْأَرْضَ فَمَا كَانَ لَهُۥ مِن فِئَةٍۢ يَنصُرُونَهُۥ مِن دُونِ ٱللَّهِ وَمَا كَانَ مِنَ ٱلْمُنتَصِرِينَ ٨١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (82, 28, 395, 'ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰﲱ ﲲ ﲳ ﲴ ﲵ ﲶ', 'وَأَصْبَحَ ٱلَّذِينَ تَمَنَّوْا۟ مَكَانَهُۥ بِٱلْأَمْسِ يَقُولُونَ وَيْكَأَنَّ ٱللَّهَ يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ مِنْ عِبَادِهِۦ وَيَقْدِرُ ۖ لَوْلَآ أَن مَّنَّ ٱللَّهُ عَلَيْنَا لَخَسَفَ بِنَا ۖ وَيْكَأَنَّهُۥ لَا يُفْلِحُ ٱلْكَـٰفِرُونَ ٨٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (83, 28, 395, 'ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂﳃ ﳄ ﳅ ﳆ', 'تِلْكَ ٱلدَّارُ ٱلْـَٔاخِرَةُ نَجْعَلُهَا لِلَّذِينَ لَا يُرِيدُونَ عُلُوًّۭا فِى ٱلْأَرْضِ وَلَا فَسَادًۭا ۚ وَٱلْعَـٰقِبَةُ لِلْمُتَّقِينَ ٨٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (84, 28, 395, 'ﳇ ﳈ ﳉ ﳊ ﳋ ﳌﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ', 'مَن جَآءَ بِٱلْحَسَنَةِ فَلَهُۥ خَيْرٌۭ مِّنْهَا ۖ وَمَن جَآءَ بِٱلسَّيِّئَةِ فَلَا يُجْزَى ٱلَّذِينَ عَمِلُوا۟ ٱلسَّيِّـَٔاتِ إِلَّا مَا كَانُوا۟ يَعْمَلُونَ ٨٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (85, 28, 396, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'إِنَّ ٱلَّذِى فَرَضَ عَلَيْكَ ٱلْقُرْءَانَ لَرَآدُّكَ إِلَىٰ مَعَادٍۢ ۚ قُل رَّبِّىٓ أَعْلَمُ مَن جَآءَ بِٱلْهُدَىٰ وَمَنْ هُوَ فِى ضَلَـٰلٍۢ مُّبِينٍۢ ٨٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (86, 28, 396, 'ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠﱡ ﱢ ﱣ ﱤ ﱥ ﱦ', 'وَمَا كُنتَ تَرْجُوٓا۟ أَن يُلْقَىٰٓ إِلَيْكَ ٱلْكِتَـٰبُ إِلَّا رَحْمَةًۭ مِّن رَّبِّكَ ۖ فَلَا تَكُونَنَّ ظَهِيرًۭا لِّلْكَـٰفِرِينَ ٨٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (87, 28, 396, 'ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯﱰ ﱱ ﱲ ﱳﱴ ﱵ ﱶ ﱷ ﱸ ﱹ', 'وَلَا يَصُدُّنَّكَ عَنْ ءَايَـٰتِ ٱللَّهِ بَعْدَ إِذْ أُنزِلَتْ إِلَيْكَ ۖ وَٱدْعُ إِلَىٰ رَبِّكَ ۖ وَلَا تَكُونَنَّ مِنَ ٱلْمُشْرِكِينَ ٨٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (88, 28, 396, 'ﱺ ﱻ ﱼ ﱽ ﱾ ﱿﲀ ﲁ ﲂ ﲃ ﲄﲅ ﲆ ﲇ ﲈ ﲉ ﲊﲋ ﲌ ﲍ ﲎ ﲏ ﲐ', 'وَلَا تَدْعُ مَعَ ٱللَّهِ إِلَـٰهًا ءَاخَرَ ۘ لَآ إِلَـٰهَ إِلَّا هُوَ ۚ كُلُّ شَىْءٍ هَالِكٌ إِلَّا وَجْهَهُۥ ۚ لَهُ ٱلْحُكْمُ وَإِلَيْهِ تُرْجَعُونَ ٨٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 29, 396, 'ﲑ ﲒ', 'الٓمٓ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 29, 396, 'ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ', 'أَحَسِبَ ٱلنَّاسُ أَن يُتْرَكُوٓا۟ أَن يَقُولُوٓا۟ ءَامَنَّا وَهُمْ لَا يُفْتَنُونَ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 29, 396, 'ﲞ ﲟ ﲠ ﲡ ﲢﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ', 'وَلَقَدْ فَتَنَّا ٱلَّذِينَ مِن قَبْلِهِمْ ۖ فَلَيَعْلَمَنَّ ٱللَّهُ ٱلَّذِينَ صَدَقُوا۟ وَلَيَعْلَمَنَّ ٱلْكَـٰذِبِينَ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 29, 396, 'ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱﲲ ﲳ ﲴ ﲵ ﲶ', 'أَمْ حَسِبَ ٱلَّذِينَ يَعْمَلُونَ ٱلسَّيِّـَٔاتِ أَن يَسْبِقُونَا ۚ سَآءَ مَا يَحْكُمُونَ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 29, 396, 'ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿﳀ ﳁ ﳂ ﳃ ﳄ', 'مَن كَانَ يَرْجُوا۟ لِقَآءَ ٱللَّهِ فَإِنَّ أَجَلَ ٱللَّهِ لَـَٔاتٍۢ ۚ وَهُوَ ٱلسَّمِيعُ ٱلْعَلِيمُ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 29, 396, 'ﳅ ﳆ ﳇ ﳈ ﳉﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ', 'وَمَن جَـٰهَدَ فَإِنَّمَا يُجَـٰهِدُ لِنَفْسِهِۦٓ ۚ إِنَّ ٱللَّهَ لَغَنِىٌّ عَنِ ٱلْعَـٰلَمِينَ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 29, 397, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ', 'وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَنُكَفِّرَنَّ عَنْهُمْ سَيِّـَٔاتِهِمْ وَلَنَجْزِيَنَّهُمْ أَحْسَنَ ٱلَّذِى كَانُوا۟ يَعْمَلُونَ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 29, 397, 'ﱎ ﱏ ﱐ ﱑﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ', 'وَوَصَّيْنَا ٱلْإِنسَـٰنَ بِوَٰلِدَيْهِ حُسْنًۭا ۖ وَإِن جَـٰهَدَاكَ لِتُشْرِكَ بِى مَا لَيْسَ لَكَ بِهِۦ عِلْمٌۭ فَلَا تُطِعْهُمَآ ۚ إِلَىَّ مَرْجِعُكُمْ فَأُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 29, 397, 'ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ', 'وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَنُدْخِلَنَّهُمْ فِى ٱلصَّـٰلِحِينَ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 29, 397, 'ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ', 'وَمِنَ ٱلنَّاسِ مَن يَقُولُ ءَامَنَّا بِٱللَّهِ فَإِذَآ أُوذِىَ فِى ٱللَّهِ جَعَلَ فِتْنَةَ ٱلنَّاسِ كَعَذَابِ ٱللَّهِ وَلَئِن جَآءَ نَصْرٌۭ مِّن رَّبِّكَ لَيَقُولُنَّ إِنَّا كُنَّا مَعَكُمْ ۚ أَوَلَيْسَ ٱللَّهُ بِأَعْلَمَ بِمَا فِى صُدُورِ ٱلْعَـٰلَمِينَ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 29, 397, 'ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ', 'وَلَيَعْلَمَنَّ ٱللَّهُ ٱلَّذِينَ ءَامَنُوا۟ وَلَيَعْلَمَنَّ ٱلْمُنَـٰفِقِينَ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 29, 397, 'ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦﲧ ﲨ ﲩ ﲪ', 'وَقَالَ ٱلَّذِينَ كَفَرُوا۟ لِلَّذِينَ ءَامَنُوا۟ ٱتَّبِعُوا۟ سَبِيلَنَا وَلْنَحْمِلْ خَطَـٰيَـٰكُمْ وَمَا هُم بِحَـٰمِلِينَ مِنْ خَطَـٰيَـٰهُم مِّن شَىْءٍ ۖ إِنَّهُمْ لَكَـٰذِبُونَ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 29, 397, 'ﲫ ﲬ ﲭ ﲮ ﲯﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ', 'وَلَيَحْمِلُنَّ أَثْقَالَهُمْ وَأَثْقَالًۭا مَّعَ أَثْقَالِهِمْ ۖ وَلَيُسْـَٔلُنَّ يَوْمَ ٱلْقِيَـٰمَةِ عَمَّا كَانُوا۟ يَفْتَرُونَ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 29, 397, 'ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ', 'وَلَقَدْ أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِۦ فَلَبِثَ فِيهِمْ أَلْفَ سَنَةٍ إِلَّا خَمْسِينَ عَامًۭا فَأَخَذَهُمُ ٱلطُّوفَانُ وَهُمْ ظَـٰلِمُونَ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 29, 398, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ', 'فَأَنجَيْنَـٰهُ وَأَصْحَـٰبَ ٱلسَّفِينَةِ وَجَعَلْنَـٰهَآ ءَايَةًۭ لِّلْعَـٰلَمِينَ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 29, 398, 'ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ', 'وَإِبْرَٰهِيمَ إِذْ قَالَ لِقَوْمِهِ ٱعْبُدُوا۟ ٱللَّهَ وَٱتَّقُوهُ ۖ ذَٰلِكُمْ خَيْرٌۭ لَّكُمْ إِن كُنتُمْ تَعْلَمُونَ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 29, 398, 'ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰﱱ ﱲ ﱳ ﱴ', 'إِنَّمَا تَعْبُدُونَ مِن دُونِ ٱللَّهِ أَوْثَـٰنًۭا وَتَخْلُقُونَ إِفْكًا ۚ إِنَّ ٱلَّذِينَ تَعْبُدُونَ مِن دُونِ ٱللَّهِ لَا يَمْلِكُونَ لَكُمْ رِزْقًۭا فَٱبْتَغُوا۟ عِندَ ٱللَّهِ ٱلرِّزْقَ وَٱعْبُدُوهُ وَٱشْكُرُوا۟ لَهُۥٓ ۖ إِلَيْهِ تُرْجَعُونَ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 29, 398, 'ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ', 'وَإِن تُكَذِّبُوا۟ فَقَدْ كَذَّبَ أُمَمٌۭ مِّن قَبْلِكُمْ ۖ وَمَا عَلَى ٱلرَّسُولِ إِلَّا ٱلْبَلَـٰغُ ٱلْمُبِينُ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 29, 398, 'ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ', 'أَوَلَمْ يَرَوْا۟ كَيْفَ يُبْدِئُ ٱللَّهُ ٱلْخَلْقَ ثُمَّ يُعِيدُهُۥٓ ۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٌۭ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 29, 398, 'ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚﲛ ﲜ ﲝ ﲞ ﲟ ﲠﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ', 'قُلْ سِيرُوا۟ فِى ٱلْأَرْضِ فَٱنظُرُوا۟ كَيْفَ بَدَأَ ٱلْخَلْقَ ۚ ثُمَّ ٱللَّهُ يُنشِئُ ٱلنَّشْأَةَ ٱلْـَٔاخِرَةَ ۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَىْءٍۢ قَدِيرٌۭ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 29, 398, 'ﲩ ﲪ ﲫ ﲬ ﲭ ﲮﲯ ﲰ ﲱ ﲲ', 'يُعَذِّبُ مَن يَشَآءُ وَيَرْحَمُ مَن يَشَآءُ ۖ وَإِلَيْهِ تُقْلَبُونَ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 29, 398, 'ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ', 'وَمَآ أَنتُم بِمُعْجِزِينَ فِى ٱلْأَرْضِ وَلَا فِى ٱلسَّمَآءِ ۖ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِىٍّۢ وَلَا نَصِيرٍۢ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 29, 398, 'ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ', 'وَٱلَّذِينَ كَفَرُوا۟ بِـَٔايَـٰتِ ٱللَّهِ وَلِقَآئِهِۦٓ أُو۟لَـٰٓئِكَ يَئِسُوا۟ مِن رَّحْمَتِى وَأُو۟لَـٰٓئِكَ لَهُمْ عَذَابٌ أَلِيمٌۭ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 29, 399, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ', 'فَمَا كَانَ جَوَابَ قَوْمِهِۦٓ إِلَّآ أَن قَالُوا۟ ٱقْتُلُوهُ أَوْ حَرِّقُوهُ فَأَنجَىٰهُ ٱللَّهُ مِنَ ٱلنَّارِ ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَـٰتٍۢ لِّقَوْمٍۢ يُؤْمِنُونَ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 29, 399, 'ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ', 'وَقَالَ إِنَّمَا ٱتَّخَذْتُم مِّن دُونِ ٱللَّهِ أَوْثَـٰنًۭا مَّوَدَّةَ بَيْنِكُمْ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا ۖ ثُمَّ يَوْمَ ٱلْقِيَـٰمَةِ يَكْفُرُ بَعْضُكُم بِبَعْضٍۢ وَيَلْعَنُ بَعْضُكُم بَعْضًۭا وَمَأْوَىٰكُمُ ٱلنَّارُ وَمَا لَكُم مِّن نَّـٰصِرِينَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 29, 399, 'ﱴﱵ ﱶ ﱷﱸ ﱹ ﱺ ﱻ ﱼ ﱽﱾ ﱿ ﲀ ﲁ ﲂ ﲃ', '۞ فَـَٔامَنَ لَهُۥ لُوطٌۭ ۘ وَقَالَ إِنِّى مُهَاجِرٌ إِلَىٰ رَبِّىٓ ۖ إِنَّهُۥ هُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 29, 399, 'ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ', 'وَوَهَبْنَا لَهُۥٓ إِسْحَـٰقَ وَيَعْقُوبَ وَجَعَلْنَا فِى ذُرِّيَّتِهِ ٱلنُّبُوَّةَ وَٱلْكِتَـٰبَ وَءَاتَيْنَـٰهُ أَجْرَهُۥ فِى ٱلدُّنْيَا ۖ وَإِنَّهُۥ فِى ٱلْـَٔاخِرَةِ لَمِنَ ٱلصَّـٰلِحِينَ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 29, 399, 'ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ', 'وَلُوطًا إِذْ قَالَ لِقَوْمِهِۦٓ إِنَّكُمْ لَتَأْتُونَ ٱلْفَـٰحِشَةَ مَا سَبَقَكُم بِهَا مِنْ أَحَدٍۢ مِّنَ ٱلْعَـٰلَمِينَ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 29, 399, 'ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ', 'أَئِنَّكُمْ لَتَأْتُونَ ٱلرِّجَالَ وَتَقْطَعُونَ ٱلسَّبِيلَ وَتَأْتُونَ فِى نَادِيكُمُ ٱلْمُنكَرَ ۖ فَمَا كَانَ جَوَابَ قَوْمِهِۦٓ إِلَّآ أَن قَالُوا۟ ٱئْتِنَا بِعَذَابِ ٱللَّهِ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 29, 399, 'ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ', 'قَالَ رَبِّ ٱنصُرْنِى عَلَى ٱلْقَوْمِ ٱلْمُفْسِدِينَ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 29, 400, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋﱌ ﱍ ﱎ ﱏ ﱐ ﱑ', 'وَلَمَّا جَآءَتْ رُسُلُنَآ إِبْرَٰهِيمَ بِٱلْبُشْرَىٰ قَالُوٓا۟ إِنَّا مُهْلِكُوٓا۟ أَهْلِ هَـٰذِهِ ٱلْقَرْيَةِ ۖ إِنَّ أَهْلَهَا كَانُوا۟ ظَـٰلِمِينَ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 29, 400, 'ﱒ ﱓ ﱔ ﱕﱖ ﱗ ﱘ ﱙ ﱚ ﱛﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ', 'قَالَ إِنَّ فِيهَا لُوطًۭا ۚ قَالُوا۟ نَحْنُ أَعْلَمُ بِمَن فِيهَا ۖ لَنُنَجِّيَنَّهُۥ وَأَهْلَهُۥٓ إِلَّا ٱمْرَأَتَهُۥ كَانَتْ مِنَ ٱلْغَـٰبِرِينَ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 29, 400, 'ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ', 'وَلَمَّآ أَن جَآءَتْ رُسُلُنَا لُوطًۭا سِىٓءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًۭا وَقَالُوا۟ لَا تَخَفْ وَلَا تَحْزَنْ ۖ إِنَّا مُنَجُّوكَ وَأَهْلَكَ إِلَّا ٱمْرَأَتَكَ كَانَتْ مِنَ ٱلْغَـٰبِرِينَ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 29, 400, 'ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ', 'إِنَّا مُنزِلُونَ عَلَىٰٓ أَهْلِ هَـٰذِهِ ٱلْقَرْيَةِ رِجْزًۭا مِّنَ ٱلسَّمَآءِ بِمَا كَانُوا۟ يَفْسُقُونَ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 29, 400, 'ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ', 'وَلَقَد تَّرَكْنَا مِنْهَآ ءَايَةًۢ بَيِّنَةًۭ لِّقَوْمٍۢ يَعْقِلُونَ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 29, 400, 'ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ', 'وَإِلَىٰ مَدْيَنَ أَخَاهُمْ شُعَيْبًۭا فَقَالَ يَـٰقَوْمِ ٱعْبُدُوا۟ ٱللَّهَ وَٱرْجُوا۟ ٱلْيَوْمَ ٱلْـَٔاخِرَ وَلَا تَعْثَوْا۟ فِى ٱلْأَرْضِ مُفْسِدِينَ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 29, 400, 'ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ', 'فَكَذَّبُوهُ فَأَخَذَتْهُمُ ٱلرَّجْفَةُ فَأَصْبَحُوا۟ فِى دَارِهِمْ جَـٰثِمِينَ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 29, 400, 'ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ', 'وَعَادًۭا وَثَمُودَا۟ وَقَد تَّبَيَّنَ لَكُم مِّن مَّسَـٰكِنِهِمْ ۖ وَزَيَّنَ لَهُمُ ٱلشَّيْطَـٰنُ أَعْمَـٰلَهُمْ فَصَدَّهُمْ عَنِ ٱلسَّبِيلِ وَكَانُوا۟ مُسْتَبْصِرِينَ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 29, 401, 'ﱁ ﱂ ﱃﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ', 'وَقَـٰرُونَ وَفِرْعَوْنَ وَهَـٰمَـٰنَ ۖ وَلَقَدْ جَآءَهُم مُّوسَىٰ بِٱلْبَيِّنَـٰتِ فَٱسْتَكْبَرُوا۟ فِى ٱلْأَرْضِ وَمَا كَانُوا۟ سَـٰبِقِينَ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 29, 401, 'ﱐ ﱑ ﱒﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ', 'فَكُلًّا أَخَذْنَا بِذَنۢبِهِۦ ۖ فَمِنْهُم مَّنْ أَرْسَلْنَا عَلَيْهِ حَاصِبًۭا وَمِنْهُم مَّنْ أَخَذَتْهُ ٱلصَّيْحَةُ وَمِنْهُم مَّنْ خَسَفْنَا بِهِ ٱلْأَرْضَ وَمِنْهُم مَّنْ أَغْرَقْنَا ۚ وَمَا كَانَ ٱللَّهُ لِيَظْلِمَهُمْ وَلَـٰكِن كَانُوٓا۟ أَنفُسَهُمْ يَظْلِمُونَ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 29, 401, 'ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹﱺ ﱻ ﱼ ﱽ ﱾ ﱿﲀ ﲁ ﲂ ﲃ ﲄ', 'مَثَلُ ٱلَّذِينَ ٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ أَوْلِيَآءَ كَمَثَلِ ٱلْعَنكَبُوتِ ٱتَّخَذَتْ بَيْتًۭا ۖ وَإِنَّ أَوْهَنَ ٱلْبُيُوتِ لَبَيْتُ ٱلْعَنكَبُوتِ ۖ لَوْ كَانُوا۟ يَعْلَمُونَ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 29, 401, 'ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍﲎ ﲏ ﲐ ﲑ ﲒ', 'إِنَّ ٱللَّهَ يَعْلَمُ مَا يَدْعُونَ مِن دُونِهِۦ مِن شَىْءٍۢ ۚ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 29, 401, 'ﲓ ﲔ ﲕ ﲖﲗ ﲘ ﲙ ﲚ ﲛ ﲜ', 'وَتِلْكَ ٱلْأَمْثَـٰلُ نَضْرِبُهَا لِلنَّاسِ ۖ وَمَا يَعْقِلُهَآ إِلَّا ٱلْعَـٰلِمُونَ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 29, 401, 'ﲝ ﲞ ﲟ ﲠ ﲡﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ', 'خَلَقَ ٱللَّهُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ بِٱلْحَقِّ ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَةًۭ لِّلْمُؤْمِنِينَ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 29, 401, 'ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷﲸ ﲹ ﲺ ﲻﲼ ﲽ ﲾ ﲿ ﳀ ﳁ', 'ٱتْلُ مَآ أُوحِىَ إِلَيْكَ مِنَ ٱلْكِتَـٰبِ وَأَقِمِ ٱلصَّلَوٰةَ ۖ إِنَّ ٱلصَّلَوٰةَ تَنْهَىٰ عَنِ ٱلْفَحْشَآءِ وَٱلْمُنكَرِ ۗ وَلَذِكْرُ ٱللَّهِ أَكْبَرُ ۗ وَٱللَّهُ يَعْلَمُ مَا تَصْنَعُونَ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 29, 402, 'ﱁﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ', '۞ وَلَا تُجَـٰدِلُوٓا۟ أَهْلَ ٱلْكِتَـٰبِ إِلَّا بِٱلَّتِى هِىَ أَحْسَنُ إِلَّا ٱلَّذِينَ ظَلَمُوا۟ مِنْهُمْ ۖ وَقُولُوٓا۟ ءَامَنَّا بِٱلَّذِىٓ أُنزِلَ إِلَيْنَا وَأُنزِلَ إِلَيْكُمْ وَإِلَـٰهُنَا وَإِلَـٰهُكُمْ وَٰحِدٌۭ وَنَحْنُ لَهُۥ مُسْلِمُونَ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 29, 402, 'ﱝ ﱞ ﱟ ﱠﱡ ﱢ ﱣ ﱤ ﱥ ﱦﱧ ﱨ ﱩ ﱪ ﱫ ﱬﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ', 'وَكَذَٰلِكَ أَنزَلْنَآ إِلَيْكَ ٱلْكِتَـٰبَ ۚ فَٱلَّذِينَ ءَاتَيْنَـٰهُمُ ٱلْكِتَـٰبَ يُؤْمِنُونَ بِهِۦ ۖ وَمِنْ هَـٰٓؤُلَآءِ مَن يُؤْمِنُ بِهِۦ ۚ وَمَا يَجْحَدُ بِـَٔايَـٰتِنَآ إِلَّا ٱلْكَـٰفِرُونَ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 29, 402, 'ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽﱾ ﱿ ﲀ ﲁ ﲂ', 'وَمَا كُنتَ تَتْلُوا۟ مِن قَبْلِهِۦ مِن كِتَـٰبٍۢ وَلَا تَخُطُّهُۥ بِيَمِينِكَ ۖ إِذًۭا لَّٱرْتَابَ ٱلْمُبْطِلُونَ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 29, 402, 'ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ', 'بَلْ هُوَ ءَايَـٰتٌۢ بَيِّنَـٰتٌۭ فِى صُدُورِ ٱلَّذِينَ أُوتُوا۟ ٱلْعِلْمَ ۚ وَمَا يَجْحَدُ بِـَٔايَـٰتِنَآ إِلَّا ٱلظَّـٰلِمُونَ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 29, 402, 'ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ', 'وَقَالُوا۟ لَوْلَآ أُنزِلَ عَلَيْهِ ءَايَـٰتٌۭ مِّن رَّبِّهِۦ ۖ قُلْ إِنَّمَا ٱلْـَٔايَـٰتُ عِندَ ٱللَّهِ وَإِنَّمَآ أَنَا۠ نَذِيرٌۭ مُّبِينٌ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 29, 402, 'ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ', 'أَوَلَمْ يَكْفِهِمْ أَنَّآ أَنزَلْنَا عَلَيْكَ ٱلْكِتَـٰبَ يُتْلَىٰ عَلَيْهِمْ ۚ إِنَّ فِى ذَٰلِكَ لَرَحْمَةًۭ وَذِكْرَىٰ لِقَوْمٍۢ يُؤْمِنُونَ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 29, 402, 'ﲶ ﲷ ﲸ ﲹ ﲺ ﲻﲼ ﲽ ﲾ ﲿ ﳀ ﳁﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ', 'قُلْ كَفَىٰ بِٱللَّهِ بَيْنِى وَبَيْنَكُمْ شَهِيدًۭا ۖ يَعْلَمُ مَا فِى ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۗ وَٱلَّذِينَ ءَامَنُوا۟ بِٱلْبَـٰطِلِ وَكَفَرُوا۟ بِٱللَّهِ أُو۟لَـٰٓئِكَ هُمُ ٱلْخَـٰسِرُونَ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 29, 403, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ', 'وَيَسْتَعْجِلُونَكَ بِٱلْعَذَابِ ۚ وَلَوْلَآ أَجَلٌۭ مُّسَمًّۭى لَّجَآءَهُمُ ٱلْعَذَابُ وَلَيَأْتِيَنَّهُم بَغْتَةًۭ وَهُمْ لَا يَشْعُرُونَ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 29, 403, 'ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'يَسْتَعْجِلُونَكَ بِٱلْعَذَابِ وَإِنَّ جَهَنَّمَ لَمُحِيطَةٌۢ بِٱلْكَـٰفِرِينَ ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (55, 29, 403, 'ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ', 'يَوْمَ يَغْشَىٰهُمُ ٱلْعَذَابُ مِن فَوْقِهِمْ وَمِن تَحْتِ أَرْجُلِهِمْ وَيَقُولُ ذُوقُوا۟ مَا كُنتُمْ تَعْمَلُونَ ٥٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (56, 29, 403, 'ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ', 'يَـٰعِبَادِىَ ٱلَّذِينَ ءَامَنُوٓا۟ إِنَّ أَرْضِى وَٰسِعَةٌۭ فَإِيَّـٰىَ فَٱعْبُدُونِ ٥٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (57, 29, 403, 'ﱭ ﱮ ﱯ ﱰﱱ ﱲ ﱳ ﱴ ﱵ', 'كُلُّ نَفْسٍۢ ذَآئِقَةُ ٱلْمَوْتِ ۖ ثُمَّ إِلَيْنَا تُرْجَعُونَ ٥٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (58, 29, 403, 'ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃﲄ ﲅ ﲆ ﲇ ﲈ', 'وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَنُبَوِّئَنَّهُم مِّنَ ٱلْجَنَّةِ غُرَفًۭا تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَـٰرُ خَـٰلِدِينَ فِيهَا ۚ نِعْمَ أَجْرُ ٱلْعَـٰمِلِينَ ٥٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (59, 29, 403, 'ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ', 'ٱلَّذِينَ صَبَرُوا۟ وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَ ٥٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (60, 29, 403, 'ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗﲘ ﲙ ﲚ ﲛ ﲜ', 'وَكَأَيِّن مِّن دَآبَّةٍۢ لَّا تَحْمِلُ رِزْقَهَا ٱللَّهُ يَرْزُقُهَا وَإِيَّاكُمْ ۚ وَهُوَ ٱلسَّمِيعُ ٱلْعَلِيمُ ٦٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (61, 29, 403, 'ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧﲨ ﲩ ﲪ ﲫ', 'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ وَسَخَّرَ ٱلشَّمْسَ وَٱلْقَمَرَ لَيَقُولُنَّ ٱللَّهُ ۖ فَأَنَّىٰ يُؤْفَكُونَ ٦١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (62, 29, 403, 'ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ', 'ٱللَّهُ يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ مِنْ عِبَادِهِۦ وَيَقْدِرُ لَهُۥٓ ۚ إِنَّ ٱللَّهَ بِكُلِّ شَىْءٍ عَلِيمٌۭ ٦٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (63, 29, 403, 'ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊﳋ ﳌ ﳍ ﳎﳏ ﳐ ﳑ ﳒ ﳓ ﳔ', 'وَلَئِن سَأَلْتَهُم مَّن نَّزَّلَ مِنَ ٱلسَّمَآءِ مَآءًۭ فَأَحْيَا بِهِ ٱلْأَرْضَ مِنۢ بَعْدِ مَوْتِهَا لَيَقُولُنَّ ٱللَّهُ ۚ قُلِ ٱلْحَمْدُ لِلَّهِ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْقِلُونَ ٦٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (64, 29, 404, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇﱈ ﱉ ﱊ ﱋ ﱌ ﱍﱎ ﱏ ﱐ ﱑ ﱒ', 'وَمَا هَـٰذِهِ ٱلْحَيَوٰةُ ٱلدُّنْيَآ إِلَّا لَهْوٌۭ وَلَعِبٌۭ ۚ وَإِنَّ ٱلدَّارَ ٱلْـَٔاخِرَةَ لَهِىَ ٱلْحَيَوَانُ ۚ لَوْ كَانُوا۟ يَعْلَمُونَ ٦٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (65, 29, 404, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ', 'فَإِذَا رَكِبُوا۟ فِى ٱلْفُلْكِ دَعَوُا۟ ٱللَّهَ مُخْلِصِينَ لَهُ ٱلدِّينَ فَلَمَّا نَجَّىٰهُمْ إِلَى ٱلْبَرِّ إِذَا هُمْ يُشْرِكُونَ ٦٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (66, 29, 404, 'ﱤ ﱥ ﱦ ﱧﱨ ﱩ ﱪ ﱫ', 'لِيَكْفُرُوا۟ بِمَآ ءَاتَيْنَـٰهُمْ وَلِيَتَمَتَّعُوا۟ ۖ فَسَوْفَ يَعْلَمُونَ ٦٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (67, 29, 404, 'ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ', 'أَوَلَمْ يَرَوْا۟ أَنَّا جَعَلْنَا حَرَمًا ءَامِنًۭا وَيُتَخَطَّفُ ٱلنَّاسُ مِنْ حَوْلِهِمْ ۚ أَفَبِٱلْبَـٰطِلِ يُؤْمِنُونَ وَبِنِعْمَةِ ٱللَّهِ يَكْفُرُونَ ٦٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (68, 29, 404, 'ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ', 'وَمَنْ أَظْلَمُ مِمَّنِ ٱفْتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوْ كَذَّبَ بِٱلْحَقِّ لَمَّا جَآءَهُۥٓ ۚ أَلَيْسَ فِى جَهَنَّمَ مَثْوًۭى لِّلْكَـٰفِرِينَ ٦٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (69, 29, 404, 'ﲐ ﲑ ﲒ ﲓ ﲔﲕ ﲖ ﲗ ﲘ ﲙ ﲚ', 'وَٱلَّذِينَ جَـٰهَدُوا۟ فِينَا لَنَهْدِيَنَّهُمْ سُبُلَنَا ۚ وَإِنَّ ٱللَّهَ لَمَعَ ٱلْمُحْسِنِينَ ٦٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 30, 404, 'ﲛ ﲜ', 'الٓمٓ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 30, 404, 'ﲝ ﲞ ﲟ', 'غُلِبَتِ ٱلرُّومُ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 30, 404, 'ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ', 'فِىٓ أَدْنَى ٱلْأَرْضِ وَهُم مِّنۢ بَعْدِ غَلَبِهِمْ سَيَغْلِبُونَ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 30, 404, 'ﲩ ﲪ ﲫﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲﲳ ﲴ ﲵ ﲶ ﲷ', 'فِى بِضْعِ سِنِينَ ۗ لِلَّهِ ٱلْأَمْرُ مِن قَبْلُ وَمِنۢ بَعْدُ ۚ وَيَوْمَئِذٍۢ يَفْرَحُ ٱلْمُؤْمِنُونَ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 30, 404, 'ﲸ ﲹﲺ ﲻ ﲼ ﲽﲾ ﲿ ﳀ ﳁ ﳂ', 'بِنَصْرِ ٱللَّهِ ۚ يَنصُرُ مَن يَشَآءُ ۖ وَهُوَ ٱلْعَزِيزُ ٱلرَّحِيمُ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 30, 405, 'ﱁ ﱂﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ', 'وَعْدَ ٱللَّهِ ۖ لَا يُخْلِفُ ٱللَّهُ وَعْدَهُۥ وَلَـٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَعْلَمُونَ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 30, 405, 'ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ', 'يَعْلَمُونَ ظَـٰهِرًۭا مِّنَ ٱلْحَيَوٰةِ ٱلدُّنْيَا وَهُمْ عَنِ ٱلْـَٔاخِرَةِ هُمْ غَـٰفِلُونَ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 30, 405, 'ﱙ ﱚ ﱛ ﱜﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ', 'أَوَلَمْ يَتَفَكَّرُوا۟ فِىٓ أَنفُسِهِم ۗ مَّا خَلَقَ ٱللَّهُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ وَمَا بَيْنَهُمَآ إِلَّا بِٱلْحَقِّ وَأَجَلٍۢ مُّسَمًّۭى ۗ وَإِنَّ كَثِيرًۭا مِّنَ ٱلنَّاسِ بِلِقَآئِ رَبِّهِمْ لَكَـٰفِرُونَ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 30, 405, 'ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ', 'أَوَلَمْ يَسِيرُوا۟ فِى ٱلْأَرْضِ فَيَنظُرُوا۟ كَيْفَ كَانَ عَـٰقِبَةُ ٱلَّذِينَ مِن قَبْلِهِمْ ۚ كَانُوٓا۟ أَشَدَّ مِنْهُمْ قُوَّةًۭ وَأَثَارُوا۟ ٱلْأَرْضَ وَعَمَرُوهَآ أَكْثَرَ مِمَّا عَمَرُوهَا وَجَآءَتْهُمْ رُسُلُهُم بِٱلْبَيِّنَـٰتِ ۖ فَمَا كَانَ ٱللَّهُ لِيَظْلِمَهُمْ وَلَـٰكِن كَانُوٓا۟ أَنفُسَهُمْ يَظْلِمُونَ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 30, 405, 'ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ', 'ثُمَّ كَانَ عَـٰقِبَةَ ٱلَّذِينَ أَسَـٰٓـُٔوا۟ ٱلسُّوٓأَىٰٓ أَن كَذَّبُوا۟ بِـَٔايَـٰتِ ٱللَّهِ وَكَانُوا۟ بِهَا يَسْتَهْزِءُونَ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 30, 405, 'ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ', 'ٱللَّهُ يَبْدَؤُا۟ ٱلْخَلْقَ ثُمَّ يُعِيدُهُۥ ثُمَّ إِلَيْهِ تُرْجَعُونَ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 30, 405, 'ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ', 'وَيَوْمَ تَقُومُ ٱلسَّاعَةُ يُبْلِسُ ٱلْمُجْرِمُونَ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 30, 405, 'ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ', 'وَلَمْ يَكُن لَّهُم مِّن شُرَكَآئِهِمْ شُفَعَـٰٓؤُا۟ وَكَانُوا۟ بِشُرَكَآئِهِمْ كَـٰفِرِينَ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 30, 405, 'ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ', 'وَيَوْمَ تَقُومُ ٱلسَّاعَةُ يَوْمَئِذٍۢ يَتَفَرَّقُونَ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 30, 405, 'ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ', 'فَأَمَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ فَهُمْ فِى رَوْضَةٍۢ يُحْبَرُونَ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 30, 406, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ', 'وَأَمَّا ٱلَّذِينَ كَفَرُوا۟ وَكَذَّبُوا۟ بِـَٔايَـٰتِنَا وَلِقَآئِ ٱلْـَٔاخِرَةِ فَأُو۟لَـٰٓئِكَ فِى ٱلْعَذَابِ مُحْضَرُونَ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 30, 406, 'ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ', 'فَسُبْحَـٰنَ ٱللَّهِ حِينَ تُمْسُونَ وَحِينَ تُصْبِحُونَ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 30, 406, 'ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ', 'وَلَهُ ٱلْحَمْدُ فِى ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ وَعَشِيًّۭا وَحِينَ تُظْهِرُونَ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 30, 406, 'ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨﱩ ﱪ ﱫ ﱬ', 'يُخْرِجُ ٱلْحَىَّ مِنَ ٱلْمَيِّتِ وَيُخْرِجُ ٱلْمَيِّتَ مِنَ ٱلْحَىِّ وَيُحْىِ ٱلْأَرْضَ بَعْدَ مَوْتِهَا ۚ وَكَذَٰلِكَ تُخْرَجُونَ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 30, 406, 'ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ', 'وَمِنْ ءَايَـٰتِهِۦٓ أَنْ خَلَقَكُم مِّن تُرَابٍۢ ثُمَّ إِذَآ أَنتُم بَشَرٌۭ تَنتَشِرُونَ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 30, 406, 'ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ', 'وَمِنْ ءَايَـٰتِهِۦٓ أَنْ خَلَقَ لَكُم مِّنْ أَنفُسِكُمْ أَزْوَٰجًۭا لِّتَسْكُنُوٓا۟ إِلَيْهَا وَجَعَلَ بَيْنَكُم مَّوَدَّةًۭ وَرَحْمَةً ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَـٰتٍۢ لِّقَوْمٍۢ يَتَفَكَّرُونَ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 30, 406, 'ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ', 'وَمِنْ ءَايَـٰتِهِۦ خَلْقُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ وَٱخْتِلَـٰفُ أَلْسِنَتِكُمْ وَأَلْوَٰنِكُمْ ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَـٰتٍۢ لِّلْعَـٰلِمِينَ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 30, 406, 'ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ', 'وَمِنْ ءَايَـٰتِهِۦ مَنَامُكُم بِٱلَّيْلِ وَٱلنَّهَارِ وَٱبْتِغَآؤُكُم مِّن فَضْلِهِۦٓ ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَـٰتٍۢ لِّقَوْمٍۢ يَسْمَعُونَ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 30, 406, 'ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ', 'وَمِنْ ءَايَـٰتِهِۦ يُرِيكُمُ ٱلْبَرْقَ خَوْفًۭا وَطَمَعًۭا وَيُنَزِّلُ مِنَ ٱلسَّمَآءِ مَآءًۭ فَيُحْىِۦ بِهِ ٱلْأَرْضَ بَعْدَ مَوْتِهَآ ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَـٰتٍۢ لِّقَوْمٍۢ يَعْقِلُونَ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 30, 407, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ', 'وَمِنْ ءَايَـٰتِهِۦٓ أَن تَقُومَ ٱلسَّمَآءُ وَٱلْأَرْضُ بِأَمْرِهِۦ ۚ ثُمَّ إِذَا دَعَاكُمْ دَعْوَةًۭ مِّنَ ٱلْأَرْضِ إِذَآ أَنتُمْ تَخْرُجُونَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 30, 407, 'ﱓ ﱔ ﱕ ﱖ ﱗﱘ ﱙ ﱚ ﱛ ﱜ', 'وَلَهُۥ مَن فِى ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۖ كُلٌّۭ لَّهُۥ قَـٰنِتُونَ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 30, 407, 'ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬﱭ ﱮ ﱯ ﱰ ﱱ', 'وَهُوَ ٱلَّذِى يَبْدَؤُا۟ ٱلْخَلْقَ ثُمَّ يُعِيدُهُۥ وَهُوَ أَهْوَنُ عَلَيْهِ ۚ وَلَهُ ٱلْمَثَلُ ٱلْأَعْلَىٰ فِى ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۚ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 30, 407, 'ﱲ ﱳ ﱴ ﱵ ﱶﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ', 'ضَرَبَ لَكُم مَّثَلًۭا مِّنْ أَنفُسِكُمْ ۖ هَل لَّكُم مِّن مَّا مَلَكَتْ أَيْمَـٰنُكُم مِّن شُرَكَآءَ فِى مَا رَزَقْنَـٰكُمْ فَأَنتُمْ فِيهِ سَوَآءٌۭ تَخَافُونَهُمْ كَخِيفَتِكُمْ أَنفُسَكُمْ ۚ كَذَٰلِكَ نُفَصِّلُ ٱلْـَٔايَـٰتِ لِقَوْمٍۢ يَعْقِلُونَ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 30, 407, 'ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖﲗ ﲘ ﲙ ﲚ ﲛ ﲜﲝ ﲞ ﲟ ﲠ ﲡ ﲢ', 'بَلِ ٱتَّبَعَ ٱلَّذِينَ ظَلَمُوٓا۟ أَهْوَآءَهُم بِغَيْرِ عِلْمٍۢ ۖ فَمَن يَهْدِى مَنْ أَضَلَّ ٱللَّهُ ۖ وَمَا لَهُم مِّن نَّـٰصِرِينَ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 30, 407, 'ﲣ ﲤ ﲥ ﲦﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭﲮ ﲯ ﲰ ﲱ ﲲﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ', 'فَأَقِمْ وَجْهَكَ لِلدِّينِ حَنِيفًۭا ۚ فِطْرَتَ ٱللَّهِ ٱلَّتِى فَطَرَ ٱلنَّاسَ عَلَيْهَا ۚ لَا تَبْدِيلَ لِخَلْقِ ٱللَّهِ ۚ ذَٰلِكَ ٱلدِّينُ ٱلْقَيِّمُ وَلَـٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَعْلَمُونَ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 30, 407, 'ﲽﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ', '۞ مُنِيبِينَ إِلَيْهِ وَٱتَّقُوهُ وَأَقِيمُوا۟ ٱلصَّلَوٰةَ وَلَا تَكُونُوا۟ مِنَ ٱلْمُشْرِكِينَ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 30, 407, 'ﳈ ﳉ ﳊ ﳋ ﳌ ﳍﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ', 'مِنَ ٱلَّذِينَ فَرَّقُوا۟ دِينَهُمْ وَكَانُوا۟ شِيَعًۭا ۖ كُلُّ حِزْبٍۭ بِمَا لَدَيْهِمْ فَرِحُونَ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 30, 408, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ', 'وَإِذَا مَسَّ ٱلنَّاسَ ضُرٌّۭ دَعَوْا۟ رَبَّهُم مُّنِيبِينَ إِلَيْهِ ثُمَّ إِذَآ أَذَاقَهُم مِّنْهُ رَحْمَةً إِذَا فَرِيقٌۭ مِّنْهُم بِرَبِّهِمْ يُشْرِكُونَ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 30, 408, 'ﱔ ﱕ ﱖﱗ ﱘ ﱙ ﱚ ﱛ', 'لِيَكْفُرُوا۟ بِمَآ ءَاتَيْنَـٰهُمْ ۚ فَتَمَتَّعُوا۟ فَسَوْفَ تَعْلَمُونَ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 30, 408, 'ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ', 'أَمْ أَنزَلْنَا عَلَيْهِمْ سُلْطَـٰنًۭا فَهُوَ يَتَكَلَّمُ بِمَا كَانُوا۟ بِهِۦ يُشْرِكُونَ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 30, 408, 'ﱧ ﱨ ﱩ ﱪ ﱫ ﱬﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ', 'وَإِذَآ أَذَقْنَا ٱلنَّاسَ رَحْمَةًۭ فَرِحُوا۟ بِهَا ۖ وَإِن تُصِبْهُمْ سَيِّئَةٌۢ بِمَا قَدَّمَتْ أَيْدِيهِمْ إِذَا هُمْ يَقْنَطُونَ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 30, 408, 'ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ', 'أَوَلَمْ يَرَوْا۟ أَنَّ ٱللَّهَ يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ وَيَقْدِرُ ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَـٰتٍۢ لِّقَوْمٍۢ يُؤْمِنُونَ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 30, 408, 'ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖﲗ ﲘ ﲙ ﲚ ﲛ', 'فَـَٔاتِ ذَا ٱلْقُرْبَىٰ حَقَّهُۥ وَٱلْمِسْكِينَ وَٱبْنَ ٱلسَّبِيلِ ۚ ذَٰلِكَ خَيْرٌۭ لِّلَّذِينَ يُرِيدُونَ وَجْهَ ٱللَّهِ ۖ وَأُو۟لَـٰٓئِكَ هُمُ ٱلْمُفْلِحُونَ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 30, 408, 'ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ', 'وَمَآ ءَاتَيْتُم مِّن رِّبًۭا لِّيَرْبُوَا۟ فِىٓ أَمْوَٰلِ ٱلنَّاسِ فَلَا يَرْبُوا۟ عِندَ ٱللَّهِ ۖ وَمَآ ءَاتَيْتُم مِّن زَكَوٰةٍۢ تُرِيدُونَ وَجْهَ ٱللَّهِ فَأُو۟لَـٰٓئِكَ هُمُ ٱلْمُضْعِفُونَ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 30, 408, 'ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆﳇ ﳈ ﳉ ﳊ ﳋ ﳌ', 'ٱللَّهُ ٱلَّذِى خَلَقَكُمْ ثُمَّ رَزَقَكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ ۖ هَلْ مِن شُرَكَآئِكُم مَّن يَفْعَلُ مِن ذَٰلِكُم مِّن شَىْءٍۢ ۚ سُبْحَـٰنَهُۥ وَتَعَـٰلَىٰ عَمَّا يُشْرِكُونَ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 30, 408, 'ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ', 'ظَهَرَ ٱلْفَسَادُ فِى ٱلْبَرِّ وَٱلْبَحْرِ بِمَا كَسَبَتْ أَيْدِى ٱلنَّاسِ لِيُذِيقَهُم بَعْضَ ٱلَّذِى عَمِلُوا۟ لَعَلَّهُمْ يَرْجِعُونَ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 30, 409, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋﱌ ﱍ ﱎ ﱏ ﱐ', 'قُلْ سِيرُوا۟ فِى ٱلْأَرْضِ فَٱنظُرُوا۟ كَيْفَ كَانَ عَـٰقِبَةُ ٱلَّذِينَ مِن قَبْلُ ۚ كَانَ أَكْثَرُهُم مُّشْرِكِينَ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 30, 409, 'ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞﱟ ﱠ ﱡ ﱢ', 'فَأَقِمْ وَجْهَكَ لِلدِّينِ ٱلْقَيِّمِ مِن قَبْلِ أَن يَأْتِىَ يَوْمٌۭ لَّا مَرَدَّ لَهُۥ مِنَ ٱللَّهِ ۖ يَوْمَئِذٍۢ يَصَّدَّعُونَ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 30, 409, 'ﱣ ﱤ ﱥ ﱦﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ', 'مَن كَفَرَ فَعَلَيْهِ كُفْرُهُۥ ۖ وَمَنْ عَمِلَ صَـٰلِحًۭا فَلِأَنفُسِهِمْ يَمْهَدُونَ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 30, 409, 'ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴﱵ ﱶ ﱷ ﱸ ﱹ ﱺ', 'لِيَجْزِىَ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ مِن فَضْلِهِۦٓ ۚ إِنَّهُۥ لَا يُحِبُّ ٱلْكَـٰفِرِينَ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 30, 409, 'ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ', 'وَمِنْ ءَايَـٰتِهِۦٓ أَن يُرْسِلَ ٱلرِّيَاحَ مُبَشِّرَٰتٍۢ وَلِيُذِيقَكُم مِّن رَّحْمَتِهِۦ وَلِتَجْرِىَ ٱلْفُلْكُ بِأَمْرِهِۦ وَلِتَبْتَغُوا۟ مِن فَضْلِهِۦ وَلَعَلَّكُمْ تَشْكُرُونَ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 30, 409, 'ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ', 'وَلَقَدْ أَرْسَلْنَا مِن قَبْلِكَ رُسُلًا إِلَىٰ قَوْمِهِمْ فَجَآءُوهُم بِٱلْبَيِّنَـٰتِ فَٱنتَقَمْنَا مِنَ ٱلَّذِينَ أَجْرَمُوا۟ ۖ وَكَانَ حَقًّا عَلَيْنَا نَصْرُ ٱلْمُؤْمِنِينَ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 30, 409, 'ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ', 'ٱللَّهُ ٱلَّذِى يُرْسِلُ ٱلرِّيَـٰحَ فَتُثِيرُ سَحَابًۭا فَيَبْسُطُهُۥ فِى ٱلسَّمَآءِ كَيْفَ يَشَآءُ وَيَجْعَلُهُۥ كِسَفًۭا فَتَرَى ٱلْوَدْقَ يَخْرُجُ مِنْ خِلَـٰلِهِۦ ۖ فَإِذَآ أَصَابَ بِهِۦ مَن يَشَآءُ مِنْ عِبَادِهِۦٓ إِذَا هُمْ يَسْتَبْشِرُونَ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 30, 409, 'ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ', 'وَإِن كَانُوا۟ مِن قَبْلِ أَن يُنَزَّلَ عَلَيْهِم مِّن قَبْلِهِۦ لَمُبْلِسِينَ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 30, 409, 'ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓﳔ ﳕ ﳖ ﳗ ﳘﳙ ﳚ ﳛ ﳜ ﳝ ﳞ ﳟ', 'فَٱنظُرْ إِلَىٰٓ ءَاثَـٰرِ رَحْمَتِ ٱللَّهِ كَيْفَ يُحْىِ ٱلْأَرْضَ بَعْدَ مَوْتِهَآ ۚ إِنَّ ذَٰلِكَ لَمُحْىِ ٱلْمَوْتَىٰ ۖ وَهُوَ عَلَىٰ كُلِّ شَىْءٍۢ قَدِيرٌۭ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 30, 410, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ', 'وَلَئِنْ أَرْسَلْنَا رِيحًۭا فَرَأَوْهُ مُصْفَرًّۭا لَّظَلُّوا۟ مِنۢ بَعْدِهِۦ يَكْفُرُونَ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 30, 410, 'ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ', 'فَإِنَّكَ لَا تُسْمِعُ ٱلْمَوْتَىٰ وَلَا تُسْمِعُ ٱلصُّمَّ ٱلدُّعَآءَ إِذَا وَلَّوْا۟ مُدْبِرِينَ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 30, 410, 'ﱗ ﱘ ﱙ ﱚ ﱛ ﱜﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ', 'وَمَآ أَنتَ بِهَـٰدِ ٱلْعُمْىِ عَن ضَلَـٰلَتِهِمْ ۖ إِن تُسْمِعُ إِلَّا مَن يُؤْمِنُ بِـَٔايَـٰتِنَا فَهُم مُّسْلِمُونَ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 30, 410, 'ﱧﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹﱺ ﱻ ﱼ ﱽﱾ ﱿ ﲀ ﲁ ﲂ', '۞ ٱللَّهُ ٱلَّذِى خَلَقَكُم مِّن ضَعْفٍۢ ثُمَّ جَعَلَ مِنۢ بَعْدِ ضَعْفٍۢ قُوَّةًۭ ثُمَّ جَعَلَ مِنۢ بَعْدِ قُوَّةٍۢ ضَعْفًۭا وَشَيْبَةًۭ ۚ يَخْلُقُ مَا يَشَآءُ ۖ وَهُوَ ٱلْعَلِيمُ ٱلْقَدِيرُ ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (55, 30, 410, 'ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋﲌ ﲍ ﲎ ﲏ ﲐ', 'وَيَوْمَ تَقُومُ ٱلسَّاعَةُ يُقْسِمُ ٱلْمُجْرِمُونَ مَا لَبِثُوا۟ غَيْرَ سَاعَةٍۢ ۚ كَذَٰلِكَ كَانُوا۟ يُؤْفَكُونَ ٥٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (56, 30, 410, 'ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ', 'وَقَالَ ٱلَّذِينَ أُوتُوا۟ ٱلْعِلْمَ وَٱلْإِيمَـٰنَ لَقَدْ لَبِثْتُمْ فِى كِتَـٰبِ ٱللَّهِ إِلَىٰ يَوْمِ ٱلْبَعْثِ ۖ فَهَـٰذَا يَوْمُ ٱلْبَعْثِ وَلَـٰكِنَّكُمْ كُنتُمْ لَا تَعْلَمُونَ ٥٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (57, 30, 410, 'ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ', 'فَيَوْمَئِذٍۢ لَّا يَنفَعُ ٱلَّذِينَ ظَلَمُوا۟ مَعْذِرَتُهُمْ وَلَا هُمْ يُسْتَعْتَبُونَ ٥٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (58, 30, 410, 'ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ', 'وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِى هَـٰذَا ٱلْقُرْءَانِ مِن كُلِّ مَثَلٍۢ ۚ وَلَئِن جِئْتَهُم بِـَٔايَةٍۢ لَّيَقُولَنَّ ٱلَّذِينَ كَفَرُوٓا۟ إِنْ أَنتُمْ إِلَّا مُبْطِلُونَ ٥٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (59, 30, 410, 'ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ', 'كَذَٰلِكَ يَطْبَعُ ٱللَّهُ عَلَىٰ قُلُوبِ ٱلَّذِينَ لَا يَعْلَمُونَ ٥٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (60, 30, 410, 'ﳏ ﳐ ﳑ ﳒ ﳓﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ', 'فَٱصْبِرْ إِنَّ وَعْدَ ٱللَّهِ حَقٌّۭ ۖ وَلَا يَسْتَخِفَّنَّكَ ٱلَّذِينَ لَا يُوقِنُونَ ٦٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 31, 411, 'ﱁ ﱂ', 'الٓمٓ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 31, 411, 'ﱃ ﱄ ﱅ ﱆ ﱇ', 'تِلْكَ ءَايَـٰتُ ٱلْكِتَـٰبِ ٱلْحَكِيمِ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 31, 411, 'ﱈ ﱉ ﱊ ﱋ', 'هُدًۭى وَرَحْمَةًۭ لِّلْمُحْسِنِينَ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 31, 411, 'ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤْتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلْـَٔاخِرَةِ هُمْ يُوقِنُونَ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 31, 411, 'ﱖ ﱗ ﱘ ﱙ ﱚﱛ ﱜ ﱝ ﱞ ﱟ', 'أُو۟لَـٰٓئِكَ عَلَىٰ هُدًۭى مِّن رَّبِّهِمْ ۖ وَأُو۟لَـٰٓئِكَ هُمُ ٱلْمُفْلِحُونَ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 31, 411, 'ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭﱮ ﱯ ﱰ ﱱ ﱲ ﱳ', 'وَمِنَ ٱلنَّاسِ مَن يَشْتَرِى لَهْوَ ٱلْحَدِيثِ لِيُضِلَّ عَن سَبِيلِ ٱللَّهِ بِغَيْرِ عِلْمٍۢ وَيَتَّخِذَهَا هُزُوًا ۚ أُو۟لَـٰٓئِكَ لَهُمْ عَذَابٌۭ مُّهِينٌۭ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 31, 411, 'ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀﲁ ﲂ ﲃ ﲄ ﲅ', 'وَإِذَا تُتْلَىٰ عَلَيْهِ ءَايَـٰتُنَا وَلَّىٰ مُسْتَكْبِرًۭا كَأَن لَّمْ يَسْمَعْهَا كَأَنَّ فِىٓ أُذُنَيْهِ وَقْرًۭا ۖ فَبَشِّرْهُ بِعَذَابٍ أَلِيمٍ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 31, 411, 'ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ', 'إِنَّ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَهُمْ جَنَّـٰتُ ٱلنَّعِيمِ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 31, 411, 'ﲏ ﲐﲑ ﲒ ﲓ ﲔﲕ ﲖ ﲗ ﲘ ﲙ', 'خَـٰلِدِينَ فِيهَا ۖ وَعْدَ ٱللَّهِ حَقًّۭا ۚ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 31, 411, 'ﲚ ﲛ ﲜ ﲝ ﲞﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ', 'خَلَقَ ٱلسَّمَـٰوَٰتِ بِغَيْرِ عَمَدٍۢ تَرَوْنَهَا ۖ وَأَلْقَىٰ فِى ٱلْأَرْضِ رَوَٰسِىَ أَن تَمِيدَ بِكُمْ وَبَثَّ فِيهَا مِن كُلِّ دَآبَّةٍۢ ۚ وَأَنزَلْنَا مِنَ ٱلسَّمَآءِ مَآءًۭ فَأَنۢبَتْنَا فِيهَا مِن كُلِّ زَوْجٍۢ كَرِيمٍ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 31, 411, 'ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ', 'هَـٰذَا خَلْقُ ٱللَّهِ فَأَرُونِى مَاذَا خَلَقَ ٱلَّذِينَ مِن دُونِهِۦ ۚ بَلِ ٱلظَّـٰلِمُونَ فِى ضَلَـٰلٍۢ مُّبِينٍۢ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 31, 412, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇﱈ ﱉ ﱊ ﱋ ﱌ ﱍﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'وَلَقَدْ ءَاتَيْنَا لُقْمَـٰنَ ٱلْحِكْمَةَ أَنِ ٱشْكُرْ لِلَّهِ ۚ وَمَن يَشْكُرْ فَإِنَّمَا يَشْكُرُ لِنَفْسِهِۦ ۖ وَمَن كَفَرَ فَإِنَّ ٱللَّهَ غَنِىٌّ حَمِيدٌۭ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 31, 412, 'ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟﱠ ﱡ ﱢ ﱣ ﱤ ﱥ', 'وَإِذْ قَالَ لُقْمَـٰنُ لِٱبْنِهِۦ وَهُوَ يَعِظُهُۥ يَـٰبُنَىَّ لَا تُشْرِكْ بِٱللَّهِ ۖ إِنَّ ٱلشِّرْكَ لَظُلْمٌ عَظِيمٌۭ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 31, 412, 'ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ', 'وَوَصَّيْنَا ٱلْإِنسَـٰنَ بِوَٰلِدَيْهِ حَمَلَتْهُ أُمُّهُۥ وَهْنًا عَلَىٰ وَهْنٍۢ وَفِصَـٰلُهُۥ فِى عَامَيْنِ أَنِ ٱشْكُرْ لِى وَلِوَٰلِدَيْكَ إِلَىَّ ٱلْمَصِيرُ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 31, 412, 'ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄﲅ ﲆ ﲇ ﲈ ﲉﲊ ﲋ ﲌ ﲍ ﲎ ﲏﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ', 'وَإِن جَـٰهَدَاكَ عَلَىٰٓ أَن تُشْرِكَ بِى مَا لَيْسَ لَكَ بِهِۦ عِلْمٌۭ فَلَا تُطِعْهُمَا ۖ وَصَاحِبْهُمَا فِى ٱلدُّنْيَا مَعْرُوفًۭا ۖ وَٱتَّبِعْ سَبِيلَ مَنْ أَنَابَ إِلَىَّ ۚ ثُمَّ إِلَىَّ مَرْجِعُكُمْ فَأُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 31, 412, 'ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬﲭ ﲮ ﲯ ﲰ ﲱ ﲲ', 'يَـٰبُنَىَّ إِنَّهَآ إِن تَكُ مِثْقَالَ حَبَّةٍۢ مِّنْ خَرْدَلٍۢ فَتَكُن فِى صَخْرَةٍ أَوْ فِى ٱلسَّمَـٰوَٰتِ أَوْ فِى ٱلْأَرْضِ يَأْتِ بِهَا ٱللَّهُ ۚ إِنَّ ٱللَّهَ لَطِيفٌ خَبِيرٌۭ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 31, 412, 'ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ', 'يَـٰبُنَىَّ أَقِمِ ٱلصَّلَوٰةَ وَأْمُرْ بِٱلْمَعْرُوفِ وَٱنْهَ عَنِ ٱلْمُنكَرِ وَٱصْبِرْ عَلَىٰ مَآ أَصَابَكَ ۖ إِنَّ ذَٰلِكَ مِنْ عَزْمِ ٱلْأُمُورِ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 31, 412, 'ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ', 'وَلَا تُصَعِّرْ خَدَّكَ لِلنَّاسِ وَلَا تَمْشِ فِى ٱلْأَرْضِ مَرَحًا ۖ إِنَّ ٱللَّهَ لَا يُحِبُّ كُلَّ مُخْتَالٍۢ فَخُورٍۢ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 31, 412, 'ﳘ ﳙ ﳚ ﳛ ﳜ ﳝﳞ ﳟ ﳠ ﳡ ﳢ ﳣ ﳤ', 'وَٱقْصِدْ فِى مَشْيِكَ وَٱغْضُضْ مِن صَوْتِكَ ۚ إِنَّ أَنكَرَ ٱلْأَصْوَٰتِ لَصَوْتُ ٱلْحَمِيرِ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 31, 413, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ', 'أَلَمْ تَرَوْا۟ أَنَّ ٱللَّهَ سَخَّرَ لَكُم مَّا فِى ٱلسَّمَـٰوَٰتِ وَمَا فِى ٱلْأَرْضِ وَأَسْبَغَ عَلَيْكُمْ نِعَمَهُۥ ظَـٰهِرَةًۭ وَبَاطِنَةًۭ ۗ وَمِنَ ٱلنَّاسِ مَن يُجَـٰدِلُ فِى ٱللَّهِ بِغَيْرِ عِلْمٍۢ وَلَا هُدًۭى وَلَا كِتَـٰبٍۢ مُّنِيرٍۢ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 31, 413, 'ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ', 'وَإِذَا قِيلَ لَهُمُ ٱتَّبِعُوا۟ مَآ أَنزَلَ ٱللَّهُ قَالُوا۟ بَلْ نَتَّبِعُ مَا وَجَدْنَا عَلَيْهِ ءَابَآءَنَآ ۚ أَوَلَوْ كَانَ ٱلشَّيْطَـٰنُ يَدْعُوهُمْ إِلَىٰ عَذَابِ ٱلسَّعِيرِ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 31, 413, 'ﱸﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃﲄ ﲅ ﲆ ﲇ ﲈ ﲉ', '۞ وَمَن يُسْلِمْ وَجْهَهُۥٓ إِلَى ٱللَّهِ وَهُوَ مُحْسِنٌۭ فَقَدِ ٱسْتَمْسَكَ بِٱلْعُرْوَةِ ٱلْوُثْقَىٰ ۗ وَإِلَى ٱللَّهِ عَـٰقِبَةُ ٱلْأُمُورِ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 31, 413, 'ﲊ ﲋ ﲌ ﲍ ﲎﲏ ﲐ ﲑ ﲒ ﲓ ﲔﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ', 'وَمَن كَفَرَ فَلَا يَحْزُنكَ كُفْرُهُۥٓ ۚ إِلَيْنَا مَرْجِعُهُمْ فَنُنَبِّئُهُم بِمَا عَمِلُوٓا۟ ۚ إِنَّ ٱللَّهَ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 31, 413, 'ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ', 'نُمَتِّعُهُمْ قَلِيلًۭا ثُمَّ نَضْطَرُّهُمْ إِلَىٰ عَذَابٍ غَلِيظٍۢ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 31, 413, 'ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫﲬ ﲭ ﲮ ﲯﲰ ﲱ ﲲ ﲳ ﲴ ﲵ', 'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ لَيَقُولُنَّ ٱللَّهُ ۚ قُلِ ٱلْحَمْدُ لِلَّهِ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 31, 413, 'ﲶ ﲷ ﲸ ﲹ ﲺﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ', 'لِلَّهِ مَا فِى ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۚ إِنَّ ٱللَّهَ هُوَ ٱلْغَنِىُّ ٱلْحَمِيدُ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 31, 413, 'ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒﳓ ﳔ ﳕ ﳖ ﳗ ﳘ', 'وَلَوْ أَنَّمَا فِى ٱلْأَرْضِ مِن شَجَرَةٍ أَقْلَـٰمٌۭ وَٱلْبَحْرُ يَمُدُّهُۥ مِنۢ بَعْدِهِۦ سَبْعَةُ أَبْحُرٍۢ مَّا نَفِدَتْ كَلِمَـٰتُ ٱللَّهِ ۗ إِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٌۭ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 31, 413, 'ﳙ ﳚ ﳛ ﳜ ﳝ ﳞ ﳟﳠ ﳡ ﳢ ﳣ ﳤ ﳥ', 'مَّا خَلْقُكُمْ وَلَا بَعْثُكُمْ إِلَّا كَنَفْسٍۢ وَٰحِدَةٍ ۗ إِنَّ ٱللَّهَ سَمِيعٌۢ بَصِيرٌ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 31, 414, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ', 'أَلَمْ تَرَ أَنَّ ٱللَّهَ يُولِجُ ٱلَّيْلَ فِى ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِى ٱلَّيْلِ وَسَخَّرَ ٱلشَّمْسَ وَٱلْقَمَرَ كُلٌّۭ يَجْرِىٓ إِلَىٰٓ أَجَلٍۢ مُّسَمًّۭى وَأَنَّ ٱللَّهَ بِمَا تَعْمَلُونَ خَبِيرٌۭ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 31, 414, 'ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ', 'ذَٰلِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلْحَقُّ وَأَنَّ مَا يَدْعُونَ مِن دُونِهِ ٱلْبَـٰطِلُ وَأَنَّ ٱللَّهَ هُوَ ٱلْعَلِىُّ ٱلْكَبِيرُ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 31, 414, 'ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ', 'أَلَمْ تَرَ أَنَّ ٱلْفُلْكَ تَجْرِى فِى ٱلْبَحْرِ بِنِعْمَتِ ٱللَّهِ لِيُرِيَكُم مِّنْ ءَايَـٰتِهِۦٓ ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَـٰتٍۢ لِّكُلِّ صَبَّارٍۢ شَكُورٍۢ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 31, 414, 'ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ', 'وَإِذَا غَشِيَهُم مَّوْجٌۭ كَٱلظُّلَلِ دَعَوُا۟ ٱللَّهَ مُخْلِصِينَ لَهُ ٱلدِّينَ فَلَمَّا نَجَّىٰهُمْ إِلَى ٱلْبَرِّ فَمِنْهُم مُّقْتَصِدٌۭ ۚ وَمَا يَجْحَدُ بِـَٔايَـٰتِنَآ إِلَّا كُلُّ خَتَّارٍۢ كَفُورٍۢ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 31, 414, 'ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫﲬ ﲭ ﲮ ﲯ ﲰﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ', 'يَـٰٓأَيُّهَا ٱلنَّاسُ ٱتَّقُوا۟ رَبَّكُمْ وَٱخْشَوْا۟ يَوْمًۭا لَّا يَجْزِى وَالِدٌ عَن وَلَدِهِۦ وَلَا مَوْلُودٌ هُوَ جَازٍ عَن وَالِدِهِۦ شَيْـًٔا ۚ إِنَّ وَعْدَ ٱللَّهِ حَقٌّۭ ۖ فَلَا تَغُرَّنَّكُمُ ٱلْحَيَوٰةُ ٱلدُّنْيَا وَلَا يَغُرَّنَّكُم بِٱللَّهِ ٱلْغَرُورُ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 31, 414, 'ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓﳔ ﳕ ﳖ ﳗ ﳘ ﳙ', 'إِنَّ ٱللَّهَ عِندَهُۥ عِلْمُ ٱلسَّاعَةِ وَيُنَزِّلُ ٱلْغَيْثَ وَيَعْلَمُ مَا فِى ٱلْأَرْحَامِ ۖ وَمَا تَدْرِى نَفْسٌۭ مَّاذَا تَكْسِبُ غَدًۭا ۖ وَمَا تَدْرِى نَفْسٌۢ بِأَىِّ أَرْضٍۢ تَمُوتُ ۚ إِنَّ ٱللَّهَ عَلِيمٌ خَبِيرٌۢ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 32, 415, 'ﱁ ﱂ', 'الٓمٓ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 32, 415, 'ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ', 'تَنزِيلُ ٱلْكِتَـٰبِ لَا رَيْبَ فِيهِ مِن رَّبِّ ٱلْعَـٰلَمِينَ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 32, 415, 'ﱌ ﱍ ﱎﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ', 'أَمْ يَقُولُونَ ٱفْتَرَىٰهُ ۚ بَلْ هُوَ ٱلْحَقُّ مِن رَّبِّكَ لِتُنذِرَ قَوْمًۭا مَّآ أَتَىٰهُم مِّن نَّذِيرٍۢ مِّن قَبْلِكَ لَعَلَّهُمْ يَهْتَدُونَ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 32, 415, 'ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶﱷ ﱸ ﱹ ﱺ', 'ٱللَّهُ ٱلَّذِى خَلَقَ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ وَمَا بَيْنَهُمَا فِى سِتَّةِ أَيَّامٍۢ ثُمَّ ٱسْتَوَىٰ عَلَى ٱلْعَرْشِ ۖ مَا لَكُم مِّن دُونِهِۦ مِن وَلِىٍّۢ وَلَا شَفِيعٍ ۚ أَفَلَا تَتَذَكَّرُونَ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 32, 415, 'ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ', 'يُدَبِّرُ ٱلْأَمْرَ مِنَ ٱلسَّمَآءِ إِلَى ٱلْأَرْضِ ثُمَّ يَعْرُجُ إِلَيْهِ فِى يَوْمٍۢ كَانَ مِقْدَارُهُۥٓ أَلْفَ سَنَةٍۢ مِّمَّا تَعُدُّونَ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 32, 415, 'ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ', 'ذَٰلِكَ عَـٰلِمُ ٱلْغَيْبِ وَٱلشَّهَـٰدَةِ ٱلْعَزِيزُ ٱلرَّحِيمُ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 32, 415, 'ﲔ ﲕ ﲖ ﲗ ﲘﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ', 'ٱلَّذِىٓ أَحْسَنَ كُلَّ شَىْءٍ خَلَقَهُۥ ۖ وَبَدَأَ خَلْقَ ٱلْإِنسَـٰنِ مِن طِينٍۢ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 32, 415, 'ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ', 'ثُمَّ جَعَلَ نَسْلَهُۥ مِن سُلَـٰلَةٍۢ مِّن مَّآءٍۢ مَّهِينٍۢ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 32, 415, 'ﲩ ﲪ ﲫ ﲬ ﲭ ﲮﲯ ﲰ ﲱ ﲲ ﲳ ﲴﲵ ﲶ ﲷ ﲸ ﲹ', 'ثُمَّ سَوَّىٰهُ وَنَفَخَ فِيهِ مِن رُّوحِهِۦ ۖ وَجَعَلَ لَكُمُ ٱلسَّمْعَ وَٱلْأَبْصَـٰرَ وَٱلْأَفْـِٔدَةَ ۚ قَلِيلًۭا مَّا تَشْكُرُونَ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 32, 415, 'ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ', 'وَقَالُوٓا۟ أَءِذَا ضَلَلْنَا فِى ٱلْأَرْضِ أَءِنَّا لَفِى خَلْقٍۢ جَدِيدٍۭ ۚ بَلْ هُم بِلِقَآءِ رَبِّهِمْ كَـٰفِرُونَ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 32, 415, 'ﳊﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ', '۞ قُلْ يَتَوَفَّىٰكُم مَّلَكُ ٱلْمَوْتِ ٱلَّذِى وُكِّلَ بِكُمْ ثُمَّ إِلَىٰ رَبِّكُمْ تُرْجَعُونَ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 32, 416, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ', 'وَلَوْ تَرَىٰٓ إِذِ ٱلْمُجْرِمُونَ نَاكِسُوا۟ رُءُوسِهِمْ عِندَ رَبِّهِمْ رَبَّنَآ أَبْصَرْنَا وَسَمِعْنَا فَٱرْجِعْنَا نَعْمَلْ صَـٰلِحًا إِنَّا مُوقِنُونَ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 32, 416, 'ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ', 'وَلَوْ شِئْنَا لَـَٔاتَيْنَا كُلَّ نَفْسٍ هُدَىٰهَا وَلَـٰكِنْ حَقَّ ٱلْقَوْلُ مِنِّى لَأَمْلَأَنَّ جَهَنَّمَ مِنَ ٱلْجِنَّةِ وَٱلنَّاسِ أَجْمَعِينَ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 32, 416, 'ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ', 'فَذُوقُوا۟ بِمَا نَسِيتُمْ لِقَآءَ يَوْمِكُمْ هَـٰذَآ إِنَّا نَسِينَـٰكُمْ ۖ وَذُوقُوا۟ عَذَابَ ٱلْخُلْدِ بِمَا كُنتُمْ تَعْمَلُونَ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 32, 416, 'ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺﱻ ﱼﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃﲄ ﲅ', 'إِنَّمَا يُؤْمِنُ بِـَٔايَـٰتِنَا ٱلَّذِينَ إِذَا ذُكِّرُوا۟ بِهَا خَرُّوا۟ سُجَّدًۭا وَسَبَّحُوا۟ بِحَمْدِ رَبِّهِمْ وَهُمْ لَا يَسْتَكْبِرُونَ ۩ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 32, 416, 'ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ', 'تَتَجَافَىٰ جُنُوبُهُمْ عَنِ ٱلْمَضَاجِعِ يَدْعُونَ رَبَّهُمْ خَوْفًۭا وَطَمَعًۭا وَمِمَّا رَزَقْنَـٰهُمْ يُنفِقُونَ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 32, 416, 'ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ', 'فَلَا تَعْلَمُ نَفْسٌۭ مَّآ أُخْفِىَ لَهُم مِّن قُرَّةِ أَعْيُنٍۢ جَزَآءًۢ بِمَا كَانُوا۟ يَعْمَلُونَ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 32, 416, 'ﲠ ﲡ ﲢ ﲣ ﲤ ﲥﲦ ﲧ ﲨ ﲩ', 'أَفَمَن كَانَ مُؤْمِنًۭا كَمَن كَانَ فَاسِقًۭا ۚ لَّا يَسْتَوُۥنَ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 32, 416, 'ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ', 'أَمَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ فَلَهُمْ جَنَّـٰتُ ٱلْمَأْوَىٰ نُزُلًۢا بِمَا كَانُوا۟ يَعْمَلُونَ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 32, 416, 'ﲷ ﲸ ﲹ ﲺ ﲻﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ', 'وَأَمَّا ٱلَّذِينَ فَسَقُوا۟ فَمَأْوَىٰهُمُ ٱلنَّارُ ۖ كُلَّمَآ أَرَادُوٓا۟ أَن يَخْرُجُوا۟ مِنْهَآ أُعِيدُوا۟ فِيهَا وَقِيلَ لَهُمْ ذُوقُوا۟ عَذَابَ ٱلنَّارِ ٱلَّذِى كُنتُم بِهِۦ تُكَذِّبُونَ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 32, 417, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ', 'وَلَنُذِيقَنَّهُم مِّنَ ٱلْعَذَابِ ٱلْأَدْنَىٰ دُونَ ٱلْعَذَابِ ٱلْأَكْبَرِ لَعَلَّهُمْ يَرْجِعُونَ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 32, 417, 'ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓﱔ ﱕ ﱖ ﱗ ﱘ ﱙ', 'وَمَنْ أَظْلَمُ مِمَّن ذُكِّرَ بِـَٔايَـٰتِ رَبِّهِۦ ثُمَّ أَعْرَضَ عَنْهَآ ۚ إِنَّا مِنَ ٱلْمُجْرِمِينَ مُنتَقِمُونَ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 32, 417, 'ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣﱤ ﱥ ﱦ ﱧ ﱨ ﱩ', 'وَلَقَدْ ءَاتَيْنَا مُوسَى ٱلْكِتَـٰبَ فَلَا تَكُن فِى مِرْيَةٍۢ مِّن لِّقَآئِهِۦ ۖ وَجَعَلْنَـٰهُ هُدًۭى لِّبَنِىٓ إِسْرَٰٓءِيلَ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 32, 417, 'ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰﱱ ﱲ ﱳ ﱴ ﱵ', 'وَجَعَلْنَا مِنْهُمْ أَئِمَّةًۭ يَهْدُونَ بِأَمْرِنَا لَمَّا صَبَرُوا۟ ۖ وَكَانُوا۟ بِـَٔايَـٰتِنَا يُوقِنُونَ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 32, 417, 'ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ', 'إِنَّ رَبَّكَ هُوَ يَفْصِلُ بَيْنَهُمْ يَوْمَ ٱلْقِيَـٰمَةِ فِيمَا كَانُوا۟ فِيهِ يَخْتَلِفُونَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 32, 417, 'ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍﲎ ﲏ ﲐ ﲑ ﲒﲓ ﲔ ﲕ ﲖ', 'أَوَلَمْ يَهْدِ لَهُمْ كَمْ أَهْلَكْنَا مِن قَبْلِهِم مِّنَ ٱلْقُرُونِ يَمْشُونَ فِى مَسَـٰكِنِهِمْ ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَـٰتٍ ۖ أَفَلَا يَسْمَعُونَ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 32, 417, 'ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥﲦ ﲧ ﲨ ﲩ', 'أَوَلَمْ يَرَوْا۟ أَنَّا نَسُوقُ ٱلْمَآءَ إِلَى ٱلْأَرْضِ ٱلْجُرُزِ فَنُخْرِجُ بِهِۦ زَرْعًۭا تَأْكُلُ مِنْهُ أَنْعَـٰمُهُمْ وَأَنفُسُهُمْ ۖ أَفَلَا يُبْصِرُونَ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 32, 417, 'ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ', 'وَيَقُولُونَ مَتَىٰ هَـٰذَا ٱلْفَتْحُ إِن كُنتُمْ صَـٰدِقِينَ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 32, 417, 'ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ', 'قُلْ يَوْمَ ٱلْفَتْحِ لَا يَنفَعُ ٱلَّذِينَ كَفَرُوٓا۟ إِيمَـٰنُهُمْ وَلَا هُمْ يُنظَرُونَ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 32, 417, 'ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ', 'فَأَعْرِضْ عَنْهُمْ وَٱنتَظِرْ إِنَّهُم مُّنتَظِرُونَ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 33, 418, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ', 'يَـٰٓأَيُّهَا ٱلنَّبِىُّ ٱتَّقِ ٱللَّهَ وَلَا تُطِعِ ٱلْكَـٰفِرِينَ وَٱلْمُنَـٰفِقِينَ ۗ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمًۭا ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 33, 418, 'ﱐ ﱑ ﱒ ﱓ ﱔ ﱕﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ', 'وَٱتَّبِعْ مَا يُوحَىٰٓ إِلَيْكَ مِن رَّبِّكَ ۚ إِنَّ ٱللَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيرًۭا ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 33, 418, 'ﱞ ﱟ ﱠﱡ ﱢ ﱣ ﱤ ﱥ', 'وَتَوَكَّلْ عَلَى ٱللَّهِ ۚ وَكَفَىٰ بِٱللَّهِ وَكِيلًۭا ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 33, 418, 'ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵﱶ ﱷ ﱸ ﱹ ﱺﱻ ﱼ ﱽ ﱾﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ', 'مَّا جَعَلَ ٱللَّهُ لِرَجُلٍۢ مِّن قَلْبَيْنِ فِى جَوْفِهِۦ ۚ وَمَا جَعَلَ أَزْوَٰجَكُمُ ٱلَّـٰٓـِٔى تُظَـٰهِرُونَ مِنْهُنَّ أُمَّهَـٰتِكُمْ ۚ وَمَا جَعَلَ أَدْعِيَآءَكُمْ أَبْنَآءَكُمْ ۚ ذَٰلِكُمْ قَوْلُكُم بِأَفْوَٰهِكُمْ ۖ وَٱللَّهُ يَقُولُ ٱلْحَقَّ وَهُوَ يَهْدِى ٱلسَّبِيلَ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 33, 418, 'ﲇ ﲈ ﲉ ﲊ ﲋ ﲌﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠﲡ ﲢ ﲣ ﲤ ﲥ ﲦ', 'ٱدْعُوهُمْ لِـَٔابَآئِهِمْ هُوَ أَقْسَطُ عِندَ ٱللَّهِ ۚ فَإِن لَّمْ تَعْلَمُوٓا۟ ءَابَآءَهُمْ فَإِخْوَٰنُكُمْ فِى ٱلدِّينِ وَمَوَٰلِيكُمْ ۚ وَلَيْسَ عَلَيْكُمْ جُنَاحٌۭ فِيمَآ أَخْطَأْتُم بِهِۦ وَلَـٰكِن مَّا تَعَمَّدَتْ قُلُوبُكُمْ ۚ وَكَانَ ٱللَّهُ غَفُورًۭا رَّحِيمًا ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 33, 418, 'ﲧ ﲨ ﲩ ﲪ ﲫﲬ ﲭ ﲮﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ', 'ٱلنَّبِىُّ أَوْلَىٰ بِٱلْمُؤْمِنِينَ مِنْ أَنفُسِهِمْ ۖ وَأَزْوَٰجُهُۥٓ أُمَّهَـٰتُهُمْ ۗ وَأُو۟لُوا۟ ٱلْأَرْحَامِ بَعْضُهُمْ أَوْلَىٰ بِبَعْضٍۢ فِى كِتَـٰبِ ٱللَّهِ مِنَ ٱلْمُؤْمِنِينَ وَٱلْمُهَـٰجِرِينَ إِلَّآ أَن تَفْعَلُوٓا۟ إِلَىٰٓ أَوْلِيَآئِكُم مَّعْرُوفًۭا ۚ كَانَ ذَٰلِكَ فِى ٱلْكِتَـٰبِ مَسْطُورًۭا ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 33, 419, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍﱎ ﱏ ﱐ ﱑ ﱒ ﱓ', 'وَإِذْ أَخَذْنَا مِنَ ٱلنَّبِيِّـۧنَ مِيثَـٰقَهُمْ وَمِنكَ وَمِن نُّوحٍۢ وَإِبْرَٰهِيمَ وَمُوسَىٰ وَعِيسَى ٱبْنِ مَرْيَمَ ۖ وَأَخَذْنَا مِنْهُم مِّيثَـٰقًا غَلِيظًۭا ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 33, 419, 'ﱔ ﱕ ﱖ ﱗﱘ ﱙ ﱚ ﱛ ﱜ ﱝ', 'لِّيَسْـَٔلَ ٱلصَّـٰدِقِينَ عَن صِدْقِهِمْ ۚ وَأَعَدَّ لِلْكَـٰفِرِينَ عَذَابًا أَلِيمًۭا ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 33, 419, 'ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ', 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ ٱذْكُرُوا۟ نِعْمَةَ ٱللَّهِ عَلَيْكُمْ إِذْ جَآءَتْكُمْ جُنُودٌۭ فَأَرْسَلْنَا عَلَيْهِمْ رِيحًۭا وَجُنُودًۭا لَّمْ تَرَوْهَا ۚ وَكَانَ ٱللَّهُ بِمَا تَعْمَلُونَ بَصِيرًا ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 33, 419, 'ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ', 'إِذْ جَآءُوكُم مِّن فَوْقِكُمْ وَمِنْ أَسْفَلَ مِنكُمْ وَإِذْ زَاغَتِ ٱلْأَبْصَـٰرُ وَبَلَغَتِ ٱلْقُلُوبُ ٱلْحَنَاجِرَ وَتَظُنُّونَ بِٱللَّهِ ٱلظُّنُونَا۠ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 33, 419, 'ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ', 'هُنَالِكَ ٱبْتُلِىَ ٱلْمُؤْمِنُونَ وَزُلْزِلُوا۟ زِلْزَالًۭا شَدِيدًۭا ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 33, 419, 'ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ', 'وَإِذْ يَقُولُ ٱلْمُنَـٰفِقُونَ وَٱلَّذِينَ فِى قُلُوبِهِم مَّرَضٌۭ مَّا وَعَدَنَا ٱللَّهُ وَرَسُولُهُۥٓ إِلَّا غُرُورًۭا ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 33, 419, 'ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰﲱ ﲲ ﲳ ﲴ ﲵ ﲶ', 'وَإِذْ قَالَت طَّآئِفَةٌۭ مِّنْهُمْ يَـٰٓأَهْلَ يَثْرِبَ لَا مُقَامَ لَكُمْ فَٱرْجِعُوا۟ ۚ وَيَسْتَـْٔذِنُ فَرِيقٌۭ مِّنْهُمُ ٱلنَّبِىَّ يَقُولُونَ إِنَّ بُيُوتَنَا عَوْرَةٌۭ وَمَا هِىَ بِعَوْرَةٍ ۖ إِن يُرِيدُونَ إِلَّا فِرَارًۭا ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 33, 419, 'ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ', 'وَلَوْ دُخِلَتْ عَلَيْهِم مِّنْ أَقْطَارِهَا ثُمَّ سُئِلُوا۟ ٱلْفِتْنَةَ لَـَٔاتَوْهَا وَمَا تَلَبَّثُوا۟ بِهَآ إِلَّا يَسِيرًۭا ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 33, 419, 'ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎﳏ ﳐ ﳑ ﳒ ﳓ ﳔ', 'وَلَقَدْ كَانُوا۟ عَـٰهَدُوا۟ ٱللَّهَ مِن قَبْلُ لَا يُوَلُّونَ ٱلْأَدْبَـٰرَ ۚ وَكَانَ عَهْدُ ٱللَّهِ مَسْـُٔولًۭا ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 33, 420, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ', 'قُل لَّن يَنفَعَكُمُ ٱلْفِرَارُ إِن فَرَرْتُم مِّنَ ٱلْمَوْتِ أَوِ ٱلْقَتْلِ وَإِذًۭا لَّا تُمَتَّعُونَ إِلَّا قَلِيلًۭا ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 33, 420, 'ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ', 'قُلْ مَن ذَا ٱلَّذِى يَعْصِمُكُم مِّنَ ٱللَّهِ إِنْ أَرَادَ بِكُمْ سُوٓءًا أَوْ أَرَادَ بِكُمْ رَحْمَةًۭ ۚ وَلَا يَجِدُونَ لَهُم مِّن دُونِ ٱللَّهِ وَلِيًّۭا وَلَا نَصِيرًۭا ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 33, 420, 'ﱫﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ', '۞ قَدْ يَعْلَمُ ٱللَّهُ ٱلْمُعَوِّقِينَ مِنكُمْ وَٱلْقَآئِلِينَ لِإِخْوَٰنِهِمْ هَلُمَّ إِلَيْنَا ۖ وَلَا يَأْتُونَ ٱلْبَأْسَ إِلَّا قَلِيلًا ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 33, 420, 'ﱼ ﱽﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ', 'أَشِحَّةً عَلَيْكُمْ ۖ فَإِذَا جَآءَ ٱلْخَوْفُ رَأَيْتَهُمْ يَنظُرُونَ إِلَيْكَ تَدُورُ أَعْيُنُهُمْ كَٱلَّذِى يُغْشَىٰ عَلَيْهِ مِنَ ٱلْمَوْتِ ۖ فَإِذَا ذَهَبَ ٱلْخَوْفُ سَلَقُوكُم بِأَلْسِنَةٍ حِدَادٍ أَشِحَّةً عَلَى ٱلْخَيْرِ ۚ أُو۟لَـٰٓئِكَ لَمْ يُؤْمِنُوا۟ فَأَحْبَطَ ٱللَّهُ أَعْمَـٰلَهُمْ ۚ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرًۭا ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 33, 420, 'ﲤ ﲥ ﲦ ﲧﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ', 'يَحْسَبُونَ ٱلْأَحْزَابَ لَمْ يَذْهَبُوا۟ ۖ وَإِن يَأْتِ ٱلْأَحْزَابُ يَوَدُّوا۟ لَوْ أَنَّهُم بَادُونَ فِى ٱلْأَعْرَابِ يَسْـَٔلُونَ عَنْ أَنۢبَآئِكُمْ ۖ وَلَوْ كَانُوا۟ فِيكُم مَّا قَـٰتَلُوٓا۟ إِلَّا قَلِيلًۭا ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 33, 420, 'ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ', 'لَّقَدْ كَانَ لَكُمْ فِى رَسُولِ ٱللَّهِ أُسْوَةٌ حَسَنَةٌۭ لِّمَن كَانَ يَرْجُوا۟ ٱللَّهَ وَٱلْيَوْمَ ٱلْـَٔاخِرَ وَذَكَرَ ٱللَّهَ كَثِيرًۭا ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 33, 420, 'ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜﳝ ﳞ ﳟ ﳠ ﳡ ﳢ ﳣ', 'وَلَمَّا رَءَا ٱلْمُؤْمِنُونَ ٱلْأَحْزَابَ قَالُوا۟ هَـٰذَا مَا وَعَدَنَا ٱللَّهُ وَرَسُولُهُۥ وَصَدَقَ ٱللَّهُ وَرَسُولُهُۥ ۚ وَمَا زَادَهُمْ إِلَّآ إِيمَـٰنًۭا وَتَسْلِيمًۭا ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 33, 421, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐﱑ ﱒ ﱓ ﱔ ﱕ', 'مِّنَ ٱلْمُؤْمِنِينَ رِجَالٌۭ صَدَقُوا۟ مَا عَـٰهَدُوا۟ ٱللَّهَ عَلَيْهِ ۖ فَمِنْهُم مَّن قَضَىٰ نَحْبَهُۥ وَمِنْهُم مَّن يَنتَظِرُ ۖ وَمَا بَدَّلُوا۟ تَبْدِيلًۭا ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 33, 421, 'ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ', 'لِّيَجْزِىَ ٱللَّهُ ٱلصَّـٰدِقِينَ بِصِدْقِهِمْ وَيُعَذِّبَ ٱلْمُنَـٰفِقِينَ إِن شَآءَ أَوْ يَتُوبَ عَلَيْهِمْ ۚ إِنَّ ٱللَّهَ كَانَ غَفُورًۭا رَّحِيمًۭا ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 33, 421, 'ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯﱰ ﱱ ﱲ ﱳ ﱴﱵ ﱶ ﱷ ﱸ ﱹ ﱺ', 'وَرَدَّ ٱللَّهُ ٱلَّذِينَ كَفَرُوا۟ بِغَيْظِهِمْ لَمْ يَنَالُوا۟ خَيْرًۭا ۚ وَكَفَى ٱللَّهُ ٱلْمُؤْمِنِينَ ٱلْقِتَالَ ۚ وَكَانَ ٱللَّهُ قَوِيًّا عَزِيزًۭا ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 33, 421, 'ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ', 'وَأَنزَلَ ٱلَّذِينَ ظَـٰهَرُوهُم مِّنْ أَهْلِ ٱلْكِتَـٰبِ مِن صَيَاصِيهِمْ وَقَذَفَ فِى قُلُوبِهِمُ ٱلرُّعْبَ فَرِيقًۭا تَقْتُلُونَ وَتَأْسِرُونَ فَرِيقًۭا ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 33, 421, 'ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ', 'وَأَوْرَثَكُمْ أَرْضَهُمْ وَدِيَـٰرَهُمْ وَأَمْوَٰلَهُمْ وَأَرْضًۭا لَّمْ تَطَـُٔوهَا ۚ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَىْءٍۢ قَدِيرًۭا ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 33, 421, 'ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ', 'يَـٰٓأَيُّهَا ٱلنَّبِىُّ قُل لِّأَزْوَٰجِكَ إِن كُنتُنَّ تُرِدْنَ ٱلْحَيَوٰةَ ٱلدُّنْيَا وَزِينَتَهَا فَتَعَالَيْنَ أُمَتِّعْكُنَّ وَأُسَرِّحْكُنَّ سَرَاحًۭا جَمِيلًۭا ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 33, 421, 'ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ', 'وَإِن كُنتُنَّ تُرِدْنَ ٱللَّهَ وَرَسُولَهُۥ وَٱلدَّارَ ٱلْـَٔاخِرَةَ فَإِنَّ ٱللَّهَ أَعَدَّ لِلْمُحْسِنَـٰتِ مِنكُنَّ أَجْرًا عَظِيمًۭا ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 33, 421, 'ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ', 'يَـٰنِسَآءَ ٱلنَّبِىِّ مَن يَأْتِ مِنكُنَّ بِفَـٰحِشَةٍۢ مُّبَيِّنَةٍۢ يُضَـٰعَفْ لَهَا ٱلْعَذَابُ ضِعْفَيْنِ ۚ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرًۭا ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 33, 422, 'ﱁﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ', '۞ وَمَن يَقْنُتْ مِنكُنَّ لِلَّهِ وَرَسُولِهِۦ وَتَعْمَلْ صَـٰلِحًۭا نُّؤْتِهَآ أَجْرَهَا مَرَّتَيْنِ وَأَعْتَدْنَا لَهَا رِزْقًۭا كَرِيمًۭا ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 33, 422, 'ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ', 'يَـٰنِسَآءَ ٱلنَّبِىِّ لَسْتُنَّ كَأَحَدٍۢ مِّنَ ٱلنِّسَآءِ ۚ إِنِ ٱتَّقَيْتُنَّ فَلَا تَخْضَعْنَ بِٱلْقَوْلِ فَيَطْمَعَ ٱلَّذِى فِى قَلْبِهِۦ مَرَضٌۭ وَقُلْنَ قَوْلًۭا مَّعْرُوفًۭا ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 33, 422, 'ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ', 'وَقَرْنَ فِى بُيُوتِكُنَّ وَلَا تَبَرَّجْنَ تَبَرُّجَ ٱلْجَـٰهِلِيَّةِ ٱلْأُولَىٰ ۖ وَأَقِمْنَ ٱلصَّلَوٰةَ وَءَاتِينَ ٱلزَّكَوٰةَ وَأَطِعْنَ ٱللَّهَ وَرَسُولَهُۥٓ ۚ إِنَّمَا يُرِيدُ ٱللَّهُ لِيُذْهِبَ عَنكُمُ ٱلرِّجْسَ أَهْلَ ٱلْبَيْتِ وَيُطَهِّرَكُمْ تَطْهِيرًۭا ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 33, 422, 'ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ', 'وَٱذْكُرْنَ مَا يُتْلَىٰ فِى بُيُوتِكُنَّ مِنْ ءَايَـٰتِ ٱللَّهِ وَٱلْحِكْمَةِ ۚ إِنَّ ٱللَّهَ كَانَ لَطِيفًا خَبِيرًا ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 33, 422, 'ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ', 'إِنَّ ٱلْمُسْلِمِينَ وَٱلْمُسْلِمَـٰتِ وَٱلْمُؤْمِنِينَ وَٱلْمُؤْمِنَـٰتِ وَٱلْقَـٰنِتِينَ وَٱلْقَـٰنِتَـٰتِ وَٱلصَّـٰدِقِينَ وَٱلصَّـٰدِقَـٰتِ وَٱلصَّـٰبِرِينَ وَٱلصَّـٰبِرَٰتِ وَٱلْخَـٰشِعِينَ وَٱلْخَـٰشِعَـٰتِ وَٱلْمُتَصَدِّقِينَ وَٱلْمُتَصَدِّقَـٰتِ وَٱلصَّـٰٓئِمِينَ وَٱلصَّـٰٓئِمَـٰتِ وَٱلْحَـٰفِظِينَ فُرُوجَهُمْ وَٱلْحَـٰفِظَـٰتِ وَٱلذَّٰكِرِينَ ٱللَّهَ كَثِيرًۭا وَٱلذَّٰكِرَٰتِ أَعَدَّ ٱللَّهُ لَهُم مَّغْفِرَةًۭ وَأَجْرًا عَظِيمًۭا ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 33, 423, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ', 'وَمَا كَانَ لِمُؤْمِنٍۢ وَلَا مُؤْمِنَةٍ إِذَا قَضَى ٱللَّهُ وَرَسُولُهُۥٓ أَمْرًا أَن يَكُونَ لَهُمُ ٱلْخِيَرَةُ مِنْ أَمْرِهِمْ ۗ وَمَن يَعْصِ ٱللَّهَ وَرَسُولَهُۥ فَقَدْ ضَلَّ ضَلَـٰلًۭا مُّبِينًۭا ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 33, 423, 'ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇﲈ ﲉ ﲊ ﲋ ﲌ ﲍ', 'وَإِذْ تَقُولُ لِلَّذِىٓ أَنْعَمَ ٱللَّهُ عَلَيْهِ وَأَنْعَمْتَ عَلَيْهِ أَمْسِكْ عَلَيْكَ زَوْجَكَ وَٱتَّقِ ٱللَّهَ وَتُخْفِى فِى نَفْسِكَ مَا ٱللَّهُ مُبْدِيهِ وَتَخْشَى ٱلنَّاسَ وَٱللَّهُ أَحَقُّ أَن تَخْشَىٰهُ ۖ فَلَمَّا قَضَىٰ زَيْدٌۭ مِّنْهَا وَطَرًۭا زَوَّجْنَـٰكَهَا لِكَىْ لَا يَكُونَ عَلَى ٱلْمُؤْمِنِينَ حَرَجٌۭ فِىٓ أَزْوَٰجِ أَدْعِيَآئِهِمْ إِذَا قَضَوْا۟ مِنْهُنَّ وَطَرًۭا ۚ وَكَانَ أَمْرُ ٱللَّهِ مَفْعُولًۭا ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 33, 423, 'ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ', 'مَّا كَانَ عَلَى ٱلنَّبِىِّ مِنْ حَرَجٍۢ فِيمَا فَرَضَ ٱللَّهُ لَهُۥ ۖ سُنَّةَ ٱللَّهِ فِى ٱلَّذِينَ خَلَوْا۟ مِن قَبْلُ ۚ وَكَانَ أَمْرُ ٱللَّهِ قَدَرًۭا مَّقْدُورًا ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 33, 423, 'ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰﲱ ﲲ ﲳ ﲴ ﲵ', 'ٱلَّذِينَ يُبَلِّغُونَ رِسَـٰلَـٰتِ ٱللَّهِ وَيَخْشَوْنَهُۥ وَلَا يَخْشَوْنَ أَحَدًا إِلَّا ٱللَّهَ ۗ وَكَفَىٰ بِٱللَّهِ حَسِيبًۭا ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 33, 423, 'ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ', 'مَّا كَانَ مُحَمَّدٌ أَبَآ أَحَدٍۢ مِّن رِّجَالِكُمْ وَلَـٰكِن رَّسُولَ ٱللَّهِ وَخَاتَمَ ٱلنَّبِيِّـۧنَ ۗ وَكَانَ ٱللَّهُ بِكُلِّ شَىْءٍ عَلِيمًۭا ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 33, 423, 'ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ', 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ ٱذْكُرُوا۟ ٱللَّهَ ذِكْرًۭا كَثِيرًۭا ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 33, 423, 'ﳑ ﳒ ﳓ ﳔ', 'وَسَبِّحُوهُ بُكْرَةًۭ وَأَصِيلًا ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 33, 423, 'ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ ﳝ ﳞﳟ ﳠ ﳡ ﳢ ﳣ', 'هُوَ ٱلَّذِى يُصَلِّى عَلَيْكُمْ وَمَلَـٰٓئِكَتُهُۥ لِيُخْرِجَكُم مِّنَ ٱلظُّلُمَـٰتِ إِلَى ٱلنُّورِ ۚ وَكَانَ بِٱلْمُؤْمِنِينَ رَحِيمًۭا ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 33, 424, 'ﱁ ﱂ ﱃ ﱄﱅ ﱆ ﱇ ﱈ ﱉ ﱊ', 'تَحِيَّتُهُمْ يَوْمَ يَلْقَوْنَهُۥ سَلَـٰمٌۭ ۚ وَأَعَدَّ لَهُمْ أَجْرًۭا كَرِيمًۭا ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 33, 424, 'ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ', 'يَـٰٓأَيُّهَا ٱلنَّبِىُّ إِنَّآ أَرْسَلْنَـٰكَ شَـٰهِدًۭا وَمُبَشِّرًۭا وَنَذِيرًۭا ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 33, 424, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ', 'وَدَاعِيًا إِلَى ٱللَّهِ بِإِذْنِهِۦ وَسِرَاجًۭا مُّنِيرًۭا ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 33, 424, 'ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ', 'وَبَشِّرِ ٱلْمُؤْمِنِينَ بِأَنَّ لَهُم مِّنَ ٱللَّهِ فَضْلًۭا كَبِيرًۭا ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 33, 424, 'ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫﱬ ﱭ ﱮ ﱯ ﱰ', 'وَلَا تُطِعِ ٱلْكَـٰفِرِينَ وَٱلْمُنَـٰفِقِينَ وَدَعْ أَذَىٰهُمْ وَتَوَكَّلْ عَلَى ٱللَّهِ ۚ وَكَفَىٰ بِٱللَّهِ وَكِيلًۭا ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 33, 424, 'ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂﲃ ﲄ ﲅ ﲆ ﲇ ﲈ', 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِذَا نَكَحْتُمُ ٱلْمُؤْمِنَـٰتِ ثُمَّ طَلَّقْتُمُوهُنَّ مِن قَبْلِ أَن تَمَسُّوهُنَّ فَمَا لَكُمْ عَلَيْهِنَّ مِنْ عِدَّةٍۢ تَعْتَدُّونَهَا ۖ فَمَتِّعُوهُنَّ وَسَرِّحُوهُنَّ سَرَاحًۭا جَمِيلًۭا ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 33, 424, 'ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂﳃ ﳄ ﳅ ﳆ ﳇ ﳈ', 'يَـٰٓأَيُّهَا ٱلنَّبِىُّ إِنَّآ أَحْلَلْنَا لَكَ أَزْوَٰجَكَ ٱلَّـٰتِىٓ ءَاتَيْتَ أُجُورَهُنَّ وَمَا مَلَكَتْ يَمِينُكَ مِمَّآ أَفَآءَ ٱللَّهُ عَلَيْكَ وَبَنَاتِ عَمِّكَ وَبَنَاتِ عَمَّـٰتِكَ وَبَنَاتِ خَالِكَ وَبَنَاتِ خَـٰلَـٰتِكَ ٱلَّـٰتِى هَاجَرْنَ مَعَكَ وَٱمْرَأَةًۭ مُّؤْمِنَةً إِن وَهَبَتْ نَفْسَهَا لِلنَّبِىِّ إِنْ أَرَادَ ٱلنَّبِىُّ أَن يَسْتَنكِحَهَا خَالِصَةًۭ لَّكَ مِن دُونِ ٱلْمُؤْمِنِينَ ۗ قَدْ عَلِمْنَا مَا فَرَضْنَا عَلَيْهِمْ فِىٓ أَزْوَٰجِهِمْ وَمَا مَلَكَتْ أَيْمَـٰنُهُمْ لِكَيْلَا يَكُونَ عَلَيْكَ حَرَجٌۭ ۗ وَكَانَ ٱللَّهُ غَفُورًۭا رَّحِيمًۭا ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 33, 425, 'ﱁﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝﱞ ﱟ ﱠ ﱡ ﱢ ﱣﱤ ﱥ ﱦ ﱧ ﱨ ﱩ', '۞ تُرْجِى مَن تَشَآءُ مِنْهُنَّ وَتُـْٔوِىٓ إِلَيْكَ مَن تَشَآءُ ۖ وَمَنِ ٱبْتَغَيْتَ مِمَّنْ عَزَلْتَ فَلَا جُنَاحَ عَلَيْكَ ۚ ذَٰلِكَ أَدْنَىٰٓ أَن تَقَرَّ أَعْيُنُهُنَّ وَلَا يَحْزَنَّ وَيَرْضَيْنَ بِمَآ ءَاتَيْتَهُنَّ كُلُّهُنَّ ۚ وَٱللَّهُ يَعْلَمُ مَا فِى قُلُوبِكُمْ ۚ وَكَانَ ٱللَّهُ عَلِيمًا حَلِيمًۭا ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 33, 425, 'ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ', 'لَّا يَحِلُّ لَكَ ٱلنِّسَآءُ مِنۢ بَعْدُ وَلَآ أَن تَبَدَّلَ بِهِنَّ مِنْ أَزْوَٰجٍۢ وَلَوْ أَعْجَبَكَ حُسْنُهُنَّ إِلَّا مَا مَلَكَتْ يَمِينُكَ ۗ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَىْءٍۢ رَّقِيبًۭا ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 33, 425, 'ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦﲧ ﲨ ﲩ ﲪ ﲫ ﲬﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴﲵ ﲶ ﲷ ﲸ ﲹﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ', 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تَدْخُلُوا۟ بُيُوتَ ٱلنَّبِىِّ إِلَّآ أَن يُؤْذَنَ لَكُمْ إِلَىٰ طَعَامٍ غَيْرَ نَـٰظِرِينَ إِنَىٰهُ وَلَـٰكِنْ إِذَا دُعِيتُمْ فَٱدْخُلُوا۟ فَإِذَا طَعِمْتُمْ فَٱنتَشِرُوا۟ وَلَا مُسْتَـْٔنِسِينَ لِحَدِيثٍ ۚ إِنَّ ذَٰلِكُمْ كَانَ يُؤْذِى ٱلنَّبِىَّ فَيَسْتَحْىِۦ مِنكُمْ ۖ وَٱللَّهُ لَا يَسْتَحْىِۦ مِنَ ٱلْحَقِّ ۚ وَإِذَا سَأَلْتُمُوهُنَّ مَتَـٰعًۭا فَسْـَٔلُوهُنَّ مِن وَرَآءِ حِجَابٍۢ ۚ ذَٰلِكُمْ أَطْهَرُ لِقُلُوبِكُمْ وَقُلُوبِهِنَّ ۚ وَمَا كَانَ لَكُمْ أَن تُؤْذُوا۟ رَسُولَ ٱللَّهِ وَلَآ أَن تَنكِحُوٓا۟ أَزْوَٰجَهُۥ مِنۢ بَعْدِهِۦٓ أَبَدًا ۚ إِنَّ ذَٰلِكُمْ كَانَ عِندَ ٱللَّهِ عَظِيمًا ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 33, 425, 'ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ', 'إِن تُبْدُوا۟ شَيْـًٔا أَوْ تُخْفُوهُ فَإِنَّ ٱللَّهَ كَانَ بِكُلِّ شَىْءٍ عَلِيمًۭا ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (55, 33, 426, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕﱖ ﱗ ﱘﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ', 'لَّا جُنَاحَ عَلَيْهِنَّ فِىٓ ءَابَآئِهِنَّ وَلَآ أَبْنَآئِهِنَّ وَلَآ إِخْوَٰنِهِنَّ وَلَآ أَبْنَآءِ إِخْوَٰنِهِنَّ وَلَآ أَبْنَآءِ أَخَوَٰتِهِنَّ وَلَا نِسَآئِهِنَّ وَلَا مَا مَلَكَتْ أَيْمَـٰنُهُنَّ ۗ وَٱتَّقِينَ ٱللَّهَ ۚ إِنَّ ٱللَّهَ كَانَ عَلَىٰ كُلِّ شَىْءٍۢ شَهِيدًا ٥٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (56, 33, 426, 'ﱢ ﱣ ﱤ ﱥ ﱦ ﱧﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ', 'إِنَّ ٱللَّهَ وَمَلَـٰٓئِكَتَهُۥ يُصَلُّونَ عَلَى ٱلنَّبِىِّ ۚ يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ صَلُّوا۟ عَلَيْهِ وَسَلِّمُوا۟ تَسْلِيمًا ٥٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (57, 33, 426, 'ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ', 'إِنَّ ٱلَّذِينَ يُؤْذُونَ ٱللَّهَ وَرَسُولَهُۥ لَعَنَهُمُ ٱللَّهُ فِى ٱلدُّنْيَا وَٱلْـَٔاخِرَةِ وَأَعَدَّ لَهُمْ عَذَابًۭا مُّهِينًۭا ٥٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (58, 33, 426, 'ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ', 'وَٱلَّذِينَ يُؤْذُونَ ٱلْمُؤْمِنِينَ وَٱلْمُؤْمِنَـٰتِ بِغَيْرِ مَا ٱكْتَسَبُوا۟ فَقَدِ ٱحْتَمَلُوا۟ بُهْتَـٰنًۭا وَإِثْمًۭا مُّبِينًۭا ٥٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (59, 33, 426, 'ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞﲟ ﲠ ﲡ ﲢ ﲣ ﲤ', 'يَـٰٓأَيُّهَا ٱلنَّبِىُّ قُل لِّأَزْوَٰجِكَ وَبَنَاتِكَ وَنِسَآءِ ٱلْمُؤْمِنِينَ يُدْنِينَ عَلَيْهِنَّ مِن جَلَـٰبِيبِهِنَّ ۚ ذَٰلِكَ أَدْنَىٰٓ أَن يُعْرَفْنَ فَلَا يُؤْذَيْنَ ۗ وَكَانَ ٱللَّهُ غَفُورًۭا رَّحِيمًۭا ٥٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (60, 33, 426, 'ﲥﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ', '۞ لَّئِن لَّمْ يَنتَهِ ٱلْمُنَـٰفِقُونَ وَٱلَّذِينَ فِى قُلُوبِهِم مَّرَضٌۭ وَٱلْمُرْجِفُونَ فِى ٱلْمَدِينَةِ لَنُغْرِيَنَّكَ بِهِمْ ثُمَّ لَا يُجَاوِرُونَكَ فِيهَآ إِلَّا قَلِيلًۭا ٦٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (61, 33, 426, 'ﲺﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ', 'مَّلْعُونِينَ ۖ أَيْنَمَا ثُقِفُوٓا۟ أُخِذُوا۟ وَقُتِّلُوا۟ تَقْتِيلًۭا ٦١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (62, 33, 426, 'ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ', 'سُنَّةَ ٱللَّهِ فِى ٱلَّذِينَ خَلَوْا۟ مِن قَبْلُ ۖ وَلَن تَجِدَ لِسُنَّةِ ٱللَّهِ تَبْدِيلًۭا ٦٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (63, 33, 427, 'ﱁ ﱂ ﱃ ﱄﱅ ﱆ ﱇ ﱈ ﱉ ﱊﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ', 'يَسْـَٔلُكَ ٱلنَّاسُ عَنِ ٱلسَّاعَةِ ۖ قُلْ إِنَّمَا عِلْمُهَا عِندَ ٱللَّهِ ۚ وَمَا يُدْرِيكَ لَعَلَّ ٱلسَّاعَةَ تَكُونُ قَرِيبًا ٦٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (64, 33, 427, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ', 'إِنَّ ٱللَّهَ لَعَنَ ٱلْكَـٰفِرِينَ وَأَعَدَّ لَهُمْ سَعِيرًا ٦٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (65, 33, 427, 'ﱛ ﱜ ﱝﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ', 'خَـٰلِدِينَ فِيهَآ أَبَدًۭا ۖ لَّا يَجِدُونَ وَلِيًّۭا وَلَا نَصِيرًۭا ٦٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (66, 33, 427, 'ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ', 'يَوْمَ تُقَلَّبُ وُجُوهُهُمْ فِى ٱلنَّارِ يَقُولُونَ يَـٰلَيْتَنَآ أَطَعْنَا ٱللَّهَ وَأَطَعْنَا ٱلرَّسُولَا۠ ٦٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (67, 33, 427, 'ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ', 'وَقَالُوا۟ رَبَّنَآ إِنَّآ أَطَعْنَا سَادَتَنَا وَكُبَرَآءَنَا فَأَضَلُّونَا ٱلسَّبِيلَا۠ ٦٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (68, 33, 427, 'ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ', 'رَبَّنَآ ءَاتِهِمْ ضِعْفَيْنِ مِنَ ٱلْعَذَابِ وَٱلْعَنْهُمْ لَعْنًۭا كَبِيرًۭا ٦٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (69, 33, 427, 'ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎﲏ ﲐ ﲑ ﲒ ﲓ ﲔ', 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تَكُونُوا۟ كَٱلَّذِينَ ءَاذَوْا۟ مُوسَىٰ فَبَرَّأَهُ ٱللَّهُ مِمَّا قَالُوا۟ ۚ وَكَانَ عِندَ ٱللَّهِ وَجِيهًۭا ٦٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (70, 33, 427, 'ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ', 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ ٱتَّقُوا۟ ٱللَّهَ وَقُولُوا۟ قَوْلًۭا سَدِيدًۭا ٧٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (71, 33, 427, 'ﲞ ﲟ ﲠ ﲡ ﲢ ﲣﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ', 'يُصْلِحْ لَكُمْ أَعْمَـٰلَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْ ۗ وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ فَقَدْ فَازَ فَوْزًا عَظِيمًا ٧١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (72, 33, 427, 'ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻﲼ ﲽ ﲾ ﲿ ﳀ ﳁ', 'إِنَّا عَرَضْنَا ٱلْأَمَانَةَ عَلَى ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ وَٱلْجِبَالِ فَأَبَيْنَ أَن يَحْمِلْنَهَا وَأَشْفَقْنَ مِنْهَا وَحَمَلَهَا ٱلْإِنسَـٰنُ ۖ إِنَّهُۥ كَانَ ظَلُومًۭا جَهُولًۭا ٧٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (73, 33, 427, 'ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌﳍ ﳎ ﳏ ﳐ ﳑ ﳒ', 'لِّيُعَذِّبَ ٱللَّهُ ٱلْمُنَـٰفِقِينَ وَٱلْمُنَـٰفِقَـٰتِ وَٱلْمُشْرِكِينَ وَٱلْمُشْرِكَـٰتِ وَيَتُوبَ ٱللَّهُ عَلَى ٱلْمُؤْمِنِينَ وَٱلْمُؤْمِنَـٰتِ ۗ وَكَانَ ٱللَّهُ غَفُورًۭا رَّحِيمًۢا ٧٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 34, 428, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎﱏ ﱐ ﱑ ﱒ ﱓ', 'ٱلْحَمْدُ لِلَّهِ ٱلَّذِى لَهُۥ مَا فِى ٱلسَّمَـٰوَٰتِ وَمَا فِى ٱلْأَرْضِ وَلَهُ ٱلْحَمْدُ فِى ٱلْـَٔاخِرَةِ ۚ وَهُوَ ٱلْحَكِيمُ ٱلْخَبِيرُ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 34, 428, 'ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢﱣ ﱤ ﱥ ﱦ ﱧ', 'يَعْلَمُ مَا يَلِجُ فِى ٱلْأَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنزِلُ مِنَ ٱلسَّمَآءِ وَمَا يَعْرُجُ فِيهَا ۚ وَهُوَ ٱلرَّحِيمُ ٱلْغَفُورُ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 34, 428, 'ﱨ ﱩ ﱪ ﱫ ﱬ ﱭﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ', 'وَقَالَ ٱلَّذِينَ كَفَرُوا۟ لَا تَأْتِينَا ٱلسَّاعَةُ ۖ قُلْ بَلَىٰ وَرَبِّى لَتَأْتِيَنَّكُمْ عَـٰلِمِ ٱلْغَيْبِ ۖ لَا يَعْزُبُ عَنْهُ مِثْقَالُ ذَرَّةٍۢ فِى ٱلسَّمَـٰوَٰتِ وَلَا فِى ٱلْأَرْضِ وَلَآ أَصْغَرُ مِن ذَٰلِكَ وَلَآ أَكْبَرُ إِلَّا فِى كِتَـٰبٍۢ مُّبِينٍۢ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 34, 428, 'ﲋ ﲌ ﲍ ﲎ ﲏﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ', 'لِّيَجْزِىَ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ ۚ أُو۟لَـٰٓئِكَ لَهُم مَّغْفِرَةٌۭ وَرِزْقٌۭ كَرِيمٌۭ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 34, 428, 'ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ', 'وَٱلَّذِينَ سَعَوْ فِىٓ ءَايَـٰتِنَا مُعَـٰجِزِينَ أُو۟لَـٰٓئِكَ لَهُمْ عَذَابٌۭ مِّن رِّجْزٍ أَلِيمٌۭ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 34, 428, 'ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ', 'وَيَرَى ٱلَّذِينَ أُوتُوا۟ ٱلْعِلْمَ ٱلَّذِىٓ أُنزِلَ إِلَيْكَ مِن رَّبِّكَ هُوَ ٱلْحَقَّ وَيَهْدِىٓ إِلَىٰ صِرَٰطِ ٱلْعَزِيزِ ٱلْحَمِيدِ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 34, 428, 'ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ', 'وَقَالَ ٱلَّذِينَ كَفَرُوا۟ هَلْ نَدُلُّكُمْ عَلَىٰ رَجُلٍۢ يُنَبِّئُكُمْ إِذَا مُزِّقْتُمْ كُلَّ مُمَزَّقٍ إِنَّكُمْ لَفِى خَلْقٍۢ جَدِيدٍ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 34, 429, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ', 'أَفْتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَم بِهِۦ جِنَّةٌۢ ۗ بَلِ ٱلَّذِينَ لَا يُؤْمِنُونَ بِٱلْـَٔاخِرَةِ فِى ٱلْعَذَابِ وَٱلضَّلَـٰلِ ٱلْبَعِيدِ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 34, 429, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ', 'أَفَلَمْ يَرَوْا۟ إِلَىٰ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُم مِّنَ ٱلسَّمَآءِ وَٱلْأَرْضِ ۚ إِن نَّشَأْ نَخْسِفْ بِهِمُ ٱلْأَرْضَ أَوْ نُسْقِطْ عَلَيْهِمْ كِسَفًۭا مِّنَ ٱلسَّمَآءِ ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَةًۭ لِّكُلِّ عَبْدٍۢ مُّنِيبٍۢ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 34, 429, 'ﱳﱴ ﱵ ﱶ ﱷ ﱸﱹ ﱺ ﱻ ﱼ ﱽﱾ ﱿ ﲀ ﲁ ﲂ', '۞ وَلَقَدْ ءَاتَيْنَا دَاوُۥدَ مِنَّا فَضْلًۭا ۖ يَـٰجِبَالُ أَوِّبِى مَعَهُۥ وَٱلطَّيْرَ ۖ وَأَلَنَّا لَهُ ٱلْحَدِيدَ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 34, 429, 'ﲃ ﲄ ﲅ ﲆ ﲇ ﲈﲉ ﲊ ﲋﲌ ﲍ ﲎ ﲏ ﲐ ﲑ', 'أَنِ ٱعْمَلْ سَـٰبِغَـٰتٍۢ وَقَدِّرْ فِى ٱلسَّرْدِ ۖ وَٱعْمَلُوا۟ صَـٰلِحًا ۖ إِنِّى بِمَا تَعْمَلُونَ بَصِيرٌۭ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 34, 429, 'ﲒ ﲓ ﲔ ﲕ ﲖ ﲗﲘ ﲙ ﲚ ﲛ ﲜﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ', 'وَلِسُلَيْمَـٰنَ ٱلرِّيحَ غُدُوُّهَا شَهْرٌۭ وَرَوَاحُهَا شَهْرٌۭ ۖ وَأَسَلْنَا لَهُۥ عَيْنَ ٱلْقِطْرِ ۖ وَمِنَ ٱلْجِنِّ مَن يَعْمَلُ بَيْنَ يَدَيْهِ بِإِذْنِ رَبِّهِۦ ۖ وَمَن يَزِغْ مِنْهُمْ عَنْ أَمْرِنَا نُذِقْهُ مِنْ عَذَابِ ٱلسَّعِيرِ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 34, 429, 'ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻﲼ ﲽ ﲾ ﲿ ﳀﳁ ﳂ ﳃ ﳄ ﳅ ﳆ', 'يَعْمَلُونَ لَهُۥ مَا يَشَآءُ مِن مَّحَـٰرِيبَ وَتَمَـٰثِيلَ وَجِفَانٍۢ كَٱلْجَوَابِ وَقُدُورٍۢ رَّاسِيَـٰتٍ ۚ ٱعْمَلُوٓا۟ ءَالَ دَاوُۥدَ شُكْرًۭا ۚ وَقَلِيلٌۭ مِّنْ عِبَادِىَ ٱلشَّكُورُ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 34, 429, 'ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ ﳡ ﳢ ﳣ', 'فَلَمَّا قَضَيْنَا عَلَيْهِ ٱلْمَوْتَ مَا دَلَّهُمْ عَلَىٰ مَوْتِهِۦٓ إِلَّا دَآبَّةُ ٱلْأَرْضِ تَأْكُلُ مِنسَأَتَهُۥ ۖ فَلَمَّا خَرَّ تَبَيَّنَتِ ٱلْجِنُّ أَن لَّوْ كَانُوا۟ يَعْلَمُونَ ٱلْغَيْبَ مَا لَبِثُوا۟ فِى ٱلْعَذَابِ ٱلْمُهِينِ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 34, 430, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆﱇ ﱈ ﱉ ﱊ ﱋﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒﱓ ﱔ ﱕ ﱖ ﱗ ﱘ', 'لَقَدْ كَانَ لِسَبَإٍۢ فِى مَسْكَنِهِمْ ءَايَةٌۭ ۖ جَنَّتَانِ عَن يَمِينٍۢ وَشِمَالٍۢ ۖ كُلُوا۟ مِن رِّزْقِ رَبِّكُمْ وَٱشْكُرُوا۟ لَهُۥ ۚ بَلْدَةٌۭ طَيِّبَةٌۭ وَرَبٌّ غَفُورٌۭ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 34, 430, 'ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ', 'فَأَعْرَضُوا۟ فَأَرْسَلْنَا عَلَيْهِمْ سَيْلَ ٱلْعَرِمِ وَبَدَّلْنَـٰهُم بِجَنَّتَيْهِمْ جَنَّتَيْنِ ذَوَاتَىْ أُكُلٍ خَمْطٍۢ وَأَثْلٍۢ وَشَىْءٍۢ مِّن سِدْرٍۢ قَلِيلٍۢ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 34, 430, 'ﱪ ﱫ ﱬ ﱭﱮ ﱯ ﱰ ﱱ ﱲ ﱳ', 'ذَٰلِكَ جَزَيْنَـٰهُم بِمَا كَفَرُوا۟ ۖ وَهَلْ نُجَـٰزِىٓ إِلَّا ٱلْكَفُورَ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 34, 430, 'ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ', 'وَجَعَلْنَا بَيْنَهُمْ وَبَيْنَ ٱلْقُرَى ٱلَّتِى بَـٰرَكْنَا فِيهَا قُرًۭى ظَـٰهِرَةًۭ وَقَدَّرْنَا فِيهَا ٱلسَّيْرَ ۖ سِيرُوا۟ فِيهَا لَيَالِىَ وَأَيَّامًا ءَامِنِينَ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 34, 430, 'ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ', 'فَقَالُوا۟ رَبَّنَا بَـٰعِدْ بَيْنَ أَسْفَارِنَا وَظَلَمُوٓا۟ أَنفُسَهُمْ فَجَعَلْنَـٰهُمْ أَحَادِيثَ وَمَزَّقْنَـٰهُمْ كُلَّ مُمَزَّقٍ ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَـٰتٍۢ لِّكُلِّ صَبَّارٍۢ شَكُورٍۢ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 34, 430, 'ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ', 'وَلَقَدْ صَدَّقَ عَلَيْهِمْ إِبْلِيسُ ظَنَّهُۥ فَٱتَّبَعُوهُ إِلَّا فَرِيقًۭا مِّنَ ٱلْمُؤْمِنِينَ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 34, 430, 'ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ', 'وَمَا كَانَ لَهُۥ عَلَيْهِم مِّن سُلْطَـٰنٍ إِلَّا لِنَعْلَمَ مَن يُؤْمِنُ بِٱلْـَٔاخِرَةِ مِمَّنْ هُوَ مِنْهَا فِى شَكٍّۢ ۗ وَرَبُّكَ عَلَىٰ كُلِّ شَىْءٍ حَفِيظٌۭ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 34, 430, 'ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ', 'قُلِ ٱدْعُوا۟ ٱلَّذِينَ زَعَمْتُم مِّن دُونِ ٱللَّهِ ۖ لَا يَمْلِكُونَ مِثْقَالَ ذَرَّةٍۢ فِى ٱلسَّمَـٰوَٰتِ وَلَا فِى ٱلْأَرْضِ وَمَا لَهُمْ فِيهِمَا مِن شِرْكٍۢ وَمَا لَهُۥ مِنْهُم مِّن ظَهِيرٍۢ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 34, 431, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒﱓ ﱔ ﱕﱖ ﱗ ﱘ ﱙ ﱚ', 'وَلَا تَنفَعُ ٱلشَّفَـٰعَةُ عِندَهُۥٓ إِلَّا لِمَنْ أَذِنَ لَهُۥ ۚ حَتَّىٰٓ إِذَا فُزِّعَ عَن قُلُوبِهِمْ قَالُوا۟ مَاذَا قَالَ رَبُّكُمْ ۖ قَالُوا۟ ٱلْحَقَّ ۖ وَهُوَ ٱلْعَلِىُّ ٱلْكَبِيرُ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 34, 431, 'ﱛﱜ ﱝ ﱞ ﱟ ﱠ ﱡﱢ ﱣ ﱤﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ', '۞ قُلْ مَن يَرْزُقُكُم مِّنَ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۖ قُلِ ٱللَّهُ ۖ وَإِنَّآ أَوْ إِيَّاكُمْ لَعَلَىٰ هُدًى أَوْ فِى ضَلَـٰلٍۢ مُّبِينٍۢ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 34, 431, 'ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ', 'قُل لَّا تُسْـَٔلُونَ عَمَّآ أَجْرَمْنَا وَلَا نُسْـَٔلُ عَمَّا تَعْمَلُونَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 34, 431, 'ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ', 'قُلْ يَجْمَعُ بَيْنَنَا رَبُّنَا ثُمَّ يَفْتَحُ بَيْنَنَا بِٱلْحَقِّ وَهُوَ ٱلْفَتَّاحُ ٱلْعَلِيمُ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 34, 431, 'ﲆ ﲇ ﲈ ﲉ ﲊ ﲋﲌ ﲍﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ', 'قُلْ أَرُونِىَ ٱلَّذِينَ أَلْحَقْتُم بِهِۦ شُرَكَآءَ ۖ كَلَّا ۚ بَلْ هُوَ ٱللَّهُ ٱلْعَزِيزُ ٱلْحَكِيمُ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 34, 431, 'ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ', 'وَمَآ أَرْسَلْنَـٰكَ إِلَّا كَآفَّةًۭ لِّلنَّاسِ بَشِيرًۭا وَنَذِيرًۭا وَلَـٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَعْلَمُونَ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 34, 431, 'ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ', 'وَيَقُولُونَ مَتَىٰ هَـٰذَا ٱلْوَعْدُ إِن كُنتُمْ صَـٰدِقِينَ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 34, 431, 'ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ', 'قُل لَّكُم مِّيعَادُ يَوْمٍۢ لَّا تَسْتَـْٔخِرُونَ عَنْهُ سَاعَةًۭ وَلَا تَسْتَقْدِمُونَ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 34, 431, 'ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ', 'وَقَالَ ٱلَّذِينَ كَفَرُوا۟ لَن نُّؤْمِنَ بِهَـٰذَا ٱلْقُرْءَانِ وَلَا بِٱلَّذِى بَيْنَ يَدَيْهِ ۗ وَلَوْ تَرَىٰٓ إِذِ ٱلظَّـٰلِمُونَ مَوْقُوفُونَ عِندَ رَبِّهِمْ يَرْجِعُ بَعْضُهُمْ إِلَىٰ بَعْضٍ ٱلْقَوْلَ يَقُولُ ٱلَّذِينَ ٱسْتُضْعِفُوا۟ لِلَّذِينَ ٱسْتَكْبَرُوا۟ لَوْلَآ أَنتُمْ لَكُنَّا مُؤْمِنِينَ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 34, 432, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌﱍ ﱎ ﱏ ﱐ ﱑ', 'قَالَ ٱلَّذِينَ ٱسْتَكْبَرُوا۟ لِلَّذِينَ ٱسْتُضْعِفُوٓا۟ أَنَحْنُ صَدَدْنَـٰكُمْ عَنِ ٱلْهُدَىٰ بَعْدَ إِذْ جَآءَكُم ۖ بَلْ كُنتُم مُّجْرِمِينَ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 34, 432, 'ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢﱣ ﱤ ﱥ ﱦ ﱧ ﱨﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ', 'وَقَالَ ٱلَّذِينَ ٱسْتُضْعِفُوا۟ لِلَّذِينَ ٱسْتَكْبَرُوا۟ بَلْ مَكْرُ ٱلَّيْلِ وَٱلنَّهَارِ إِذْ تَأْمُرُونَنَآ أَن نَّكْفُرَ بِٱللَّهِ وَنَجْعَلَ لَهُۥٓ أَندَادًۭا ۚ وَأَسَرُّوا۟ ٱلنَّدَامَةَ لَمَّا رَأَوُا۟ ٱلْعَذَابَ وَجَعَلْنَا ٱلْأَغْلَـٰلَ فِىٓ أَعْنَاقِ ٱلَّذِينَ كَفَرُوا۟ ۚ هَلْ يُجْزَوْنَ إِلَّا مَا كَانُوا۟ يَعْمَلُونَ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 34, 432, 'ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ', 'وَمَآ أَرْسَلْنَا فِى قَرْيَةٍۢ مِّن نَّذِيرٍ إِلَّا قَالَ مُتْرَفُوهَآ إِنَّا بِمَآ أُرْسِلْتُم بِهِۦ كَـٰفِرُونَ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 34, 432, 'ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ', 'وَقَالُوا۟ نَحْنُ أَكْثَرُ أَمْوَٰلًۭا وَأَوْلَـٰدًۭا وَمَا نَحْنُ بِمُعَذَّبِينَ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 34, 432, 'ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ', 'قُلْ إِنَّ رَبِّى يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ وَيَقْدِرُ وَلَـٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَعْلَمُونَ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 34, 432, 'ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ', 'وَمَآ أَمْوَٰلُكُمْ وَلَآ أَوْلَـٰدُكُم بِٱلَّتِى تُقَرِّبُكُمْ عِندَنَا زُلْفَىٰٓ إِلَّا مَنْ ءَامَنَ وَعَمِلَ صَـٰلِحًۭا فَأُو۟لَـٰٓئِكَ لَهُمْ جَزَآءُ ٱلضِّعْفِ بِمَا عَمِلُوا۟ وَهُمْ فِى ٱلْغُرُفَـٰتِ ءَامِنُونَ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 34, 432, 'ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ', 'وَٱلَّذِينَ يَسْعَوْنَ فِىٓ ءَايَـٰتِنَا مُعَـٰجِزِينَ أُو۟لَـٰٓئِكَ فِى ٱلْعَذَابِ مُحْضَرُونَ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 34, 432, 'ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑﳒ ﳓ ﳔ ﳕ ﳖ', 'قُلْ إِنَّ رَبِّى يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ مِنْ عِبَادِهِۦ وَيَقْدِرُ لَهُۥ ۚ وَمَآ أَنفَقْتُم مِّن شَىْءٍۢ فَهُوَ يُخْلِفُهُۥ ۖ وَهُوَ خَيْرُ ٱلرَّٰزِقِينَ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 34, 433, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ', 'وَيَوْمَ يَحْشُرُهُمْ جَمِيعًۭا ثُمَّ يَقُولُ لِلْمَلَـٰٓئِكَةِ أَهَـٰٓؤُلَآءِ إِيَّاكُمْ كَانُوا۟ يَعْبُدُونَ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 34, 433, 'ﱌ ﱍ ﱎ ﱏ ﱐ ﱑﱒ ﱓ ﱔ ﱕ ﱖﱗ ﱘ ﱙ ﱚ ﱛ', 'قَالُوا۟ سُبْحَـٰنَكَ أَنتَ وَلِيُّنَا مِن دُونِهِم ۖ بَلْ كَانُوا۟ يَعْبُدُونَ ٱلْجِنَّ ۖ أَكْثَرُهُم بِهِم مُّؤْمِنُونَ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 34, 433, 'ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ', 'فَٱلْيَوْمَ لَا يَمْلِكُ بَعْضُكُمْ لِبَعْضٍۢ نَّفْعًۭا وَلَا ضَرًّۭا وَنَقُولُ لِلَّذِينَ ظَلَمُوا۟ ذُوقُوا۟ عَذَابَ ٱلنَّارِ ٱلَّتِى كُنتُم بِهَا تُكَذِّبُونَ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 34, 433, 'ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ', 'وَإِذَا تُتْلَىٰ عَلَيْهِمْ ءَايَـٰتُنَا بَيِّنَـٰتٍۢ قَالُوا۟ مَا هَـٰذَآ إِلَّا رَجُلٌۭ يُرِيدُ أَن يَصُدَّكُمْ عَمَّا كَانَ يَعْبُدُ ءَابَآؤُكُمْ وَقَالُوا۟ مَا هَـٰذَآ إِلَّآ إِفْكٌۭ مُّفْتَرًۭى ۚ وَقَالَ ٱلَّذِينَ كَفَرُوا۟ لِلْحَقِّ لَمَّا جَآءَهُمْ إِنْ هَـٰذَآ إِلَّا سِحْرٌۭ مُّبِينٌۭ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 34, 433, 'ﲓ ﲔ ﲕ ﲖ ﲗﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ', 'وَمَآ ءَاتَيْنَـٰهُم مِّن كُتُبٍۢ يَدْرُسُونَهَا ۖ وَمَآ أَرْسَلْنَآ إِلَيْهِمْ قَبْلَكَ مِن نَّذِيرٍۢ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 34, 433, 'ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪﲫ ﲬ ﲭ ﲮ ﲯ', 'وَكَذَّبَ ٱلَّذِينَ مِن قَبْلِهِمْ وَمَا بَلَغُوا۟ مِعْشَارَ مَآ ءَاتَيْنَـٰهُمْ فَكَذَّبُوا۟ رُسُلِى ۖ فَكَيْفَ كَانَ نَكِيرِ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 34, 433, 'ﲰﲱ ﲲ ﲳ ﲴﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼﲽ ﲾ ﲿ ﳀ ﳁﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ', '۞ قُلْ إِنَّمَآ أَعِظُكُم بِوَٰحِدَةٍ ۖ أَن تَقُومُوا۟ لِلَّهِ مَثْنَىٰ وَفُرَٰدَىٰ ثُمَّ تَتَفَكَّرُوا۟ ۚ مَا بِصَاحِبِكُم مِّن جِنَّةٍ ۚ إِنْ هُوَ إِلَّا نَذِيرٌۭ لَّكُم بَيْنَ يَدَىْ عَذَابٍۢ شَدِيدٍۢ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 34, 433, 'ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓﳔ ﳕ ﳖ ﳗ ﳘ ﳙﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ', 'قُلْ مَا سَأَلْتُكُم مِّنْ أَجْرٍۢ فَهُوَ لَكُمْ ۖ إِنْ أَجْرِىَ إِلَّا عَلَى ٱللَّهِ ۖ وَهُوَ عَلَىٰ كُلِّ شَىْءٍۢ شَهِيدٌۭ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 34, 433, 'ﳡ ﳢ ﳣ ﳤ ﳥ ﳦ ﳧ ﳨ', 'قُلْ إِنَّ رَبِّى يَقْذِفُ بِٱلْحَقِّ عَلَّـٰمُ ٱلْغُيُوبِ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 34, 434, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ', 'قُلْ جَآءَ ٱلْحَقُّ وَمَا يُبْدِئُ ٱلْبَـٰطِلُ وَمَا يُعِيدُ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 34, 434, 'ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗﱘ ﱙ ﱚ ﱛ ﱜ', 'قُلْ إِن ضَلَلْتُ فَإِنَّمَآ أَضِلُّ عَلَىٰ نَفْسِى ۖ وَإِنِ ٱهْتَدَيْتُ فَبِمَا يُوحِىٓ إِلَىَّ رَبِّىٓ ۚ إِنَّهُۥ سَمِيعٌۭ قَرِيبٌۭ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 34, 434, 'ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ', 'وَلَوْ تَرَىٰٓ إِذْ فَزِعُوا۟ فَلَا فَوْتَ وَأُخِذُوا۟ مِن مَّكَانٍۢ قَرِيبٍۢ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 34, 434, 'ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ', 'وَقَالُوٓا۟ ءَامَنَّا بِهِۦ وَأَنَّىٰ لَهُمُ ٱلتَّنَاوُشُ مِن مَّكَانٍۭ بَعِيدٍۢ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 34, 434, 'ﱲ ﱳ ﱴ ﱵ ﱶﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ', 'وَقَدْ كَفَرُوا۟ بِهِۦ مِن قَبْلُ ۖ وَيَقْذِفُونَ بِٱلْغَيْبِ مِن مَّكَانٍۭ بَعِيدٍۢ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 34, 434, 'ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ', 'وَحِيلَ بَيْنَهُمْ وَبَيْنَ مَا يَشْتَهُونَ كَمَا فُعِلَ بِأَشْيَاعِهِم مِّن قَبْلُ ۚ إِنَّهُمْ كَانُوا۟ فِى شَكٍّۢ مُّرِيبٍۭ ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 35, 434, 'ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛﲜ ﲝ ﲞ ﲟ ﲠ ﲡﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ', 'ٱلْحَمْدُ لِلَّهِ فَاطِرِ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ جَاعِلِ ٱلْمَلَـٰٓئِكَةِ رُسُلًا أُو۟لِىٓ أَجْنِحَةٍۢ مَّثْنَىٰ وَثُلَـٰثَ وَرُبَـٰعَ ۚ يَزِيدُ فِى ٱلْخَلْقِ مَا يَشَآءُ ۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَىْءٍۢ قَدِيرٌۭ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 35, 434, 'ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺﲻ ﲼ ﲽ ﲾ ﲿ', 'مَّا يَفْتَحِ ٱللَّهُ لِلنَّاسِ مِن رَّحْمَةٍۢ فَلَا مُمْسِكَ لَهَا ۖ وَمَا يُمْسِكْ فَلَا مُرْسِلَ لَهُۥ مِنۢ بَعْدِهِۦ ۚ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 35, 434, 'ﳀ ﳁ ﳂ ﳃ ﳄ ﳅﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏﳐ ﳑ ﳒ ﳓ ﳔﳕ ﳖ ﳗ ﳘ', 'يَـٰٓأَيُّهَا ٱلنَّاسُ ٱذْكُرُوا۟ نِعْمَتَ ٱللَّهِ عَلَيْكُمْ ۚ هَلْ مِنْ خَـٰلِقٍ غَيْرُ ٱللَّهِ يَرْزُقُكُم مِّنَ ٱلسَّمَآءِ وَٱلْأَرْضِ ۚ لَآ إِلَـٰهَ إِلَّا هُوَ ۖ فَأَنَّىٰ تُؤْفَكُونَ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 35, 435, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇﱈ ﱉ ﱊ ﱋ ﱌ ﱍ', 'وَإِن يُكَذِّبُوكَ فَقَدْ كُذِّبَتْ رُسُلٌۭ مِّن قَبْلِكَ ۚ وَإِلَى ٱللَّهِ تُرْجَعُ ٱلْأُمُورُ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 35, 435, 'ﱎ ﱏ ﱐ ﱑ ﱒ ﱓﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ', 'يَـٰٓأَيُّهَا ٱلنَّاسُ إِنَّ وَعْدَ ٱللَّهِ حَقٌّۭ ۖ فَلَا تَغُرَّنَّكُمُ ٱلْحَيَوٰةُ ٱلدُّنْيَا ۖ وَلَا يَغُرَّنَّكُم بِٱللَّهِ ٱلْغَرُورُ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 35, 435, 'ﱞ ﱟ ﱠ ﱡ ﱢ ﱣﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ', 'إِنَّ ٱلشَّيْطَـٰنَ لَكُمْ عَدُوٌّۭ فَٱتَّخِذُوهُ عَدُوًّا ۚ إِنَّمَا يَدْعُوا۟ حِزْبَهُۥ لِيَكُونُوا۟ مِنْ أَصْحَـٰبِ ٱلسَّعِيرِ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 35, 435, 'ﱭ ﱮ ﱯ ﱰ ﱱﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ', 'ٱلَّذِينَ كَفَرُوا۟ لَهُمْ عَذَابٌۭ شَدِيدٌۭ ۖ وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَهُم مَّغْفِرَةٌۭ وَأَجْرٌۭ كَبِيرٌ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 35, 435, 'ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋﲌ ﲍ ﲎ ﲏ ﲐ ﲑﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ', 'أَفَمَن زُيِّنَ لَهُۥ سُوٓءُ عَمَلِهِۦ فَرَءَاهُ حَسَنًۭا ۖ فَإِنَّ ٱللَّهَ يُضِلُّ مَن يَشَآءُ وَيَهْدِى مَن يَشَآءُ ۖ فَلَا تَذْهَبْ نَفْسُكَ عَلَيْهِمْ حَسَرَٰتٍ ۚ إِنَّ ٱللَّهَ عَلِيمٌۢ بِمَا يَصْنَعُونَ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 35, 435, 'ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧﲨ ﲩ ﲪ ﲫ', 'وَٱللَّهُ ٱلَّذِىٓ أَرْسَلَ ٱلرِّيَـٰحَ فَتُثِيرُ سَحَابًۭا فَسُقْنَـٰهُ إِلَىٰ بَلَدٍۢ مَّيِّتٍۢ فَأَحْيَيْنَا بِهِ ٱلْأَرْضَ بَعْدَ مَوْتِهَا ۚ كَذَٰلِكَ ٱلنُّشُورُ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 35, 435, 'ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁﳂ ﳃ ﳄ ﳅ ﳆ ﳇ', 'مَن كَانَ يُرِيدُ ٱلْعِزَّةَ فَلِلَّهِ ٱلْعِزَّةُ جَمِيعًا ۚ إِلَيْهِ يَصْعَدُ ٱلْكَلِمُ ٱلطَّيِّبُ وَٱلْعَمَلُ ٱلصَّـٰلِحُ يَرْفَعُهُۥ ۚ وَٱلَّذِينَ يَمْكُرُونَ ٱلسَّيِّـَٔاتِ لَهُمْ عَذَابٌۭ شَدِيدٌۭ ۖ وَمَكْرُ أُو۟لَـٰٓئِكَ هُوَ يَبُورُ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 35, 435, 'ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚﳛ ﳜ ﳝ ﳞ ﳟ ﳠ ﳡ ﳢ ﳣ ﳤ ﳥ ﳦﳧ ﳨ ﳩ ﳪ ﳫ ﳬ ﳭ', 'وَٱللَّهُ خَلَقَكُم مِّن تُرَابٍۢ ثُمَّ مِن نُّطْفَةٍۢ ثُمَّ جَعَلَكُمْ أَزْوَٰجًۭا ۚ وَمَا تَحْمِلُ مِنْ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلْمِهِۦ ۚ وَمَا يُعَمَّرُ مِن مُّعَمَّرٍۢ وَلَا يُنقَصُ مِنْ عُمُرِهِۦٓ إِلَّا فِى كِتَـٰبٍ ۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٌۭ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 35, 436, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ', 'وَمَا يَسْتَوِى ٱلْبَحْرَانِ هَـٰذَا عَذْبٌۭ فُرَاتٌۭ سَآئِغٌۭ شَرَابُهُۥ وَهَـٰذَا مِلْحٌ أُجَاجٌۭ ۖ وَمِن كُلٍّۢ تَأْكُلُونَ لَحْمًۭا طَرِيًّۭا وَتَسْتَخْرِجُونَ حِلْيَةًۭ تَلْبَسُونَهَا ۖ وَتَرَى ٱلْفُلْكَ فِيهِ مَوَاخِرَ لِتَبْتَغُوا۟ مِن فَضْلِهِۦ وَلَعَلَّكُمْ تَشْكُرُونَ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 35, 436, 'ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪﱫ ﱬ ﱭ ﱮ ﱯﱰ ﱱ ﱲ ﱳ ﱴ ﱵﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ', 'يُولِجُ ٱلَّيْلَ فِى ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِى ٱلَّيْلِ وَسَخَّرَ ٱلشَّمْسَ وَٱلْقَمَرَ كُلٌّۭ يَجْرِى لِأَجَلٍۢ مُّسَمًّۭى ۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمْ لَهُ ٱلْمُلْكُ ۚ وَٱلَّذِينَ تَدْعُونَ مِن دُونِهِۦ مَا يَمْلِكُونَ مِن قِطْمِيرٍ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 35, 436, 'ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉﲊ ﲋ ﲌ ﲍ ﲎﲏ ﲐ ﲑ ﲒ ﲓ ﲔ', 'إِن تَدْعُوهُمْ لَا يَسْمَعُوا۟ دُعَآءَكُمْ وَلَوْ سَمِعُوا۟ مَا ٱسْتَجَابُوا۟ لَكُمْ ۖ وَيَوْمَ ٱلْقِيَـٰمَةِ يَكْفُرُونَ بِشِرْكِكُمْ ۚ وَلَا يُنَبِّئُكَ مِثْلُ خَبِيرٍۢ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 35, 436, 'ﲕﲖ ﲗ ﲘ ﲙ ﲚ ﲛﲜ ﲝ ﲞ ﲟ ﲠ ﲡ', '۞ يَـٰٓأَيُّهَا ٱلنَّاسُ أَنتُمُ ٱلْفُقَرَآءُ إِلَى ٱللَّهِ ۖ وَٱللَّهُ هُوَ ٱلْغَنِىُّ ٱلْحَمِيدُ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 35, 436, 'ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ', 'إِن يَشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍۢ جَدِيدٍۢ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 35, 436, 'ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ', 'وَمَا ذَٰلِكَ عَلَى ٱللَّهِ بِعَزِيزٍۢ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 35, 436, 'ﲯ ﲰ ﲱ ﲲ ﲳﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊﳋ ﳌ ﳍ ﳎ ﳏ ﳐﳑ ﳒ ﳓ ﳔ ﳕ', 'وَلَا تَزِرُ وَازِرَةٌۭ وِزْرَ أُخْرَىٰ ۚ وَإِن تَدْعُ مُثْقَلَةٌ إِلَىٰ حِمْلِهَا لَا يُحْمَلْ مِنْهُ شَىْءٌۭ وَلَوْ كَانَ ذَا قُرْبَىٰٓ ۗ إِنَّمَا تُنذِرُ ٱلَّذِينَ يَخْشَوْنَ رَبَّهُم بِٱلْغَيْبِ وَأَقَامُوا۟ ٱلصَّلَوٰةَ ۚ وَمَن تَزَكَّىٰ فَإِنَّمَا يَتَزَكَّىٰ لِنَفْسِهِۦ ۚ وَإِلَى ٱللَّهِ ٱلْمَصِيرُ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 35, 437, 'ﱁ ﱂ ﱃ ﱄ ﱅ', 'وَمَا يَسْتَوِى ٱلْأَعْمَىٰ وَٱلْبَصِيرُ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 35, 437, 'ﱆ ﱇ ﱈ ﱉ ﱊ', 'وَلَا ٱلظُّلُمَـٰتُ وَلَا ٱلنُّورُ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 35, 437, 'ﱋ ﱌ ﱍ ﱎ ﱏ', 'وَلَا ٱلظِّلُّ وَلَا ٱلْحَرُورُ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 35, 437, 'ﱐ ﱑ ﱒ ﱓ ﱔﱕ ﱖ ﱗ ﱘ ﱙ ﱚﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ', 'وَمَا يَسْتَوِى ٱلْأَحْيَآءُ وَلَا ٱلْأَمْوَٰتُ ۚ إِنَّ ٱللَّهَ يُسْمِعُ مَن يَشَآءُ ۖ وَمَآ أَنتَ بِمُسْمِعٍۢ مَّن فِى ٱلْقُبُورِ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 35, 437, 'ﱣ ﱤ ﱥ ﱦ ﱧ', 'إِنْ أَنتَ إِلَّا نَذِيرٌ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 35, 437, 'ﱨ ﱩ ﱪ ﱫ ﱬﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ', 'إِنَّآ أَرْسَلْنَـٰكَ بِٱلْحَقِّ بَشِيرًۭا وَنَذِيرًۭا ۚ وَإِن مِّنْ أُمَّةٍ إِلَّا خَلَا فِيهَا نَذِيرٌۭ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 35, 437, 'ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ', 'وَإِن يُكَذِّبُوكَ فَقَدْ كَذَّبَ ٱلَّذِينَ مِن قَبْلِهِمْ جَآءَتْهُمْ رُسُلُهُم بِٱلْبَيِّنَـٰتِ وَبِٱلزُّبُرِ وَبِٱلْكِتَـٰبِ ٱلْمُنِيرِ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 35, 437, 'ﲄ ﲅ ﲆ ﲇﲈ ﲉ ﲊ ﲋ ﲌ', 'ثُمَّ أَخَذْتُ ٱلَّذِينَ كَفَرُوا۟ ۖ فَكَيْفَ كَانَ نَكِيرِ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 35, 437, 'ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ', 'أَلَمْ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءًۭ فَأَخْرَجْنَا بِهِۦ ثَمَرَٰتٍۢ مُّخْتَلِفًا أَلْوَٰنُهَا ۚ وَمِنَ ٱلْجِبَالِ جُدَدٌۢ بِيضٌۭ وَحُمْرٌۭ مُّخْتَلِفٌ أَلْوَٰنُهَا وَغَرَابِيبُ سُودٌۭ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 35, 437, 'ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲﲳ ﲴ ﲵ ﲶ ﲷ ﲸ', 'وَمِنَ ٱلنَّاسِ وَٱلدَّوَآبِّ وَٱلْأَنْعَـٰمِ مُخْتَلِفٌ أَلْوَٰنُهُۥ كَذَٰلِكَ ۗ إِنَّمَا يَخْشَى ٱللَّهَ مِنْ عِبَادِهِ ٱلْعُلَمَـٰٓؤُا۟ ۗ إِنَّ ٱللَّهَ عَزِيزٌ غَفُورٌ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 35, 437, 'ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ', 'إِنَّ ٱلَّذِينَ يَتْلُونَ كِتَـٰبَ ٱللَّهِ وَأَقَامُوا۟ ٱلصَّلَوٰةَ وَأَنفَقُوا۟ مِمَّا رَزَقْنَـٰهُمْ سِرًّۭا وَعَلَانِيَةًۭ يَرْجُونَ تِجَـٰرَةًۭ لَّن تَبُورَ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 35, 437, 'ﳊ ﳋ ﳌ ﳍ ﳎﳏ ﳐ ﳑ ﳒ ﳓ', 'لِيُوَفِّيَهُمْ أُجُورَهُمْ وَيَزِيدَهُم مِّن فَضْلِهِۦٓ ۚ إِنَّهُۥ غَفُورٌۭ شَكُورٌۭ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 35, 438, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ', 'وَٱلَّذِىٓ أَوْحَيْنَآ إِلَيْكَ مِنَ ٱلْكِتَـٰبِ هُوَ ٱلْحَقُّ مُصَدِّقًۭا لِّمَا بَيْنَ يَدَيْهِ ۗ إِنَّ ٱللَّهَ بِعِبَادِهِۦ لَخَبِيرٌۢ بَصِيرٌۭ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 35, 438, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤﱥ ﱦ ﱧ ﱨ ﱩ ﱪ', 'ثُمَّ أَوْرَثْنَا ٱلْكِتَـٰبَ ٱلَّذِينَ ٱصْطَفَيْنَا مِنْ عِبَادِنَا ۖ فَمِنْهُمْ ظَالِمٌۭ لِّنَفْسِهِۦ وَمِنْهُم مُّقْتَصِدٌۭ وَمِنْهُمْ سَابِقٌۢ بِٱلْخَيْرَٰتِ بِإِذْنِ ٱللَّهِ ۚ ذَٰلِكَ هُوَ ٱلْفَضْلُ ٱلْكَبِيرُ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 35, 438, 'ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴﱵ ﱶ ﱷ ﱸ ﱹ', 'جَنَّـٰتُ عَدْنٍۢ يَدْخُلُونَهَا يُحَلَّوْنَ فِيهَا مِنْ أَسَاوِرَ مِن ذَهَبٍۢ وَلُؤْلُؤًۭا ۖ وَلِبَاسُهُمْ فِيهَا حَرِيرٌۭ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 35, 438, 'ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀﲁ ﲂ ﲃ ﲄ ﲅ ﲆ', 'وَقَالُوا۟ ٱلْحَمْدُ لِلَّهِ ٱلَّذِىٓ أَذْهَبَ عَنَّا ٱلْحَزَنَ ۖ إِنَّ رَبَّنَا لَغَفُورٌۭ شَكُورٌ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 35, 438, 'ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ', 'ٱلَّذِىٓ أَحَلَّنَا دَارَ ٱلْمُقَامَةِ مِن فَضْلِهِۦ لَا يَمَسُّنَا فِيهَا نَصَبٌۭ وَلَا يَمَسُّنَا فِيهَا لُغُوبٌۭ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 35, 438, 'ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣﲤ ﲥ ﲦ ﲧ ﲨ ﲩ', 'وَٱلَّذِينَ كَفَرُوا۟ لَهُمْ نَارُ جَهَنَّمَ لَا يُقْضَىٰ عَلَيْهِمْ فَيَمُوتُوا۟ وَلَا يُخَفَّفُ عَنْهُم مِّنْ عَذَابِهَا ۚ كَذَٰلِكَ نَجْزِى كُلَّ كَفُورٍۢ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 35, 438, 'ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ', 'وَهُمْ يَصْطَرِخُونَ فِيهَا رَبَّنَآ أَخْرِجْنَا نَعْمَلْ صَـٰلِحًا غَيْرَ ٱلَّذِى كُنَّا نَعْمَلُ ۚ أَوَلَمْ نُعَمِّرْكُم مَّا يَتَذَكَّرُ فِيهِ مَن تَذَكَّرَ وَجَآءَكُمُ ٱلنَّذِيرُ ۖ فَذُوقُوا۟ فَمَا لِلظَّـٰلِمِينَ مِن نَّصِيرٍ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 35, 438, 'ﳆ ﳇ ﳈ ﳉ ﳊ ﳋﳌ ﳍ ﳎ ﳏ ﳐ ﳑ', 'إِنَّ ٱللَّهَ عَـٰلِمُ غَيْبِ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۚ إِنَّهُۥ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 35, 439, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆﱇ ﱈ ﱉ ﱊ ﱋﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ', 'هُوَ ٱلَّذِى جَعَلَكُمْ خَلَـٰٓئِفَ فِى ٱلْأَرْضِ ۚ فَمَن كَفَرَ فَعَلَيْهِ كُفْرُهُۥ ۖ وَلَا يَزِيدُ ٱلْكَـٰفِرِينَ كُفْرُهُمْ عِندَ رَبِّهِمْ إِلَّا مَقْتًۭا ۖ وَلَا يَزِيدُ ٱلْكَـٰفِرِينَ كُفْرُهُمْ إِلَّا خَسَارًۭا ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 35, 439, 'ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ', 'قُلْ أَرَءَيْتُمْ شُرَكَآءَكُمُ ٱلَّذِينَ تَدْعُونَ مِن دُونِ ٱللَّهِ أَرُونِى مَاذَا خَلَقُوا۟ مِنَ ٱلْأَرْضِ أَمْ لَهُمْ شِرْكٌۭ فِى ٱلسَّمَـٰوَٰتِ أَمْ ءَاتَيْنَـٰهُمْ كِتَـٰبًۭا فَهُمْ عَلَىٰ بَيِّنَتٍۢ مِّنْهُ ۚ بَلْ إِن يَعِدُ ٱلظَّـٰلِمُونَ بَعْضُهُم بَعْضًا إِلَّا غُرُورًا ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 35, 439, 'ﲀﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐﲑ ﲒ ﲓ ﲔ ﲕ ﲖ', '۞ إِنَّ ٱللَّهَ يُمْسِكُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ أَن تَزُولَا ۚ وَلَئِن زَالَتَآ إِنْ أَمْسَكَهُمَا مِنْ أَحَدٍۢ مِّنۢ بَعْدِهِۦٓ ۚ إِنَّهُۥ كَانَ حَلِيمًا غَفُورًۭا ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 35, 439, 'ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ', 'وَأَقْسَمُوا۟ بِٱللَّهِ جَهْدَ أَيْمَـٰنِهِمْ لَئِن جَآءَهُمْ نَذِيرٌۭ لَّيَكُونُنَّ أَهْدَىٰ مِنْ إِحْدَى ٱلْأُمَمِ ۖ فَلَمَّا جَآءَهُمْ نَذِيرٌۭ مَّا زَادَهُمْ إِلَّا نُفُورًا ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 35, 439, 'ﲬ ﲭ ﲮ ﲯ ﲰﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷﲸ ﲹ ﲺ ﲻ ﲼ ﲽﲾ ﲿ ﳀ ﳁ ﳂ ﳃﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ', 'ٱسْتِكْبَارًۭا فِى ٱلْأَرْضِ وَمَكْرَ ٱلسَّيِّئِ ۚ وَلَا يَحِيقُ ٱلْمَكْرُ ٱلسَّيِّئُ إِلَّا بِأَهْلِهِۦ ۚ فَهَلْ يَنظُرُونَ إِلَّا سُنَّتَ ٱلْأَوَّلِينَ ۚ فَلَن تَجِدَ لِسُنَّتِ ٱللَّهِ تَبْدِيلًۭا ۖ وَلَن تَجِدَ لِسُنَّتِ ٱللَّهِ تَحْوِيلًا ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 35, 439, 'ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ ﳡ ﳢ ﳣ ﳤ ﳥﳦ ﳧ ﳨ ﳩ ﳪ ﳫ', 'أَوَلَمْ يَسِيرُوا۟ فِى ٱلْأَرْضِ فَيَنظُرُوا۟ كَيْفَ كَانَ عَـٰقِبَةُ ٱلَّذِينَ مِن قَبْلِهِمْ وَكَانُوٓا۟ أَشَدَّ مِنْهُمْ قُوَّةًۭ ۚ وَمَا كَانَ ٱللَّهُ لِيُعْجِزَهُۥ مِن شَىْءٍۢ فِى ٱلسَّمَـٰوَٰتِ وَلَا فِى ٱلْأَرْضِ ۚ إِنَّهُۥ كَانَ عَلِيمًۭا قَدِيرًۭا ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 35, 440, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ', 'وَلَوْ يُؤَاخِذُ ٱللَّهُ ٱلنَّاسَ بِمَا كَسَبُوا۟ مَا تَرَكَ عَلَىٰ ظَهْرِهَا مِن دَآبَّةٍۢ وَلَـٰكِن يُؤَخِّرُهُمْ إِلَىٰٓ أَجَلٍۢ مُّسَمًّۭى ۖ فَإِذَا جَآءَ أَجَلُهُمْ فَإِنَّ ٱللَّهَ كَانَ بِعِبَادِهِۦ بَصِيرًۢا ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 36, 440, 'ﱜ ﱝ', 'يسٓ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 36, 440, 'ﱞ ﱟ ﱠ', 'وَٱلْقُرْءَانِ ٱلْحَكِيمِ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 36, 440, 'ﱡ ﱢ ﱣ ﱤ', 'إِنَّكَ لَمِنَ ٱلْمُرْسَلِينَ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 36, 440, 'ﱥ ﱦ ﱧ ﱨ', 'عَلَىٰ صِرَٰطٍۢ مُّسْتَقِيمٍۢ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 36, 440, 'ﱩ ﱪ ﱫ ﱬ', 'تَنزِيلَ ٱلْعَزِيزِ ٱلرَّحِيمِ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 36, 440, 'ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ', 'لِتُنذِرَ قَوْمًۭا مَّآ أُنذِرَ ءَابَآؤُهُمْ فَهُمْ غَـٰفِلُونَ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 36, 440, 'ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ', 'لَقَدْ حَقَّ ٱلْقَوْلُ عَلَىٰٓ أَكْثَرِهِمْ فَهُمْ لَا يُؤْمِنُونَ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 36, 440, 'ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ', 'إِنَّا جَعَلْنَا فِىٓ أَعْنَـٰقِهِمْ أَغْلَـٰلًۭا فَهِىَ إِلَى ٱلْأَذْقَانِ فَهُم مُّقْمَحُونَ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 36, 440, 'ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ', 'وَجَعَلْنَا مِنۢ بَيْنِ أَيْدِيهِمْ سَدًّۭا وَمِنْ خَلْفِهِمْ سَدًّۭا فَأَغْشَيْنَـٰهُمْ فَهُمْ لَا يُبْصِرُونَ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 36, 440, 'ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ', 'وَسَوَآءٌ عَلَيْهِمْ ءَأَنذَرْتَهُمْ أَمْ لَمْ تُنذِرْهُمْ لَا يُؤْمِنُونَ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 36, 440, 'ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦﲧ ﲨ ﲩ ﲪ ﲫ ﲬ', 'إِنَّمَا تُنذِرُ مَنِ ٱتَّبَعَ ٱلذِّكْرَ وَخَشِىَ ٱلرَّحْمَـٰنَ بِٱلْغَيْبِ ۖ فَبَشِّرْهُ بِمَغْفِرَةٍۢ وَأَجْرٍۢ كَرِيمٍ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 36, 440, 'ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ', 'إِنَّا نَحْنُ نُحْىِ ٱلْمَوْتَىٰ وَنَكْتُبُ مَا قَدَّمُوا۟ وَءَاثَـٰرَهُمْ ۚ وَكُلَّ شَىْءٍ أَحْصَيْنَـٰهُ فِىٓ إِمَامٍۢ مُّبِينٍۢ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 36, 441, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ', 'وَٱضْرِبْ لَهُم مَّثَلًا أَصْحَـٰبَ ٱلْقَرْيَةِ إِذْ جَآءَهَا ٱلْمُرْسَلُونَ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 36, 441, 'ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'إِذْ أَرْسَلْنَآ إِلَيْهِمُ ٱثْنَيْنِ فَكَذَّبُوهُمَا فَعَزَّزْنَا بِثَالِثٍۢ فَقَالُوٓا۟ إِنَّآ إِلَيْكُم مُّرْسَلُونَ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 36, 441, 'ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ', 'قَالُوا۟ مَآ أَنتُمْ إِلَّا بَشَرٌۭ مِّثْلُنَا وَمَآ أَنزَلَ ٱلرَّحْمَـٰنُ مِن شَىْءٍ إِنْ أَنتُمْ إِلَّا تَكْذِبُونَ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 36, 441, 'ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ', 'قَالُوا۟ رَبُّنَا يَعْلَمُ إِنَّآ إِلَيْكُمْ لَمُرْسَلُونَ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 36, 441, 'ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ', 'وَمَا عَلَيْنَآ إِلَّا ٱلْبَلَـٰغُ ٱلْمُبِينُ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 36, 441, 'ﱳ ﱴ ﱵ ﱶﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ', 'قَالُوٓا۟ إِنَّا تَطَيَّرْنَا بِكُمْ ۖ لَئِن لَّمْ تَنتَهُوا۟ لَنَرْجُمَنَّكُمْ وَلَيَمَسَّنَّكُم مِّنَّا عَذَابٌ أَلِيمٌۭ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 36, 441, 'ﲁ ﲂ ﲃ ﲄ ﲅﲆ ﲇ ﲈ ﲉ ﲊ ﲋ', 'قَالُوا۟ طَـٰٓئِرُكُم مَّعَكُمْ ۚ أَئِن ذُكِّرْتُم ۚ بَلْ أَنتُمْ قَوْمٌۭ مُّسْرِفُونَ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 36, 441, 'ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ', 'وَجَآءَ مِنْ أَقْصَا ٱلْمَدِينَةِ رَجُلٌۭ يَسْعَىٰ قَالَ يَـٰقَوْمِ ٱتَّبِعُوا۟ ٱلْمُرْسَلِينَ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 36, 441, 'ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ', 'ٱتَّبِعُوا۟ مَن لَّا يَسْـَٔلُكُمْ أَجْرًۭا وَهُم مُّهْتَدُونَ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 36, 441, 'ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ', 'وَمَا لِىَ لَآ أَعْبُدُ ٱلَّذِى فَطَرَنِى وَإِلَيْهِ تُرْجَعُونَ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 36, 441, 'ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ', 'ءَأَتَّخِذُ مِن دُونِهِۦٓ ءَالِهَةً إِن يُرِدْنِ ٱلرَّحْمَـٰنُ بِضُرٍّۢ لَّا تُغْنِ عَنِّى شَفَـٰعَتُهُمْ شَيْـًۭٔا وَلَا يُنقِذُونِ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 36, 441, 'ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ', 'إِنِّىٓ إِذًۭا لَّفِى ضَلَـٰلٍۢ مُّبِينٍ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 36, 441, 'ﲾ ﲿ ﳀ ﳁ ﳂ', 'إِنِّىٓ ءَامَنتُ بِرَبِّكُمْ فَٱسْمَعُونِ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 36, 441, 'ﳃ ﳄ ﳅﳆ ﳇ ﳈ ﳉ ﳊ ﳋ', 'قِيلَ ٱدْخُلِ ٱلْجَنَّةَ ۖ قَالَ يَـٰلَيْتَ قَوْمِى يَعْلَمُونَ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 36, 441, 'ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ', 'بِمَا غَفَرَ لِى رَبِّى وَجَعَلَنِى مِنَ ٱلْمُكْرَمِينَ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 36, 442, 'ﱁﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ', '۞ وَمَآ أَنزَلْنَا عَلَىٰ قَوْمِهِۦ مِنۢ بَعْدِهِۦ مِن جُندٍۢ مِّنَ ٱلسَّمَآءِ وَمَا كُنَّا مُنزِلِينَ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 36, 442, 'ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ', 'إِن كَانَتْ إِلَّا صَيْحَةًۭ وَٰحِدَةًۭ فَإِذَا هُمْ خَـٰمِدُونَ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 36, 442, 'ﱙ ﱚ ﱛﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ', 'يَـٰحَسْرَةً عَلَى ٱلْعِبَادِ ۚ مَا يَأْتِيهِم مِّن رَّسُولٍ إِلَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 36, 442, 'ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ', 'أَلَمْ يَرَوْا۟ كَمْ أَهْلَكْنَا قَبْلَهُم مِّنَ ٱلْقُرُونِ أَنَّهُمْ إِلَيْهِمْ لَا يَرْجِعُونَ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 36, 442, 'ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ', 'وَإِن كُلٌّۭ لَّمَّا جَمِيعٌۭ لَّدَيْنَا مُحْضَرُونَ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 36, 442, 'ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ', 'وَءَايَةٌۭ لَّهُمُ ٱلْأَرْضُ ٱلْمَيْتَةُ أَحْيَيْنَـٰهَا وَأَخْرَجْنَا مِنْهَا حَبًّۭا فَمِنْهُ يَأْكُلُونَ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 36, 442, 'ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ', 'وَجَعَلْنَا فِيهَا جَنَّـٰتٍۢ مِّن نَّخِيلٍۢ وَأَعْنَـٰبٍۢ وَفَجَّرْنَا فِيهَا مِنَ ٱلْعُيُونِ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 36, 442, 'ﲏ ﲐ ﲑ ﲒ ﲓ ﲔﲕ ﲖ ﲗ ﲘ', 'لِيَأْكُلُوا۟ مِن ثَمَرِهِۦ وَمَا عَمِلَتْهُ أَيْدِيهِمْ ۖ أَفَلَا يَشْكُرُونَ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 36, 442, 'ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ', 'سُبْحَـٰنَ ٱلَّذِى خَلَقَ ٱلْأَزْوَٰجَ كُلَّهَا مِمَّا تُنۢبِتُ ٱلْأَرْضُ وَمِنْ أَنفُسِهِمْ وَمِمَّا لَا يَعْلَمُونَ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 36, 442, 'ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ', 'وَءَايَةٌۭ لَّهُمُ ٱلَّيْلُ نَسْلَخُ مِنْهُ ٱلنَّهَارَ فَإِذَا هُم مُّظْلِمُونَ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 36, 442, 'ﲱ ﲲ ﲳ ﲴﲵ ﲶ ﲷ ﲸ ﲹ ﲺ', 'وَٱلشَّمْسُ تَجْرِى لِمُسْتَقَرٍّۢ لَّهَا ۚ ذَٰلِكَ تَقْدِيرُ ٱلْعَزِيزِ ٱلْعَلِيمِ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 36, 442, 'ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ', 'وَٱلْقَمَرَ قَدَّرْنَـٰهُ مَنَازِلَ حَتَّىٰ عَادَ كَٱلْعُرْجُونِ ٱلْقَدِيمِ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 36, 442, 'ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍﳎ ﳏ ﳐ ﳑ ﳒ ﳓ', 'لَا ٱلشَّمْسُ يَنۢبَغِى لَهَآ أَن تُدْرِكَ ٱلْقَمَرَ وَلَا ٱلَّيْلُ سَابِقُ ٱلنَّهَارِ ۚ وَكُلٌّۭ فِى فَلَكٍۢ يَسْبَحُونَ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 36, 443, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ', 'وَءَايَةٌۭ لَّهُمْ أَنَّا حَمَلْنَا ذُرِّيَّتَهُمْ فِى ٱلْفُلْكِ ٱلْمَشْحُونِ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 36, 443, 'ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ', 'وَخَلَقْنَا لَهُم مِّن مِّثْلِهِۦ مَا يَرْكَبُونَ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 36, 443, 'ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ', 'وَإِن نَّشَأْ نُغْرِقْهُمْ فَلَا صَرِيخَ لَهُمْ وَلَا هُمْ يُنقَذُونَ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 36, 443, 'ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ', 'إِلَّا رَحْمَةًۭ مِّنَّا وَمَتَـٰعًا إِلَىٰ حِينٍۢ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 36, 443, 'ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ', 'وَإِذَا قِيلَ لَهُمُ ٱتَّقُوا۟ مَا بَيْنَ أَيْدِيكُمْ وَمَا خَلْفَكُمْ لَعَلَّكُمْ تُرْحَمُونَ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 36, 443, 'ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ', 'وَمَا تَأْتِيهِم مِّنْ ءَايَةٍۢ مِّنْ ءَايَـٰتِ رَبِّهِمْ إِلَّا كَانُوا۟ عَنْهَا مُعْرِضِينَ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 36, 443, 'ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ', 'وَإِذَا قِيلَ لَهُمْ أَنفِقُوا۟ مِمَّا رَزَقَكُمُ ٱللَّهُ قَالَ ٱلَّذِينَ كَفَرُوا۟ لِلَّذِينَ ءَامَنُوٓا۟ أَنُطْعِمُ مَن لَّوْ يَشَآءُ ٱللَّهُ أَطْعَمَهُۥٓ إِنْ أَنتُمْ إِلَّا فِى ضَلَـٰلٍۢ مُّبِينٍۢ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 36, 443, 'ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ', 'وَيَقُولُونَ مَتَىٰ هَـٰذَا ٱلْوَعْدُ إِن كُنتُمْ صَـٰدِقِينَ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 36, 443, 'ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ', 'مَا يَنظُرُونَ إِلَّا صَيْحَةًۭ وَٰحِدَةًۭ تَأْخُذُهُمْ وَهُمْ يَخِصِّمُونَ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 36, 443, 'ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ', 'فَلَا يَسْتَطِيعُونَ تَوْصِيَةًۭ وَلَآ إِلَىٰٓ أَهْلِهِمْ يَرْجِعُونَ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 36, 443, 'ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ', 'وَنُفِخَ فِى ٱلصُّورِ فَإِذَا هُم مِّنَ ٱلْأَجْدَاثِ إِلَىٰ رَبِّهِمْ يَنسِلُونَ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 36, 443, 'ﲷ ﲸ ﲹ ﲺ ﲻ ﲼﲽﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ', 'قَالُوا۟ يَـٰوَيْلَنَا مَنۢ بَعَثَنَا مِن مَّرْقَدِنَا ۜ ۗ هَـٰذَا مَا وَعَدَ ٱلرَّحْمَـٰنُ وَصَدَقَ ٱلْمُرْسَلُونَ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 36, 443, 'ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ', 'إِن كَانَتْ إِلَّا صَيْحَةًۭ وَٰحِدَةًۭ فَإِذَا هُمْ جَمِيعٌۭ لَّدَيْنَا مُحْضَرُونَ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 36, 443, 'ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ', 'فَٱلْيَوْمَ لَا تُظْلَمُ نَفْسٌۭ شَيْـًۭٔا وَلَا تُجْزَوْنَ إِلَّا مَا كُنتُمْ تَعْمَلُونَ ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (55, 36, 444, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ', 'إِنَّ أَصْحَـٰبَ ٱلْجَنَّةِ ٱلْيَوْمَ فِى شُغُلٍۢ فَـٰكِهُونَ ٥٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (56, 36, 444, 'ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ', 'هُمْ وَأَزْوَٰجُهُمْ فِى ظِلَـٰلٍ عَلَى ٱلْأَرَآئِكِ مُتَّكِـُٔونَ ٥٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (57, 36, 444, 'ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ', 'لَهُمْ فِيهَا فَـٰكِهَةٌۭ وَلَهُم مَّا يَدَّعُونَ ٥٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (58, 36, 444, 'ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ', 'سَلَـٰمٌۭ قَوْلًۭا مِّن رَّبٍّۢ رَّحِيمٍۢ ٥٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (59, 36, 444, 'ﱞ ﱟ ﱠ ﱡ ﱢ', 'وَٱمْتَـٰزُوا۟ ٱلْيَوْمَ أَيُّهَا ٱلْمُجْرِمُونَ ٥٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (60, 36, 444, 'ﱣﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬﱭ ﱮ ﱯ ﱰ ﱱ ﱲ', '۞ أَلَمْ أَعْهَدْ إِلَيْكُمْ يَـٰبَنِىٓ ءَادَمَ أَن لَّا تَعْبُدُوا۟ ٱلشَّيْطَـٰنَ ۖ إِنَّهُۥ لَكُمْ عَدُوٌّۭ مُّبِينٌۭ ٦٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (61, 36, 444, 'ﱳ ﱴﱵ ﱶ ﱷ ﱸ ﱹ', 'وَأَنِ ٱعْبُدُونِى ۚ هَـٰذَا صِرَٰطٌۭ مُّسْتَقِيمٌۭ ٦١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (62, 36, 444, 'ﱺ ﱻ ﱼ ﱽ ﱾﱿ ﲀ ﲁ ﲂ ﲃ', 'وَلَقَدْ أَضَلَّ مِنكُمْ جِبِلًّۭا كَثِيرًا ۖ أَفَلَمْ تَكُونُوا۟ تَعْقِلُونَ ٦٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (63, 36, 444, 'ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ', 'هَـٰذِهِۦ جَهَنَّمُ ٱلَّتِى كُنتُمْ تُوعَدُونَ ٦٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (64, 36, 444, 'ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ', 'ٱصْلَوْهَا ٱلْيَوْمَ بِمَا كُنتُمْ تَكْفُرُونَ ٦٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (65, 36, 444, 'ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ', 'ٱلْيَوْمَ نَخْتِمُ عَلَىٰٓ أَفْوَٰهِهِمْ وَتُكَلِّمُنَآ أَيْدِيهِمْ وَتَشْهَدُ أَرْجُلُهُم بِمَا كَانُوا۟ يَكْسِبُونَ ٦٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (66, 36, 444, 'ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ', 'وَلَوْ نَشَآءُ لَطَمَسْنَا عَلَىٰٓ أَعْيُنِهِمْ فَٱسْتَبَقُوا۟ ٱلصِّرَٰطَ فَأَنَّىٰ يُبْصِرُونَ ٦٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (67, 36, 444, 'ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ', 'وَلَوْ نَشَآءُ لَمَسَخْنَـٰهُمْ عَلَىٰ مَكَانَتِهِمْ فَمَا ٱسْتَطَـٰعُوا۟ مُضِيًّۭا وَلَا يَرْجِعُونَ ٦٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (68, 36, 444, 'ﲱ ﲲ ﲳ ﲴ ﲵﲶ ﲷ ﲸ ﲹ', 'وَمَن نُّعَمِّرْهُ نُنَكِّسْهُ فِى ٱلْخَلْقِ ۖ أَفَلَا يَعْقِلُونَ ٦٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (69, 36, 444, 'ﲺ ﲻ ﲼ ﲽ ﲾ ﲿﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ', 'وَمَا عَلَّمْنَـٰهُ ٱلشِّعْرَ وَمَا يَنۢبَغِى لَهُۥٓ ۚ إِنْ هُوَ إِلَّا ذِكْرٌۭ وَقُرْءَانٌۭ مُّبِينٌۭ ٦٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (70, 36, 444, 'ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ', 'لِّيُنذِرَ مَن كَانَ حَيًّۭا وَيَحِقَّ ٱلْقَوْلُ عَلَى ٱلْكَـٰفِرِينَ ٧٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (71, 36, 445, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ', 'أَوَلَمْ يَرَوْا۟ أَنَّا خَلَقْنَا لَهُم مِّمَّا عَمِلَتْ أَيْدِينَآ أَنْعَـٰمًۭا فَهُمْ لَهَا مَـٰلِكُونَ ٧١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (72, 36, 445, 'ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ', 'وَذَلَّلْنَـٰهَا لَهُمْ فَمِنْهَا رَكُوبُهُمْ وَمِنْهَا يَأْكُلُونَ ٧٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (73, 36, 445, 'ﱕ ﱖ ﱗ ﱘﱙ ﱚ ﱛ ﱜ', 'وَلَهُمْ فِيهَا مَنَـٰفِعُ وَمَشَارِبُ ۖ أَفَلَا يَشْكُرُونَ ٧٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (74, 36, 445, 'ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ', 'وَٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ ءَالِهَةًۭ لَّعَلَّهُمْ يُنصَرُونَ ٧٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (75, 36, 445, 'ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ', 'لَا يَسْتَطِيعُونَ نَصْرَهُمْ وَهُمْ لَهُمْ جُندٌۭ مُّحْضَرُونَ ٧٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (76, 36, 445, 'ﱭ ﱮ ﱯﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ', 'فَلَا يَحْزُنكَ قَوْلُهُمْ ۘ إِنَّا نَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ ٧٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (77, 36, 445, 'ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ', 'أَوَلَمْ يَرَ ٱلْإِنسَـٰنُ أَنَّا خَلَقْنَـٰهُ مِن نُّطْفَةٍۢ فَإِذَا هُوَ خَصِيمٌۭ مُّبِينٌۭ ٧٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (78, 36, 445, 'ﲄ ﲅ ﲆ ﲇ ﲈﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ', 'وَضَرَبَ لَنَا مَثَلًۭا وَنَسِىَ خَلْقَهُۥ ۖ قَالَ مَن يُحْىِ ٱلْعِظَـٰمَ وَهِىَ رَمِيمٌۭ ٧٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (79, 36, 445, 'ﲑ ﲒ ﲓ ﲔ ﲕ ﲖﲗ ﲘ ﲙ ﲚ ﲛ ﲜ', 'قُلْ يُحْيِيهَا ٱلَّذِىٓ أَنشَأَهَآ أَوَّلَ مَرَّةٍۢ ۖ وَهُوَ بِكُلِّ خَلْقٍ عَلِيمٌ ٧٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (80, 36, 445, 'ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ', 'ٱلَّذِى جَعَلَ لَكُم مِّنَ ٱلشَّجَرِ ٱلْأَخْضَرِ نَارًۭا فَإِذَآ أَنتُم مِّنْهُ تُوقِدُونَ ٨٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (81, 36, 445, 'ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲﲳ ﲴ ﲵ ﲶ ﲷ ﲸ', 'أَوَلَيْسَ ٱلَّذِى خَلَقَ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ بِقَـٰدِرٍ عَلَىٰٓ أَن يَخْلُقَ مِثْلَهُم ۚ بَلَىٰ وَهُوَ ٱلْخَلَّـٰقُ ٱلْعَلِيمُ ٨١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (82, 36, 445, 'ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ', 'إِنَّمَآ أَمْرُهُۥٓ إِذَآ أَرَادَ شَيْـًٔا أَن يَقُولَ لَهُۥ كُن فَيَكُونُ ٨٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (83, 36, 445, 'ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ', 'فَسُبْحَـٰنَ ٱلَّذِى بِيَدِهِۦ مَلَكُوتُ كُلِّ شَىْءٍۢ وَإِلَيْهِ تُرْجَعُونَ ٨٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 37, 446, 'ﱁ ﱂ ﱃ', 'وَٱلصَّـٰٓفَّـٰتِ صَفًّۭا ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 37, 446, 'ﱄ ﱅ ﱆ', 'فَٱلزَّٰجِرَٰتِ زَجْرًۭا ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 37, 446, 'ﱇ ﱈ ﱉ', 'فَٱلتَّـٰلِيَـٰتِ ذِكْرًا ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 37, 446, 'ﱊ ﱋ ﱌ ﱍ', 'إِنَّ إِلَـٰهَكُمْ لَوَٰحِدٌۭ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 37, 446, 'ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'رَّبُّ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ وَمَا بَيْنَهُمَا وَرَبُّ ٱلْمَشَـٰرِقِ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 37, 446, 'ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ', 'إِنَّا زَيَّنَّا ٱلسَّمَآءَ ٱلدُّنْيَا بِزِينَةٍ ٱلْكَوَاكِبِ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 37, 446, 'ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ', 'وَحِفْظًۭا مِّن كُلِّ شَيْطَـٰنٍۢ مَّارِدٍۢ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 37, 446, 'ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ', 'لَّا يَسَّمَّعُونَ إِلَى ٱلْمَلَإِ ٱلْأَعْلَىٰ وَيُقْذَفُونَ مِن كُلِّ جَانِبٍۢ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 37, 446, 'ﱭﱮ ﱯ ﱰ ﱱ ﱲ', 'دُحُورًۭا ۖ وَلَهُمْ عَذَابٌۭ وَاصِبٌ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 37, 446, 'ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ', 'إِلَّا مَنْ خَطِفَ ٱلْخَطْفَةَ فَأَتْبَعَهُۥ شِهَابٌۭ ثَاقِبٌۭ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 37, 446, 'ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ', 'فَٱسْتَفْتِهِمْ أَهُمْ أَشَدُّ خَلْقًا أَم مَّنْ خَلَقْنَآ ۚ إِنَّا خَلَقْنَـٰهُم مِّن طِينٍۢ لَّازِبٍۭ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 37, 446, 'ﲉ ﲊ ﲋ ﲌ', 'بَلْ عَجِبْتَ وَيَسْخَرُونَ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 37, 446, 'ﲍ ﲎ ﲏ ﲐ ﲑ', 'وَإِذَا ذُكِّرُوا۟ لَا يَذْكُرُونَ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 37, 446, 'ﲒ ﲓ ﲔ ﲕ ﲖ', 'وَإِذَا رَأَوْا۟ ءَايَةًۭ يَسْتَسْخِرُونَ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 37, 446, 'ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ', 'وَقَالُوٓا۟ إِنْ هَـٰذَآ إِلَّا سِحْرٌۭ مُّبِينٌ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 37, 446, 'ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ', 'أَءِذَا مِتْنَا وَكُنَّا تُرَابًۭا وَعِظَـٰمًا أَءِنَّا لَمَبْعُوثُونَ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 37, 446, 'ﲦ ﲧ ﲨ', 'أَوَءَابَآؤُنَا ٱلْأَوَّلُونَ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 37, 446, 'ﲩ ﲪ ﲫ ﲬ ﲭ', 'قُلْ نَعَمْ وَأَنتُمْ دَٰخِرُونَ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 37, 446, 'ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ', 'فَإِنَّمَا هِىَ زَجْرَةٌۭ وَٰحِدَةٌۭ فَإِذَا هُمْ يَنظُرُونَ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 37, 446, 'ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ', 'وَقَالُوا۟ يَـٰوَيْلَنَا هَـٰذَا يَوْمُ ٱلدِّينِ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 37, 446, 'ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ', 'هَـٰذَا يَوْمُ ٱلْفَصْلِ ٱلَّذِى كُنتُم بِهِۦ تُكَذِّبُونَ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 37, 446, 'ﳄﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ', '۞ ٱحْشُرُوا۟ ٱلَّذِينَ ظَلَمُوا۟ وَأَزْوَٰجَهُمْ وَمَا كَانُوا۟ يَعْبُدُونَ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 37, 446, 'ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ', 'مِن دُونِ ٱللَّهِ فَٱهْدُوهُمْ إِلَىٰ صِرَٰطِ ٱلْجَحِيمِ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 37, 446, 'ﳕﳖ ﳗ ﳘ ﳙ', 'وَقِفُوهُمْ ۖ إِنَّهُم مَّسْـُٔولُونَ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 37, 447, 'ﱁ ﱂ ﱃ ﱄ ﱅ', 'مَا لَكُمْ لَا تَنَاصَرُونَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 37, 447, 'ﱆ ﱇ ﱈ ﱉ ﱊ', 'بَلْ هُمُ ٱلْيَوْمَ مُسْتَسْلِمُونَ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 37, 447, 'ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ', 'وَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍۢ يَتَسَآءَلُونَ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 37, 447, 'ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ', 'قَالُوٓا۟ إِنَّكُمْ كُنتُمْ تَأْتُونَنَا عَنِ ٱلْيَمِينِ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 37, 447, 'ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ', 'قَالُوا۟ بَل لَّمْ تَكُونُوا۟ مُؤْمِنِينَ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 37, 447, 'ﱞ ﱟ ﱠ ﱡ ﱢ ﱣﱤ ﱥ ﱦ ﱧ ﱨ ﱩ', 'وَمَا كَانَ لَنَا عَلَيْكُم مِّن سُلْطَـٰنٍۭ ۖ بَلْ كُنتُمْ قَوْمًۭا طَـٰغِينَ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 37, 447, 'ﱪ ﱫ ﱬ ﱭﱮ ﱯ ﱰ ﱱ', 'فَحَقَّ عَلَيْنَا قَوْلُ رَبِّنَآ ۖ إِنَّا لَذَآئِقُونَ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 37, 447, 'ﱲ ﱳ ﱴ ﱵ ﱶ', 'فَأَغْوَيْنَـٰكُمْ إِنَّا كُنَّا غَـٰوِينَ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 37, 447, 'ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ', 'فَإِنَّهُمْ يَوْمَئِذٍۢ فِى ٱلْعَذَابِ مُشْتَرِكُونَ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 37, 447, 'ﱽ ﱾ ﱿ ﲀ ﲁ', 'إِنَّا كَذَٰلِكَ نَفْعَلُ بِٱلْمُجْرِمِينَ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 37, 447, 'ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ', 'إِنَّهُمْ كَانُوٓا۟ إِذَا قِيلَ لَهُمْ لَآ إِلَـٰهَ إِلَّا ٱللَّهُ يَسْتَكْبِرُونَ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 37, 447, 'ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ', 'وَيَقُولُونَ أَئِنَّا لَتَارِكُوٓا۟ ءَالِهَتِنَا لِشَاعِرٍۢ مَّجْنُونٍۭ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 37, 447, 'ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ', 'بَلْ جَآءَ بِٱلْحَقِّ وَصَدَّقَ ٱلْمُرْسَلِينَ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 37, 447, 'ﲚ ﲛ ﲜ ﲝ ﲞ', 'إِنَّكُمْ لَذَآئِقُوا۟ ٱلْعَذَابِ ٱلْأَلِيمِ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 37, 447, 'ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ', 'وَمَا تُجْزَوْنَ إِلَّا مَا كُنتُمْ تَعْمَلُونَ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 37, 447, 'ﲦ ﲧ ﲨ ﲩ ﲪ', 'إِلَّا عِبَادَ ٱللَّهِ ٱلْمُخْلَصِينَ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 37, 447, 'ﲫ ﲬ ﲭ ﲮ ﲯ', 'أُو۟لَـٰٓئِكَ لَهُمْ رِزْقٌۭ مَّعْلُومٌۭ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 37, 447, 'ﲰ ﲱ ﲲ ﲳ', 'فَوَٰكِهُ ۖ وَهُم مُّكْرَمُونَ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 37, 447, 'ﲴ ﲵ ﲶ ﲷ', 'فِى جَنَّـٰتِ ٱلنَّعِيمِ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 37, 447, 'ﲸ ﲹ ﲺ ﲻ', 'عَلَىٰ سُرُرٍۢ مُّتَقَـٰبِلِينَ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 37, 447, 'ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ', 'يُطَافُ عَلَيْهِم بِكَأْسٍۢ مِّن مَّعِينٍۭ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 37, 447, 'ﳂ ﳃ ﳄ ﳅ', 'بَيْضَآءَ لَذَّةٍۢ لِّلشَّـٰرِبِينَ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 37, 447, 'ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ', 'لَا فِيهَا غَوْلٌۭ وَلَا هُمْ عَنْهَا يُنزَفُونَ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 37, 447, 'ﳎ ﳏ ﳐ ﳑ ﳒ', 'وَعِندَهُمْ قَـٰصِرَٰتُ ٱلطَّرْفِ عِينٌۭ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 37, 447, 'ﳓ ﳔ ﳕ ﳖ', 'كَأَنَّهُنَّ بَيْضٌۭ مَّكْنُونٌۭ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 37, 447, 'ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ', 'فَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍۢ يَتَسَآءَلُونَ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 37, 447, 'ﳝ ﳞ ﳟ ﳠ ﳡ ﳢ ﳣ ﳤ', 'قَالَ قَآئِلٌۭ مِّنْهُمْ إِنِّى كَانَ لِى قَرِينٌۭ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 37, 448, 'ﱁ ﱂ ﱃ ﱄ ﱅ', 'يَقُولُ أَءِنَّكَ لَمِنَ ٱلْمُصَدِّقِينَ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 37, 448, 'ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ', 'أَءِذَا مِتْنَا وَكُنَّا تُرَابًۭا وَعِظَـٰمًا أَءِنَّا لَمَدِينُونَ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 37, 448, 'ﱎ ﱏ ﱐ ﱑ ﱒ', 'قَالَ هَلْ أَنتُم مُّطَّلِعُونَ ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (55, 37, 448, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ', 'فَٱطَّلَعَ فَرَءَاهُ فِى سَوَآءِ ٱلْجَحِيمِ ٥٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (56, 37, 448, 'ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ', 'قَالَ تَٱللَّهِ إِن كِدتَّ لَتُرْدِينِ ٥٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (57, 37, 448, 'ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ', 'وَلَوْلَا نِعْمَةُ رَبِّى لَكُنتُ مِنَ ٱلْمُحْضَرِينَ ٥٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (58, 37, 448, 'ﱦ ﱧ ﱨ ﱩ', 'أَفَمَا نَحْنُ بِمَيِّتِينَ ٥٨');
