INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (103, 37, 450, 'ﱁ ﱂ ﱃ ﱄ ﱅ', 'فَلَمَّآ أَسْلَمَا وَتَلَّهُۥ لِلْجَبِينِ ١٠٣');
