INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (131, 37, 451, 'ﱔ ﱕ ﱖ ﱗ ﱘ', 'إِنَّا كَذَٰلِكَ نَجْزِى ٱلْمُحْسِنِينَ ١٣١');
