INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (133, 37, 451, 'ﱞ ﱟ ﱠ ﱡ ﱢ', 'وَإِنَّ لُوطًۭا لَّمِنَ ٱلْمُرْسَلِينَ ١٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (134, 37, 451, 'ﱣ ﱤ ﱥ ﱦ ﱧ', 'إِذْ نَجَّيْنَـٰهُ وَأَهْلَهُۥٓ أَجْمَعِينَ ١٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (135, 37, 451, 'ﱨ ﱩ ﱪ ﱫ ﱬ', 'إِلَّا عَجُوزًۭا فِى ٱلْغَـٰبِرِينَ ١٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (136, 37, 451, 'ﱭ ﱮ ﱯ ﱰ', 'ثُمَّ دَمَّرْنَا ٱلْـَٔاخَرِينَ ١٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (137, 37, 451, 'ﱱ ﱲ ﱳ ﱴ ﱵ', 'وَإِنَّكُمْ لَتَمُرُّونَ عَلَيْهِم مُّصْبِحِينَ ١٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (138, 37, 451, 'ﱶﱷ ﱸ ﱹ ﱺ', 'وَبِٱلَّيْلِ ۗ أَفَلَا تَعْقِلُونَ ١٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (139, 37, 451, 'ﱻ ﱼ ﱽ ﱾ ﱿ', 'وَإِنَّ يُونُسَ لَمِنَ ٱلْمُرْسَلِينَ ١٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (140, 37, 451, 'ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ', 'إِذْ أَبَقَ إِلَى ٱلْفُلْكِ ٱلْمَشْحُونِ ١٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (141, 37, 451, 'ﲆ ﲇ ﲈ ﲉ ﲊ', 'فَسَاهَمَ فَكَانَ مِنَ ٱلْمُدْحَضِينَ ١٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (142, 37, 451, 'ﲋ ﲌ ﲍ ﲎ ﲏ', 'فَٱلْتَقَمَهُ ٱلْحُوتُ وَهُوَ مُلِيمٌۭ ١٤٢');
