INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (144, 37, 451, 'ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ', 'لَلَبِثَ فِى بَطْنِهِۦٓ إِلَىٰ يَوْمِ يُبْعَثُونَ ١٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (145, 37, 451, 'ﲝﲞ ﲟ ﲠ ﲡ ﲢ', '۞ فَنَبَذْنَـٰهُ بِٱلْعَرَآءِ وَهُوَ سَقِيمٌۭ ١٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (146, 37, 451, 'ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ', 'وَأَنۢبَتْنَا عَلَيْهِ شَجَرَةًۭ مِّن يَقْطِينٍۢ ١٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (147, 37, 451, 'ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ', 'وَأَرْسَلْنَـٰهُ إِلَىٰ مِا۟ئَةِ أَلْفٍ أَوْ يَزِيدُونَ ١٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (148, 37, 451, 'ﲰ ﲱ ﲲ ﲳ ﲴ', 'فَـَٔامَنُوا۟ فَمَتَّعْنَـٰهُمْ إِلَىٰ حِينٍۢ ١٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (149, 37, 451, 'ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ', 'فَٱسْتَفْتِهِمْ أَلِرَبِّكَ ٱلْبَنَاتُ وَلَهُمُ ٱلْبَنُونَ ١٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (150, 37, 451, 'ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ', 'أَمْ خَلَقْنَا ٱلْمَلَـٰٓئِكَةَ إِنَـٰثًۭا وَهُمْ شَـٰهِدُونَ ١٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (151, 37, 451, 'ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ', 'أَلَآ إِنَّهُم مِّنْ إِفْكِهِمْ لَيَقُولُونَ ١٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (152, 37, 451, 'ﳈ ﳉ ﳊ ﳋ ﳌ', 'وَلَدَ ٱللَّهُ وَإِنَّهُمْ لَكَـٰذِبُونَ ١٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (153, 37, 451, 'ﳍ ﳎ ﳏ ﳐ ﳑ', 'أَصْطَفَى ٱلْبَنَاتِ عَلَى ٱلْبَنِينَ ١٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (154, 37, 452, 'ﱁ ﱂ ﱃ ﱄ ﱅ', 'مَا لَكُمْ كَيْفَ تَحْكُمُونَ ١٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (155, 37, 452, 'ﱆ ﱇ ﱈ', 'أَفَلَا تَذَكَّرُونَ ١٥٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (156, 37, 452, 'ﱉ ﱊ ﱋ ﱌ ﱍ', 'أَمْ لَكُمْ سُلْطَـٰنٌۭ مُّبِينٌۭ ١٥٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (157, 37, 452, 'ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ', 'فَأْتُوا۟ بِكِتَـٰبِكُمْ إِن كُنتُمْ صَـٰدِقِينَ ١٥٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (158, 37, 452, 'ﱔ ﱕ ﱖ ﱗ ﱘﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ', 'وَجَعَلُوا۟ بَيْنَهُۥ وَبَيْنَ ٱلْجِنَّةِ نَسَبًۭا ۚ وَلَقَدْ عَلِمَتِ ٱلْجِنَّةُ إِنَّهُمْ لَمُحْضَرُونَ ١٥٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (159, 37, 452, 'ﱠ ﱡ ﱢ ﱣ ﱤ', 'سُبْحَـٰنَ ٱللَّهِ عَمَّا يَصِفُونَ ١٥٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (160, 37, 452, 'ﱥ ﱦ ﱧ ﱨ ﱩ', 'إِلَّا عِبَادَ ٱللَّهِ ٱلْمُخْلَصِينَ ١٦٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (161, 37, 452, 'ﱪ ﱫ ﱬ ﱭ', 'فَإِنَّكُمْ وَمَا تَعْبُدُونَ ١٦١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (162, 37, 452, 'ﱮ ﱯ ﱰ ﱱ ﱲ', 'مَآ أَنتُمْ عَلَيْهِ بِفَـٰتِنِينَ ١٦٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (163, 37, 452, 'ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ', 'إِلَّا مَنْ هُوَ صَالِ ٱلْجَحِيمِ ١٦٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (164, 37, 452, 'ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ', 'وَمَا مِنَّآ إِلَّا لَهُۥ مَقَامٌۭ مَّعْلُومٌۭ ١٦٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (165, 37, 452, 'ﲀ ﲁ ﲂ ﲃ', 'وَإِنَّا لَنَحْنُ ٱلصَّآفُّونَ ١٦٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (166, 37, 452, 'ﲄ ﲅ ﲆ ﲇ', 'وَإِنَّا لَنَحْنُ ٱلْمُسَبِّحُونَ ١٦٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (167, 37, 452, 'ﲈ ﲉ ﲊ ﲋ', 'وَإِن كَانُوا۟ لَيَقُولُونَ ١٦٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (168, 37, 452, 'ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ', 'لَوْ أَنَّ عِندَنَا ذِكْرًۭا مِّنَ ٱلْأَوَّلِينَ ١٦٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (169, 37, 452, 'ﲓ ﲔ ﲕ ﲖ ﲗ', 'لَكُنَّا عِبَادَ ٱللَّهِ ٱلْمُخْلَصِينَ ١٦٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (170, 37, 452, 'ﲘ ﲙﲚ ﲛ ﲜ ﲝ', 'فَكَفَرُوا۟ بِهِۦ ۖ فَسَوْفَ يَعْلَمُونَ ١٧٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (171, 37, 452, 'ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ', 'وَلَقَدْ سَبَقَتْ كَلِمَتُنَا لِعِبَادِنَا ٱلْمُرْسَلِينَ ١٧١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (172, 37, 452, 'ﲤ ﲥ ﲦ ﲧ', 'إِنَّهُمْ لَهُمُ ٱلْمَنصُورُونَ ١٧٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (173, 37, 452, 'ﲨ ﲩ ﲪ ﲫ ﲬ', 'وَإِنَّ جُندَنَا لَهُمُ ٱلْغَـٰلِبُونَ ١٧٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (174, 37, 452, 'ﲭ ﲮ ﲯ ﲰ ﲱ', 'فَتَوَلَّ عَنْهُمْ حَتَّىٰ حِينٍۢ ١٧٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (175, 37, 452, 'ﲲ ﲳ ﲴ ﲵ', 'وَأَبْصِرْهُمْ فَسَوْفَ يُبْصِرُونَ ١٧٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (176, 37, 452, 'ﲶ ﲷ ﲸ', 'أَفَبِعَذَابِنَا يَسْتَعْجِلُونَ ١٧٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (177, 37, 452, 'ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ', 'فَإِذَا نَزَلَ بِسَاحَتِهِمْ فَسَآءَ صَبَاحُ ٱلْمُنذَرِينَ ١٧٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (178, 37, 452, 'ﳀ ﳁ ﳂ ﳃ ﳄ', 'وَتَوَلَّ عَنْهُمْ حَتَّىٰ حِينٍۢ ١٧٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (179, 37, 452, 'ﳅ ﳆ ﳇ ﳈ', 'وَأَبْصِرْ فَسَوْفَ يُبْصِرُونَ ١٧٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (180, 37, 452, 'ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ', 'سُبْحَـٰنَ رَبِّكَ رَبِّ ٱلْعِزَّةِ عَمَّا يَصِفُونَ ١٨٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (181, 37, 452, 'ﳐ ﳑ ﳒ ﳓ', 'وَسَلَـٰمٌ عَلَى ٱلْمُرْسَلِينَ ١٨١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (182, 37, 452, 'ﳔ ﳕ ﳖ ﳗ ﳘ', 'وَٱلْحَمْدُ لِلَّهِ رَبِّ ٱلْعَـٰلَمِينَ ١٨٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 38, 453, 'ﱁﱂ ﱃ ﱄ ﱅ ﱆ', 'صٓ ۚ وَٱلْقُرْءَانِ ذِى ٱلذِّكْرِ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 38, 453, 'ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ', 'بَلِ ٱلَّذِينَ كَفَرُوا۟ فِى عِزَّةٍۢ وَشِقَاقٍۢ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 38, 453, 'ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ', 'كَمْ أَهْلَكْنَا مِن قَبْلِهِم مِّن قَرْنٍۢ فَنَادَوا۟ وَّلَاتَ حِينَ مَنَاصٍۢ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 38, 453, 'ﱙ ﱚ ﱛ ﱜ ﱝﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ', 'وَعَجِبُوٓا۟ أَن جَآءَهُم مُّنذِرٌۭ مِّنْهُمْ ۖ وَقَالَ ٱلْكَـٰفِرُونَ هَـٰذَا سَـٰحِرٌۭ كَذَّابٌ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 38, 453, 'ﱥ ﱦ ﱧ ﱨﱩ ﱪ ﱫ ﱬ ﱭ ﱮ', 'أَجَعَلَ ٱلْـَٔالِهَةَ إِلَـٰهًۭا وَٰحِدًا ۖ إِنَّ هَـٰذَا لَشَىْءٌ عُجَابٌۭ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 38, 453, 'ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶﱷ ﱸ ﱹ ﱺ ﱻ ﱼ', 'وَٱنطَلَقَ ٱلْمَلَأُ مِنْهُمْ أَنِ ٱمْشُوا۟ وَٱصْبِرُوا۟ عَلَىٰٓ ءَالِهَتِكُمْ ۖ إِنَّ هَـٰذَا لَشَىْءٌۭ يُرَادُ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 38, 453, 'ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ', 'مَا سَمِعْنَا بِهَـٰذَا فِى ٱلْمِلَّةِ ٱلْـَٔاخِرَةِ إِنْ هَـٰذَآ إِلَّا ٱخْتِلَـٰقٌ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 38, 453, 'ﲈ ﲉ ﲊ ﲋ ﲌﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓﲔ ﲕ ﲖ ﲗ ﲘ ﲙ', 'أَءُنزِلَ عَلَيْهِ ٱلذِّكْرُ مِنۢ بَيْنِنَا ۚ بَلْ هُمْ فِى شَكٍّۢ مِّن ذِكْرِى ۖ بَل لَّمَّا يَذُوقُوا۟ عَذَابِ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 38, 453, 'ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ', 'أَمْ عِندَهُمْ خَزَآئِنُ رَحْمَةِ رَبِّكَ ٱلْعَزِيزِ ٱلْوَهَّابِ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 38, 453, 'ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨﲩ ﲪ ﲫ ﲬ ﲭ', 'أَمْ لَهُم مُّلْكُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ وَمَا بَيْنَهُمَا ۖ فَلْيَرْتَقُوا۟ فِى ٱلْأَسْبَـٰبِ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 38, 453, 'ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ', 'جُندٌۭ مَّا هُنَالِكَ مَهْزُومٌۭ مِّنَ ٱلْأَحْزَابِ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 38, 453, 'ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ', 'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍۢ وَعَادٌۭ وَفِرْعَوْنُ ذُو ٱلْأَوْتَادِ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 38, 453, 'ﲾ ﲿ ﳀ ﳁ ﳂﳃ ﳄ ﳅ ﳆ', 'وَثَمُودُ وَقَوْمُ لُوطٍۢ وَأَصْحَـٰبُ لْـَٔيْكَةِ ۚ أُو۟لَـٰٓئِكَ ٱلْأَحْزَابُ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 38, 453, 'ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ', 'إِن كُلٌّ إِلَّا كَذَّبَ ٱلرُّسُلَ فَحَقَّ عِقَابِ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 38, 453, 'ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ', 'وَمَا يَنظُرُ هَـٰٓؤُلَآءِ إِلَّا صَيْحَةًۭ وَٰحِدَةًۭ مَّا لَهَا مِن فَوَاقٍۢ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 38, 453, 'ﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ ﳡ ﳢ', 'وَقَالُوا۟ رَبَّنَا عَجِّل لَّنَا قِطَّنَا قَبْلَ يَوْمِ ٱلْحِسَابِ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 38, 454, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉﱊ ﱋ ﱌ ﱍ', 'ٱصْبِرْ عَلَىٰ مَا يَقُولُونَ وَٱذْكُرْ عَبْدَنَا دَاوُۥدَ ذَا ٱلْأَيْدِ ۖ إِنَّهُۥٓ أَوَّابٌ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 38, 454, 'ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'إِنَّا سَخَّرْنَا ٱلْجِبَالَ مَعَهُۥ يُسَبِّحْنَ بِٱلْعَشِىِّ وَٱلْإِشْرَاقِ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 38, 454, 'ﱖ ﱗﱘ ﱙ ﱚ ﱛ ﱜ', 'وَٱلطَّيْرَ مَحْشُورَةًۭ ۖ كُلٌّۭ لَّهُۥٓ أَوَّابٌۭ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 38, 454, 'ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ', 'وَشَدَدْنَا مُلْكَهُۥ وَءَاتَيْنَـٰهُ ٱلْحِكْمَةَ وَفَصْلَ ٱلْخِطَابِ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 38, 454, 'ﱤﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ', '۞ وَهَلْ أَتَىٰكَ نَبَؤُا۟ ٱلْخَصْمِ إِذْ تَسَوَّرُوا۟ ٱلْمِحْرَابَ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 38, 454, 'ﱭ ﱮ ﱯ ﱰ ﱱ ﱲﱳ ﱴ ﱵ ﱶﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ', 'إِذْ دَخَلُوا۟ عَلَىٰ دَاوُۥدَ فَفَزِعَ مِنْهُمْ ۖ قَالُوا۟ لَا تَخَفْ ۖ خَصْمَانِ بَغَىٰ بَعْضُنَا عَلَىٰ بَعْضٍۢ فَٱحْكُم بَيْنَنَا بِٱلْحَقِّ وَلَا تُشْطِطْ وَٱهْدِنَآ إِلَىٰ سَوَآءِ ٱلصِّرَٰطِ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 38, 454, 'ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ', 'إِنَّ هَـٰذَآ أَخِى لَهُۥ تِسْعٌۭ وَتِسْعُونَ نَعْجَةًۭ وَلِىَ نَعْجَةٌۭ وَٰحِدَةٌۭ فَقَالَ أَكْفِلْنِيهَا وَعَزَّنِى فِى ٱلْخِطَابِ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 38, 454, 'ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶﲷ ﲸﲹ ﲺﲻ ﲼ', 'قَالَ لَقَدْ ظَلَمَكَ بِسُؤَالِ نَعْجَتِكَ إِلَىٰ نِعَاجِهِۦ ۖ وَإِنَّ كَثِيرًۭا مِّنَ ٱلْخُلَطَآءِ لَيَبْغِى بَعْضُهُمْ عَلَىٰ بَعْضٍ إِلَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ وَقَلِيلٌۭ مَّا هُمْ ۗ وَظَنَّ دَاوُۥدُ أَنَّمَا فَتَنَّـٰهُ فَٱسْتَغْفَرَ رَبَّهُۥ وَخَرَّ رَاكِعًۭا وَأَنَابَ ۩ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 38, 454, 'ﲽ ﲾ ﲿﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ', 'فَغَفَرْنَا لَهُۥ ذَٰلِكَ ۖ وَإِنَّ لَهُۥ عِندَنَا لَزُلْفَىٰ وَحُسْنَ مَـَٔابٍۢ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 38, 454, 'ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘﳙ ﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ ﳡ ﳢ ﳣ ﳤ ﳥ ﳦ ﳧ', 'يَـٰدَاوُۥدُ إِنَّا جَعَلْنَـٰكَ خَلِيفَةًۭ فِى ٱلْأَرْضِ فَٱحْكُم بَيْنَ ٱلنَّاسِ بِٱلْحَقِّ وَلَا تَتَّبِعِ ٱلْهَوَىٰ فَيُضِلَّكَ عَن سَبِيلِ ٱللَّهِ ۚ إِنَّ ٱلَّذِينَ يَضِلُّونَ عَن سَبِيلِ ٱللَّهِ لَهُمْ عَذَابٌۭ شَدِيدٌۢ بِمَا نَسُوا۟ يَوْمَ ٱلْحِسَابِ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 38, 455, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇﱈ ﱉ ﱊ ﱋ ﱌﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ', 'وَمَا خَلَقْنَا ٱلسَّمَآءَ وَٱلْأَرْضَ وَمَا بَيْنَهُمَا بَـٰطِلًۭا ۚ ذَٰلِكَ ظَنُّ ٱلَّذِينَ كَفَرُوا۟ ۚ فَوَيْلٌۭ لِّلَّذِينَ كَفَرُوا۟ مِنَ ٱلنَّارِ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 38, 455, 'ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ', 'أَمْ نَجْعَلُ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ كَٱلْمُفْسِدِينَ فِى ٱلْأَرْضِ أَمْ نَجْعَلُ ٱلْمُتَّقِينَ كَٱلْفُجَّارِ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 38, 455, 'ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ', 'كِتَـٰبٌ أَنزَلْنَـٰهُ إِلَيْكَ مُبَـٰرَكٌۭ لِّيَدَّبَّرُوٓا۟ ءَايَـٰتِهِۦ وَلِيَتَذَكَّرَ أُو۟لُوا۟ ٱلْأَلْبَـٰبِ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 38, 455, 'ﱬ ﱭ ﱮﱯ ﱰ ﱱ ﱲ ﱳ ﱴ', 'وَوَهَبْنَا لِدَاوُۥدَ سُلَيْمَـٰنَ ۚ نِعْمَ ٱلْعَبْدُ ۖ إِنَّهُۥٓ أَوَّابٌ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 38, 455, 'ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ', 'إِذْ عُرِضَ عَلَيْهِ بِٱلْعَشِىِّ ٱلصَّـٰفِنَـٰتُ ٱلْجِيَادُ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 38, 455, 'ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ', 'فَقَالَ إِنِّىٓ أَحْبَبْتُ حُبَّ ٱلْخَيْرِ عَن ذِكْرِ رَبِّى حَتَّىٰ تَوَارَتْ بِٱلْحِجَابِ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 38, 455, 'ﲈ ﲉﲊ ﲋ ﲌ ﲍ ﲎ ﲏ', 'رُدُّوهَا عَلَىَّ ۖ فَطَفِقَ مَسْحًۢا بِٱلسُّوقِ وَٱلْأَعْنَاقِ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 38, 455, 'ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ', 'وَلَقَدْ فَتَنَّا سُلَيْمَـٰنَ وَأَلْقَيْنَا عَلَىٰ كُرْسِيِّهِۦ جَسَدًۭا ثُمَّ أَنَابَ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 38, 455, 'ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥﲦ ﲧ ﲨ ﲩ ﲪ', 'قَالَ رَبِّ ٱغْفِرْ لِى وَهَبْ لِى مُلْكًۭا لَّا يَنۢبَغِى لِأَحَدٍۢ مِّنۢ بَعْدِىٓ ۖ إِنَّكَ أَنتَ ٱلْوَهَّابُ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 38, 455, 'ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ', 'فَسَخَّرْنَا لَهُ ٱلرِّيحَ تَجْرِى بِأَمْرِهِۦ رُخَآءً حَيْثُ أَصَابَ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 38, 455, 'ﲴ ﲵ ﲶ ﲷ ﲸ', 'وَٱلشَّيَـٰطِينَ كُلَّ بَنَّآءٍۢ وَغَوَّاصٍۢ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 38, 455, 'ﲹ ﲺ ﲻ ﲼ ﲽ', 'وَءَاخَرِينَ مُقَرَّنِينَ فِى ٱلْأَصْفَادِ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 38, 455, 'ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ', 'هَـٰذَا عَطَآؤُنَا فَٱمْنُنْ أَوْ أَمْسِكْ بِغَيْرِ حِسَابٍۢ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 38, 455, 'ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ', 'وَإِنَّ لَهُۥ عِندَنَا لَزُلْفَىٰ وَحُسْنَ مَـَٔابٍۢ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 38, 455, 'ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ', 'وَٱذْكُرْ عَبْدَنَآ أَيُّوبَ إِذْ نَادَىٰ رَبَّهُۥٓ أَنِّى مَسَّنِىَ ٱلشَّيْطَـٰنُ بِنُصْبٍۢ وَعَذَابٍ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 38, 455, 'ﳙ ﳚﳛ ﳜ ﳝ ﳞ ﳟ ﳠ', 'ٱرْكُضْ بِرِجْلِكَ ۖ هَـٰذَا مُغْتَسَلٌۢ بَارِدٌۭ وَشَرَابٌۭ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 38, 456, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ', 'وَوَهَبْنَا لَهُۥٓ أَهْلَهُۥ وَمِثْلَهُم مَّعَهُمْ رَحْمَةًۭ مِّنَّا وَذِكْرَىٰ لِأُو۟لِى ٱلْأَلْبَـٰبِ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 38, 456, 'ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒﱓ ﱔ ﱕ ﱖﱗ ﱘ ﱙ ﱚ ﱛ ﱜ', 'وَخُذْ بِيَدِكَ ضِغْثًۭا فَٱضْرِب بِّهِۦ وَلَا تَحْنَثْ ۗ إِنَّا وَجَدْنَـٰهُ صَابِرًۭا ۚ نِّعْمَ ٱلْعَبْدُ ۖ إِنَّهُۥٓ أَوَّابٌۭ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 38, 456, 'ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ', 'وَٱذْكُرْ عِبَـٰدَنَآ إِبْرَٰهِيمَ وَإِسْحَـٰقَ وَيَعْقُوبَ أُو۟لِى ٱلْأَيْدِى وَٱلْأَبْصَـٰرِ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 38, 456, 'ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ', 'إِنَّآ أَخْلَصْنَـٰهُم بِخَالِصَةٍۢ ذِكْرَى ٱلدَّارِ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 38, 456, 'ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ', 'وَإِنَّهُمْ عِندَنَا لَمِنَ ٱلْمُصْطَفَيْنَ ٱلْأَخْيَارِ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 38, 456, 'ﱲ ﱳ ﱴ ﱵ ﱶﱷ ﱸ ﱹ ﱺ ﱻ', 'وَٱذْكُرْ إِسْمَـٰعِيلَ وَٱلْيَسَعَ وَذَا ٱلْكِفْلِ ۖ وَكُلٌّۭ مِّنَ ٱلْأَخْيَارِ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 38, 456, 'ﱼ ﱽﱾ ﱿ ﲀ ﲁ ﲂ ﲃ', 'هَـٰذَا ذِكْرٌۭ ۚ وَإِنَّ لِلْمُتَّقِينَ لَحُسْنَ مَـَٔابٍۢ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 38, 456, 'ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ', 'جَنَّـٰتِ عَدْنٍۢ مُّفَتَّحَةًۭ لَّهُمُ ٱلْأَبْوَٰبُ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 38, 456, 'ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ', 'مُتَّكِـِٔينَ فِيهَا يَدْعُونَ فِيهَا بِفَـٰكِهَةٍۢ كَثِيرَةٍۢ وَشَرَابٍۢ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 38, 456, 'ﲒﲓ ﲔ ﲕ ﲖ ﲗ', '۞ وَعِندَهُمْ قَـٰصِرَٰتُ ٱلطَّرْفِ أَتْرَابٌ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 38, 456, 'ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ', 'هَـٰذَا مَا تُوعَدُونَ لِيَوْمِ ٱلْحِسَابِ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 38, 456, 'ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ', 'إِنَّ هَـٰذَا لَرِزْقُنَا مَا لَهُۥ مِن نَّفَادٍ ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (55, 38, 456, 'ﲦﲧ ﲨ ﲩ ﲪ ﲫ ﲬ', 'هَـٰذَا ۚ وَإِنَّ لِلطَّـٰغِينَ لَشَرَّ مَـَٔابٍۢ ٥٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (56, 38, 456, 'ﲭ ﲮ ﲯ ﲰ ﲱ', 'جَهَنَّمَ يَصْلَوْنَهَا فَبِئْسَ ٱلْمِهَادُ ٥٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (57, 38, 456, 'ﲲ ﲳ ﲴ ﲵ ﲶ', 'هَـٰذَا فَلْيَذُوقُوهُ حَمِيمٌۭ وَغَسَّاقٌۭ ٥٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (58, 38, 456, 'ﲷ ﲸ ﲹ ﲺ ﲻ', 'وَءَاخَرُ مِن شَكْلِهِۦٓ أَزْوَٰجٌ ٥٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (59, 38, 456, 'ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂﳃ ﳄ ﳅ ﳆ ﳇ', 'هَـٰذَا فَوْجٌۭ مُّقْتَحِمٌۭ مَّعَكُمْ ۖ لَا مَرْحَبًۢا بِهِمْ ۚ إِنَّهُمْ صَالُوا۟ ٱلنَّارِ ٥٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (60, 38, 456, 'ﳈ ﳉ ﳊ ﳋ ﳌ ﳍﳎ ﳏ ﳐ ﳑﳒ ﳓ ﳔ ﳕ', 'قَالُوا۟ بَلْ أَنتُمْ لَا مَرْحَبًۢا بِكُمْ ۖ أَنتُمْ قَدَّمْتُمُوهُ لَنَا ۖ فَبِئْسَ ٱلْقَرَارُ ٦٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (61, 38, 456, 'ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ ﳡ', 'قَالُوا۟ رَبَّنَا مَن قَدَّمَ لَنَا هَـٰذَا فَزِدْهُ عَذَابًۭا ضِعْفًۭا فِى ٱلنَّارِ ٦١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (62, 38, 457, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ', 'وَقَالُوا۟ مَا لَنَا لَا نَرَىٰ رِجَالًۭا كُنَّا نَعُدُّهُم مِّنَ ٱلْأَشْرَارِ ٦٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (63, 38, 457, 'ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ', 'أَتَّخَذْنَـٰهُمْ سِخْرِيًّا أَمْ زَاغَتْ عَنْهُمُ ٱلْأَبْصَـٰرُ ٦٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (64, 38, 457, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ', 'إِنَّ ذَٰلِكَ لَحَقٌّۭ تَخَاصُمُ أَهْلِ ٱلنَّارِ ٦٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (65, 38, 457, 'ﱚ ﱛ ﱜ ﱝﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ', 'قُلْ إِنَّمَآ أَنَا۠ مُنذِرٌۭ ۖ وَمَا مِنْ إِلَـٰهٍ إِلَّا ٱللَّهُ ٱلْوَٰحِدُ ٱلْقَهَّارُ ٦٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (66, 38, 457, 'ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ', 'رَبُّ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ وَمَا بَيْنَهُمَا ٱلْعَزِيزُ ٱلْغَفَّـٰرُ ٦٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (67, 38, 457, 'ﱯ ﱰ ﱱ ﱲ ﱳ', 'قُلْ هُوَ نَبَؤٌا۟ عَظِيمٌ ٦٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (68, 38, 457, 'ﱴ ﱵ ﱶ ﱷ', 'أَنتُمْ عَنْهُ مُعْرِضُونَ ٦٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (69, 38, 457, 'ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ', 'مَا كَانَ لِىَ مِنْ عِلْمٍۭ بِٱلْمَلَإِ ٱلْأَعْلَىٰٓ إِذْ يَخْتَصِمُونَ ٦٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (70, 38, 457, 'ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ', 'إِن يُوحَىٰٓ إِلَىَّ إِلَّآ أَنَّمَآ أَنَا۠ نَذِيرٌۭ مُّبِينٌ ٧٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (71, 38, 457, 'ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ', 'إِذْ قَالَ رَبُّكَ لِلْمَلَـٰٓئِكَةِ إِنِّى خَـٰلِقٌۢ بَشَرًۭا مِّن طِينٍۢ ٧١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (72, 38, 457, 'ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ', 'فَإِذَا سَوَّيْتُهُۥ وَنَفَخْتُ فِيهِ مِن رُّوحِى فَقَعُوا۟ لَهُۥ سَـٰجِدِينَ ٧٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (73, 38, 457, 'ﲟ ﲠ ﲡ ﲢ ﲣ', 'فَسَجَدَ ٱلْمَلَـٰٓئِكَةُ كُلُّهُمْ أَجْمَعُونَ ٧٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (74, 38, 457, 'ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ', 'إِلَّآ إِبْلِيسَ ٱسْتَكْبَرَ وَكَانَ مِنَ ٱلْكَـٰفِرِينَ ٧٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (75, 38, 457, 'ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ', 'قَالَ يَـٰٓإِبْلِيسُ مَا مَنَعَكَ أَن تَسْجُدَ لِمَا خَلَقْتُ بِيَدَىَّ ۖ أَسْتَكْبَرْتَ أَمْ كُنتَ مِنَ ٱلْعَالِينَ ٧٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (76, 38, 457, 'ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ', 'قَالَ أَنَا۠ خَيْرٌۭ مِّنْهُ ۖ خَلَقْتَنِى مِن نَّارٍۢ وَخَلَقْتَهُۥ مِن طِينٍۢ ٧٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (77, 38, 457, 'ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ', 'قَالَ فَٱخْرُجْ مِنْهَا فَإِنَّكَ رَجِيمٌۭ ٧٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (78, 38, 457, 'ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ', 'وَإِنَّ عَلَيْكَ لَعْنَتِىٓ إِلَىٰ يَوْمِ ٱلدِّينِ ٧٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (79, 38, 457, 'ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ', 'قَالَ رَبِّ فَأَنظِرْنِىٓ إِلَىٰ يَوْمِ يُبْعَثُونَ ٧٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (80, 38, 457, 'ﳚ ﳛ ﳜ ﳝ ﳞ', 'قَالَ فَإِنَّكَ مِنَ ٱلْمُنظَرِينَ ٨٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (81, 38, 457, 'ﳟ ﳠ ﳡ ﳢ ﳣ', 'إِلَىٰ يَوْمِ ٱلْوَقْتِ ٱلْمَعْلُومِ ٨١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (82, 38, 457, 'ﳤ ﳥ ﳦ ﳧ ﳨ', 'قَالَ فَبِعِزَّتِكَ لَأُغْوِيَنَّهُمْ أَجْمَعِينَ ٨٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (83, 38, 457, 'ﳩ ﳪ ﳫ ﳬ ﳭ', 'إِلَّا عِبَادَكَ مِنْهُمُ ٱلْمُخْلَصِينَ ٨٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (84, 38, 458, 'ﱁ ﱂ ﱃ ﱄ ﱅ', 'قَالَ فَٱلْحَقُّ وَٱلْحَقَّ أَقُولُ ٨٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (85, 38, 458, 'ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ', 'لَأَمْلَأَنَّ جَهَنَّمَ مِنكَ وَمِمَّن تَبِعَكَ مِنْهُمْ أَجْمَعِينَ ٨٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (86, 38, 458, 'ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ', 'قُلْ مَآ أَسْـَٔلُكُمْ عَلَيْهِ مِنْ أَجْرٍۢ وَمَآ أَنَا۠ مِنَ ٱلْمُتَكَلِّفِينَ ٨٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (87, 38, 458, 'ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ', 'إِنْ هُوَ إِلَّا ذِكْرٌۭ لِّلْعَـٰلَمِينَ ٨٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (88, 38, 458, 'ﱟ ﱠ ﱡ ﱢ ﱣ', 'وَلَتَعْلَمُنَّ نَبَأَهُۥ بَعْدَ حِينٍۭ ٨٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 39, 458, 'ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ', 'تَنزِيلُ ٱلْكِتَـٰبِ مِنَ ٱللَّهِ ٱلْعَزِيزِ ٱلْحَكِيمِ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 39, 458, 'ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ', 'إِنَّآ أَنزَلْنَآ إِلَيْكَ ٱلْكِتَـٰبَ بِٱلْحَقِّ فَٱعْبُدِ ٱللَّهَ مُخْلِصًۭا لَّهُ ٱلدِّينَ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 39, 458, 'ﱶ ﱷ ﱸ ﱹﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ', 'أَلَا لِلَّهِ ٱلدِّينُ ٱلْخَالِصُ ۚ وَٱلَّذِينَ ٱتَّخَذُوا۟ مِن دُونِهِۦٓ أَوْلِيَآءَ مَا نَعْبُدُهُمْ إِلَّا لِيُقَرِّبُونَآ إِلَى ٱللَّهِ زُلْفَىٰٓ إِنَّ ٱللَّهَ يَحْكُمُ بَيْنَهُمْ فِى مَا هُمْ فِيهِ يَخْتَلِفُونَ ۗ إِنَّ ٱللَّهَ لَا يَهْدِى مَنْ هُوَ كَـٰذِبٌۭ كَفَّارٌۭ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 39, 458, 'ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤﲥ ﲦﲧ ﲨ ﲩ ﲪ ﲫ ﲬ', 'لَّوْ أَرَادَ ٱللَّهُ أَن يَتَّخِذَ وَلَدًۭا لَّٱصْطَفَىٰ مِمَّا يَخْلُقُ مَا يَشَآءُ ۚ سُبْحَـٰنَهُۥ ۖ هُوَ ٱللَّهُ ٱلْوَٰحِدُ ٱلْقَهَّارُ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 39, 458, 'ﲭ ﲮ ﲯ ﲰﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹﲺ ﲻ ﲼ ﲽﲾ ﲿ ﳀ ﳁ ﳂﳃ ﳄ ﳅ ﳆ ﳇ ﳈ', 'خَلَقَ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ بِٱلْحَقِّ ۖ يُكَوِّرُ ٱلَّيْلَ عَلَى ٱلنَّهَارِ وَيُكَوِّرُ ٱلنَّهَارَ عَلَى ٱلَّيْلِ ۖ وَسَخَّرَ ٱلشَّمْسَ وَٱلْقَمَرَ ۖ كُلٌّۭ يَجْرِى لِأَجَلٍۢ مُّسَمًّى ۗ أَلَا هُوَ ٱلْعَزِيزُ ٱلْغَفَّـٰرُ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 39, 459, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚﱛ ﱜ ﱝ ﱞ ﱟ ﱠﱡ ﱢ ﱣ ﱤ ﱥﱦ ﱧ ﱨ ﱩ', 'خَلَقَكُم مِّن نَّفْسٍۢ وَٰحِدَةٍۢ ثُمَّ جَعَلَ مِنْهَا زَوْجَهَا وَأَنزَلَ لَكُم مِّنَ ٱلْأَنْعَـٰمِ ثَمَـٰنِيَةَ أَزْوَٰجٍۢ ۚ يَخْلُقُكُمْ فِى بُطُونِ أُمَّهَـٰتِكُمْ خَلْقًۭا مِّنۢ بَعْدِ خَلْقٍۢ فِى ظُلُمَـٰتٍۢ ثَلَـٰثٍۢ ۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمْ لَهُ ٱلْمُلْكُ ۖ لَآ إِلَـٰهَ إِلَّا هُوَ ۖ فَأَنَّىٰ تُصْرَفُونَ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 39, 459, 'ﱪ ﱫ ﱬ ﱭ ﱮ ﱯﱰ ﱱ ﱲ ﱳ ﱴﱵ ﱶ ﱷ ﱸ ﱹﱺ ﱻ ﱼ ﱽ ﱾ ﱿﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈﲉ ﲊ ﲋ ﲌ ﲍ ﲎ', 'إِن تَكْفُرُوا۟ فَإِنَّ ٱللَّهَ غَنِىٌّ عَنكُمْ ۖ وَلَا يَرْضَىٰ لِعِبَادِهِ ٱلْكُفْرَ ۖ وَإِن تَشْكُرُوا۟ يَرْضَهُ لَكُمْ ۗ وَلَا تَزِرُ وَازِرَةٌۭ وِزْرَ أُخْرَىٰ ۗ ثُمَّ إِلَىٰ رَبِّكُم مَّرْجِعُكُمْ فَيُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ ۚ إِنَّهُۥ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 39, 459, 'ﲏﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ', '۞ وَإِذَا مَسَّ ٱلْإِنسَـٰنَ ضُرٌّۭ دَعَا رَبَّهُۥ مُنِيبًا إِلَيْهِ ثُمَّ إِذَا خَوَّلَهُۥ نِعْمَةًۭ مِّنْهُ نَسِىَ مَا كَانَ يَدْعُوٓا۟ إِلَيْهِ مِن قَبْلُ وَجَعَلَ لِلَّهِ أَندَادًۭا لِّيُضِلَّ عَن سَبِيلِهِۦ ۚ قُلْ تَمَتَّعْ بِكُفْرِكَ قَلِيلًا ۖ إِنَّكَ مِنْ أَصْحَـٰبِ ٱلنَّارِ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 39, 459, 'ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈﳉ ﳊ ﳋ ﳌ ﳍ ﳎ', 'أَمَّنْ هُوَ قَـٰنِتٌ ءَانَآءَ ٱلَّيْلِ سَاجِدًۭا وَقَآئِمًۭا يَحْذَرُ ٱلْـَٔاخِرَةَ وَيَرْجُوا۟ رَحْمَةَ رَبِّهِۦ ۗ قُلْ هَلْ يَسْتَوِى ٱلَّذِينَ يَعْلَمُونَ وَٱلَّذِينَ لَا يَعْلَمُونَ ۗ إِنَّمَا يَتَذَكَّرُ أُو۟لُوا۟ ٱلْأَلْبَـٰبِ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 39, 459, 'ﳏ ﳐ ﳑ ﳒ ﳓ ﳔﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛﳜ ﳝ ﳞ ﳟﳠ ﳡ ﳢ ﳣ ﳤ ﳥ ﳦ ﳧ', 'قُلْ يَـٰعِبَادِ ٱلَّذِينَ ءَامَنُوا۟ ٱتَّقُوا۟ رَبَّكُمْ ۚ لِلَّذِينَ أَحْسَنُوا۟ فِى هَـٰذِهِ ٱلدُّنْيَا حَسَنَةٌۭ ۗ وَأَرْضُ ٱللَّهِ وَٰسِعَةٌ ۗ إِنَّمَا يُوَفَّى ٱلصَّـٰبِرُونَ أَجْرَهُم بِغَيْرِ حِسَابٍۢ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 39, 460, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ', 'قُلْ إِنِّىٓ أُمِرْتُ أَنْ أَعْبُدَ ٱللَّهَ مُخْلِصًۭا لَّهُ ٱلدِّينَ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 39, 460, 'ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ', 'وَأُمِرْتُ لِأَنْ أَكُونَ أَوَّلَ ٱلْمُسْلِمِينَ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 39, 460, 'ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ', 'قُلْ إِنِّىٓ أَخَافُ إِنْ عَصَيْتُ رَبِّى عَذَابَ يَوْمٍ عَظِيمٍۢ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 39, 460, 'ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ', 'قُلِ ٱللَّهَ أَعْبُدُ مُخْلِصًۭا لَّهُۥ دِينِى ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 39, 460, 'ﱢ ﱣ ﱤ ﱥ ﱦﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ', 'فَٱعْبُدُوا۟ مَا شِئْتُم مِّن دُونِهِۦ ۗ قُلْ إِنَّ ٱلْخَـٰسِرِينَ ٱلَّذِينَ خَسِرُوٓا۟ أَنفُسَهُمْ وَأَهْلِيهِمْ يَوْمَ ٱلْقِيَـٰمَةِ ۗ أَلَا ذَٰلِكَ هُوَ ٱلْخُسْرَانُ ٱلْمُبِينُ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 39, 460, 'ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀﲁ ﲂ ﲃ ﲄ ﲅ ﲆﲇ ﲈ ﲉ ﲊ', 'لَهُم مِّن فَوْقِهِمْ ظُلَلٌۭ مِّنَ ٱلنَّارِ وَمِن تَحْتِهِمْ ظُلَلٌۭ ۚ ذَٰلِكَ يُخَوِّفُ ٱللَّهُ بِهِۦ عِبَادَهُۥ ۚ يَـٰعِبَادِ فَٱتَّقُونِ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 39, 460, 'ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔﲕ ﲖ ﲗ ﲘ', 'وَٱلَّذِينَ ٱجْتَنَبُوا۟ ٱلطَّـٰغُوتَ أَن يَعْبُدُوهَا وَأَنَابُوٓا۟ إِلَى ٱللَّهِ لَهُمُ ٱلْبُشْرَىٰ ۚ فَبَشِّرْ عِبَادِ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 39, 460, 'ﲙ ﲚ ﲛ ﲜ ﲝﲞ ﲟ ﲠ ﲡ ﲢﲣ ﲤ ﲥ ﲦ ﲧ ﲨ', 'ٱلَّذِينَ يَسْتَمِعُونَ ٱلْقَوْلَ فَيَتَّبِعُونَ أَحْسَنَهُۥٓ ۚ أُو۟لَـٰٓئِكَ ٱلَّذِينَ هَدَىٰهُمُ ٱللَّهُ ۖ وَأُو۟لَـٰٓئِكَ هُمْ أُو۟لُوا۟ ٱلْأَلْبَـٰبِ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 39, 460, 'ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ', 'أَفَمَنْ حَقَّ عَلَيْهِ كَلِمَةُ ٱلْعَذَابِ أَفَأَنتَ تُنقِذُ مَن فِى ٱلنَّارِ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 39, 460, 'ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ', 'لَـٰكِنِ ٱلَّذِينَ ٱتَّقَوْا۟ رَبَّهُمْ لَهُمْ غُرَفٌۭ مِّن فَوْقِهَا غُرَفٌۭ مَّبْنِيَّةٌۭ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَـٰرُ ۖ وَعْدَ ٱللَّهِ ۖ لَا يُخْلِفُ ٱللَّهُ ٱلْمِيعَادَ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 39, 460, 'ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ ﳡ ﳢﳣ ﳤ ﳥ ﳦ ﳧ ﳨ ﳩ ﳪ', 'أَلَمْ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءًۭ فَسَلَكَهُۥ يَنَـٰبِيعَ فِى ٱلْأَرْضِ ثُمَّ يُخْرِجُ بِهِۦ زَرْعًۭا مُّخْتَلِفًا أَلْوَٰنُهُۥ ثُمَّ يَهِيجُ فَتَرَىٰهُ مُصْفَرًّۭا ثُمَّ يَجْعَلُهُۥ حُطَـٰمًا ۚ إِنَّ فِى ذَٰلِكَ لَذِكْرَىٰ لِأُو۟لِى ٱلْأَلْبَـٰبِ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 39, 461, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑﱒ ﱓ ﱔ ﱕ ﱖ ﱗ', 'أَفَمَن شَرَحَ ٱللَّهُ صَدْرَهُۥ لِلْإِسْلَـٰمِ فَهُوَ عَلَىٰ نُورٍۢ مِّن رَّبِّهِۦ ۚ فَوَيْلٌۭ لِّلْقَـٰسِيَةِ قُلُوبُهُم مِّن ذِكْرِ ٱللَّهِ ۚ أُو۟لَـٰٓئِكَ فِى ضَلَـٰلٍۢ مُّبِينٍ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 39, 461, 'ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ', 'ٱللَّهُ نَزَّلَ أَحْسَنَ ٱلْحَدِيثِ كِتَـٰبًۭا مُّتَشَـٰبِهًۭا مَّثَانِىَ تَقْشَعِرُّ مِنْهُ جُلُودُ ٱلَّذِينَ يَخْشَوْنَ رَبَّهُمْ ثُمَّ تَلِينُ جُلُودُهُمْ وَقُلُوبُهُمْ إِلَىٰ ذِكْرِ ٱللَّهِ ۚ ذَٰلِكَ هُدَى ٱللَّهِ يَهْدِى بِهِۦ مَن يَشَآءُ ۚ وَمَن يُضْلِلِ ٱللَّهُ فَمَا لَهُۥ مِنْ هَادٍ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 39, 461, 'ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ', 'أَفَمَن يَتَّقِى بِوَجْهِهِۦ سُوٓءَ ٱلْعَذَابِ يَوْمَ ٱلْقِيَـٰمَةِ ۚ وَقِيلَ لِلظَّـٰلِمِينَ ذُوقُوا۟ مَا كُنتُمْ تَكْسِبُونَ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 39, 461, 'ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ', 'كَذَّبَ ٱلَّذِينَ مِن قَبْلِهِمْ فَأَتَىٰهُمُ ٱلْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُونَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 39, 461, 'ﲗ ﲘ ﲙ ﲚ ﲛ ﲜﲝ ﲞ ﲟ ﲠﲡ ﲢ ﲣ ﲤ ﲥ', 'فَأَذَاقَهُمُ ٱللَّهُ ٱلْخِزْىَ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا ۖ وَلَعَذَابُ ٱلْـَٔاخِرَةِ أَكْبَرُ ۚ لَوْ كَانُوا۟ يَعْلَمُونَ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 39, 461, 'ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ', 'وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِى هَـٰذَا ٱلْقُرْءَانِ مِن كُلِّ مَثَلٍۢ لَّعَلَّهُمْ يَتَذَكَّرُونَ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 39, 461, 'ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ', 'قُرْءَانًا عَرَبِيًّا غَيْرَ ذِى عِوَجٍۢ لَّعَلَّهُمْ يَتَّقُونَ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 39, 461, 'ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆﳇ ﳈ ﳉﳊ ﳋ ﳌ ﳍ ﳎ ﳏ', 'ضَرَبَ ٱللَّهُ مَثَلًۭا رَّجُلًۭا فِيهِ شُرَكَآءُ مُتَشَـٰكِسُونَ وَرَجُلًۭا سَلَمًۭا لِّرَجُلٍ هَلْ يَسْتَوِيَانِ مَثَلًا ۚ ٱلْحَمْدُ لِلَّهِ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 39, 461, 'ﳐ ﳑ ﳒ ﳓ ﳔ', 'إِنَّكَ مَيِّتٌۭ وَإِنَّهُم مَّيِّتُونَ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 39, 461, 'ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ', 'ثُمَّ إِنَّكُمْ يَوْمَ ٱلْقِيَـٰمَةِ عِندَ رَبِّكُمْ تَخْتَصِمُونَ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 39, 462, 'ﱁﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ', '۞ فَمَنْ أَظْلَمُ مِمَّن كَذَبَ عَلَى ٱللَّهِ وَكَذَّبَ بِٱلصِّدْقِ إِذْ جَآءَهُۥٓ ۚ أَلَيْسَ فِى جَهَنَّمَ مَثْوًۭى لِّلْكَـٰفِرِينَ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 39, 462, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ', 'وَٱلَّذِى جَآءَ بِٱلصِّدْقِ وَصَدَّقَ بِهِۦٓ ۙ أُو۟لَـٰٓئِكَ هُمُ ٱلْمُتَّقُونَ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 39, 462, 'ﱜ ﱝ ﱞ ﱟ ﱠﱡ ﱢ ﱣ ﱤ ﱥ', 'لَهُم مَّا يَشَآءُونَ عِندَ رَبِّهِمْ ۚ ذَٰلِكَ جَزَآءُ ٱلْمُحْسِنِينَ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 39, 462, 'ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ', 'لِيُكَفِّرَ ٱللَّهُ عَنْهُمْ أَسْوَأَ ٱلَّذِى عَمِلُوا۟ وَيَجْزِيَهُمْ أَجْرَهُم بِأَحْسَنِ ٱلَّذِى كَانُوا۟ يَعْمَلُونَ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 39, 462, 'ﱳ ﱴ ﱵ ﱶﱷ ﱸ ﱹ ﱺ ﱻﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ', 'أَلَيْسَ ٱللَّهُ بِكَافٍ عَبْدَهُۥ ۖ وَيُخَوِّفُونَكَ بِٱلَّذِينَ مِن دُونِهِۦ ۚ وَمَن يُضْلِلِ ٱللَّهُ فَمَا لَهُۥ مِنْ هَادٍۢ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 39, 462, 'ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ', 'وَمَن يَهْدِ ٱللَّهُ فَمَا لَهُۥ مِن مُّضِلٍّ ۗ أَلَيْسَ ٱللَّهُ بِعَزِيزٍۢ ذِى ٱنتِقَامٍۢ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 39, 462, 'ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱﲲ ﲳ ﲴ ﲵﲶ ﲷ ﲸ ﲹ ﲺ', 'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ لَيَقُولُنَّ ٱللَّهُ ۚ قُلْ أَفَرَءَيْتُم مَّا تَدْعُونَ مِن دُونِ ٱللَّهِ إِنْ أَرَادَنِىَ ٱللَّهُ بِضُرٍّ هَلْ هُنَّ كَـٰشِفَـٰتُ ضُرِّهِۦٓ أَوْ أَرَادَنِى بِرَحْمَةٍ هَلْ هُنَّ مُمْسِكَـٰتُ رَحْمَتِهِۦ ۚ قُلْ حَسْبِىَ ٱللَّهُ ۖ عَلَيْهِ يَتَوَكَّلُ ٱلْمُتَوَكِّلُونَ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 39, 462, 'ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁﳂ ﳃ ﳄ ﳅ', 'قُلْ يَـٰقَوْمِ ٱعْمَلُوا۟ عَلَىٰ مَكَانَتِكُمْ إِنِّى عَـٰمِلٌۭ ۖ فَسَوْفَ تَعْلَمُونَ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 39, 462, 'ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ', 'مَن يَأْتِيهِ عَذَابٌۭ يُخْزِيهِ وَيَحِلُّ عَلَيْهِ عَذَابٌۭ مُّقِيمٌ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 39, 463, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆﱇ ﱈ ﱉ ﱊﱋ ﱌ ﱍ ﱎ ﱏ ﱐﱑ ﱒ ﱓ ﱔ ﱕ ﱖ', 'إِنَّآ أَنزَلْنَا عَلَيْكَ ٱلْكِتَـٰبَ لِلنَّاسِ بِٱلْحَقِّ ۖ فَمَنِ ٱهْتَدَىٰ فَلِنَفْسِهِۦ ۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيْهَا ۖ وَمَآ أَنتَ عَلَيْهِم بِوَكِيلٍ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 39, 463, 'ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ', 'ٱللَّهُ يَتَوَفَّى ٱلْأَنفُسَ حِينَ مَوْتِهَا وَٱلَّتِى لَمْ تَمُتْ فِى مَنَامِهَا ۖ فَيُمْسِكُ ٱلَّتِى قَضَىٰ عَلَيْهَا ٱلْمَوْتَ وَيُرْسِلُ ٱلْأُخْرَىٰٓ إِلَىٰٓ أَجَلٍۢ مُّسَمًّى ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَـٰتٍۢ لِّقَوْمٍۢ يَتَفَكَّرُونَ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 39, 463, 'ﱴ ﱵ ﱶ ﱷ ﱸ ﱹﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ', 'أَمِ ٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ شُفَعَآءَ ۚ قُلْ أَوَلَوْ كَانُوا۟ لَا يَمْلِكُونَ شَيْـًۭٔا وَلَا يَعْقِلُونَ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 39, 463, 'ﲄ ﲅ ﲆ ﲇﲈ ﲉ ﲊ ﲋ ﲌﲍ ﲎ ﲏ ﲐ ﲑ', 'قُل لِّلَّهِ ٱلشَّفَـٰعَةُ جَمِيعًۭا ۖ لَّهُۥ مُلْكُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۖ ثُمَّ إِلَيْهِ تُرْجَعُونَ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 39, 463, 'ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ', 'وَإِذَا ذُكِرَ ٱللَّهُ وَحْدَهُ ٱشْمَأَزَّتْ قُلُوبُ ٱلَّذِينَ لَا يُؤْمِنُونَ بِٱلْـَٔاخِرَةِ ۖ وَإِذَا ذُكِرَ ٱلَّذِينَ مِن دُونِهِۦٓ إِذَا هُمْ يَسْتَبْشِرُونَ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 39, 463, 'ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ', 'قُلِ ٱللَّهُمَّ فَاطِرَ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ عَـٰلِمَ ٱلْغَيْبِ وَٱلشَّهَـٰدَةِ أَنتَ تَحْكُمُ بَيْنَ عِبَادِكَ فِى مَا كَانُوا۟ فِيهِ يَخْتَلِفُونَ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 39, 463, 'ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ', 'وَلَوْ أَنَّ لِلَّذِينَ ظَلَمُوا۟ مَا فِى ٱلْأَرْضِ جَمِيعًۭا وَمِثْلَهُۥ مَعَهُۥ لَٱفْتَدَوْا۟ بِهِۦ مِن سُوٓءِ ٱلْعَذَابِ يَوْمَ ٱلْقِيَـٰمَةِ ۚ وَبَدَا لَهُم مِّنَ ٱللَّهِ مَا لَمْ يَكُونُوا۟ يَحْتَسِبُونَ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 39, 464, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ', 'وَبَدَا لَهُمْ سَيِّـَٔاتُ مَا كَسَبُوا۟ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 39, 464, 'ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ', 'فَإِذَا مَسَّ ٱلْإِنسَـٰنَ ضُرٌّۭ دَعَانَا ثُمَّ إِذَا خَوَّلْنَـٰهُ نِعْمَةًۭ مِّنَّا قَالَ إِنَّمَآ أُوتِيتُهُۥ عَلَىٰ عِلْمٍۭ ۚ بَلْ هِىَ فِتْنَةٌۭ وَلَـٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 39, 464, 'ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ', 'قَدْ قَالَهَا ٱلَّذِينَ مِن قَبْلِهِمْ فَمَآ أَغْنَىٰ عَنْهُم مَّا كَانُوا۟ يَكْسِبُونَ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 39, 464, 'ﱱ ﱲ ﱳ ﱴﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ', 'فَأَصَابَهُمْ سَيِّـَٔاتُ مَا كَسَبُوا۟ ۚ وَٱلَّذِينَ ظَلَمُوا۟ مِنْ هَـٰٓؤُلَآءِ سَيُصِيبُهُمْ سَيِّـَٔاتُ مَا كَسَبُوا۟ وَمَا هُم بِمُعْجِزِينَ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 39, 464, 'ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ', 'أَوَلَمْ يَعْلَمُوٓا۟ أَنَّ ٱللَّهَ يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ وَيَقْدِرُ ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَـٰتٍۢ لِّقَوْمٍۢ يُؤْمِنُونَ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 39, 464, 'ﲓﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞﲟ ﲠ ﲡ ﲢ ﲣ ﲤﲥ ﲦ ﲧ ﲨ ﲩ ﲪ', '۞ قُلْ يَـٰعِبَادِىَ ٱلَّذِينَ أَسْرَفُوا۟ عَلَىٰٓ أَنفُسِهِمْ لَا تَقْنَطُوا۟ مِن رَّحْمَةِ ٱللَّهِ ۚ إِنَّ ٱللَّهَ يَغْفِرُ ٱلذُّنُوبَ جَمِيعًا ۚ إِنَّهُۥ هُوَ ٱلْغَفُورُ ٱلرَّحِيمُ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 39, 464, 'ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ', 'وَأَنِيبُوٓا۟ إِلَىٰ رَبِّكُمْ وَأَسْلِمُوا۟ لَهُۥ مِن قَبْلِ أَن يَأْتِيَكُمُ ٱلْعَذَابُ ثُمَّ لَا تُنصَرُونَ ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (55, 39, 464, 'ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ', 'وَٱتَّبِعُوٓا۟ أَحْسَنَ مَآ أُنزِلَ إِلَيْكُم مِّن رَّبِّكُم مِّن قَبْلِ أَن يَأْتِيَكُمُ ٱلْعَذَابُ بَغْتَةًۭ وَأَنتُمْ لَا تَشْعُرُونَ ٥٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (56, 39, 464, 'ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ', 'أَن تَقُولَ نَفْسٌۭ يَـٰحَسْرَتَىٰ عَلَىٰ مَا فَرَّطتُ فِى جَنۢبِ ٱللَّهِ وَإِن كُنتُ لَمِنَ ٱلسَّـٰخِرِينَ ٥٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (57, 39, 465, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ', 'أَوْ تَقُولَ لَوْ أَنَّ ٱللَّهَ هَدَىٰنِى لَكُنتُ مِنَ ٱلْمُتَّقِينَ ٥٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (58, 39, 465, 'ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ', 'أَوْ تَقُولَ حِينَ تَرَى ٱلْعَذَابَ لَوْ أَنَّ لِى كَرَّةًۭ فَأَكُونَ مِنَ ٱلْمُحْسِنِينَ ٥٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (59, 39, 465, 'ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ', 'بَلَىٰ قَدْ جَآءَتْكَ ءَايَـٰتِى فَكَذَّبْتَ بِهَا وَٱسْتَكْبَرْتَ وَكُنتَ مِنَ ٱلْكَـٰفِرِينَ ٥٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (60, 39, 465, 'ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ', 'وَيَوْمَ ٱلْقِيَـٰمَةِ تَرَى ٱلَّذِينَ كَذَبُوا۟ عَلَى ٱللَّهِ وُجُوهُهُم مُّسْوَدَّةٌ ۚ أَلَيْسَ فِى جَهَنَّمَ مَثْوًۭى لِّلْمُتَكَبِّرِينَ ٦٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (61, 39, 465, 'ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ', 'وَيُنَجِّى ٱللَّهُ ٱلَّذِينَ ٱتَّقَوْا۟ بِمَفَازَتِهِمْ لَا يَمَسُّهُمُ ٱلسُّوٓءُ وَلَا هُمْ يَحْزَنُونَ ٦١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (62, 39, 465, 'ﱿ ﲀ ﲁ ﲂﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ', 'ٱللَّهُ خَـٰلِقُ كُلِّ شَىْءٍۢ ۖ وَهُوَ عَلَىٰ كُلِّ شَىْءٍۢ وَكِيلٌۭ ٦٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (63, 39, 465, 'ﲊ ﲋ ﲌ ﲍﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ', 'لَّهُۥ مَقَالِيدُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۗ وَٱلَّذِينَ كَفَرُوا۟ بِـَٔايَـٰتِ ٱللَّهِ أُو۟لَـٰٓئِكَ هُمُ ٱلْخَـٰسِرُونَ ٦٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (64, 39, 465, 'ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ', 'قُلْ أَفَغَيْرَ ٱللَّهِ تَأْمُرُوٓنِّىٓ أَعْبُدُ أَيُّهَا ٱلْجَـٰهِلُونَ ٦٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (65, 39, 465, 'ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ', 'وَلَقَدْ أُوحِىَ إِلَيْكَ وَإِلَى ٱلَّذِينَ مِن قَبْلِكَ لَئِنْ أَشْرَكْتَ لَيَحْبَطَنَّ عَمَلُكَ وَلَتَكُونَنَّ مِنَ ٱلْخَـٰسِرِينَ ٦٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (66, 39, 465, 'ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ', 'بَلِ ٱللَّهَ فَٱعْبُدْ وَكُن مِّنَ ٱلشَّـٰكِرِينَ ٦٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (67, 39, 465, 'ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁﳂ ﳃ ﳄ ﳅ ﳆ ﳇ', 'وَمَا قَدَرُوا۟ ٱللَّهَ حَقَّ قَدْرِهِۦ وَٱلْأَرْضُ جَمِيعًۭا قَبْضَتُهُۥ يَوْمَ ٱلْقِيَـٰمَةِ وَٱلسَّمَـٰوَٰتُ مَطْوِيَّـٰتٌۢ بِيَمِينِهِۦ ۚ سُبْحَـٰنَهُۥ وَتَعَـٰلَىٰ عَمَّا يُشْرِكُونَ ٦٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (68, 39, 466, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ', 'وَنُفِخَ فِى ٱلصُّورِ فَصَعِقَ مَن فِى ٱلسَّمَـٰوَٰتِ وَمَن فِى ٱلْأَرْضِ إِلَّا مَن شَآءَ ٱللَّهُ ۖ ثُمَّ نُفِخَ فِيهِ أُخْرَىٰ فَإِذَا هُمْ قِيَامٌۭ يَنظُرُونَ ٦٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (69, 39, 466, 'ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ', 'وَأَشْرَقَتِ ٱلْأَرْضُ بِنُورِ رَبِّهَا وَوُضِعَ ٱلْكِتَـٰبُ وَجِا۟ىٓءَ بِٱلنَّبِيِّـۧنَ وَٱلشُّهَدَآءِ وَقُضِىَ بَيْنَهُم بِٱلْحَقِّ وَهُمْ لَا يُظْلَمُونَ ٦٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (70, 39, 466, 'ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ', 'وَوُفِّيَتْ كُلُّ نَفْسٍۢ مَّا عَمِلَتْ وَهُوَ أَعْلَمُ بِمَا يَفْعَلُونَ ٧٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (71, 39, 466, 'ﱳ ﱴ ﱵ ﱶ ﱷ ﱸﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ', 'وَسِيقَ ٱلَّذِينَ كَفَرُوٓا۟ إِلَىٰ جَهَنَّمَ زُمَرًا ۖ حَتَّىٰٓ إِذَا جَآءُوهَا فُتِحَتْ أَبْوَٰبُهَا وَقَالَ لَهُمْ خَزَنَتُهَآ أَلَمْ يَأْتِكُمْ رُسُلٌۭ مِّنكُمْ يَتْلُونَ عَلَيْكُمْ ءَايَـٰتِ رَبِّكُمْ وَيُنذِرُونَكُمْ لِقَآءَ يَوْمِكُمْ هَـٰذَا ۚ قَالُوا۟ بَلَىٰ وَلَـٰكِنْ حَقَّتْ كَلِمَةُ ٱلْعَذَابِ عَلَى ٱلْكَـٰفِرِينَ ٧١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (72, 39, 466, 'ﲘ ﲙ ﲚ ﲛ ﲜ ﲝﲞ ﲟ ﲠ ﲡ ﲢ', 'قِيلَ ٱدْخُلُوٓا۟ أَبْوَٰبَ جَهَنَّمَ خَـٰلِدِينَ فِيهَا ۖ فَبِئْسَ مَثْوَى ٱلْمُتَكَبِّرِينَ ٧٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (73, 39, 466, 'ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ', 'وَسِيقَ ٱلَّذِينَ ٱتَّقَوْا۟ رَبَّهُمْ إِلَى ٱلْجَنَّةِ زُمَرًا ۖ حَتَّىٰٓ إِذَا جَآءُوهَا وَفُتِحَتْ أَبْوَٰبُهَا وَقَالَ لَهُمْ خَزَنَتُهَا سَلَـٰمٌ عَلَيْكُمْ طِبْتُمْ فَٱدْخُلُوهَا خَـٰلِدِينَ ٧٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (74, 39, 466, 'ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅﳆ ﳇ ﳈ ﳉ ﳊ', 'وَقَالُوا۟ ٱلْحَمْدُ لِلَّهِ ٱلَّذِى صَدَقَنَا وَعْدَهُۥ وَأَوْرَثَنَا ٱلْأَرْضَ نَتَبَوَّأُ مِنَ ٱلْجَنَّةِ حَيْثُ نَشَآءُ ۖ فَنِعْمَ أَجْرُ ٱلْعَـٰمِلِينَ ٧٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (75, 39, 467, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉﱊ ﱋ ﱌ ﱍﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ', 'وَتَرَى ٱلْمَلَـٰٓئِكَةَ حَآفِّينَ مِنْ حَوْلِ ٱلْعَرْشِ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ ۖ وَقُضِىَ بَيْنَهُم بِٱلْحَقِّ وَقِيلَ ٱلْحَمْدُ لِلَّهِ رَبِّ ٱلْعَـٰلَمِينَ ٧٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 40, 467, 'ﱕ ﱖ', 'حمٓ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 40, 467, 'ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ', 'تَنزِيلُ ٱلْكِتَـٰبِ مِنَ ٱللَّهِ ٱلْعَزِيزِ ٱلْعَلِيمِ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 40, 467, 'ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥﱦ ﱧ ﱨ ﱩ ﱪﱫ ﱬ ﱭ ﱮ', 'غَافِرِ ٱلذَّنۢبِ وَقَابِلِ ٱلتَّوْبِ شَدِيدِ ٱلْعِقَابِ ذِى ٱلطَّوْلِ ۖ لَآ إِلَـٰهَ إِلَّا هُوَ ۖ إِلَيْهِ ٱلْمَصِيرُ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 40, 467, 'ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ', 'مَا يُجَـٰدِلُ فِىٓ ءَايَـٰتِ ٱللَّهِ إِلَّا ٱلَّذِينَ كَفَرُوا۟ فَلَا يَغْرُرْكَ تَقَلُّبُهُمْ فِى ٱلْبِلَـٰدِ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 40, 467, 'ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃﲄ ﲅ ﲆ ﲇ ﲈ ﲉﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐﲑ ﲒ ﲓ ﲔ ﲕ', 'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍۢ وَٱلْأَحْزَابُ مِنۢ بَعْدِهِمْ ۖ وَهَمَّتْ كُلُّ أُمَّةٍۭ بِرَسُولِهِمْ لِيَأْخُذُوهُ ۖ وَجَـٰدَلُوا۟ بِٱلْبَـٰطِلِ لِيُدْحِضُوا۟ بِهِ ٱلْحَقَّ فَأَخَذْتُهُمْ ۖ فَكَيْفَ كَانَ عِقَابِ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 40, 467, 'ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ', 'وَكَذَٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى ٱلَّذِينَ كَفَرُوٓا۟ أَنَّهُمْ أَصْحَـٰبُ ٱلنَّارِ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 40, 467, 'ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ', 'ٱلَّذِينَ يَحْمِلُونَ ٱلْعَرْشَ وَمَنْ حَوْلَهُۥ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَيُؤْمِنُونَ بِهِۦ وَيَسْتَغْفِرُونَ لِلَّذِينَ ءَامَنُوا۟ رَبَّنَا وَسِعْتَ كُلَّ شَىْءٍۢ رَّحْمَةًۭ وَعِلْمًۭا فَٱغْفِرْ لِلَّذِينَ تَابُوا۟ وَٱتَّبَعُوا۟ سَبِيلَكَ وَقِهِمْ عَذَابَ ٱلْجَحِيمِ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 40, 468, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌﱍ ﱎ ﱏ ﱐ ﱑ ﱒ', 'رَبَّنَا وَأَدْخِلْهُمْ جَنَّـٰتِ عَدْنٍ ٱلَّتِى وَعَدتَّهُمْ وَمَن صَلَحَ مِنْ ءَابَآئِهِمْ وَأَزْوَٰجِهِمْ وَذُرِّيَّـٰتِهِمْ ۚ إِنَّكَ أَنتَ ٱلْعَزِيزُ ٱلْحَكِيمُ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 40, 468, 'ﱓ ﱔﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛﱜ ﱝ ﱞ ﱟ ﱠ ﱡ', 'وَقِهِمُ ٱلسَّيِّـَٔاتِ ۚ وَمَن تَقِ ٱلسَّيِّـَٔاتِ يَوْمَئِذٍۢ فَقَدْ رَحِمْتَهُۥ ۚ وَذَٰلِكَ هُوَ ٱلْفَوْزُ ٱلْعَظِيمُ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 40, 468, 'ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ', 'إِنَّ ٱلَّذِينَ كَفَرُوا۟ يُنَادَوْنَ لَمَقْتُ ٱللَّهِ أَكْبَرُ مِن مَّقْتِكُمْ أَنفُسَكُمْ إِذْ تُدْعَوْنَ إِلَى ٱلْإِيمَـٰنِ فَتَكْفُرُونَ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 40, 468, 'ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ', 'قَالُوا۟ رَبَّنَآ أَمَتَّنَا ٱثْنَتَيْنِ وَأَحْيَيْتَنَا ٱثْنَتَيْنِ فَٱعْتَرَفْنَا بِذُنُوبِنَا فَهَلْ إِلَىٰ خُرُوجٍۢ مِّن سَبِيلٍۢ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 40, 468, 'ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊﲋ ﲌ ﲍ ﲎ ﲏ ﲐ', 'ذَٰلِكُم بِأَنَّهُۥٓ إِذَا دُعِىَ ٱللَّهُ وَحْدَهُۥ كَفَرْتُمْ ۖ وَإِن يُشْرَكْ بِهِۦ تُؤْمِنُوا۟ ۚ فَٱلْحُكْمُ لِلَّهِ ٱلْعَلِىِّ ٱلْكَبِيرِ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 40, 468, 'ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ', 'هُوَ ٱلَّذِى يُرِيكُمْ ءَايَـٰتِهِۦ وَيُنَزِّلُ لَكُم مِّنَ ٱلسَّمَآءِ رِزْقًۭا ۚ وَمَا يَتَذَكَّرُ إِلَّا مَن يُنِيبُ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 40, 468, 'ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ', 'فَٱدْعُوا۟ ٱللَّهَ مُخْلِصِينَ لَهُ ٱلدِّينَ وَلَوْ كَرِهَ ٱلْكَـٰفِرُونَ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 40, 468, 'ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ', 'رَفِيعُ ٱلدَّرَجَـٰتِ ذُو ٱلْعَرْشِ يُلْقِى ٱلرُّوحَ مِنْ أَمْرِهِۦ عَلَىٰ مَن يَشَآءُ مِنْ عِبَادِهِۦ لِيُنذِرَ يَوْمَ ٱلتَّلَاقِ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 40, 468, 'ﲻ ﲼ ﲽﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄﳅ ﳆ ﳇ ﳈﳉ ﳊ ﳋ ﳌ ﳍ', 'يَوْمَ هُم بَـٰرِزُونَ ۖ لَا يَخْفَىٰ عَلَى ٱللَّهِ مِنْهُمْ شَىْءٌۭ ۚ لِّمَنِ ٱلْمُلْكُ ٱلْيَوْمَ ۖ لِلَّهِ ٱلْوَٰحِدِ ٱلْقَهَّارِ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 40, 469, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆﱇ ﱈ ﱉ ﱊﱋ ﱌ ﱍ ﱎ ﱏ ﱐ', 'ٱلْيَوْمَ تُجْزَىٰ كُلُّ نَفْسٍۭ بِمَا كَسَبَتْ ۚ لَا ظُلْمَ ٱلْيَوْمَ ۚ إِنَّ ٱللَّهَ سَرِيعُ ٱلْحِسَابِ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 40, 469, 'ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ', 'وَأَنذِرْهُمْ يَوْمَ ٱلْـَٔازِفَةِ إِذِ ٱلْقُلُوبُ لَدَى ٱلْحَنَاجِرِ كَـٰظِمِينَ ۚ مَا لِلظَّـٰلِمِينَ مِنْ حَمِيمٍۢ وَلَا شَفِيعٍۢ يُطَاعُ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 40, 469, 'ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ', 'يَعْلَمُ خَآئِنَةَ ٱلْأَعْيُنِ وَمَا تُخْفِى ٱلصُّدُورُ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 40, 469, 'ﱩ ﱪ ﱫﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ', 'وَٱللَّهُ يَقْضِى بِٱلْحَقِّ ۖ وَٱلَّذِينَ يَدْعُونَ مِن دُونِهِۦ لَا يَقْضُونَ بِشَىْءٍ ۗ إِنَّ ٱللَّهَ هُوَ ٱلسَّمِيعُ ٱلْبَصِيرُ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 40, 469, 'ﱻﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ', '۞ أَوَلَمْ يَسِيرُوا۟ فِى ٱلْأَرْضِ فَيَنظُرُوا۟ كَيْفَ كَانَ عَـٰقِبَةُ ٱلَّذِينَ كَانُوا۟ مِن قَبْلِهِمْ ۚ كَانُوا۟ هُمْ أَشَدَّ مِنْهُمْ قُوَّةًۭ وَءَاثَارًۭا فِى ٱلْأَرْضِ فَأَخَذَهُمُ ٱللَّهُ بِذُنُوبِهِمْ وَمَا كَانَ لَهُم مِّنَ ٱللَّهِ مِن وَاقٍۢ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 40, 469, 'ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤﲥ ﲦ ﲧ ﲨ ﲩ ﲪ', 'ذَٰلِكَ بِأَنَّهُمْ كَانَت تَّأْتِيهِمْ رُسُلُهُم بِٱلْبَيِّنَـٰتِ فَكَفَرُوا۟ فَأَخَذَهُمُ ٱللَّهُ ۚ إِنَّهُۥ قَوِىٌّۭ شَدِيدُ ٱلْعِقَابِ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 40, 469, 'ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ', 'وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِـَٔايَـٰتِنَا وَسُلْطَـٰنٍۢ مُّبِينٍ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 40, 469, 'ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ', 'إِلَىٰ فِرْعَوْنَ وَهَـٰمَـٰنَ وَقَـٰرُونَ فَقَالُوا۟ سَـٰحِرٌۭ كَذَّابٌۭ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 40, 469, 'ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ', 'فَلَمَّا جَآءَهُم بِٱلْحَقِّ مِنْ عِندِنَا قَالُوا۟ ٱقْتُلُوٓا۟ أَبْنَآءَ ٱلَّذِينَ ءَامَنُوا۟ مَعَهُۥ وَٱسْتَحْيُوا۟ نِسَآءَهُمْ ۚ وَمَا كَيْدُ ٱلْكَـٰفِرِينَ إِلَّا فِى ضَلَـٰلٍۢ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 40, 470, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ', 'وَقَالَ فِرْعَوْنُ ذَرُونِىٓ أَقْتُلْ مُوسَىٰ وَلْيَدْعُ رَبَّهُۥٓ ۖ إِنِّىٓ أَخَافُ أَن يُبَدِّلَ دِينَكُمْ أَوْ أَن يُظْهِرَ فِى ٱلْأَرْضِ ٱلْفَسَادَ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 40, 470, 'ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ', 'وَقَالَ مُوسَىٰٓ إِنِّى عُذْتُ بِرَبِّى وَرَبِّكُم مِّن كُلِّ مُتَكَبِّرٍۢ لَّا يُؤْمِنُ بِيَوْمِ ٱلْحِسَابِ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 40, 470, 'ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵﱶ ﱷ ﱸ ﱹ ﱺ ﱻﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ', 'وَقَالَ رَجُلٌۭ مُّؤْمِنٌۭ مِّنْ ءَالِ فِرْعَوْنَ يَكْتُمُ إِيمَـٰنَهُۥٓ أَتَقْتُلُونَ رَجُلًا أَن يَقُولَ رَبِّىَ ٱللَّهُ وَقَدْ جَآءَكُم بِٱلْبَيِّنَـٰتِ مِن رَّبِّكُمْ ۖ وَإِن يَكُ كَـٰذِبًۭا فَعَلَيْهِ كَذِبُهُۥ ۖ وَإِن يَكُ صَادِقًۭا يُصِبْكُم بَعْضُ ٱلَّذِى يَعِدُكُمْ ۖ إِنَّ ٱللَّهَ لَا يَهْدِى مَنْ هُوَ مُسْرِفٌۭ كَذَّابٌۭ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 40, 470, 'ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ', 'يَـٰقَوْمِ لَكُمُ ٱلْمُلْكُ ٱلْيَوْمَ ظَـٰهِرِينَ فِى ٱلْأَرْضِ فَمَن يَنصُرُنَا مِنۢ بَأْسِ ٱللَّهِ إِن جَآءَنَا ۚ قَالَ فِرْعَوْنُ مَآ أُرِيكُمْ إِلَّا مَآ أَرَىٰ وَمَآ أَهْدِيكُمْ إِلَّا سَبِيلَ ٱلرَّشَادِ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 40, 470, 'ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ', 'وَقَالَ ٱلَّذِىٓ ءَامَنَ يَـٰقَوْمِ إِنِّىٓ أَخَافُ عَلَيْكُم مِّثْلَ يَوْمِ ٱلْأَحْزَابِ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 40, 470, 'ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ', 'مِثْلَ دَأْبِ قَوْمِ نُوحٍۢ وَعَادٍۢ وَثَمُودَ وَٱلَّذِينَ مِنۢ بَعْدِهِمْ ۚ وَمَا ٱللَّهُ يُرِيدُ ظُلْمًۭا لِّلْعِبَادِ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 40, 470, 'ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ', 'وَيَـٰقَوْمِ إِنِّىٓ أَخَافُ عَلَيْكُمْ يَوْمَ ٱلتَّنَادِ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 40, 470, 'ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ ﳝ', 'يَوْمَ تُوَلُّونَ مُدْبِرِينَ مَا لَكُم مِّنَ ٱللَّهِ مِنْ عَاصِمٍۢ ۗ وَمَن يُضْلِلِ ٱللَّهُ فَمَا لَهُۥ مِنْ هَادٍۢ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 40, 471, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ', 'وَلَقَدْ جَآءَكُمْ يُوسُفُ مِن قَبْلُ بِٱلْبَيِّنَـٰتِ فَمَا زِلْتُمْ فِى شَكٍّۢ مِّمَّا جَآءَكُم بِهِۦ ۖ حَتَّىٰٓ إِذَا هَلَكَ قُلْتُمْ لَن يَبْعَثَ ٱللَّهُ مِنۢ بَعْدِهِۦ رَسُولًۭا ۚ كَذَٰلِكَ يُضِلُّ ٱللَّهُ مَنْ هُوَ مُسْرِفٌۭ مُّرْتَابٌ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 40, 471, 'ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ', 'ٱلَّذِينَ يُجَـٰدِلُونَ فِىٓ ءَايَـٰتِ ٱللَّهِ بِغَيْرِ سُلْطَـٰنٍ أَتَىٰهُمْ ۖ كَبُرَ مَقْتًا عِندَ ٱللَّهِ وَعِندَ ٱلَّذِينَ ءَامَنُوا۟ ۚ كَذَٰلِكَ يَطْبَعُ ٱللَّهُ عَلَىٰ كُلِّ قَلْبِ مُتَكَبِّرٍۢ جَبَّارٍۢ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 40, 471, 'ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ', 'وَقَالَ فِرْعَوْنُ يَـٰهَـٰمَـٰنُ ٱبْنِ لِى صَرْحًۭا لَّعَلِّىٓ أَبْلُغُ ٱلْأَسْبَـٰبَ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 40, 471, 'ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ', 'أَسْبَـٰبَ ٱلسَّمَـٰوَٰتِ فَأَطَّلِعَ إِلَىٰٓ إِلَـٰهِ مُوسَىٰ وَإِنِّى لَأَظُنُّهُۥ كَـٰذِبًۭا ۚ وَكَذَٰلِكَ زُيِّنَ لِفِرْعَوْنَ سُوٓءُ عَمَلِهِۦ وَصُدَّ عَنِ ٱلسَّبِيلِ ۚ وَمَا كَيْدُ فِرْعَوْنَ إِلَّا فِى تَبَابٍۢ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 40, 471, 'ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ', 'وَقَالَ ٱلَّذِىٓ ءَامَنَ يَـٰقَوْمِ ٱتَّبِعُونِ أَهْدِكُمْ سَبِيلَ ٱلرَّشَادِ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 40, 471, 'ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ', 'يَـٰقَوْمِ إِنَّمَا هَـٰذِهِ ٱلْحَيَوٰةُ ٱلدُّنْيَا مَتَـٰعٌۭ وَإِنَّ ٱلْـَٔاخِرَةَ هِىَ دَارُ ٱلْقَرَارِ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 40, 471, 'ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ', 'مَنْ عَمِلَ سَيِّئَةًۭ فَلَا يُجْزَىٰٓ إِلَّا مِثْلَهَا ۖ وَمَنْ عَمِلَ صَـٰلِحًۭا مِّن ذَكَرٍ أَوْ أُنثَىٰ وَهُوَ مُؤْمِنٌۭ فَأُو۟لَـٰٓئِكَ يَدْخُلُونَ ٱلْجَنَّةَ يُرْزَقُونَ فِيهَا بِغَيْرِ حِسَابٍۢ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 40, 472, 'ﱁﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ', '۞ وَيَـٰقَوْمِ مَا لِىٓ أَدْعُوكُمْ إِلَى ٱلنَّجَوٰةِ وَتَدْعُونَنِىٓ إِلَى ٱلنَّارِ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 40, 472, 'ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ', 'تَدْعُونَنِى لِأَكْفُرَ بِٱللَّهِ وَأُشْرِكَ بِهِۦ مَا لَيْسَ لِى بِهِۦ عِلْمٌۭ وَأَنَا۠ أَدْعُوكُمْ إِلَى ٱلْعَزِيزِ ٱلْغَفَّـٰرِ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 40, 472, 'ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ', 'لَا جَرَمَ أَنَّمَا تَدْعُونَنِىٓ إِلَيْهِ لَيْسَ لَهُۥ دَعْوَةٌۭ فِى ٱلدُّنْيَا وَلَا فِى ٱلْـَٔاخِرَةِ وَأَنَّ مَرَدَّنَآ إِلَى ٱللَّهِ وَأَنَّ ٱلْمُسْرِفِينَ هُمْ أَصْحَـٰبُ ٱلنَّارِ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 40, 472, 'ﱳ ﱴ ﱵ ﱶﱷ ﱸ ﱹ ﱺ ﱻﱼ ﱽ ﱾ ﱿ ﲀ ﲁ', 'فَسَتَذْكُرُونَ مَآ أَقُولُ لَكُمْ ۚ وَأُفَوِّضُ أَمْرِىٓ إِلَى ٱللَّهِ ۚ إِنَّ ٱللَّهَ بَصِيرٌۢ بِٱلْعِبَادِ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 40, 472, 'ﲂ ﲃ ﲄ ﲅ ﲆﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ', 'فَوَقَىٰهُ ٱللَّهُ سَيِّـَٔاتِ مَا مَكَرُوا۟ ۖ وَحَاقَ بِـَٔالِ فِرْعَوْنَ سُوٓءُ ٱلْعَذَابِ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 40, 472, 'ﲎ ﲏ ﲐ ﲑ ﲒﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ', 'ٱلنَّارُ يُعْرَضُونَ عَلَيْهَا غُدُوًّۭا وَعَشِيًّۭا ۖ وَيَوْمَ تَقُومُ ٱلسَّاعَةُ أَدْخِلُوٓا۟ ءَالَ فِرْعَوْنَ أَشَدَّ ٱلْعَذَابِ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 40, 472, 'ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ', 'وَإِذْ يَتَحَآجُّونَ فِى ٱلنَّارِ فَيَقُولُ ٱلضُّعَفَـٰٓؤُا۟ لِلَّذِينَ ٱسْتَكْبَرُوٓا۟ إِنَّا كُنَّا لَكُمْ تَبَعًۭا فَهَلْ أَنتُم مُّغْنُونَ عَنَّا نَصِيبًۭا مِّنَ ٱلنَّارِ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 40, 472, 'ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ', 'قَالَ ٱلَّذِينَ ٱسْتَكْبَرُوٓا۟ إِنَّا كُلٌّۭ فِيهَآ إِنَّ ٱللَّهَ قَدْ حَكَمَ بَيْنَ ٱلْعِبَادِ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 40, 472, 'ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ', 'وَقَالَ ٱلَّذِينَ فِى ٱلنَّارِ لِخَزَنَةِ جَهَنَّمَ ٱدْعُوا۟ رَبَّكُمْ يُخَفِّفْ عَنَّا يَوْمًۭا مِّنَ ٱلْعَذَابِ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 40, 473, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆﱇ ﱈ ﱉﱊ ﱋ ﱌﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ', 'قَالُوٓا۟ أَوَلَمْ تَكُ تَأْتِيكُمْ رُسُلُكُم بِٱلْبَيِّنَـٰتِ ۖ قَالُوا۟ بَلَىٰ ۚ قَالُوا۟ فَٱدْعُوا۟ ۗ وَمَا دُعَـٰٓؤُا۟ ٱلْكَـٰفِرِينَ إِلَّا فِى ضَلَـٰلٍ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 40, 473, 'ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ', 'إِنَّا لَنَنصُرُ رُسُلَنَا وَٱلَّذِينَ ءَامَنُوا۟ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا وَيَوْمَ يَقُومُ ٱلْأَشْهَـٰدُ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 40, 473, 'ﱡ ﱢ ﱣ ﱤ ﱥﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ', 'يَوْمَ لَا يَنفَعُ ٱلظَّـٰلِمِينَ مَعْذِرَتُهُمْ ۖ وَلَهُمُ ٱللَّعْنَةُ وَلَهُمْ سُوٓءُ ٱلدَّارِ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 40, 473, 'ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ', 'وَلَقَدْ ءَاتَيْنَا مُوسَى ٱلْهُدَىٰ وَأَوْرَثْنَا بَنِىٓ إِسْرَٰٓءِيلَ ٱلْكِتَـٰبَ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 40, 473, 'ﱶ ﱷ ﱸ ﱹ ﱺ', 'هُدًۭى وَذِكْرَىٰ لِأُو۟لِى ٱلْأَلْبَـٰبِ ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (55, 40, 473, 'ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ', 'فَٱصْبِرْ إِنَّ وَعْدَ ٱللَّهِ حَقٌّۭ وَٱسْتَغْفِرْ لِذَنۢبِكَ وَسَبِّحْ بِحَمْدِ رَبِّكَ بِٱلْعَشِىِّ وَٱلْإِبْكَـٰرِ ٥٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (56, 40, 473, 'ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘﲙ ﲚ ﲛﲜ ﲝ ﲞ ﲟ ﲠ ﲡ', 'إِنَّ ٱلَّذِينَ يُجَـٰدِلُونَ فِىٓ ءَايَـٰتِ ٱللَّهِ بِغَيْرِ سُلْطَـٰنٍ أَتَىٰهُمْ ۙ إِن فِى صُدُورِهِمْ إِلَّا كِبْرٌۭ مَّا هُم بِبَـٰلِغِيهِ ۚ فَٱسْتَعِذْ بِٱللَّهِ ۖ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلْبَصِيرُ ٥٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (57, 40, 473, 'ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ', 'لَخَلْقُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ أَكْبَرُ مِنْ خَلْقِ ٱلنَّاسِ وَلَـٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَعْلَمُونَ ٥٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (58, 40, 473, 'ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸﲹ ﲺ ﲻ ﲼ ﲽ', 'وَمَا يَسْتَوِى ٱلْأَعْمَىٰ وَٱلْبَصِيرُ وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ وَلَا ٱلْمُسِىٓءُ ۚ قَلِيلًۭا مَّا تَتَذَكَّرُونَ ٥٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (59, 40, 474, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ', 'إِنَّ ٱلسَّاعَةَ لَـَٔاتِيَةٌۭ لَّا رَيْبَ فِيهَا وَلَـٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يُؤْمِنُونَ ٥٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (60, 40, 474, 'ﱍ ﱎ ﱏ ﱐ ﱑﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ', 'وَقَالَ رَبُّكُمُ ٱدْعُونِىٓ أَسْتَجِبْ لَكُمْ ۚ إِنَّ ٱلَّذِينَ يَسْتَكْبِرُونَ عَنْ عِبَادَتِى سَيَدْخُلُونَ جَهَنَّمَ دَاخِرِينَ ٦٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (61, 40, 474, 'ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ', 'ٱللَّهُ ٱلَّذِى جَعَلَ لَكُمُ ٱلَّيْلَ لِتَسْكُنُوا۟ فِيهِ وَٱلنَّهَارَ مُبْصِرًا ۚ إِنَّ ٱللَّهَ لَذُو فَضْلٍ عَلَى ٱلنَّاسِ وَلَـٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَشْكُرُونَ ٦١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (62, 40, 474, 'ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻﱼ ﱽ ﱾ ﱿ', 'ذَٰلِكُمُ ٱللَّهُ رَبُّكُمْ خَـٰلِقُ كُلِّ شَىْءٍۢ لَّآ إِلَـٰهَ إِلَّا هُوَ ۖ فَأَنَّىٰ تُؤْفَكُونَ ٦٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (63, 40, 474, 'ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ', 'كَذَٰلِكَ يُؤْفَكُ ٱلَّذِينَ كَانُوا۟ بِـَٔايَـٰتِ ٱللَّهِ يَجْحَدُونَ ٦٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (64, 40, 474, 'ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕﲖ ﲗ ﲘ ﲙﲚ ﲛ ﲜ ﲝ ﲞ ﲟ', 'ٱللَّهُ ٱلَّذِى جَعَلَ لَكُمُ ٱلْأَرْضَ قَرَارًۭا وَٱلسَّمَآءَ بِنَآءًۭ وَصَوَّرَكُمْ فَأَحْسَنَ صُوَرَكُمْ وَرَزَقَكُم مِّنَ ٱلطَّيِّبَـٰتِ ۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمْ ۖ فَتَبَارَكَ ٱللَّهُ رَبُّ ٱلْعَـٰلَمِينَ ٦٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (65, 40, 474, 'ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩﲪ ﲫ ﲬ ﲭ ﲮ ﲯ', 'هُوَ ٱلْحَىُّ لَآ إِلَـٰهَ إِلَّا هُوَ فَٱدْعُوهُ مُخْلِصِينَ لَهُ ٱلدِّينَ ۗ ٱلْحَمْدُ لِلَّهِ رَبِّ ٱلْعَـٰلَمِينَ ٦٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (66, 40, 474, 'ﲰﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ', '۞ قُلْ إِنِّى نُهِيتُ أَنْ أَعْبُدَ ٱلَّذِينَ تَدْعُونَ مِن دُونِ ٱللَّهِ لَمَّا جَآءَنِىَ ٱلْبَيِّنَـٰتُ مِن رَّبِّى وَأُمِرْتُ أَنْ أُسْلِمَ لِرَبِّ ٱلْعَـٰلَمِينَ ٦٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (67, 40, 475, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔﱕ ﱖ ﱗ ﱘ ﱙ ﱚﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ', 'هُوَ ٱلَّذِى خَلَقَكُم مِّن تُرَابٍۢ ثُمَّ مِن نُّطْفَةٍۢ ثُمَّ مِنْ عَلَقَةٍۢ ثُمَّ يُخْرِجُكُمْ طِفْلًۭا ثُمَّ لِتَبْلُغُوٓا۟ أَشُدَّكُمْ ثُمَّ لِتَكُونُوا۟ شُيُوخًۭا ۚ وَمِنكُم مَّن يُتَوَفَّىٰ مِن قَبْلُ ۖ وَلِتَبْلُغُوٓا۟ أَجَلًۭا مُّسَمًّۭى وَلَعَلَّكُمْ تَعْقِلُونَ ٦٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (68, 40, 475, 'ﱢ ﱣ ﱤ ﱥﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ', 'هُوَ ٱلَّذِى يُحْىِۦ وَيُمِيتُ ۖ فَإِذَا قَضَىٰٓ أَمْرًۭا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ ٦٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (69, 40, 475, 'ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ', 'أَلَمْ تَرَ إِلَى ٱلَّذِينَ يُجَـٰدِلُونَ فِىٓ ءَايَـٰتِ ٱللَّهِ أَنَّىٰ يُصْرَفُونَ ٦٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (70, 40, 475, 'ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁﲂ ﲃ ﲄ ﲅ', 'ٱلَّذِينَ كَذَّبُوا۟ بِٱلْكِتَـٰبِ وَبِمَآ أَرْسَلْنَا بِهِۦ رُسُلَنَا ۖ فَسَوْفَ يَعْلَمُونَ ٧٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (71, 40, 475, 'ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ', 'إِذِ ٱلْأَغْلَـٰلُ فِىٓ أَعْنَـٰقِهِمْ وَٱلسَّلَـٰسِلُ يُسْحَبُونَ ٧١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (72, 40, 475, 'ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ', 'فِى ٱلْحَمِيمِ ثُمَّ فِى ٱلنَّارِ يُسْجَرُونَ ٧٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (73, 40, 475, 'ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ', 'ثُمَّ قِيلَ لَهُمْ أَيْنَ مَا كُنتُمْ تُشْرِكُونَ ٧٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (74, 40, 475, 'ﲜ ﲝ ﲞﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩﲪ ﲫ ﲬ ﲭ ﲮ ﲯ', 'مِن دُونِ ٱللَّهِ ۖ قَالُوا۟ ضَلُّوا۟ عَنَّا بَل لَّمْ نَكُن نَّدْعُوا۟ مِن قَبْلُ شَيْـًۭٔا ۚ كَذَٰلِكَ يُضِلُّ ٱللَّهُ ٱلْكَـٰفِرِينَ ٧٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (75, 40, 475, 'ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ', 'ذَٰلِكُم بِمَا كُنتُمْ تَفْرَحُونَ فِى ٱلْأَرْضِ بِغَيْرِ ٱلْحَقِّ وَبِمَا كُنتُمْ تَمْرَحُونَ ٧٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (76, 40, 475, 'ﲼ ﲽ ﲾ ﲿ ﳀﳁ ﳂ ﳃ ﳄ ﳅ', 'ٱدْخُلُوٓا۟ أَبْوَٰبَ جَهَنَّمَ خَـٰلِدِينَ فِيهَا ۖ فَبِئْسَ مَثْوَى ٱلْمُتَكَبِّرِينَ ٧٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (77, 40, 475, 'ﳆ ﳇ ﳈ ﳉﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ', 'فَٱصْبِرْ إِنَّ وَعْدَ ٱللَّهِ حَقٌّۭ ۚ فَإِمَّا نُرِيَنَّكَ بَعْضَ ٱلَّذِى نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِلَيْنَا يُرْجَعُونَ ٧٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (78, 40, 476, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ', 'وَلَقَدْ أَرْسَلْنَا رُسُلًۭا مِّن قَبْلِكَ مِنْهُم مَّن قَصَصْنَا عَلَيْكَ وَمِنْهُم مَّن لَّمْ نَقْصُصْ عَلَيْكَ ۗ وَمَا كَانَ لِرَسُولٍ أَن يَأْتِىَ بِـَٔايَةٍ إِلَّا بِإِذْنِ ٱللَّهِ ۚ فَإِذَا جَآءَ أَمْرُ ٱللَّهِ قُضِىَ بِٱلْحَقِّ وَخَسِرَ هُنَالِكَ ٱلْمُبْطِلُونَ ٧٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (79, 40, 476, 'ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ', 'ٱللَّهُ ٱلَّذِى جَعَلَ لَكُمُ ٱلْأَنْعَـٰمَ لِتَرْكَبُوا۟ مِنْهَا وَمِنْهَا تَأْكُلُونَ ٧٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (80, 40, 476, 'ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ', 'وَلَكُمْ فِيهَا مَنَـٰفِعُ وَلِتَبْلُغُوا۟ عَلَيْهَا حَاجَةًۭ فِى صُدُورِكُمْ وَعَلَيْهَا وَعَلَى ٱلْفُلْكِ تُحْمَلُونَ ٨٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (81, 40, 476, 'ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ', 'وَيُرِيكُمْ ءَايَـٰتِهِۦ فَأَىَّ ءَايَـٰتِ ٱللَّهِ تُنكِرُونَ ٨١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (82, 40, 476, 'ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ', 'أَفَلَمْ يَسِيرُوا۟ فِى ٱلْأَرْضِ فَيَنظُرُوا۟ كَيْفَ كَانَ عَـٰقِبَةُ ٱلَّذِينَ مِن قَبْلِهِمْ ۚ كَانُوٓا۟ أَكْثَرَ مِنْهُمْ وَأَشَدَّ قُوَّةًۭ وَءَاثَارًۭا فِى ٱلْأَرْضِ فَمَآ أَغْنَىٰ عَنْهُم مَّا كَانُوا۟ يَكْسِبُونَ ٨٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (83, 40, 476, 'ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ', 'فَلَمَّا جَآءَتْهُمْ رُسُلُهُم بِٱلْبَيِّنَـٰتِ فَرِحُوا۟ بِمَا عِندَهُم مِّنَ ٱلْعِلْمِ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ ٨٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (84, 40, 476, 'ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ', 'فَلَمَّا رَأَوْا۟ بَأْسَنَا قَالُوٓا۟ ءَامَنَّا بِٱللَّهِ وَحْدَهُۥ وَكَفَرْنَا بِمَا كُنَّا بِهِۦ مُشْرِكِينَ ٨٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (85, 40, 476, 'ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈﳉ ﳊ ﳋ ﳌ ﳍ', 'فَلَمْ يَكُ يَنفَعُهُمْ إِيمَـٰنُهُمْ لَمَّا رَأَوْا۟ بَأْسَنَا ۖ سُنَّتَ ٱللَّهِ ٱلَّتِى قَدْ خَلَتْ فِى عِبَادِهِۦ ۖ وَخَسِرَ هُنَالِكَ ٱلْكَـٰفِرُونَ ٨٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 41, 477, 'ﱁ ﱂ', 'حمٓ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 41, 477, 'ﱃ ﱄ ﱅ ﱆ ﱇ', 'تَنزِيلٌۭ مِّنَ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 41, 477, 'ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ', 'كِتَـٰبٌۭ فُصِّلَتْ ءَايَـٰتُهُۥ قُرْءَانًا عَرَبِيًّۭا لِّقَوْمٍۢ يَعْلَمُونَ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 41, 477, 'ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ', 'بَشِيرًۭا وَنَذِيرًۭا فَأَعْرَضَ أَكْثَرُهُمْ فَهُمْ لَا يَسْمَعُونَ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 41, 477, 'ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ', 'وَقَالُوا۟ قُلُوبُنَا فِىٓ أَكِنَّةٍۢ مِّمَّا تَدْعُونَآ إِلَيْهِ وَفِىٓ ءَاذَانِنَا وَقْرٌۭ وَمِنۢ بَيْنِنَا وَبَيْنِكَ حِجَابٌۭ فَٱعْمَلْ إِنَّنَا عَـٰمِلُونَ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 41, 477, 'ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷﱸ ﱹ ﱺ ﱻ', 'قُلْ إِنَّمَآ أَنَا۠ بَشَرٌۭ مِّثْلُكُمْ يُوحَىٰٓ إِلَىَّ أَنَّمَآ إِلَـٰهُكُمْ إِلَـٰهٌۭ وَٰحِدٌۭ فَٱسْتَقِيمُوٓا۟ إِلَيْهِ وَٱسْتَغْفِرُوهُ ۗ وَوَيْلٌۭ لِّلْمُشْرِكِينَ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 41, 477, 'ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ', 'ٱلَّذِينَ لَا يُؤْتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلْـَٔاخِرَةِ هُمْ كَـٰفِرُونَ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 41, 477, 'ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ', 'إِنَّ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍۢ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 41, 477, 'ﲏﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚﲛ ﲜ ﲝ ﲞ ﲟ', '۞ قُلْ أَئِنَّكُمْ لَتَكْفُرُونَ بِٱلَّذِى خَلَقَ ٱلْأَرْضَ فِى يَوْمَيْنِ وَتَجْعَلُونَ لَهُۥٓ أَندَادًۭا ۚ ذَٰلِكَ رَبُّ ٱلْعَـٰلَمِينَ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 41, 477, 'ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ', 'وَجَعَلَ فِيهَا رَوَٰسِىَ مِن فَوْقِهَا وَبَـٰرَكَ فِيهَا وَقَدَّرَ فِيهَآ أَقْوَٰتَهَا فِىٓ أَرْبَعَةِ أَيَّامٍۢ سَوَآءًۭ لِّلسَّآئِلِينَ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 41, 477, 'ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ', 'ثُمَّ ٱسْتَوَىٰٓ إِلَى ٱلسَّمَآءِ وَهِىَ دُخَانٌۭ فَقَالَ لَهَا وَلِلْأَرْضِ ٱئْتِيَا طَوْعًا أَوْ كَرْهًۭا قَالَتَآ أَتَيْنَا طَآئِعِينَ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 41, 478, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊﱋ ﱌ ﱍ ﱎ ﱏ ﱐﱑ ﱒ ﱓ ﱔ ﱕ ﱖ', 'فَقَضَىٰهُنَّ سَبْعَ سَمَـٰوَاتٍۢ فِى يَوْمَيْنِ وَأَوْحَىٰ فِى كُلِّ سَمَآءٍ أَمْرَهَا ۚ وَزَيَّنَّا ٱلسَّمَآءَ ٱلدُّنْيَا بِمَصَـٰبِيحَ وَحِفْظًۭا ۚ ذَٰلِكَ تَقْدِيرُ ٱلْعَزِيزِ ٱلْعَلِيمِ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 41, 478, 'ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ', 'فَإِنْ أَعْرَضُوا۟ فَقُلْ أَنذَرْتُكُمْ صَـٰعِقَةًۭ مِّثْلَ صَـٰعِقَةِ عَادٍۢ وَثَمُودَ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 41, 478, 'ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ', 'إِذْ جَآءَتْهُمُ ٱلرُّسُلُ مِنۢ بَيْنِ أَيْدِيهِمْ وَمِنْ خَلْفِهِمْ أَلَّا تَعْبُدُوٓا۟ إِلَّا ٱللَّهَ ۖ قَالُوا۟ لَوْ شَآءَ رَبُّنَا لَأَنزَلَ مَلَـٰٓئِكَةًۭ فَإِنَّا بِمَآ أُرْسِلْتُم بِهِۦ كَـٰفِرُونَ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 41, 478, 'ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐﲑ ﲒ ﲓ ﲔ ﲕ', 'فَأَمَّا عَادٌۭ فَٱسْتَكْبَرُوا۟ فِى ٱلْأَرْضِ بِغَيْرِ ٱلْحَقِّ وَقَالُوا۟ مَنْ أَشَدُّ مِنَّا قُوَّةً ۖ أَوَلَمْ يَرَوْا۟ أَنَّ ٱللَّهَ ٱلَّذِى خَلَقَهُمْ هُوَ أَشَدُّ مِنْهُمْ قُوَّةًۭ ۖ وَكَانُوا۟ بِـَٔايَـٰتِنَا يَجْحَدُونَ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 41, 478, 'ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢﲣ ﲤ ﲥ ﲦﲧ ﲨ ﲩ ﲪ ﲫ', 'فَأَرْسَلْنَا عَلَيْهِمْ رِيحًۭا صَرْصَرًۭا فِىٓ أَيَّامٍۢ نَّحِسَاتٍۢ لِّنُذِيقَهُمْ عَذَابَ ٱلْخِزْىِ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا ۖ وَلَعَذَابُ ٱلْـَٔاخِرَةِ أَخْزَىٰ ۖ وَهُمْ لَا يُنصَرُونَ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 41, 478, 'ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ', 'وَأَمَّا ثَمُودُ فَهَدَيْنَـٰهُمْ فَٱسْتَحَبُّوا۟ ٱلْعَمَىٰ عَلَى ٱلْهُدَىٰ فَأَخَذَتْهُمْ صَـٰعِقَةُ ٱلْعَذَابِ ٱلْهُونِ بِمَا كَانُوا۟ يَكْسِبُونَ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 41, 478, 'ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ', 'وَنَجَّيْنَا ٱلَّذِينَ ءَامَنُوا۟ وَكَانُوا۟ يَتَّقُونَ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 41, 478, 'ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ', 'وَيَوْمَ يُحْشَرُ أَعْدَآءُ ٱللَّهِ إِلَى ٱلنَّارِ فَهُمْ يُوزَعُونَ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 41, 478, 'ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ', 'حَتَّىٰٓ إِذَا مَا جَآءُوهَا شَهِدَ عَلَيْهِمْ سَمْعُهُمْ وَأَبْصَـٰرُهُمْ وَجُلُودُهُم بِمَا كَانُوا۟ يَعْمَلُونَ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 41, 479, 'ﱁ ﱂ ﱃ ﱄ ﱅﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'وَقَالُوا۟ لِجُلُودِهِمْ لِمَ شَهِدتُّمْ عَلَيْنَا ۖ قَالُوٓا۟ أَنطَقَنَا ٱللَّهُ ٱلَّذِىٓ أَنطَقَ كُلَّ شَىْءٍۢ وَهُوَ خَلَقَكُمْ أَوَّلَ مَرَّةٍۢ وَإِلَيْهِ تُرْجَعُونَ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 41, 479, 'ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ', 'وَمَا كُنتُمْ تَسْتَتِرُونَ أَن يَشْهَدَ عَلَيْكُمْ سَمْعُكُمْ وَلَآ أَبْصَـٰرُكُمْ وَلَا جُلُودُكُمْ وَلَـٰكِن ظَنَنتُمْ أَنَّ ٱللَّهَ لَا يَعْلَمُ كَثِيرًۭا مِّمَّا تَعْمَلُونَ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 41, 479, 'ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ', 'وَذَٰلِكُمْ ظَنُّكُمُ ٱلَّذِى ظَنَنتُم بِرَبِّكُمْ أَرْدَىٰكُمْ فَأَصْبَحْتُم مِّنَ ٱلْخَـٰسِرِينَ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 41, 479, 'ﱵ ﱶ ﱷ ﱸ ﱹﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ', 'فَإِن يَصْبِرُوا۟ فَٱلنَّارُ مَثْوًۭى لَّهُمْ ۖ وَإِن يَسْتَعْتِبُوا۟ فَمَا هُم مِّنَ ٱلْمُعْتَبِينَ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 41, 479, 'ﲂﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘﲙ ﲚ ﲛ ﲜ ﲝ', '۞ وَقَيَّضْنَا لَهُمْ قُرَنَآءَ فَزَيَّنُوا۟ لَهُم مَّا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَحَقَّ عَلَيْهِمُ ٱلْقَوْلُ فِىٓ أُمَمٍۢ قَدْ خَلَتْ مِن قَبْلِهِم مِّنَ ٱلْجِنِّ وَٱلْإِنسِ ۖ إِنَّهُمْ كَانُوا۟ خَـٰسِرِينَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 41, 479, 'ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ', 'وَقَالَ ٱلَّذِينَ كَفَرُوا۟ لَا تَسْمَعُوا۟ لِهَـٰذَا ٱلْقُرْءَانِ وَٱلْغَوْا۟ فِيهِ لَعَلَّكُمْ تَغْلِبُونَ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 41, 479, 'ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ', 'فَلَنُذِيقَنَّ ٱلَّذِينَ كَفَرُوا۟ عَذَابًۭا شَدِيدًۭا وَلَنَجْزِيَنَّهُمْ أَسْوَأَ ٱلَّذِى كَانُوا۟ يَعْمَلُونَ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 41, 479, 'ﲵ ﲶ ﲷ ﲸ ﲹﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ', 'ذَٰلِكَ جَزَآءُ أَعْدَآءِ ٱللَّهِ ٱلنَّارُ ۖ لَهُمْ فِيهَا دَارُ ٱلْخُلْدِ ۖ جَزَآءًۢ بِمَا كَانُوا۟ بِـَٔايَـٰتِنَا يَجْحَدُونَ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 41, 479, 'ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ', 'وَقَالَ ٱلَّذِينَ كَفَرُوا۟ رَبَّنَآ أَرِنَا ٱلَّذَيْنِ أَضَلَّانَا مِنَ ٱلْجِنِّ وَٱلْإِنسِ نَجْعَلْهُمَا تَحْتَ أَقْدَامِنَا لِيَكُونَا مِنَ ٱلْأَسْفَلِينَ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 41, 480, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ', 'إِنَّ ٱلَّذِينَ قَالُوا۟ رَبُّنَا ٱللَّهُ ثُمَّ ٱسْتَقَـٰمُوا۟ تَتَنَزَّلُ عَلَيْهِمُ ٱلْمَلَـٰٓئِكَةُ أَلَّا تَخَافُوا۟ وَلَا تَحْزَنُوا۟ وَأَبْشِرُوا۟ بِٱلْجَنَّةِ ٱلَّتِى كُنتُمْ تُوعَدُونَ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 41, 480, 'ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ', 'نَحْنُ أَوْلِيَآؤُكُمْ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا وَفِى ٱلْـَٔاخِرَةِ ۖ وَلَكُمْ فِيهَا مَا تَشْتَهِىٓ أَنفُسُكُمْ وَلَكُمْ فِيهَا مَا تَدَّعُونَ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 41, 480, 'ﱧ ﱨ ﱩ ﱪ ﱫ', 'نُزُلًۭا مِّنْ غَفُورٍۢ رَّحِيمٍۢ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 41, 480, 'ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ', 'وَمَنْ أَحْسَنُ قَوْلًۭا مِّمَّن دَعَآ إِلَى ٱللَّهِ وَعَمِلَ صَـٰلِحًۭا وَقَالَ إِنَّنِى مِنَ ٱلْمُسْلِمِينَ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 41, 480, 'ﱺ ﱻ ﱼ ﱽ ﱾﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ', 'وَلَا تَسْتَوِى ٱلْحَسَنَةُ وَلَا ٱلسَّيِّئَةُ ۚ ٱدْفَعْ بِٱلَّتِى هِىَ أَحْسَنُ فَإِذَا ٱلَّذِى بَيْنَكَ وَبَيْنَهُۥ عَدَٰوَةٌۭ كَأَنَّهُۥ وَلِىٌّ حَمِيمٌۭ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 41, 480, 'ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ', 'وَمَا يُلَقَّىٰهَآ إِلَّا ٱلَّذِينَ صَبَرُوا۟ وَمَا يُلَقَّىٰهَآ إِلَّا ذُو حَظٍّ عَظِيمٍۢ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 41, 480, 'ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟﲠ ﲡ ﲢ ﲣ ﲤ ﲥ', 'وَإِمَّا يَنزَغَنَّكَ مِنَ ٱلشَّيْطَـٰنِ نَزْغٌۭ فَٱسْتَعِذْ بِٱللَّهِ ۖ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلْعَلِيمُ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 41, 480, 'ﲦ ﲧ ﲨ ﲩ ﲪ ﲫﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲﲳ ﲴﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ', 'وَمِنْ ءَايَـٰتِهِ ٱلَّيْلُ وَٱلنَّهَارُ وَٱلشَّمْسُ وَٱلْقَمَرُ ۚ لَا تَسْجُدُوا۟ لِلشَّمْسِ وَلَا لِلْقَمَرِ وَٱسْجُدُوا۟ لِلَّهِ ٱلَّذِى خَلَقَهُنَّ إِن كُنتُمْ إِيَّاهُ تَعْبُدُونَ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 41, 480, 'ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈﳉ ﳊ', 'فَإِنِ ٱسْتَكْبَرُوا۟ فَٱلَّذِينَ عِندَ رَبِّكَ يُسَبِّحُونَ لَهُۥ بِٱلَّيْلِ وَٱلنَّهَارِ وَهُمْ لَا يَسْـَٔمُونَ ۩ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 41, 481, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌﱍ ﱎ ﱏ ﱐ ﱑ ﱒﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ', 'وَمِنْ ءَايَـٰتِهِۦٓ أَنَّكَ تَرَى ٱلْأَرْضَ خَـٰشِعَةًۭ فَإِذَآ أَنزَلْنَا عَلَيْهَا ٱلْمَآءَ ٱهْتَزَّتْ وَرَبَتْ ۚ إِنَّ ٱلَّذِىٓ أَحْيَاهَا لَمُحْىِ ٱلْمَوْتَىٰٓ ۚ إِنَّهُۥ عَلَىٰ كُلِّ شَىْءٍۢ قَدِيرٌ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 41, 481, 'ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ', 'إِنَّ ٱلَّذِينَ يُلْحِدُونَ فِىٓ ءَايَـٰتِنَا لَا يَخْفَوْنَ عَلَيْنَآ ۗ أَفَمَن يُلْقَىٰ فِى ٱلنَّارِ خَيْرٌ أَم مَّن يَأْتِىٓ ءَامِنًۭا يَوْمَ ٱلْقِيَـٰمَةِ ۚ ٱعْمَلُوا۟ مَا شِئْتُمْ ۖ إِنَّهُۥ بِمَا تَعْمَلُونَ بَصِيرٌ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 41, 481, 'ﱷ ﱸ ﱹ ﱺ ﱻ ﱼﱽ ﱾ ﱿ ﲀ ﲁ', 'إِنَّ ٱلَّذِينَ كَفَرُوا۟ بِٱلذِّكْرِ لَمَّا جَآءَهُمْ ۖ وَإِنَّهُۥ لَكِتَـٰبٌ عَزِيزٌۭ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 41, 481, 'ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊﲋ ﲌ ﲍ ﲎ ﲏ ﲐ', 'لَّا يَأْتِيهِ ٱلْبَـٰطِلُ مِنۢ بَيْنِ يَدَيْهِ وَلَا مِنْ خَلْفِهِۦ ۖ تَنزِيلٌۭ مِّنْ حَكِيمٍ حَمِيدٍۢ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 41, 481, 'ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ', 'مَّا يُقَالُ لَكَ إِلَّا مَا قَدْ قِيلَ لِلرُّسُلِ مِن قَبْلِكَ ۚ إِنَّ رَبَّكَ لَذُو مَغْفِرَةٍۢ وَذُو عِقَابٍ أَلِيمٍۢ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 41, 481, 'ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫﲬ ﲭ ﲮﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ', 'وَلَوْ جَعَلْنَـٰهُ قُرْءَانًا أَعْجَمِيًّۭا لَّقَالُوا۟ لَوْلَا فُصِّلَتْ ءَايَـٰتُهُۥٓ ۖ ءَا۬عْجَمِىٌّۭ وَعَرَبِىٌّۭ ۗ قُلْ هُوَ لِلَّذِينَ ءَامَنُوا۟ هُدًۭى وَشِفَآءٌۭ ۖ وَٱلَّذِينَ لَا يُؤْمِنُونَ فِىٓ ءَاذَانِهِمْ وَقْرٌۭ وَهُوَ عَلَيْهِمْ عَمًى ۚ أُو۟لَـٰٓئِكَ يُنَادَوْنَ مِن مَّكَانٍۭ بَعِيدٍۢ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 41, 481, 'ﳇ ﳈ ﳉ ﳊ ﳋ ﳌﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ', 'وَلَقَدْ ءَاتَيْنَا مُوسَى ٱلْكِتَـٰبَ فَٱخْتُلِفَ فِيهِ ۗ وَلَوْلَا كَلِمَةٌۭ سَبَقَتْ مِن رَّبِّكَ لَقُضِىَ بَيْنَهُمْ ۚ وَإِنَّهُمْ لَفِى شَكٍّۢ مِّنْهُ مُرِيبٍۢ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 41, 481, 'ﳜ ﳝ ﳞ ﳟﳠ ﳡ ﳢ ﳣﳤ ﳥ ﳦ ﳧ ﳨ ﳩ', 'مَّنْ عَمِلَ صَـٰلِحًۭا فَلِنَفْسِهِۦ ۖ وَمَنْ أَسَآءَ فَعَلَيْهَا ۗ وَمَا رَبُّكَ بِظَلَّـٰمٍۢ لِّلْعَبِيدِ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 41, 482, 'ﱁﱂ ﱃ ﱄ ﱅﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ', '۞ إِلَيْهِ يُرَدُّ عِلْمُ ٱلسَّاعَةِ ۚ وَمَا تَخْرُجُ مِن ثَمَرَٰتٍۢ مِّنْ أَكْمَامِهَا وَمَا تَحْمِلُ مِنْ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلْمِهِۦ ۚ وَيَوْمَ يُنَادِيهِمْ أَيْنَ شُرَكَآءِى قَالُوٓا۟ ءَاذَنَّـٰكَ مَا مِنَّا مِن شَهِيدٍۢ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 41, 482, 'ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ', 'وَضَلَّ عَنْهُم مَّا كَانُوا۟ يَدْعُونَ مِن قَبْلُ ۖ وَظَنُّوا۟ مَا لَهُم مِّن مَّحِيصٍۢ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 41, 482, 'ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ', 'لَّا يَسْـَٔمُ ٱلْإِنسَـٰنُ مِن دُعَآءِ ٱلْخَيْرِ وَإِن مَّسَّهُ ٱلشَّرُّ فَيَـُٔوسٌۭ قَنُوطٌۭ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 41, 482, 'ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ', 'وَلَئِنْ أَذَقْنَـٰهُ رَحْمَةًۭ مِّنَّا مِنۢ بَعْدِ ضَرَّآءَ مَسَّتْهُ لَيَقُولَنَّ هَـٰذَا لِى وَمَآ أَظُنُّ ٱلسَّاعَةَ قَآئِمَةًۭ وَلَئِن رُّجِعْتُ إِلَىٰ رَبِّىٓ إِنَّ لِى عِندَهُۥ لَلْحُسْنَىٰ ۚ فَلَنُنَبِّئَنَّ ٱلَّذِينَ كَفَرُوا۟ بِمَا عَمِلُوا۟ وَلَنُذِيقَنَّهُم مِّنْ عَذَابٍ غَلِيظٍۢ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 41, 482, 'ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ', 'وَإِذَآ أَنْعَمْنَا عَلَى ٱلْإِنسَـٰنِ أَعْرَضَ وَنَـَٔا بِجَانِبِهِۦ وَإِذَا مَسَّهُ ٱلشَّرُّ فَذُو دُعَآءٍ عَرِيضٍۢ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 41, 482, 'ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ', 'قُلْ أَرَءَيْتُمْ إِن كَانَ مِنْ عِندِ ٱللَّهِ ثُمَّ كَفَرْتُم بِهِۦ مَنْ أَضَلُّ مِمَّنْ هُوَ فِى شِقَاقٍۭ بَعِيدٍۢ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 41, 482, 'ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ', 'سَنُرِيهِمْ ءَايَـٰتِنَا فِى ٱلْـَٔافَاقِ وَفِىٓ أَنفُسِهِمْ حَتَّىٰ يَتَبَيَّنَ لَهُمْ أَنَّهُ ٱلْحَقُّ ۗ أَوَلَمْ يَكْفِ بِرَبِّكَ أَنَّهُۥ عَلَىٰ كُلِّ شَىْءٍۢ شَهِيدٌ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 41, 482, 'ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘﳙ ﳚ ﳛ ﳜ ﳝ ﳞ ﳟ', 'أَلَآ إِنَّهُمْ فِى مِرْيَةٍۢ مِّن لِّقَآءِ رَبِّهِمْ ۗ أَلَآ إِنَّهُۥ بِكُلِّ شَىْءٍۢ مُّحِيطٌۢ ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 42, 483, 'ﱁ ﱂ', 'حمٓ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 42, 483, 'ﱃ ﱄ', 'عٓسٓقٓ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 42, 483, 'ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ', 'كَذَٰلِكَ يُوحِىٓ إِلَيْكَ وَإِلَى ٱلَّذِينَ مِن قَبْلِكَ ٱللَّهُ ٱلْعَزِيزُ ٱلْحَكِيمُ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 42, 483, 'ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖﱗ ﱘ ﱙ ﱚ ﱛ', 'لَهُۥ مَا فِى ٱلسَّمَـٰوَٰتِ وَمَا فِى ٱلْأَرْضِ ۖ وَهُوَ ٱلْعَلِىُّ ٱلْعَظِيمُ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 42, 483, 'ﱜ ﱝ ﱞ ﱟ ﱠﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ', 'تَكَادُ ٱلسَّمَـٰوَٰتُ يَتَفَطَّرْنَ مِن فَوْقِهِنَّ ۚ وَٱلْمَلَـٰٓئِكَةُ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَيَسْتَغْفِرُونَ لِمَن فِى ٱلْأَرْضِ ۗ أَلَآ إِنَّ ٱللَّهَ هُوَ ٱلْغَفُورُ ٱلرَّحِيمُ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 42, 483, 'ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ', 'وَٱلَّذِينَ ٱتَّخَذُوا۟ مِن دُونِهِۦٓ أَوْلِيَآءَ ٱللَّهُ حَفِيظٌ عَلَيْهِمْ وَمَآ أَنتَ عَلَيْهِم بِوَكِيلٍۢ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 42, 483, 'ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ', 'وَكَذَٰلِكَ أَوْحَيْنَآ إِلَيْكَ قُرْءَانًا عَرَبِيًّۭا لِّتُنذِرَ أُمَّ ٱلْقُرَىٰ وَمَنْ حَوْلَهَا وَتُنذِرَ يَوْمَ ٱلْجَمْعِ لَا رَيْبَ فِيهِ ۚ فَرِيقٌۭ فِى ٱلْجَنَّةِ وَفَرِيقٌۭ فِى ٱلسَّعِيرِ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 42, 483, 'ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ', 'وَلَوْ شَآءَ ٱللَّهُ لَجَعَلَهُمْ أُمَّةًۭ وَٰحِدَةًۭ وَلَـٰكِن يُدْخِلُ مَن يَشَآءُ فِى رَحْمَتِهِۦ ۚ وَٱلظَّـٰلِمُونَ مَا لَهُم مِّن وَلِىٍّۢ وَلَا نَصِيرٍ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 42, 483, 'ﲬ ﲭ ﲮ ﲯ ﲰﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ', 'أَمِ ٱتَّخَذُوا۟ مِن دُونِهِۦٓ أَوْلِيَآءَ ۖ فَٱللَّهُ هُوَ ٱلْوَلِىُّ وَهُوَ يُحْىِ ٱلْمَوْتَىٰ وَهُوَ عَلَىٰ كُلِّ شَىْءٍۢ قَدِيرٌۭ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 42, 483, 'ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ', 'وَمَا ٱخْتَلَفْتُمْ فِيهِ مِن شَىْءٍۢ فَحُكْمُهُۥٓ إِلَى ٱللَّهِ ۚ ذَٰلِكُمُ ٱللَّهُ رَبِّى عَلَيْهِ تَوَكَّلْتُ وَإِلَيْهِ أُنِيبُ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 42, 484, 'ﱁ ﱂ ﱃﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎﱏ ﱐ ﱑ ﱒﱓ ﱔ ﱕ ﱖ ﱗ', 'فَاطِرُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۚ جَعَلَ لَكُم مِّنْ أَنفُسِكُمْ أَزْوَٰجًۭا وَمِنَ ٱلْأَنْعَـٰمِ أَزْوَٰجًۭا ۖ يَذْرَؤُكُمْ فِيهِ ۚ لَيْسَ كَمِثْلِهِۦ شَىْءٌۭ ۖ وَهُوَ ٱلسَّمِيعُ ٱلْبَصِيرُ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 42, 484, 'ﱘ ﱙ ﱚ ﱛﱜ ﱝ ﱞ ﱟ ﱠ ﱡﱢ ﱣ ﱤ ﱥ ﱦ ﱧ', 'لَهُۥ مَقَالِيدُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۖ يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ وَيَقْدِرُ ۚ إِنَّهُۥ بِكُلِّ شَىْءٍ عَلِيمٌۭ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 42, 484, 'ﱨﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ', '۞ شَرَعَ لَكُم مِّنَ ٱلدِّينِ مَا وَصَّىٰ بِهِۦ نُوحًۭا وَٱلَّذِىٓ أَوْحَيْنَآ إِلَيْكَ وَمَا وَصَّيْنَا بِهِۦٓ إِبْرَٰهِيمَ وَمُوسَىٰ وَعِيسَىٰٓ ۖ أَنْ أَقِيمُوا۟ ٱلدِّينَ وَلَا تَتَفَرَّقُوا۟ فِيهِ ۚ كَبُرَ عَلَى ٱلْمُشْرِكِينَ مَا تَدْعُوهُمْ إِلَيْهِ ۚ ٱللَّهُ يَجْتَبِىٓ إِلَيْهِ مَن يَشَآءُ وَيَهْدِىٓ إِلَيْهِ مَن يُنِيبُ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 42, 484, 'ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ', 'وَمَا تَفَرَّقُوٓا۟ إِلَّا مِنۢ بَعْدِ مَا جَآءَهُمُ ٱلْعِلْمُ بَغْيًۢا بَيْنَهُمْ ۚ وَلَوْلَا كَلِمَةٌۭ سَبَقَتْ مِن رَّبِّكَ إِلَىٰٓ أَجَلٍۢ مُّسَمًّۭى لَّقُضِىَ بَيْنَهُمْ ۚ وَإِنَّ ٱلَّذِينَ أُورِثُوا۟ ٱلْكِتَـٰبَ مِنۢ بَعْدِهِمْ لَفِى شَكٍّۢ مِّنْهُ مُرِيبٍۢ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 42, 484, 'ﲴ ﲵﲶ ﲷ ﲸ ﲹﲺ ﲻ ﲼ ﲽﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅﳆ ﳇ ﳈ ﳉﳊ ﳋ ﳌ ﳍﳎ ﳏ ﳐ ﳑ ﳒﳓ ﳔ ﳕ ﳖ ﳗﳘ ﳙ ﳚ ﳛﳜ ﳝ ﳞ ﳟ', 'فَلِذَٰلِكَ فَٱدْعُ ۖ وَٱسْتَقِمْ كَمَآ أُمِرْتَ ۖ وَلَا تَتَّبِعْ أَهْوَآءَهُمْ ۖ وَقُلْ ءَامَنتُ بِمَآ أَنزَلَ ٱللَّهُ مِن كِتَـٰبٍۢ ۖ وَأُمِرْتُ لِأَعْدِلَ بَيْنَكُمُ ۖ ٱللَّهُ رَبُّنَا وَرَبُّكُمْ ۖ لَنَآ أَعْمَـٰلُنَا وَلَكُمْ أَعْمَـٰلُكُمْ ۖ لَا حُجَّةَ بَيْنَنَا وَبَيْنَكُمُ ۖ ٱللَّهُ يَجْمَعُ بَيْنَنَا ۖ وَإِلَيْهِ ٱلْمَصِيرُ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 42, 485, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ', 'وَٱلَّذِينَ يُحَآجُّونَ فِى ٱللَّهِ مِنۢ بَعْدِ مَا ٱسْتُجِيبَ لَهُۥ حُجَّتُهُمْ دَاحِضَةٌ عِندَ رَبِّهِمْ وَعَلَيْهِمْ غَضَبٌۭ وَلَهُمْ عَذَابٌۭ شَدِيدٌ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 42, 485, 'ﱔ ﱕ ﱖ ﱗ ﱘ ﱙﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ', 'ٱللَّهُ ٱلَّذِىٓ أَنزَلَ ٱلْكِتَـٰبَ بِٱلْحَقِّ وَٱلْمِيزَانَ ۗ وَمَا يُدْرِيكَ لَعَلَّ ٱلسَّاعَةَ قَرِيبٌۭ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 42, 485, 'ﱡ ﱢ ﱣ ﱤ ﱥ ﱦﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ', 'يَسْتَعْجِلُ بِهَا ٱلَّذِينَ لَا يُؤْمِنُونَ بِهَا ۖ وَٱلَّذِينَ ءَامَنُوا۟ مُشْفِقُونَ مِنْهَا وَيَعْلَمُونَ أَنَّهَا ٱلْحَقُّ ۗ أَلَآ إِنَّ ٱلَّذِينَ يُمَارُونَ فِى ٱلسَّاعَةِ لَفِى ضَلَـٰلٍۭ بَعِيدٍ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 42, 485, 'ﱺ ﱻ ﱼ ﱽ ﱾ ﱿﲀ ﲁ ﲂ ﲃ ﲄ', 'ٱللَّهُ لَطِيفٌۢ بِعِبَادِهِۦ يَرْزُقُ مَن يَشَآءُ ۖ وَهُوَ ٱلْقَوِىُّ ٱلْعَزِيزُ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 42, 485, 'ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ', 'مَن كَانَ يُرِيدُ حَرْثَ ٱلْـَٔاخِرَةِ نَزِدْ لَهُۥ فِى حَرْثِهِۦ ۖ وَمَن كَانَ يُرِيدُ حَرْثَ ٱلدُّنْيَا نُؤْتِهِۦ مِنْهَا وَمَا لَهُۥ فِى ٱلْـَٔاخِرَةِ مِن نَّصِيبٍ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 42, 485, 'ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨﲩ ﲪ ﲫ ﲬ ﲭ ﲮﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ', 'أَمْ لَهُمْ شُرَكَـٰٓؤُا۟ شَرَعُوا۟ لَهُم مِّنَ ٱلدِّينِ مَا لَمْ يَأْذَنۢ بِهِ ٱللَّهُ ۚ وَلَوْلَا كَلِمَةُ ٱلْفَصْلِ لَقُضِىَ بَيْنَهُمْ ۗ وَإِنَّ ٱلظَّـٰلِمِينَ لَهُمْ عَذَابٌ أَلِيمٌۭ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 42, 485, 'ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅﳆ ﳇ ﳈ ﳉ ﳊ ﳋﳌ ﳍ ﳎ ﳏ ﳐ ﳑ', 'تَرَى ٱلظَّـٰلِمِينَ مُشْفِقِينَ مِمَّا كَسَبُوا۟ وَهُوَ وَاقِعٌۢ بِهِمْ ۗ وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ فِى رَوْضَاتِ ٱلْجَنَّاتِ ۖ لَهُم مَّا يَشَآءُونَ عِندَ رَبِّهِمْ ۚ ذَٰلِكَ هُوَ ٱلْفَضْلُ ٱلْكَبِيرُ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 42, 486, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛﱜ ﱝ ﱞ ﱟ ﱠ ﱡ', 'ذَٰلِكَ ٱلَّذِى يُبَشِّرُ ٱللَّهُ عِبَادَهُ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ ۗ قُل لَّآ أَسْـَٔلُكُمْ عَلَيْهِ أَجْرًا إِلَّا ٱلْمَوَدَّةَ فِى ٱلْقُرْبَىٰ ۗ وَمَن يَقْتَرِفْ حَسَنَةًۭ نَّزِدْ لَهُۥ فِيهَا حُسْنًا ۚ إِنَّ ٱللَّهَ غَفُورٌۭ شَكُورٌ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 42, 486, 'ﱢ ﱣ ﱤ ﱥ ﱦ ﱧﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵﱶ ﱷ ﱸ ﱹ ﱺ ﱻ', 'أَمْ يَقُولُونَ ٱفْتَرَىٰ عَلَى ٱللَّهِ كَذِبًۭا ۖ فَإِن يَشَإِ ٱللَّهُ يَخْتِمْ عَلَىٰ قَلْبِكَ ۗ وَيَمْحُ ٱللَّهُ ٱلْبَـٰطِلَ وَيُحِقُّ ٱلْحَقَّ بِكَلِمَـٰتِهِۦٓ ۚ إِنَّهُۥ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 42, 486, 'ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ', 'وَهُوَ ٱلَّذِى يَقْبَلُ ٱلتَّوْبَةَ عَنْ عِبَادِهِۦ وَيَعْفُوا۟ عَنِ ٱلسَّيِّـَٔاتِ وَيَعْلَمُ مَا تَفْعَلُونَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 42, 486, 'ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐﲑ ﲒ ﲓ ﲔ ﲕ ﲖ', 'وَيَسْتَجِيبُ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ وَيَزِيدُهُم مِّن فَضْلِهِۦ ۚ وَٱلْكَـٰفِرُونَ لَهُمْ عَذَابٌۭ شَدِيدٌۭ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 42, 486, 'ﲗﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤﲥ ﲦ ﲧ ﲨ ﲩ ﲪ', '۞ وَلَوْ بَسَطَ ٱللَّهُ ٱلرِّزْقَ لِعِبَادِهِۦ لَبَغَوْا۟ فِى ٱلْأَرْضِ وَلَـٰكِن يُنَزِّلُ بِقَدَرٍۢ مَّا يَشَآءُ ۚ إِنَّهُۥ بِعِبَادِهِۦ خَبِيرٌۢ بَصِيرٌۭ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 42, 486, 'ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴﲵ ﲶ ﲷ ﲸ ﲹ', 'وَهُوَ ٱلَّذِى يُنَزِّلُ ٱلْغَيْثَ مِنۢ بَعْدِ مَا قَنَطُوا۟ وَيَنشُرُ رَحْمَتَهُۥ ۚ وَهُوَ ٱلْوَلِىُّ ٱلْحَمِيدُ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 42, 486, 'ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ', 'وَمِنْ ءَايَـٰتِهِۦ خَلْقُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ وَمَا بَثَّ فِيهِمَا مِن دَآبَّةٍۢ ۚ وَهُوَ عَلَىٰ جَمْعِهِمْ إِذَا يَشَآءُ قَدِيرٌۭ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 42, 486, 'ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ', 'وَمَآ أَصَـٰبَكُم مِّن مُّصِيبَةٍۢ فَبِمَا كَسَبَتْ أَيْدِيكُمْ وَيَعْفُوا۟ عَن كَثِيرٍۢ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 42, 486, 'ﳗ ﳘ ﳙ ﳚ ﳛﳜ ﳝ ﳞ ﳟ ﳠ ﳡ ﳢ ﳣ ﳤ ﳥ ﳦ', 'وَمَآ أَنتُم بِمُعْجِزِينَ فِى ٱلْأَرْضِ ۖ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِىٍّۢ وَلَا نَصِيرٍۢ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 42, 487, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ', 'وَمِنْ ءَايَـٰتِهِ ٱلْجَوَارِ فِى ٱلْبَحْرِ كَٱلْأَعْلَـٰمِ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 42, 487, 'ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ', 'إِن يَشَأْ يُسْكِنِ ٱلرِّيحَ فَيَظْلَلْنَ رَوَاكِدَ عَلَىٰ ظَهْرِهِۦٓ ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَـٰتٍۢ لِّكُلِّ صَبَّارٍۢ شَكُورٍ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 42, 487, 'ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ', 'أَوْ يُوبِقْهُنَّ بِمَا كَسَبُوا۟ وَيَعْفُ عَن كَثِيرٍۢ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 42, 487, 'ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ', 'وَيَعْلَمَ ٱلَّذِينَ يُجَـٰدِلُونَ فِىٓ ءَايَـٰتِنَا مَا لَهُم مِّن مَّحِيصٍۢ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 42, 487, 'ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ', 'فَمَآ أُوتِيتُم مِّن شَىْءٍۢ فَمَتَـٰعُ ٱلْحَيَوٰةِ ٱلدُّنْيَا ۖ وَمَا عِندَ ٱللَّهِ خَيْرٌۭ وَأَبْقَىٰ لِلَّذِينَ ءَامَنُوا۟ وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 42, 487, 'ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ', 'وَٱلَّذِينَ يَجْتَنِبُونَ كَبَـٰٓئِرَ ٱلْإِثْمِ وَٱلْفَوَٰحِشَ وَإِذَا مَا غَضِبُوا۟ هُمْ يَغْفِرُونَ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 42, 487, 'ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ', 'وَٱلَّذِينَ ٱسْتَجَابُوا۟ لِرَبِّهِمْ وَأَقَامُوا۟ ٱلصَّلَوٰةَ وَأَمْرُهُمْ شُورَىٰ بَيْنَهُمْ وَمِمَّا رَزَقْنَـٰهُمْ يُنفِقُونَ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 42, 487, 'ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ', 'وَٱلَّذِينَ إِذَآ أَصَابَهُمُ ٱلْبَغْىُ هُمْ يَنتَصِرُونَ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 42, 487, 'ﲜ ﲝ ﲞ ﲟﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦﲧ ﲨ ﲩ ﲪ ﲫ ﲬ', 'وَجَزَٰٓؤُا۟ سَيِّئَةٍۢ سَيِّئَةٌۭ مِّثْلُهَا ۖ فَمَنْ عَفَا وَأَصْلَحَ فَأَجْرُهُۥ عَلَى ٱللَّهِ ۚ إِنَّهُۥ لَا يُحِبُّ ٱلظَّـٰلِمِينَ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 42, 487, 'ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ', 'وَلَمَنِ ٱنتَصَرَ بَعْدَ ظُلْمِهِۦ فَأُو۟لَـٰٓئِكَ مَا عَلَيْهِم مِّن سَبِيلٍ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 42, 487, 'ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁﳂ ﳃ ﳄ ﳅ ﳆ ﳇ', 'إِنَّمَا ٱلسَّبِيلُ عَلَى ٱلَّذِينَ يَظْلِمُونَ ٱلنَّاسَ وَيَبْغُونَ فِى ٱلْأَرْضِ بِغَيْرِ ٱلْحَقِّ ۚ أُو۟لَـٰٓئِكَ لَهُمْ عَذَابٌ أَلِيمٌۭ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 42, 487, 'ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ', 'وَلَمَن صَبَرَ وَغَفَرَ إِنَّ ذَٰلِكَ لَمِنْ عَزْمِ ٱلْأُمُورِ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 42, 487, 'ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ ﳡ ﳢ ﳣ ﳤ ﳥ ﳦ', 'وَمَن يُضْلِلِ ٱللَّهُ فَمَا لَهُۥ مِن وَلِىٍّۢ مِّنۢ بَعْدِهِۦ ۗ وَتَرَى ٱلظَّـٰلِمِينَ لَمَّا رَأَوُا۟ ٱلْعَذَابَ يَقُولُونَ هَلْ إِلَىٰ مَرَدٍّۢ مِّن سَبِيلٍۢ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 42, 488, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ', 'وَتَرَىٰهُمْ يُعْرَضُونَ عَلَيْهَا خَـٰشِعِينَ مِنَ ٱلذُّلِّ يَنظُرُونَ مِن طَرْفٍ خَفِىٍّۢ ۗ وَقَالَ ٱلَّذِينَ ءَامَنُوٓا۟ إِنَّ ٱلْخَـٰسِرِينَ ٱلَّذِينَ خَسِرُوٓا۟ أَنفُسَهُمْ وَأَهْلِيهِمْ يَوْمَ ٱلْقِيَـٰمَةِ ۗ أَلَآ إِنَّ ٱلظَّـٰلِمِينَ فِى عَذَابٍۢ مُّقِيمٍۢ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 42, 488, 'ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ', 'وَمَا كَانَ لَهُم مِّنْ أَوْلِيَآءَ يَنصُرُونَهُم مِّن دُونِ ٱللَّهِ ۗ وَمَن يُضْلِلِ ٱللَّهُ فَمَا لَهُۥ مِن سَبِيلٍ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 42, 488, 'ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ', 'ٱسْتَجِيبُوا۟ لِرَبِّكُم مِّن قَبْلِ أَن يَأْتِىَ يَوْمٌۭ لَّا مَرَدَّ لَهُۥ مِنَ ٱللَّهِ ۚ مَا لَكُم مِّن مَّلْجَإٍۢ يَوْمَئِذٍۢ وَمَا لَكُم مِّن نَّكِيرٍۢ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 42, 488, 'ﲈ ﲉ ﲊ ﲋ ﲌ ﲍﲎ ﲏ ﲐ ﲑ ﲒﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ', 'فَإِنْ أَعْرَضُوا۟ فَمَآ أَرْسَلْنَـٰكَ عَلَيْهِمْ حَفِيظًا ۖ إِنْ عَلَيْكَ إِلَّا ٱلْبَلَـٰغُ ۗ وَإِنَّآ إِذَآ أَذَقْنَا ٱلْإِنسَـٰنَ مِنَّا رَحْمَةًۭ فَرِحَ بِهَا ۖ وَإِن تُصِبْهُمْ سَيِّئَةٌۢ بِمَا قَدَّمَتْ أَيْدِيهِمْ فَإِنَّ ٱلْإِنسَـٰنَ كَفُورٌۭ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 42, 488, 'ﲧ ﲨ ﲩ ﲪﲫ ﲬ ﲭ ﲮﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ', 'لِّلَّهِ مُلْكُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۚ يَخْلُقُ مَا يَشَآءُ ۚ يَهَبُ لِمَن يَشَآءُ إِنَـٰثًۭا وَيَهَبُ لِمَن يَشَآءُ ٱلذُّكُورَ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 42, 488, 'ﲹ ﲺ ﲻ ﲼﲽ ﲾ ﲿ ﳀ ﳁﳂ ﳃ ﳄ ﳅ ﳆ', 'أَوْ يُزَوِّجُهُمْ ذُكْرَانًۭا وَإِنَـٰثًۭا ۖ وَيَجْعَلُ مَن يَشَآءُ عَقِيمًا ۚ إِنَّهُۥ عَلِيمٌۭ قَدِيرٌۭ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 42, 488, 'ﳇﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚﳛ ﳜ ﳝ ﳞ ﳟ', '۞ وَمَا كَانَ لِبَشَرٍ أَن يُكَلِّمَهُ ٱللَّهُ إِلَّا وَحْيًا أَوْ مِن وَرَآئِ حِجَابٍ أَوْ يُرْسِلَ رَسُولًۭا فَيُوحِىَ بِإِذْنِهِۦ مَا يَشَآءُ ۚ إِنَّهُۥ عَلِىٌّ حَكِيمٌۭ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 42, 489, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ', 'وَكَذَٰلِكَ أَوْحَيْنَآ إِلَيْكَ رُوحًۭا مِّنْ أَمْرِنَا ۚ مَا كُنتَ تَدْرِى مَا ٱلْكِتَـٰبُ وَلَا ٱلْإِيمَـٰنُ وَلَـٰكِن جَعَلْنَـٰهُ نُورًۭا نَّهْدِى بِهِۦ مَن نَّشَآءُ مِنْ عِبَادِنَا ۚ وَإِنَّكَ لَتَهْدِىٓ إِلَىٰ صِرَٰطٍۢ مُّسْتَقِيمٍۢ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 42, 489, 'ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ', 'صِرَٰطِ ٱللَّهِ ٱلَّذِى لَهُۥ مَا فِى ٱلسَّمَـٰوَٰتِ وَمَا فِى ٱلْأَرْضِ ۗ أَلَآ إِلَى ٱللَّهِ تَصِيرُ ٱلْأُمُورُ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 43, 489, 'ﱰ ﱱ', 'حمٓ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 43, 489, 'ﱲ ﱳ ﱴ', 'وَٱلْكِتَـٰبِ ٱلْمُبِينِ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 43, 489, 'ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ', 'إِنَّا جَعَلْنَـٰهُ قُرْءَٰنًا عَرَبِيًّۭا لَّعَلَّكُمْ تَعْقِلُونَ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 43, 489, 'ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ', 'وَإِنَّهُۥ فِىٓ أُمِّ ٱلْكِتَـٰبِ لَدَيْنَا لَعَلِىٌّ حَكِيمٌ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 43, 489, 'ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ', 'أَفَنَضْرِبُ عَنكُمُ ٱلذِّكْرَ صَفْحًا أَن كُنتُمْ قَوْمًۭا مُّسْرِفِينَ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 43, 489, 'ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ', 'وَكَمْ أَرْسَلْنَا مِن نَّبِىٍّۢ فِى ٱلْأَوَّلِينَ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 43, 489, 'ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ', 'وَمَا يَأْتِيهِم مِّن نَّبِىٍّ إِلَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 43, 489, 'ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ', 'فَأَهْلَكْنَآ أَشَدَّ مِنْهُم بَطْشًۭا وَمَضَىٰ مَثَلُ ٱلْأَوَّلِينَ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 43, 489, 'ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ', 'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ لَيَقُولُنَّ خَلَقَهُنَّ ٱلْعَزِيزُ ٱلْعَلِيمُ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 43, 489, 'ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ', 'ٱلَّذِى جَعَلَ لَكُمُ ٱلْأَرْضَ مَهْدًۭا وَجَعَلَ لَكُمْ فِيهَا سُبُلًۭا لَّعَلَّكُمْ تَهْتَدُونَ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 43, 490, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊﱋ ﱌ ﱍ ﱎ', 'وَٱلَّذِى نَزَّلَ مِنَ ٱلسَّمَآءِ مَآءًۢ بِقَدَرٍۢ فَأَنشَرْنَا بِهِۦ بَلْدَةًۭ مَّيْتًۭا ۚ كَذَٰلِكَ تُخْرَجُونَ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 43, 490, 'ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ', 'وَٱلَّذِى خَلَقَ ٱلْأَزْوَٰجَ كُلَّهَا وَجَعَلَ لَكُم مِّنَ ٱلْفُلْكِ وَٱلْأَنْعَـٰمِ مَا تَرْكَبُونَ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 43, 490, 'ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ', 'لِتَسْتَوُۥا۟ عَلَىٰ ظُهُورِهِۦ ثُمَّ تَذْكُرُوا۟ نِعْمَةَ رَبِّكُمْ إِذَا ٱسْتَوَيْتُمْ عَلَيْهِ وَتَقُولُوا۟ سُبْحَـٰنَ ٱلَّذِى سَخَّرَ لَنَا هَـٰذَا وَمَا كُنَّا لَهُۥ مُقْرِنِينَ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 43, 490, 'ﱰ ﱱ ﱲ ﱳ ﱴ', 'وَإِنَّآ إِلَىٰ رَبِّنَا لَمُنقَلِبُونَ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 43, 490, 'ﱵ ﱶ ﱷ ﱸ ﱹﱺ ﱻ ﱼ ﱽ ﱾ ﱿ', 'وَجَعَلُوا۟ لَهُۥ مِنْ عِبَادِهِۦ جُزْءًا ۚ إِنَّ ٱلْإِنسَـٰنَ لَكَفُورٌۭ مُّبِينٌ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 43, 490, 'ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ', 'أَمِ ٱتَّخَذَ مِمَّا يَخْلُقُ بَنَاتٍۢ وَأَصْفَىٰكُم بِٱلْبَنِينَ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 43, 490, 'ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ', 'وَإِذَا بُشِّرَ أَحَدُهُم بِمَا ضَرَبَ لِلرَّحْمَـٰنِ مَثَلًۭا ظَلَّ وَجْهُهُۥ مُسْوَدًّۭا وَهُوَ كَظِيمٌ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 43, 490, 'ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ', 'أَوَمَن يُنَشَّؤُا۟ فِى ٱلْحِلْيَةِ وَهُوَ فِى ٱلْخِصَامِ غَيْرُ مُبِينٍۢ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 43, 490, 'ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥﲦ ﲧ ﲨﲩ ﲪ ﲫ ﲬ ﲭ', 'وَجَعَلُوا۟ ٱلْمَلَـٰٓئِكَةَ ٱلَّذِينَ هُمْ عِبَـٰدُ ٱلرَّحْمَـٰنِ إِنَـٰثًا ۚ أَشَهِدُوا۟ خَلْقَهُمْ ۚ سَتُكْتَبُ شَهَـٰدَتُهُمْ وَيُسْـَٔلُونَ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 43, 490, 'ﲮ ﲯ ﲰ ﲱ ﲲ ﲳﲴ ﲵ ﲶ ﲷ ﲸ ﲹﲺ ﲻ ﲼ ﲽ ﲾ ﲿ', 'وَقَالُوا۟ لَوْ شَآءَ ٱلرَّحْمَـٰنُ مَا عَبَدْنَـٰهُم ۗ مَّا لَهُم بِذَٰلِكَ مِنْ عِلْمٍ ۖ إِنْ هُمْ إِلَّا يَخْرُصُونَ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 43, 490, 'ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ', 'أَمْ ءَاتَيْنَـٰهُمْ كِتَـٰبًۭا مِّن قَبْلِهِۦ فَهُم بِهِۦ مُسْتَمْسِكُونَ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 43, 490, 'ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ', 'بَلْ قَالُوٓا۟ إِنَّا وَجَدْنَآ ءَابَآءَنَا عَلَىٰٓ أُمَّةٍۢ وَإِنَّا عَلَىٰٓ ءَاثَـٰرِهِم مُّهْتَدُونَ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 43, 491, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ', 'وَكَذَٰلِكَ مَآ أَرْسَلْنَا مِن قَبْلِكَ فِى قَرْيَةٍۢ مِّن نَّذِيرٍ إِلَّا قَالَ مُتْرَفُوهَآ إِنَّا وَجَدْنَآ ءَابَآءَنَا عَلَىٰٓ أُمَّةٍۢ وَإِنَّا عَلَىٰٓ ءَاثَـٰرِهِم مُّقْتَدُونَ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 43, 491, 'ﱗﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ', '۞ قَـٰلَ أَوَلَوْ جِئْتُكُم بِأَهْدَىٰ مِمَّا وَجَدتُّمْ عَلَيْهِ ءَابَآءَكُمْ ۖ قَالُوٓا۟ إِنَّا بِمَآ أُرْسِلْتُم بِهِۦ كَـٰفِرُونَ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 43, 491, 'ﱨ ﱩﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ', 'فَٱنتَقَمْنَا مِنْهُمْ ۖ فَٱنظُرْ كَيْفَ كَانَ عَـٰقِبَةُ ٱلْمُكَذِّبِينَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 43, 491, 'ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ', 'وَإِذْ قَالَ إِبْرَٰهِيمُ لِأَبِيهِ وَقَوْمِهِۦٓ إِنَّنِى بَرَآءٌۭ مِّمَّا تَعْبُدُونَ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 43, 491, 'ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ', 'إِلَّا ٱلَّذِى فَطَرَنِى فَإِنَّهُۥ سَيَهْدِينِ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 43, 491, 'ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ', 'وَجَعَلَهَا كَلِمَةًۢ بَاقِيَةًۭ فِى عَقِبِهِۦ لَعَلَّهُمْ يَرْجِعُونَ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 43, 491, 'ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ', 'بَلْ مَتَّعْتُ هَـٰٓؤُلَآءِ وَءَابَآءَهُمْ حَتَّىٰ جَآءَهُمُ ٱلْحَقُّ وَرَسُولٌۭ مُّبِينٌۭ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 43, 491, 'ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ', 'وَلَمَّا جَآءَهُمُ ٱلْحَقُّ قَالُوا۟ هَـٰذَا سِحْرٌۭ وَإِنَّا بِهِۦ كَـٰفِرُونَ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 43, 491, 'ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ', 'وَقَالُوا۟ لَوْلَا نُزِّلَ هَـٰذَا ٱلْقُرْءَانُ عَلَىٰ رَجُلٍۢ مِّنَ ٱلْقَرْيَتَيْنِ عَظِيمٍ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 43, 491, 'ﲨ ﲩ ﲪ ﲫﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ', 'أَهُمْ يَقْسِمُونَ رَحْمَتَ رَبِّكَ ۚ نَحْنُ قَسَمْنَا بَيْنَهُم مَّعِيشَتَهُمْ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا ۚ وَرَفَعْنَا بَعْضَهُمْ فَوْقَ بَعْضٍۢ دَرَجَـٰتٍۢ لِّيَتَّخِذَ بَعْضُهُم بَعْضًۭا سُخْرِيًّۭا ۗ وَرَحْمَتُ رَبِّكَ خَيْرٌۭ مِّمَّا يَجْمَعُونَ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 43, 491, 'ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ', 'وَلَوْلَآ أَن يَكُونَ ٱلنَّاسُ أُمَّةًۭ وَٰحِدَةًۭ لَّجَعَلْنَا لِمَن يَكْفُرُ بِٱلرَّحْمَـٰنِ لِبُيُوتِهِمْ سُقُفًۭا مِّن فِضَّةٍۢ وَمَعَارِجَ عَلَيْهَا يَظْهَرُونَ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 43, 492, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ', 'وَلِبُيُوتِهِمْ أَبْوَٰبًۭا وَسُرُرًا عَلَيْهَا يَتَّكِـُٔونَ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 43, 492, 'ﱇﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'وَزُخْرُفًۭا ۚ وَإِن كُلُّ ذَٰلِكَ لَمَّا مَتَـٰعُ ٱلْحَيَوٰةِ ٱلدُّنْيَا ۚ وَٱلْـَٔاخِرَةُ عِندَ رَبِّكَ لِلْمُتَّقِينَ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 43, 492, 'ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ', 'وَمَن يَعْشُ عَن ذِكْرِ ٱلرَّحْمَـٰنِ نُقَيِّضْ لَهُۥ شَيْطَـٰنًۭا فَهُوَ لَهُۥ قَرِينٌۭ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 43, 492, 'ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ', 'وَإِنَّهُمْ لَيَصُدُّونَهُمْ عَنِ ٱلسَّبِيلِ وَيَحْسَبُونَ أَنَّهُم مُّهْتَدُونَ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 43, 492, 'ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ', 'حَتَّىٰٓ إِذَا جَآءَنَا قَالَ يَـٰلَيْتَ بَيْنِى وَبَيْنَكَ بُعْدَ ٱلْمَشْرِقَيْنِ فَبِئْسَ ٱلْقَرِينُ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 43, 492, 'ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ', 'وَلَن يَنفَعَكُمُ ٱلْيَوْمَ إِذ ظَّلَمْتُمْ أَنَّكُمْ فِى ٱلْعَذَابِ مُشْتَرِكُونَ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 43, 492, 'ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ', 'أَفَأَنتَ تُسْمِعُ ٱلصُّمَّ أَوْ تَهْدِى ٱلْعُمْىَ وَمَن كَانَ فِى ضَلَـٰلٍۢ مُّبِينٍۢ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 43, 492, 'ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ', 'فَإِمَّا نَذْهَبَنَّ بِكَ فَإِنَّا مِنْهُم مُّنتَقِمُونَ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 43, 492, 'ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ', 'أَوْ نُرِيَنَّكَ ٱلَّذِى وَعَدْنَـٰهُمْ فَإِنَّا عَلَيْهِم مُّقْتَدِرُونَ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 43, 492, 'ﲛ ﲜ ﲝ ﲞﲟ ﲠ ﲡ ﲢ ﲣ ﲤ', 'فَٱسْتَمْسِكْ بِٱلَّذِىٓ أُوحِىَ إِلَيْكَ ۖ إِنَّكَ عَلَىٰ صِرَٰطٍۢ مُّسْتَقِيمٍۢ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 43, 492, 'ﲥ ﲦ ﲧ ﲨﲩ ﲪ ﲫ ﲬ', 'وَإِنَّهُۥ لَذِكْرٌۭ لَّكَ وَلِقَوْمِكَ ۖ وَسَوْفَ تُسْـَٔلُونَ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 43, 492, 'ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ', 'وَسْـَٔلْ مَنْ أَرْسَلْنَا مِن قَبْلِكَ مِن رُّسُلِنَآ أَجَعَلْنَا مِن دُونِ ٱلرَّحْمَـٰنِ ءَالِهَةًۭ يُعْبَدُونَ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 43, 492, 'ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ', 'وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِـَٔايَـٰتِنَآ إِلَىٰ فِرْعَوْنَ وَمَلَإِي۟هِۦ فَقَالَ إِنِّى رَسُولُ رَبِّ ٱلْعَـٰلَمِينَ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 43, 492, 'ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ', 'فَلَمَّا جَآءَهُم بِـَٔايَـٰتِنَآ إِذَا هُم مِّنْهَا يَضْحَكُونَ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 43, 493, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉﱊ ﱋ ﱌ ﱍ ﱎ ﱏ', 'وَمَا نُرِيهِم مِّنْ ءَايَةٍ إِلَّا هِىَ أَكْبَرُ مِنْ أُخْتِهَا ۖ وَأَخَذْنَـٰهُم بِٱلْعَذَابِ لَعَلَّهُمْ يَرْجِعُونَ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 43, 493, 'ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ', 'وَقَالُوا۟ يَـٰٓأَيُّهَ ٱلسَّاحِرُ ٱدْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِندَكَ إِنَّنَا لَمُهْتَدُونَ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 43, 493, 'ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ', 'فَلَمَّا كَشَفْنَا عَنْهُمُ ٱلْعَذَابَ إِذَا هُمْ يَنكُثُونَ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 43, 493, 'ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲﱳ ﱴ ﱵ ﱶ', 'وَنَادَىٰ فِرْعَوْنُ فِى قَوْمِهِۦ قَالَ يَـٰقَوْمِ أَلَيْسَ لِى مُلْكُ مِصْرَ وَهَـٰذِهِ ٱلْأَنْهَـٰرُ تَجْرِى مِن تَحْتِىٓ ۖ أَفَلَا تُبْصِرُونَ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 43, 493, 'ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ', 'أَمْ أَنَا۠ خَيْرٌۭ مِّنْ هَـٰذَا ٱلَّذِى هُوَ مَهِينٌۭ وَلَا يَكَادُ يُبِينُ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 43, 493, 'ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ', 'فَلَوْلَآ أُلْقِىَ عَلَيْهِ أَسْوِرَةٌۭ مِّن ذَهَبٍ أَوْ جَآءَ مَعَهُ ٱلْمَلَـٰٓئِكَةُ مُقْتَرِنِينَ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 43, 493, 'ﲏ ﲐ ﲑﲒ ﲓ ﲔ ﲕ ﲖ ﲗ', 'فَٱسْتَخَفَّ قَوْمَهُۥ فَأَطَاعُوهُ ۚ إِنَّهُمْ كَانُوا۟ قَوْمًۭا فَـٰسِقِينَ ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (55, 43, 493, 'ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ', 'فَلَمَّآ ءَاسَفُونَا ٱنتَقَمْنَا مِنْهُمْ فَأَغْرَقْنَـٰهُمْ أَجْمَعِينَ ٥٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (56, 43, 493, 'ﲟ ﲠ ﲡ ﲢ ﲣ', 'فَجَعَلْنَـٰهُمْ سَلَفًۭا وَمَثَلًۭا لِّلْـَٔاخِرِينَ ٥٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (57, 43, 493, 'ﲤﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ', '۞ وَلَمَّا ضُرِبَ ٱبْنُ مَرْيَمَ مَثَلًا إِذَا قَوْمُكَ مِنْهُ يَصِدُّونَ ٥٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (58, 43, 493, 'ﲯ ﲰ ﲱ ﲲ ﲳﲴ ﲵ ﲶ ﲷ ﲸ ﲹﲺ ﲻ ﲼ ﲽ ﲾ ﲿ', 'وَقَالُوٓا۟ ءَأَـٰلِهَتُنَا خَيْرٌ أَمْ هُوَ ۚ مَا ضَرَبُوهُ لَكَ إِلَّا جَدَلًۢا ۚ بَلْ هُمْ قَوْمٌ خَصِمُونَ ٥٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (59, 43, 493, 'ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ', 'إِنْ هُوَ إِلَّا عَبْدٌ أَنْعَمْنَا عَلَيْهِ وَجَعَلْنَـٰهُ مَثَلًۭا لِّبَنِىٓ إِسْرَٰٓءِيلَ ٥٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (60, 43, 493, 'ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ', 'وَلَوْ نَشَآءُ لَجَعَلْنَا مِنكُم مَّلَـٰٓئِكَةًۭ فِى ٱلْأَرْضِ يَخْلُفُونَ ٦٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (61, 43, 494, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇﱈ ﱉ ﱊ ﱋ ﱌ', 'وَإِنَّهُۥ لَعِلْمٌۭ لِّلسَّاعَةِ فَلَا تَمْتَرُنَّ بِهَا وَٱتَّبِعُونِ ۚ هَـٰذَا صِرَٰطٌۭ مُّسْتَقِيمٌۭ ٦١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (62, 43, 494, 'ﱍ ﱎ ﱏﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'وَلَا يَصُدَّنَّكُمُ ٱلشَّيْطَـٰنُ ۖ إِنَّهُۥ لَكُمْ عَدُوٌّۭ مُّبِينٌۭ ٦٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (63, 43, 494, 'ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣﱤ ﱥ ﱦ ﱧ ﱨ', 'وَلَمَّا جَآءَ عِيسَىٰ بِٱلْبَيِّنَـٰتِ قَالَ قَدْ جِئْتُكُم بِٱلْحِكْمَةِ وَلِأُبَيِّنَ لَكُم بَعْضَ ٱلَّذِى تَخْتَلِفُونَ فِيهِ ۖ فَٱتَّقُوا۟ ٱللَّهَ وَأَطِيعُونِ ٦٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (64, 43, 494, 'ﱩ ﱪ ﱫ ﱬ ﱭ ﱮﱯ ﱰ ﱱ ﱲ ﱳ', 'إِنَّ ٱللَّهَ هُوَ رَبِّى وَرَبُّكُمْ فَٱعْبُدُوهُ ۚ هَـٰذَا صِرَٰطٌۭ مُّسْتَقِيمٌۭ ٦٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (65, 43, 494, 'ﱴ ﱵ ﱶ ﱷﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ', 'فَٱخْتَلَفَ ٱلْأَحْزَابُ مِنۢ بَيْنِهِمْ ۖ فَوَيْلٌۭ لِّلَّذِينَ ظَلَمُوا۟ مِنْ عَذَابِ يَوْمٍ أَلِيمٍ ٦٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (66, 43, 494, 'ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ', 'هَلْ يَنظُرُونَ إِلَّا ٱلسَّاعَةَ أَن تَأْتِيَهُم بَغْتَةًۭ وَهُمْ لَا يَشْعُرُونَ ٦٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (67, 43, 494, 'ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ', 'ٱلْأَخِلَّآءُ يَوْمَئِذٍۭ بَعْضُهُمْ لِبَعْضٍ عَدُوٌّ إِلَّا ٱلْمُتَّقِينَ ٦٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (68, 43, 494, 'ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ', 'يَـٰعِبَادِ لَا خَوْفٌ عَلَيْكُمُ ٱلْيَوْمَ وَلَآ أَنتُمْ تَحْزَنُونَ ٦٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (69, 43, 494, 'ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ', 'ٱلَّذِينَ ءَامَنُوا۟ بِـَٔايَـٰتِنَا وَكَانُوا۟ مُسْلِمِينَ ٦٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (70, 43, 494, 'ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ', 'ٱدْخُلُوا۟ ٱلْجَنَّةَ أَنتُمْ وَأَزْوَٰجُكُمْ تُحْبَرُونَ ٧٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (71, 43, 494, 'ﲩ ﲪ ﲫ ﲬ ﲭ ﲮﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵﲶ ﲷ ﲸ ﲹ ﲺ', 'يُطَافُ عَلَيْهِم بِصِحَافٍۢ مِّن ذَهَبٍۢ وَأَكْوَابٍۢ ۖ وَفِيهَا مَا تَشْتَهِيهِ ٱلْأَنفُسُ وَتَلَذُّ ٱلْأَعْيُنُ ۖ وَأَنتُمْ فِيهَا خَـٰلِدُونَ ٧١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (72, 43, 494, 'ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ', 'وَتِلْكَ ٱلْجَنَّةُ ٱلَّتِىٓ أُورِثْتُمُوهَا بِمَا كُنتُمْ تَعْمَلُونَ ٧٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (73, 43, 494, 'ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ', 'لَكُمْ فِيهَا فَـٰكِهَةٌۭ كَثِيرَةٌۭ مِّنْهَا تَأْكُلُونَ ٧٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (74, 43, 495, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ', 'إِنَّ ٱلْمُجْرِمِينَ فِى عَذَابِ جَهَنَّمَ خَـٰلِدُونَ ٧٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (75, 43, 495, 'ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ', 'لَا يُفَتَّرُ عَنْهُمْ وَهُمْ فِيهِ مُبْلِسُونَ ٧٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (76, 43, 495, 'ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'وَمَا ظَلَمْنَـٰهُمْ وَلَـٰكِن كَانُوا۟ هُمُ ٱلظَّـٰلِمِينَ ٧٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (77, 43, 495, 'ﱖ ﱗ ﱘ ﱙ ﱚﱛ ﱜ ﱝ ﱞ ﱟ', 'وَنَادَوْا۟ يَـٰمَـٰلِكُ لِيَقْضِ عَلَيْنَا رَبُّكَ ۖ قَالَ إِنَّكُم مَّـٰكِثُونَ ٧٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (78, 43, 495, 'ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ', 'لَقَدْ جِئْنَـٰكُم بِٱلْحَقِّ وَلَـٰكِنَّ أَكْثَرَكُمْ لِلْحَقِّ كَـٰرِهُونَ ٧٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (79, 43, 495, 'ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ', 'أَمْ أَبْرَمُوٓا۟ أَمْرًۭا فَإِنَّا مُبْرِمُونَ ٧٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (80, 43, 495, 'ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴﱵ ﱶ ﱷ ﱸ ﱹ ﱺ', 'أَمْ يَحْسَبُونَ أَنَّا لَا نَسْمَعُ سِرَّهُمْ وَنَجْوَىٰهُم ۚ بَلَىٰ وَرُسُلُنَا لَدَيْهِمْ يَكْتُبُونَ ٨٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (81, 43, 495, 'ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ', 'قُلْ إِن كَانَ لِلرَّحْمَـٰنِ وَلَدٌۭ فَأَنَا۠ أَوَّلُ ٱلْعَـٰبِدِينَ ٨١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (82, 43, 495, 'ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ', 'سُبْحَـٰنَ رَبِّ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ رَبِّ ٱلْعَرْشِ عَمَّا يَصِفُونَ ٨٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (83, 43, 495, 'ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ', 'فَذَرْهُمْ يَخُوضُوا۟ وَيَلْعَبُوا۟ حَتَّىٰ يُلَـٰقُوا۟ يَوْمَهُمُ ٱلَّذِى يُوعَدُونَ ٨٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (84, 43, 495, 'ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝﲞ ﲟ ﲠ ﲡ ﲢ', 'وَهُوَ ٱلَّذِى فِى ٱلسَّمَآءِ إِلَـٰهٌۭ وَفِى ٱلْأَرْضِ إِلَـٰهٌۭ ۚ وَهُوَ ٱلْحَكِيمُ ٱلْعَلِيمُ ٨٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (85, 43, 495, 'ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ', 'وَتَبَارَكَ ٱلَّذِى لَهُۥ مُلْكُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ وَمَا بَيْنَهُمَا وَعِندَهُۥ عِلْمُ ٱلسَّاعَةِ وَإِلَيْهِ تُرْجَعُونَ ٨٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (86, 43, 495, 'ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ', 'وَلَا يَمْلِكُ ٱلَّذِينَ يَدْعُونَ مِن دُونِهِ ٱلشَّفَـٰعَةَ إِلَّا مَن شَهِدَ بِٱلْحَقِّ وَهُمْ يَعْلَمُونَ ٨٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (87, 43, 495, 'ﲿ ﳀ ﳁ ﳂ ﳃ ﳄﳅ ﳆ ﳇ ﳈ', 'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَهُمْ لَيَقُولُنَّ ٱللَّهُ ۖ فَأَنَّىٰ يُؤْفَكُونَ ٨٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (88, 43, 495, 'ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ', 'وَقِيلِهِۦ يَـٰرَبِّ إِنَّ هَـٰٓؤُلَآءِ قَوْمٌۭ لَّا يُؤْمِنُونَ ٨٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (89, 43, 495, 'ﳑ ﳒ ﳓ ﳔﳕ ﳖ ﳗ ﳘ', 'فَٱصْفَحْ عَنْهُمْ وَقُلْ سَلَـٰمٌۭ ۚ فَسَوْفَ يَعْلَمُونَ ٨٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 44, 496, 'ﱁ ﱂ', 'حمٓ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 44, 496, 'ﱃ ﱄ ﱅ', 'وَٱلْكِتَـٰبِ ٱلْمُبِينِ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 44, 496, 'ﱆ ﱇ ﱈ ﱉ ﱊﱋ ﱌ ﱍ ﱎ ﱏ', 'إِنَّآ أَنزَلْنَـٰهُ فِى لَيْلَةٍۢ مُّبَـٰرَكَةٍ ۚ إِنَّا كُنَّا مُنذِرِينَ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 44, 496, 'ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'فِيهَا يُفْرَقُ كُلُّ أَمْرٍ حَكِيمٍ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 44, 496, 'ﱖ ﱗ ﱘﱙ ﱚ ﱛ ﱜ ﱝ', 'أَمْرًۭا مِّنْ عِندِنَآ ۚ إِنَّا كُنَّا مُرْسِلِينَ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 44, 496, 'ﱞ ﱟ ﱠﱡ ﱢ ﱣ ﱤ ﱥ ﱦ', 'رَحْمَةًۭ مِّن رَّبِّكَ ۚ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلْعَلِيمُ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 44, 496, 'ﱧ ﱨ ﱩ ﱪ ﱫﱬ ﱭ ﱮ ﱯ ﱰ', 'رَبِّ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ وَمَا بَيْنَهُمَآ ۖ إِن كُنتُم مُّوقِنِينَ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 44, 496, 'ﱱ ﱲ ﱳ ﱴ ﱵ ﱶﱷ ﱸ ﱹ ﱺ ﱻ ﱼ', 'لَآ إِلَـٰهَ إِلَّا هُوَ يُحْىِۦ وَيُمِيتُ ۖ رَبُّكُمْ وَرَبُّ ءَابَآئِكُمُ ٱلْأَوَّلِينَ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 44, 496, 'ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ', 'بَلْ هُمْ فِى شَكٍّۢ يَلْعَبُونَ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 44, 496, 'ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ', 'فَٱرْتَقِبْ يَوْمَ تَأْتِى ٱلسَّمَآءُ بِدُخَانٍۢ مُّبِينٍۢ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 44, 496, 'ﲊ ﲋﲌ ﲍ ﲎ ﲏ ﲐ', 'يَغْشَى ٱلنَّاسَ ۖ هَـٰذَا عَذَابٌ أَلِيمٌۭ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 44, 496, 'ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ', 'رَّبَّنَا ٱكْشِفْ عَنَّا ٱلْعَذَابَ إِنَّا مُؤْمِنُونَ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 44, 496, 'ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ', 'أَنَّىٰ لَهُمُ ٱلذِّكْرَىٰ وَقَدْ جَآءَهُمْ رَسُولٌۭ مُّبِينٌۭ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 44, 496, 'ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ', 'ثُمَّ تَوَلَّوْا۟ عَنْهُ وَقَالُوا۟ مُعَلَّمٌۭ مَّجْنُونٌ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 44, 496, 'ﲧ ﲨ ﲩ ﲪﲫ ﲬ ﲭ ﲮ', 'إِنَّا كَاشِفُوا۟ ٱلْعَذَابِ قَلِيلًا ۚ إِنَّكُمْ عَآئِدُونَ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 44, 496, 'ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ', 'يَوْمَ نَبْطِشُ ٱلْبَطْشَةَ ٱلْكُبْرَىٰٓ إِنَّا مُنتَقِمُونَ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 44, 496, 'ﲶﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ', '۞ وَلَقَدْ فَتَنَّا قَبْلَهُمْ قَوْمَ فِرْعَوْنَ وَجَآءَهُمْ رَسُولٌۭ كَرِيمٌ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 44, 496, 'ﳀ ﳁ ﳂ ﳃ ﳄﳅ ﳆ ﳇ ﳈ ﳉ ﳊ', 'أَنْ أَدُّوٓا۟ إِلَىَّ عِبَادَ ٱللَّهِ ۖ إِنِّى لَكُمْ رَسُولٌ أَمِينٌۭ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 44, 497, 'ﱁ ﱂ ﱃ ﱄ ﱅﱆ ﱇ ﱈ ﱉ ﱊ ﱋ', 'وَأَن لَّا تَعْلُوا۟ عَلَى ٱللَّهِ ۖ إِنِّىٓ ءَاتِيكُم بِسُلْطَـٰنٍۢ مُّبِينٍۢ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 44, 497, 'ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ', 'وَإِنِّى عُذْتُ بِرَبِّى وَرَبِّكُمْ أَن تَرْجُمُونِ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 44, 497, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ', 'وَإِن لَّمْ تُؤْمِنُوا۟ لِى فَٱعْتَزِلُونِ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 44, 497, 'ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ', 'فَدَعَا رَبَّهُۥٓ أَنَّ هَـٰٓؤُلَآءِ قَوْمٌۭ مُّجْرِمُونَ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 44, 497, 'ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ', 'فَأَسْرِ بِعِبَادِى لَيْلًا إِنَّكُم مُّتَّبَعُونَ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 44, 497, 'ﱦ ﱧ ﱨﱩ ﱪ ﱫ ﱬ ﱭ', 'وَٱتْرُكِ ٱلْبَحْرَ رَهْوًا ۖ إِنَّهُمْ جُندٌۭ مُّغْرَقُونَ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 44, 497, 'ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ', 'كَمْ تَرَكُوا۟ مِن جَنَّـٰتٍۢ وَعُيُونٍۢ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 44, 497, 'ﱴ ﱵ ﱶ ﱷ', 'وَزُرُوعٍۢ وَمَقَامٍۢ كَرِيمٍۢ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 44, 497, 'ﱸ ﱹ ﱺ ﱻ ﱼ', 'وَنَعْمَةٍۢ كَانُوا۟ فِيهَا فَـٰكِهِينَ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 44, 497, 'ﱽﱾ ﱿ ﲀ ﲁ ﲂ', 'كَذَٰلِكَ ۖ وَأَوْرَثْنَـٰهَا قَوْمًا ءَاخَرِينَ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 44, 497, 'ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ', 'فَمَا بَكَتْ عَلَيْهِمُ ٱلسَّمَآءُ وَٱلْأَرْضُ وَمَا كَانُوا۟ مُنظَرِينَ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 44, 497, 'ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ', 'وَلَقَدْ نَجَّيْنَا بَنِىٓ إِسْرَٰٓءِيلَ مِنَ ٱلْعَذَابِ ٱلْمُهِينِ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 44, 497, 'ﲔ ﲕﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ', 'مِن فِرْعَوْنَ ۚ إِنَّهُۥ كَانَ عَالِيًۭا مِّنَ ٱلْمُسْرِفِينَ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 44, 497, 'ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ', 'وَلَقَدِ ٱخْتَرْنَـٰهُمْ عَلَىٰ عِلْمٍ عَلَى ٱلْعَـٰلَمِينَ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 44, 497, 'ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ', 'وَءَاتَيْنَـٰهُم مِّنَ ٱلْـَٔايَـٰتِ مَا فِيهِ بَلَـٰٓؤٌۭا۟ مُّبِينٌ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 44, 497, 'ﲬ ﲭ ﲮ ﲯ', 'إِنَّ هَـٰٓؤُلَآءِ لَيَقُولُونَ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 44, 497, 'ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ', 'إِنْ هِىَ إِلَّا مَوْتَتُنَا ٱلْأُولَىٰ وَمَا نَحْنُ بِمُنشَرِينَ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 44, 497, 'ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ', 'فَأْتُوا۟ بِـَٔابَآئِنَآ إِن كُنتُمْ صَـٰدِقِينَ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 44, 497, 'ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇﳈ ﳉ ﳊ ﳋ ﳌ', 'أَهُمْ خَيْرٌ أَمْ قَوْمُ تُبَّعٍۢ وَٱلَّذِينَ مِن قَبْلِهِمْ ۚ أَهْلَكْنَـٰهُمْ ۖ إِنَّهُمْ كَانُوا۟ مُجْرِمِينَ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 44, 497, 'ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ', 'وَمَا خَلَقْنَا ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ وَمَا بَيْنَهُمَا لَـٰعِبِينَ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 44, 497, 'ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ ﳝ', 'مَا خَلَقْنَـٰهُمَآ إِلَّا بِٱلْحَقِّ وَلَـٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 44, 498, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ', 'إِنَّ يَوْمَ ٱلْفَصْلِ مِيقَـٰتُهُمْ أَجْمَعِينَ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 44, 498, 'ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ', 'يَوْمَ لَا يُغْنِى مَوْلًى عَن مَّوْلًۭى شَيْـًۭٔا وَلَا هُمْ يُنصَرُونَ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 44, 498, 'ﱒ ﱓ ﱔ ﱕﱖ ﱗ ﱘ ﱙ ﱚ ﱛ', 'إِلَّا مَن رَّحِمَ ٱللَّهُ ۚ إِنَّهُۥ هُوَ ٱلْعَزِيزُ ٱلرَّحِيمُ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 44, 498, 'ﱜ ﱝ ﱞ ﱟ', 'إِنَّ شَجَرَتَ ٱلزَّقُّومِ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 44, 498, 'ﱠ ﱡ ﱢ', 'طَعَامُ ٱلْأَثِيمِ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 44, 498, 'ﱣ ﱤ ﱥ ﱦ ﱧ', 'كَٱلْمُهْلِ يَغْلِى فِى ٱلْبُطُونِ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 44, 498, 'ﱨ ﱩ ﱪ', 'كَغَلْىِ ٱلْحَمِيمِ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 44, 498, 'ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ', 'خُذُوهُ فَٱعْتِلُوهُ إِلَىٰ سَوَآءِ ٱلْجَحِيمِ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 44, 498, 'ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ', 'ثُمَّ صُبُّوا۟ فَوْقَ رَأْسِهِۦ مِنْ عَذَابِ ٱلْحَمِيمِ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 44, 498, 'ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ', 'ذُقْ إِنَّكَ أَنتَ ٱلْعَزِيزُ ٱلْكَرِيمُ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 44, 498, 'ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ', 'إِنَّ هَـٰذَا مَا كُنتُم بِهِۦ تَمْتَرُونَ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 44, 498, 'ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ', 'إِنَّ ٱلْمُتَّقِينَ فِى مَقَامٍ أَمِينٍۢ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 44, 498, 'ﲌ ﲍ ﲎ ﲏ', 'فِى جَنَّـٰتٍۢ وَعُيُونٍۢ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 44, 498, 'ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ', 'يَلْبَسُونَ مِن سُندُسٍۢ وَإِسْتَبْرَقٍۢ مُّتَقَـٰبِلِينَ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 44, 498, 'ﲖ ﲗ ﲘ ﲙ ﲚ', 'كَذَٰلِكَ وَزَوَّجْنَـٰهُم بِحُورٍ عِينٍۢ ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (55, 44, 498, 'ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ', 'يَدْعُونَ فِيهَا بِكُلِّ فَـٰكِهَةٍ ءَامِنِينَ ٥٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (56, 44, 498, 'ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧﲨ ﲩ ﲪ ﲫ ﲬ', 'لَا يَذُوقُونَ فِيهَا ٱلْمَوْتَ إِلَّا ٱلْمَوْتَةَ ٱلْأُولَىٰ ۖ وَوَقَىٰهُمْ عَذَابَ ٱلْجَحِيمِ ٥٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (57, 44, 498, 'ﲭ ﲮ ﲯﲰ ﲱ ﲲ ﲳ ﲴ ﲵ', 'فَضْلًۭا مِّن رَّبِّكَ ۚ ذَٰلِكَ هُوَ ٱلْفَوْزُ ٱلْعَظِيمُ ٥٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (58, 44, 498, 'ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ', 'فَإِنَّمَا يَسَّرْنَـٰهُ بِلِسَانِكَ لَعَلَّهُمْ يَتَذَكَّرُونَ ٥٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (59, 44, 498, 'ﲼ ﲽ ﲾ ﲿ', 'فَٱرْتَقِبْ إِنَّهُم مُّرْتَقِبُونَ ٥٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 45, 499, 'ﱁ ﱂ', 'حمٓ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 45, 499, 'ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ', 'تَنزِيلُ ٱلْكِتَـٰبِ مِنَ ٱللَّهِ ٱلْعَزِيزِ ٱلْحَكِيمِ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 45, 499, 'ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ', 'إِنَّ فِى ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ لَـَٔايَـٰتٍۢ لِّلْمُؤْمِنِينَ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 45, 499, 'ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ', 'وَفِى خَلْقِكُمْ وَمَا يَبُثُّ مِن دَآبَّةٍ ءَايَـٰتٌۭ لِّقَوْمٍۢ يُوقِنُونَ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 45, 499, 'ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ', 'وَٱخْتِلَـٰفِ ٱلَّيْلِ وَٱلنَّهَارِ وَمَآ أَنزَلَ ٱللَّهُ مِنَ ٱلسَّمَآءِ مِن رِّزْقٍۢ فَأَحْيَا بِهِ ٱلْأَرْضَ بَعْدَ مَوْتِهَا وَتَصْرِيفِ ٱلرِّيَـٰحِ ءَايَـٰتٌۭ لِّقَوْمٍۢ يَعْقِلُونَ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 45, 499, 'ﱰ ﱱ ﱲ ﱳ ﱴ ﱵﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ', 'تِلْكَ ءَايَـٰتُ ٱللَّهِ نَتْلُوهَا عَلَيْكَ بِٱلْحَقِّ ۖ فَبِأَىِّ حَدِيثٍۭ بَعْدَ ٱللَّهِ وَءَايَـٰتِهِۦ يُؤْمِنُونَ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 45, 499, 'ﱾ ﱿ ﲀ ﲁ ﲂ', 'وَيْلٌۭ لِّكُلِّ أَفَّاكٍ أَثِيمٍۢ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 45, 499, 'ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍﲎ ﲏ ﲐ ﲑ ﲒ', 'يَسْمَعُ ءَايَـٰتِ ٱللَّهِ تُتْلَىٰ عَلَيْهِ ثُمَّ يُصِرُّ مُسْتَكْبِرًۭا كَأَن لَّمْ يَسْمَعْهَا ۖ فَبَشِّرْهُ بِعَذَابٍ أَلِيمٍۢ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 45, 499, 'ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙﲚ ﲛ ﲜ ﲝ ﲞ ﲟ', 'وَإِذَا عَلِمَ مِنْ ءَايَـٰتِنَا شَيْـًٔا ٱتَّخَذَهَا هُزُوًا ۚ أُو۟لَـٰٓئِكَ لَهُمْ عَذَابٌۭ مُّهِينٌۭ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 45, 499, 'ﲠ ﲡ ﲢﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰﲱ ﲲ ﲳ ﲴ ﲵ', 'مِّن وَرَآئِهِمْ جَهَنَّمُ ۖ وَلَا يُغْنِى عَنْهُم مَّا كَسَبُوا۟ شَيْـًۭٔا وَلَا مَا ٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ أَوْلِيَآءَ ۖ وَلَهُمْ عَذَابٌ عَظِيمٌ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 45, 499, 'ﲶ ﲷﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ', 'هَـٰذَا هُدًۭى ۖ وَٱلَّذِينَ كَفَرُوا۟ بِـَٔايَـٰتِ رَبِّهِمْ لَهُمْ عَذَابٌۭ مِّن رِّجْزٍ أَلِيمٌ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 45, 499, 'ﳃﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ', '۞ ٱللَّهُ ٱلَّذِى سَخَّرَ لَكُمُ ٱلْبَحْرَ لِتَجْرِىَ ٱلْفُلْكُ فِيهِ بِأَمْرِهِۦ وَلِتَبْتَغُوا۟ مِن فَضْلِهِۦ وَلَعَلَّكُمْ تَشْكُرُونَ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 45, 499, 'ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜﳝ ﳞ ﳟ ﳠ ﳡ ﳢ ﳣ ﳤ', 'وَسَخَّرَ لَكُم مَّا فِى ٱلسَّمَـٰوَٰتِ وَمَا فِى ٱلْأَرْضِ جَمِيعًۭا مِّنْهُ ۚ إِنَّ فِى ذَٰلِكَ لَـَٔايَـٰتٍۢ لِّقَوْمٍۢ يَتَفَكَّرُونَ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 45, 500, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ', 'قُل لِّلَّذِينَ ءَامَنُوا۟ يَغْفِرُوا۟ لِلَّذِينَ لَا يَرْجُونَ أَيَّامَ ٱللَّهِ لِيَجْزِىَ قَوْمًۢا بِمَا كَانُوا۟ يَكْسِبُونَ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 45, 500, 'ﱐ ﱑ ﱒ ﱓﱔ ﱕ ﱖ ﱗﱘ ﱙ ﱚ ﱛ ﱜ ﱝ', 'مَنْ عَمِلَ صَـٰلِحًۭا فَلِنَفْسِهِۦ ۖ وَمَنْ أَسَآءَ فَعَلَيْهَا ۖ ثُمَّ إِلَىٰ رَبِّكُمْ تُرْجَعُونَ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 45, 500, 'ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ', 'وَلَقَدْ ءَاتَيْنَا بَنِىٓ إِسْرَٰٓءِيلَ ٱلْكِتَـٰبَ وَٱلْحُكْمَ وَٱلنُّبُوَّةَ وَرَزَقْنَـٰهُم مِّنَ ٱلطَّيِّبَـٰتِ وَفَضَّلْنَـٰهُمْ عَلَى ٱلْعَـٰلَمِينَ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 45, 500, 'ﱬ ﱭ ﱮ ﱯﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ', 'وَءَاتَيْنَـٰهُم بَيِّنَـٰتٍۢ مِّنَ ٱلْأَمْرِ ۖ فَمَا ٱخْتَلَفُوٓا۟ إِلَّا مِنۢ بَعْدِ مَا جَآءَهُمُ ٱلْعِلْمُ بَغْيًۢا بَيْنَهُمْ ۚ إِنَّ رَبَّكَ يَقْضِى بَيْنَهُمْ يَوْمَ ٱلْقِيَـٰمَةِ فِيمَا كَانُوا۟ فِيهِ يَخْتَلِفُونَ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 45, 500, 'ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ', 'ثُمَّ جَعَلْنَـٰكَ عَلَىٰ شَرِيعَةٍۢ مِّنَ ٱلْأَمْرِ فَٱتَّبِعْهَا وَلَا تَتَّبِعْ أَهْوَآءَ ٱلَّذِينَ لَا يَعْلَمُونَ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 45, 500, 'ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛﲜ ﲝ ﲞ ﲟ ﲠ ﲡﲢ ﲣ ﲤ ﲥ ﲦ', 'إِنَّهُمْ لَن يُغْنُوا۟ عَنكَ مِنَ ٱللَّهِ شَيْـًۭٔا ۚ وَإِنَّ ٱلظَّـٰلِمِينَ بَعْضُهُمْ أَوْلِيَآءُ بَعْضٍۢ ۖ وَٱللَّهُ وَلِىُّ ٱلْمُتَّقِينَ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 45, 500, 'ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ', 'هَـٰذَا بَصَـٰٓئِرُ لِلنَّاسِ وَهُدًۭى وَرَحْمَةٌۭ لِّقَوْمٍۢ يُوقِنُونَ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 45, 500, 'ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼﲽ ﲾ ﲿ ﳀ ﳁ', 'أَمْ حَسِبَ ٱلَّذِينَ ٱجْتَرَحُوا۟ ٱلسَّيِّـَٔاتِ أَن نَّجْعَلَهُمْ كَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ سَوَآءًۭ مَّحْيَاهُمْ وَمَمَاتُهُمْ ۚ سَآءَ مَا يَحْكُمُونَ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 45, 500, 'ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ', 'وَخَلَقَ ٱللَّهُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ بِٱلْحَقِّ وَلِتُجْزَىٰ كُلُّ نَفْسٍۭ بِمَا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 45, 501, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖﱗ ﱘ ﱙ ﱚ', 'أَفَرَءَيْتَ مَنِ ٱتَّخَذَ إِلَـٰهَهُۥ هَوَىٰهُ وَأَضَلَّهُ ٱللَّهُ عَلَىٰ عِلْمٍۢ وَخَتَمَ عَلَىٰ سَمْعِهِۦ وَقَلْبِهِۦ وَجَعَلَ عَلَىٰ بَصَرِهِۦ غِشَـٰوَةًۭ فَمَن يَهْدِيهِ مِنۢ بَعْدِ ٱللَّهِ ۚ أَفَلَا تَذَكَّرُونَ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 45, 501, 'ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦﱧ ﱨ ﱩ ﱪ ﱫ ﱬﱭ ﱮ ﱯ ﱰ ﱱ ﱲ', 'وَقَالُوا۟ مَا هِىَ إِلَّا حَيَاتُنَا ٱلدُّنْيَا نَمُوتُ وَنَحْيَا وَمَا يُهْلِكُنَآ إِلَّا ٱلدَّهْرُ ۚ وَمَا لَهُم بِذَٰلِكَ مِنْ عِلْمٍ ۖ إِنْ هُمْ إِلَّا يَظُنُّونَ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 45, 501, 'ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ', 'وَإِذَا تُتْلَىٰ عَلَيْهِمْ ءَايَـٰتُنَا بَيِّنَـٰتٍۢ مَّا كَانَ حُجَّتَهُمْ إِلَّآ أَن قَالُوا۟ ٱئْتُوا۟ بِـَٔابَآئِنَآ إِن كُنتُمْ صَـٰدِقِينَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 45, 501, 'ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ', 'قُلِ ٱللَّهُ يُحْيِيكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يَجْمَعُكُمْ إِلَىٰ يَوْمِ ٱلْقِيَـٰمَةِ لَا رَيْبَ فِيهِ وَلَـٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَعْلَمُونَ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 45, 501, 'ﲗ ﲘ ﲙ ﲚﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ', 'وَلِلَّهِ مُلْكُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۚ وَيَوْمَ تَقُومُ ٱلسَّاعَةُ يَوْمَئِذٍۢ يَخْسَرُ ٱلْمُبْطِلُونَ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 45, 501, 'ﲣ ﲤ ﲥ ﲦﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ', 'وَتَرَىٰ كُلَّ أُمَّةٍۢ جَاثِيَةًۭ ۚ كُلُّ أُمَّةٍۢ تُدْعَىٰٓ إِلَىٰ كِتَـٰبِهَا ٱلْيَوْمَ تُجْزَوْنَ مَا كُنتُمْ تَعْمَلُونَ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 45, 501, 'ﲳ ﲴ ﲵ ﲶ ﲷﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ', 'هَـٰذَا كِتَـٰبُنَا يَنطِقُ عَلَيْكُم بِٱلْحَقِّ ۚ إِنَّا كُنَّا نَسْتَنسِخُ مَا كُنتُمْ تَعْمَلُونَ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 45, 501, 'ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈﳉ ﳊ ﳋ ﳌ ﳍ ﳎ', 'فَأَمَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ فَيُدْخِلُهُمْ رَبُّهُمْ فِى رَحْمَتِهِۦ ۚ ذَٰلِكَ هُوَ ٱلْفَوْزُ ٱلْمُبِينُ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 45, 501, 'ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ', 'وَأَمَّا ٱلَّذِينَ كَفَرُوٓا۟ أَفَلَمْ تَكُنْ ءَايَـٰتِى تُتْلَىٰ عَلَيْكُمْ فَٱسْتَكْبَرْتُمْ وَكُنتُمْ قَوْمًۭا مُّجْرِمِينَ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 45, 501, 'ﳜ ﳝ ﳞ ﳟ ﳠ ﳡ ﳢ ﳣ ﳤ ﳥ ﳦ ﳧ ﳨ ﳩ ﳪ ﳫ ﳬ ﳭ ﳮ ﳯ ﳰ ﳱ ﳲ', 'وَإِذَا قِيلَ إِنَّ وَعْدَ ٱللَّهِ حَقٌّۭ وَٱلسَّاعَةُ لَا رَيْبَ فِيهَا قُلْتُم مَّا نَدْرِى مَا ٱلسَّاعَةُ إِن نَّظُنُّ إِلَّا ظَنًّۭا وَمَا نَحْنُ بِمُسْتَيْقِنِينَ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 45, 502, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ', 'وَبَدَا لَهُمْ سَيِّـَٔاتُ مَا عَمِلُوا۟ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 45, 502, 'ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ', 'وَقِيلَ ٱلْيَوْمَ نَنسَىٰكُمْ كَمَا نَسِيتُمْ لِقَآءَ يَوْمِكُمْ هَـٰذَا وَمَأْوَىٰكُمُ ٱلنَّارُ وَمَا لَكُم مِّن نَّـٰصِرِينَ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 45, 502, 'ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ', 'ذَٰلِكُم بِأَنَّكُمُ ٱتَّخَذْتُمْ ءَايَـٰتِ ٱللَّهِ هُزُوًۭا وَغَرَّتْكُمُ ٱلْحَيَوٰةُ ٱلدُّنْيَا ۚ فَٱلْيَوْمَ لَا يُخْرَجُونَ مِنْهَا وَلَا هُمْ يُسْتَعْتَبُونَ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 45, 502, 'ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ', 'فَلِلَّهِ ٱلْحَمْدُ رَبِّ ٱلسَّمَـٰوَٰتِ وَرَبِّ ٱلْأَرْضِ رَبِّ ٱلْعَـٰلَمِينَ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 45, 502, 'ﱷ ﱸ ﱹ ﱺ ﱻﱼ ﱽ ﱾ ﱿ ﲀ', 'وَلَهُ ٱلْكِبْرِيَآءُ فِى ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۖ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 46, 502, 'ﲁ ﲂ', 'حمٓ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 46, 502, 'ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ', 'تَنزِيلُ ٱلْكِتَـٰبِ مِنَ ٱللَّهِ ٱلْعَزِيزِ ٱلْحَكِيمِ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 46, 502, 'ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ', 'مَا خَلَقْنَا ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ وَمَا بَيْنَهُمَآ إِلَّا بِٱلْحَقِّ وَأَجَلٍۢ مُّسَمًّۭى ۚ وَٱلَّذِينَ كَفَرُوا۟ عَمَّآ أُنذِرُوا۟ مُعْرِضُونَ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 46, 502, 'ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ', 'قُلْ أَرَءَيْتُم مَّا تَدْعُونَ مِن دُونِ ٱللَّهِ أَرُونِى مَاذَا خَلَقُوا۟ مِنَ ٱلْأَرْضِ أَمْ لَهُمْ شِرْكٌۭ فِى ٱلسَّمَـٰوَٰتِ ۖ ٱئْتُونِى بِكِتَـٰبٍۢ مِّن قَبْلِ هَـٰذَآ أَوْ أَثَـٰرَةٍۢ مِّنْ عِلْمٍ إِن كُنتُمْ صَـٰدِقِينَ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 46, 502, 'ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ', 'وَمَنْ أَضَلُّ مِمَّن يَدْعُوا۟ مِن دُونِ ٱللَّهِ مَن لَّا يَسْتَجِيبُ لَهُۥٓ إِلَىٰ يَوْمِ ٱلْقِيَـٰمَةِ وَهُمْ عَن دُعَآئِهِمْ غَـٰفِلُونَ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 46, 503, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ', 'وَإِذَا حُشِرَ ٱلنَّاسُ كَانُوا۟ لَهُمْ أَعْدَآءًۭ وَكَانُوا۟ بِعِبَادَتِهِمْ كَـٰفِرِينَ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 46, 503, 'ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ', 'وَإِذَا تُتْلَىٰ عَلَيْهِمْ ءَايَـٰتُنَا بَيِّنَـٰتٍۢ قَالَ ٱلَّذِينَ كَفَرُوا۟ لِلْحَقِّ لَمَّا جَآءَهُمْ هَـٰذَا سِحْرٌۭ مُّبِينٌ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 46, 503, 'ﱚ ﱛ ﱜﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦﱧ ﱨ ﱩ ﱪ ﱫ ﱬﱭ ﱮ ﱯ ﱰ ﱱ ﱲﱳ ﱴ ﱵ ﱶ ﱷ', 'أَمْ يَقُولُونَ ٱفْتَرَىٰهُ ۖ قُلْ إِنِ ٱفْتَرَيْتُهُۥ فَلَا تَمْلِكُونَ لِى مِنَ ٱللَّهِ شَيْـًٔا ۖ هُوَ أَعْلَمُ بِمَا تُفِيضُونَ فِيهِ ۖ كَفَىٰ بِهِۦ شَهِيدًۢا بَيْنِى وَبَيْنَكُمْ ۖ وَهُوَ ٱلْغَفُورُ ٱلرَّحِيمُ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 46, 503, 'ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ', 'قُلْ مَا كُنتُ بِدْعًۭا مِّنَ ٱلرُّسُلِ وَمَآ أَدْرِى مَا يُفْعَلُ بِى وَلَا بِكُمْ ۖ إِنْ أَتَّبِعُ إِلَّا مَا يُوحَىٰٓ إِلَىَّ وَمَآ أَنَا۠ إِلَّا نَذِيرٌۭ مُّبِينٌۭ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 46, 503, 'ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ', 'قُلْ أَرَءَيْتُمْ إِن كَانَ مِنْ عِندِ ٱللَّهِ وَكَفَرْتُم بِهِۦ وَشَهِدَ شَاهِدٌۭ مِّنۢ بَنِىٓ إِسْرَٰٓءِيلَ عَلَىٰ مِثْلِهِۦ فَـَٔامَنَ وَٱسْتَكْبَرْتُمْ ۖ إِنَّ ٱللَّهَ لَا يَهْدِى ٱلْقَوْمَ ٱلظَّـٰلِمِينَ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 46, 503, 'ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ', 'وَقَالَ ٱلَّذِينَ كَفَرُوا۟ لِلَّذِينَ ءَامَنُوا۟ لَوْ كَانَ خَيْرًۭا مَّا سَبَقُونَآ إِلَيْهِ ۚ وَإِذْ لَمْ يَهْتَدُوا۟ بِهِۦ فَسَيَقُولُونَ هَـٰذَآ إِفْكٌۭ قَدِيمٌۭ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 46, 503, 'ﳁ ﳂ ﳃ ﳄ ﳅ ﳆﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ', 'وَمِن قَبْلِهِۦ كِتَـٰبُ مُوسَىٰٓ إِمَامًۭا وَرَحْمَةًۭ ۚ وَهَـٰذَا كِتَـٰبٌۭ مُّصَدِّقٌۭ لِّسَانًا عَرَبِيًّۭا لِّيُنذِرَ ٱلَّذِينَ ظَلَمُوا۟ وَبُشْرَىٰ لِلْمُحْسِنِينَ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 46, 503, 'ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ', 'إِنَّ ٱلَّذِينَ قَالُوا۟ رَبُّنَا ٱللَّهُ ثُمَّ ٱسْتَقَـٰمُوا۟ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 46, 503, 'ﳡ ﳢ ﳣ ﳤ ﳥ ﳦ ﳧ ﳨ ﳩ ﳪ', 'أُو۟لَـٰٓئِكَ أَصْحَـٰبُ ٱلْجَنَّةِ خَـٰلِدِينَ فِيهَا جَزَآءًۢ بِمَا كَانُوا۟ يَعْمَلُونَ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 46, 504, 'ﱁ ﱂ ﱃ ﱄﱅ ﱆ ﱇ ﱈ ﱉ ﱊﱋ ﱌ ﱍ ﱎ ﱏﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ', 'وَوَصَّيْنَا ٱلْإِنسَـٰنَ بِوَٰلِدَيْهِ إِحْسَـٰنًا ۖ حَمَلَتْهُ أُمُّهُۥ كُرْهًۭا وَوَضَعَتْهُ كُرْهًۭا ۖ وَحَمْلُهُۥ وَفِصَـٰلُهُۥ ثَلَـٰثُونَ شَهْرًا ۚ حَتَّىٰٓ إِذَا بَلَغَ أَشُدَّهُۥ وَبَلَغَ أَرْبَعِينَ سَنَةًۭ قَالَ رَبِّ أَوْزِعْنِىٓ أَنْ أَشْكُرَ نِعْمَتَكَ ٱلَّتِىٓ أَنْعَمْتَ عَلَىَّ وَعَلَىٰ وَٰلِدَىَّ وَأَنْ أَعْمَلَ صَـٰلِحًۭا تَرْضَىٰهُ وَأَصْلِحْ لِى فِى ذُرِّيَّتِىٓ ۖ إِنِّى تُبْتُ إِلَيْكَ وَإِنِّى مِنَ ٱلْمُسْلِمِينَ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 46, 504, 'ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ', 'أُو۟لَـٰٓئِكَ ٱلَّذِينَ نَتَقَبَّلُ عَنْهُمْ أَحْسَنَ مَا عَمِلُوا۟ وَنَتَجَاوَزُ عَن سَيِّـَٔاتِهِمْ فِىٓ أَصْحَـٰبِ ٱلْجَنَّةِ ۖ وَعْدَ ٱلصِّدْقِ ٱلَّذِى كَانُوا۟ يُوعَدُونَ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 46, 504, 'ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ', 'وَٱلَّذِى قَالَ لِوَٰلِدَيْهِ أُفٍّۢ لَّكُمَآ أَتَعِدَانِنِىٓ أَنْ أُخْرَجَ وَقَدْ خَلَتِ ٱلْقُرُونُ مِن قَبْلِى وَهُمَا يَسْتَغِيثَانِ ٱللَّهَ وَيْلَكَ ءَامِنْ إِنَّ وَعْدَ ٱللَّهِ حَقٌّۭ فَيَقُولُ مَا هَـٰذَآ إِلَّآ أَسَـٰطِيرُ ٱلْأَوَّلِينَ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 46, 504, 'ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱﲲ ﲳ ﲴ ﲵ ﲶ', 'أُو۟لَـٰٓئِكَ ٱلَّذِينَ حَقَّ عَلَيْهِمُ ٱلْقَوْلُ فِىٓ أُمَمٍۢ قَدْ خَلَتْ مِن قَبْلِهِم مِّنَ ٱلْجِنِّ وَٱلْإِنسِ ۖ إِنَّهُمْ كَانُوا۟ خَـٰسِرِينَ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 46, 504, 'ﲷ ﲸ ﲹ ﲺﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ', 'وَلِكُلٍّۢ دَرَجَـٰتٌۭ مِّمَّا عَمِلُوا۟ ۖ وَلِيُوَفِّيَهُمْ أَعْمَـٰلَهُمْ وَهُمْ لَا يُظْلَمُونَ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 46, 504, 'ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ ﳝ', 'وَيَوْمَ يُعْرَضُ ٱلَّذِينَ كَفَرُوا۟ عَلَى ٱلنَّارِ أَذْهَبْتُمْ طَيِّبَـٰتِكُمْ فِى حَيَاتِكُمُ ٱلدُّنْيَا وَٱسْتَمْتَعْتُم بِهَا فَٱلْيَوْمَ تُجْزَوْنَ عَذَابَ ٱلْهُونِ بِمَا كُنتُمْ تَسْتَكْبِرُونَ فِى ٱلْأَرْضِ بِغَيْرِ ٱلْحَقِّ وَبِمَا كُنتُمْ تَفْسُقُونَ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 46, 505, 'ﱁﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ', '۞ وَٱذْكُرْ أَخَا عَادٍ إِذْ أَنذَرَ قَوْمَهُۥ بِٱلْأَحْقَافِ وَقَدْ خَلَتِ ٱلنُّذُرُ مِنۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِۦٓ أَلَّا تَعْبُدُوٓا۟ إِلَّا ٱللَّهَ إِنِّىٓ أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيمٍۢ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 46, 505, 'ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ', 'قَالُوٓا۟ أَجِئْتَنَا لِتَأْفِكَنَا عَنْ ءَالِهَتِنَا فَأْتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 46, 505, 'ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ', 'قَالَ إِنَّمَا ٱلْعِلْمُ عِندَ ٱللَّهِ وَأُبَلِّغُكُم مَّآ أُرْسِلْتُ بِهِۦ وَلَـٰكِنِّىٓ أَرَىٰكُمْ قَوْمًۭا تَجْهَلُونَ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 46, 505, 'ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿﲀ ﲁ ﲂ ﲃ ﲄ ﲅﲆ ﲇ ﲈ ﲉ ﲊ ﲋ', 'فَلَمَّا رَأَوْهُ عَارِضًۭا مُّسْتَقْبِلَ أَوْدِيَتِهِمْ قَالُوا۟ هَـٰذَا عَارِضٌۭ مُّمْطِرُنَا ۚ بَلْ هُوَ مَا ٱسْتَعْجَلْتُم بِهِۦ ۖ رِيحٌۭ فِيهَا عَذَابٌ أَلِيمٌۭ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 46, 505, 'ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕﲖ ﲗ ﲘ ﲙ ﲚ ﲛ', 'تُدَمِّرُ كُلَّ شَىْءٍۭ بِأَمْرِ رَبِّهَا فَأَصْبَحُوا۟ لَا يُرَىٰٓ إِلَّا مَسَـٰكِنُهُمْ ۚ كَذَٰلِكَ نَجْزِى ٱلْقَوْمَ ٱلْمُجْرِمِينَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 46, 505, 'ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ', 'وَلَقَدْ مَكَّنَّـٰهُمْ فِيمَآ إِن مَّكَّنَّـٰكُمْ فِيهِ وَجَعَلْنَا لَهُمْ سَمْعًۭا وَأَبْصَـٰرًۭا وَأَفْـِٔدَةًۭ فَمَآ أَغْنَىٰ عَنْهُمْ سَمْعُهُمْ وَلَآ أَبْصَـٰرُهُمْ وَلَآ أَفْـِٔدَتُهُم مِّن شَىْءٍ إِذْ كَانُوا۟ يَجْحَدُونَ بِـَٔايَـٰتِ ٱللَّهِ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 46, 505, 'ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ', 'وَلَقَدْ أَهْلَكْنَا مَا حَوْلَكُم مِّنَ ٱلْقُرَىٰ وَصَرَّفْنَا ٱلْـَٔايَـٰتِ لَعَلَّهُمْ يَرْجِعُونَ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 46, 505, 'ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐﳑ ﳒ ﳓ ﳔﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ', 'فَلَوْلَا نَصَرَهُمُ ٱلَّذِينَ ٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ قُرْبَانًا ءَالِهَةًۢ ۖ بَلْ ضَلُّوا۟ عَنْهُمْ ۚ وَذَٰلِكَ إِفْكُهُمْ وَمَا كَانُوا۟ يَفْتَرُونَ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 46, 506, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ', 'وَإِذْ صَرَفْنَآ إِلَيْكَ نَفَرًۭا مِّنَ ٱلْجِنِّ يَسْتَمِعُونَ ٱلْقُرْءَانَ فَلَمَّا حَضَرُوهُ قَالُوٓا۟ أَنصِتُوا۟ ۖ فَلَمَّا قُضِىَ وَلَّوْا۟ إِلَىٰ قَوْمِهِم مُّنذِرِينَ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 46, 506, 'ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ', 'قَالُوا۟ يَـٰقَوْمَنَآ إِنَّا سَمِعْنَا كِتَـٰبًا أُنزِلَ مِنۢ بَعْدِ مُوسَىٰ مُصَدِّقًۭا لِّمَا بَيْنَ يَدَيْهِ يَهْدِىٓ إِلَى ٱلْحَقِّ وَإِلَىٰ طَرِيقٍۢ مُّسْتَقِيمٍۢ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 46, 506, 'ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ', 'يَـٰقَوْمَنَآ أَجِيبُوا۟ دَاعِىَ ٱللَّهِ وَءَامِنُوا۟ بِهِۦ يَغْفِرْ لَكُم مِّن ذُنُوبِكُمْ وَيُجِرْكُم مِّنْ عَذَابٍ أَلِيمٍۢ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 46, 506, 'ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅﲆ ﲇ ﲈ ﲉ ﲊ ﲋ', 'وَمَن لَّا يُجِبْ دَاعِىَ ٱللَّهِ فَلَيْسَ بِمُعْجِزٍۢ فِى ٱلْأَرْضِ وَلَيْسَ لَهُۥ مِن دُونِهِۦٓ أَوْلِيَآءُ ۚ أُو۟لَـٰٓئِكَ فِى ضَلَـٰلٍۢ مُّبِينٍ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 46, 506, 'ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛﲜ ﲝﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ', 'أَوَلَمْ يَرَوْا۟ أَنَّ ٱللَّهَ ٱلَّذِى خَلَقَ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ وَلَمْ يَعْىَ بِخَلْقِهِنَّ بِقَـٰدِرٍ عَلَىٰٓ أَن يُحْـِۧىَ ٱلْمَوْتَىٰ ۚ بَلَىٰٓ إِنَّهُۥ عَلَىٰ كُلِّ شَىْءٍۢ قَدِيرٌۭ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 46, 506, 'ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭﲮ ﲯ ﲰ ﲱﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ', 'وَيَوْمَ يُعْرَضُ ٱلَّذِينَ كَفَرُوا۟ عَلَى ٱلنَّارِ أَلَيْسَ هَـٰذَا بِٱلْحَقِّ ۖ قَالُوا۟ بَلَىٰ وَرَبِّنَا ۚ قَالَ فَذُوقُوا۟ ٱلْعَذَابَ بِمَا كُنتُمْ تَكْفُرُونَ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 46, 506, 'ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏﳐ ﳑﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ', 'فَٱصْبِرْ كَمَا صَبَرَ أُو۟لُوا۟ ٱلْعَزْمِ مِنَ ٱلرُّسُلِ وَلَا تَسْتَعْجِل لَّهُمْ ۚ كَأَنَّهُمْ يَوْمَ يَرَوْنَ مَا يُوعَدُونَ لَمْ يَلْبَثُوٓا۟ إِلَّا سَاعَةًۭ مِّن نَّهَارٍۭ ۚ بَلَـٰغٌۭ ۚ فَهَلْ يُهْلَكُ إِلَّا ٱلْقَوْمُ ٱلْفَـٰسِقُونَ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 47, 507, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ', 'ٱلَّذِينَ كَفَرُوا۟ وَصَدُّوا۟ عَن سَبِيلِ ٱللَّهِ أَضَلَّ أَعْمَـٰلَهُمْ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 47, 507, 'ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ', 'وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ وَءَامَنُوا۟ بِمَا نُزِّلَ عَلَىٰ مُحَمَّدٍۢ وَهُوَ ٱلْحَقُّ مِن رَّبِّهِمْ ۙ كَفَّرَ عَنْهُمْ سَيِّـَٔاتِهِمْ وَأَصْلَحَ بَالَهُمْ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 47, 507, 'ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ', 'ذَٰلِكَ بِأَنَّ ٱلَّذِينَ كَفَرُوا۟ ٱتَّبَعُوا۟ ٱلْبَـٰطِلَ وَأَنَّ ٱلَّذِينَ ءَامَنُوا۟ ٱتَّبَعُوا۟ ٱلْحَقَّ مِن رَّبِّهِمْ ۚ كَذَٰلِكَ يَضْرِبُ ٱللَّهُ لِلنَّاسِ أَمْثَـٰلَهُمْ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 47, 507, 'ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄﲅ ﲆﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ', 'فَإِذَا لَقِيتُمُ ٱلَّذِينَ كَفَرُوا۟ فَضَرْبَ ٱلرِّقَابِ حَتَّىٰٓ إِذَآ أَثْخَنتُمُوهُمْ فَشُدُّوا۟ ٱلْوَثَاقَ فَإِمَّا مَنًّۢا بَعْدُ وَإِمَّا فِدَآءً حَتَّىٰ تَضَعَ ٱلْحَرْبُ أَوْزَارَهَا ۚ ذَٰلِكَ وَلَوْ يَشَآءُ ٱللَّهُ لَٱنتَصَرَ مِنْهُمْ وَلَـٰكِن لِّيَبْلُوَا۟ بَعْضَكُم بِبَعْضٍۢ ۗ وَٱلَّذِينَ قُتِلُوا۟ فِى سَبِيلِ ٱللَّهِ فَلَن يُضِلَّ أَعْمَـٰلَهُمْ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 47, 507, 'ﲛ ﲜ ﲝ ﲞ', 'سَيَهْدِيهِمْ وَيُصْلِحُ بَالَهُمْ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 47, 507, 'ﲟ ﲠ ﲡ ﲢ ﲣ', 'وَيُدْخِلُهُمُ ٱلْجَنَّةَ عَرَّفَهَا لَهُمْ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 47, 507, 'ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ', 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِن تَنصُرُوا۟ ٱللَّهَ يَنصُرْكُمْ وَيُثَبِّتْ أَقْدَامَكُمْ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 47, 507, 'ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ', 'وَٱلَّذِينَ كَفَرُوا۟ فَتَعْسًۭا لَّهُمْ وَأَضَلَّ أَعْمَـٰلَهُمْ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 47, 507, 'ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ', 'ذَٰلِكَ بِأَنَّهُمْ كَرِهُوا۟ مَآ أَنزَلَ ٱللَّهُ فَأَحْبَطَ أَعْمَـٰلَهُمْ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 47, 507, 'ﲾﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉﳊ ﳋ ﳌ ﳍﳎ ﳏ ﳐ ﳑ', '۞ أَفَلَمْ يَسِيرُوا۟ فِى ٱلْأَرْضِ فَيَنظُرُوا۟ كَيْفَ كَانَ عَـٰقِبَةُ ٱلَّذِينَ مِن قَبْلِهِمْ ۚ دَمَّرَ ٱللَّهُ عَلَيْهِمْ ۖ وَلِلْكَـٰفِرِينَ أَمْثَـٰلُهَا ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 47, 507, 'ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ ﳝ', 'ذَٰلِكَ بِأَنَّ ٱللَّهَ مَوْلَى ٱلَّذِينَ ءَامَنُوا۟ وَأَنَّ ٱلْكَـٰفِرِينَ لَا مَوْلَىٰ لَهُمْ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 47, 508, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ', 'إِنَّ ٱللَّهَ يُدْخِلُ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ جَنَّـٰتٍۢ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَـٰرُ ۖ وَٱلَّذِينَ كَفَرُوا۟ يَتَمَتَّعُونَ وَيَأْكُلُونَ كَمَا تَأْكُلُ ٱلْأَنْعَـٰمُ وَٱلنَّارُ مَثْوًۭى لَّهُمْ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 47, 508, 'ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ', 'وَكَأَيِّن مِّن قَرْيَةٍ هِىَ أَشَدُّ قُوَّةًۭ مِّن قَرْيَتِكَ ٱلَّتِىٓ أَخْرَجَتْكَ أَهْلَكْنَـٰهُمْ فَلَا نَاصِرَ لَهُمْ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 47, 508, 'ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ', 'أَفَمَن كَانَ عَلَىٰ بَيِّنَةٍۢ مِّن رَّبِّهِۦ كَمَن زُيِّنَ لَهُۥ سُوٓءُ عَمَلِهِۦ وَٱتَّبَعُوٓا۟ أَهْوَآءَهُم ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 47, 508, 'ﱶ ﱷ ﱸ ﱹ ﱺﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ', 'مَّثَلُ ٱلْجَنَّةِ ٱلَّتِى وُعِدَ ٱلْمُتَّقُونَ ۖ فِيهَآ أَنْهَـٰرٌۭ مِّن مَّآءٍ غَيْرِ ءَاسِنٍۢ وَأَنْهَـٰرٌۭ مِّن لَّبَنٍۢ لَّمْ يَتَغَيَّرْ طَعْمُهُۥ وَأَنْهَـٰرٌۭ مِّنْ خَمْرٍۢ لَّذَّةٍۢ لِّلشَّـٰرِبِينَ وَأَنْهَـٰرٌۭ مِّنْ عَسَلٍۢ مُّصَفًّۭى ۖ وَلَهُمْ فِيهَا مِن كُلِّ ٱلثَّمَرَٰتِ وَمَغْفِرَةٌۭ مِّن رَّبِّهِمْ ۖ كَمَنْ هُوَ خَـٰلِدٌۭ فِى ٱلنَّارِ وَسُقُوا۟ مَآءً حَمِيمًۭا فَقَطَّعَ أَمْعَآءَهُمْ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 47, 508, 'ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ', 'وَمِنْهُم مَّن يَسْتَمِعُ إِلَيْكَ حَتَّىٰٓ إِذَا خَرَجُوا۟ مِنْ عِندِكَ قَالُوا۟ لِلَّذِينَ أُوتُوا۟ ٱلْعِلْمَ مَاذَا قَالَ ءَانِفًا ۚ أُو۟لَـٰٓئِكَ ٱلَّذِينَ طَبَعَ ٱللَّهُ عَلَىٰ قُلُوبِهِمْ وَٱتَّبَعُوٓا۟ أَهْوَآءَهُمْ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 47, 508, 'ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ', 'وَٱلَّذِينَ ٱهْتَدَوْا۟ زَادَهُمْ هُدًۭى وَءَاتَىٰهُمْ تَقْوَىٰهُمْ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 47, 508, 'ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍﳎ ﳏ ﳐ ﳑﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ', 'فَهَلْ يَنظُرُونَ إِلَّا ٱلسَّاعَةَ أَن تَأْتِيَهُم بَغْتَةًۭ ۖ فَقَدْ جَآءَ أَشْرَاطُهَا ۚ فَأَنَّىٰ لَهُمْ إِذَا جَآءَتْهُمْ ذِكْرَىٰهُمْ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 47, 508, 'ﳙ ﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ ﳡ ﳢﳣ ﳤ ﳥ ﳦ ﳧ ﳨ', 'فَٱعْلَمْ أَنَّهُۥ لَآ إِلَـٰهَ إِلَّا ٱللَّهُ وَٱسْتَغْفِرْ لِذَنۢبِكَ وَلِلْمُؤْمِنِينَ وَٱلْمُؤْمِنَـٰتِ ۗ وَٱللَّهُ يَعْلَمُ مُتَقَلَّبَكُمْ وَمَثْوَىٰكُمْ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 47, 509, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚﱛ ﱜ ﱝ ﱞ', 'وَيَقُولُ ٱلَّذِينَ ءَامَنُوا۟ لَوْلَا نُزِّلَتْ سُورَةٌۭ ۖ فَإِذَآ أُنزِلَتْ سُورَةٌۭ مُّحْكَمَةٌۭ وَذُكِرَ فِيهَا ٱلْقِتَالُ ۙ رَأَيْتَ ٱلَّذِينَ فِى قُلُوبِهِم مَّرَضٌۭ يَنظُرُونَ إِلَيْكَ نَظَرَ ٱلْمَغْشِىِّ عَلَيْهِ مِنَ ٱلْمَوْتِ ۖ فَأَوْلَىٰ لَهُمْ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 47, 509, 'ﱟ ﱠ ﱡﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ', 'طَاعَةٌۭ وَقَوْلٌۭ مَّعْرُوفٌۭ ۚ فَإِذَا عَزَمَ ٱلْأَمْرُ فَلَوْ صَدَقُوا۟ ٱللَّهَ لَكَانَ خَيْرًۭا لَّهُمْ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 47, 509, 'ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ', 'فَهَلْ عَسَيْتُمْ إِن تَوَلَّيْتُمْ أَن تُفْسِدُوا۟ فِى ٱلْأَرْضِ وَتُقَطِّعُوٓا۟ أَرْحَامَكُمْ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 47, 509, 'ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ', 'أُو۟لَـٰٓئِكَ ٱلَّذِينَ لَعَنَهُمُ ٱللَّهُ فَأَصَمَّهُمْ وَأَعْمَىٰٓ أَبْصَـٰرَهُمْ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 47, 509, 'ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ', 'أَفَلَا يَتَدَبَّرُونَ ٱلْقُرْءَانَ أَمْ عَلَىٰ قُلُوبٍ أَقْفَالُهَآ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 47, 509, 'ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ', 'إِنَّ ٱلَّذِينَ ٱرْتَدُّوا۟ عَلَىٰٓ أَدْبَـٰرِهِم مِّنۢ بَعْدِ مَا تَبَيَّنَ لَهُمُ ٱلْهُدَى ۙ ٱلشَّيْطَـٰنُ سَوَّلَ لَهُمْ وَأَمْلَىٰ لَهُمْ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 47, 509, 'ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤﲥ ﲦ ﲧ ﲨ ﲩ', 'ذَٰلِكَ بِأَنَّهُمْ قَالُوا۟ لِلَّذِينَ كَرِهُوا۟ مَا نَزَّلَ ٱللَّهُ سَنُطِيعُكُمْ فِى بَعْضِ ٱلْأَمْرِ ۖ وَٱللَّهُ يَعْلَمُ إِسْرَارَهُمْ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 47, 509, 'ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ', 'فَكَيْفَ إِذَا تَوَفَّتْهُمُ ٱلْمَلَـٰٓئِكَةُ يَضْرِبُونَ وُجُوهَهُمْ وَأَدْبَـٰرَهُمْ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 47, 509, 'ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ', 'ذَٰلِكَ بِأَنَّهُمُ ٱتَّبَعُوا۟ مَآ أَسْخَطَ ٱللَّهَ وَكَرِهُوا۟ رِضْوَٰنَهُۥ فَأَحْبَطَ أَعْمَـٰلَهُمْ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 47, 509, 'ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ', 'أَمْ حَسِبَ ٱلَّذِينَ فِى قُلُوبِهِم مَّرَضٌ أَن لَّن يُخْرِجَ ٱللَّهُ أَضْغَـٰنَهُمْ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 47, 510, 'ﱁ ﱂ ﱃ ﱄ ﱅﱆ ﱇ ﱈ ﱉ ﱊﱋ ﱌ ﱍ ﱎ ﱏ', 'وَلَوْ نَشَآءُ لَأَرَيْنَـٰكَهُمْ فَلَعَرَفْتَهُم بِسِيمَـٰهُمْ ۚ وَلَتَعْرِفَنَّهُمْ فِى لَحْنِ ٱلْقَوْلِ ۚ وَٱللَّهُ يَعْلَمُ أَعْمَـٰلَكُمْ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 47, 510, 'ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ', 'وَلَنَبْلُوَنَّكُمْ حَتَّىٰ نَعْلَمَ ٱلْمُجَـٰهِدِينَ مِنكُمْ وَٱلصَّـٰبِرِينَ وَنَبْلُوَا۟ أَخْبَارَكُمْ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 47, 510, 'ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ', 'إِنَّ ٱلَّذِينَ كَفَرُوا۟ وَصَدُّوا۟ عَن سَبِيلِ ٱللَّهِ وَشَآقُّوا۟ ٱلرَّسُولَ مِنۢ بَعْدِ مَا تَبَيَّنَ لَهُمُ ٱلْهُدَىٰ لَن يَضُرُّوا۟ ٱللَّهَ شَيْـًۭٔا وَسَيُحْبِطُ أَعْمَـٰلَهُمْ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 47, 510, 'ﱯﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ', '۞ يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ أَطِيعُوا۟ ٱللَّهَ وَأَطِيعُوا۟ ٱلرَّسُولَ وَلَا تُبْطِلُوٓا۟ أَعْمَـٰلَكُمْ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 47, 510, 'ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ', 'إِنَّ ٱلَّذِينَ كَفَرُوا۟ وَصَدُّوا۟ عَن سَبِيلِ ٱللَّهِ ثُمَّ مَاتُوا۟ وَهُمْ كُفَّارٌۭ فَلَن يَغْفِرَ ٱللَّهُ لَهُمْ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 47, 510, 'ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ', 'فَلَا تَهِنُوا۟ وَتَدْعُوٓا۟ إِلَى ٱلسَّلْمِ وَأَنتُمُ ٱلْأَعْلَوْنَ وَٱللَّهُ مَعَكُمْ وَلَن يَتِرَكُمْ أَعْمَـٰلَكُمْ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 47, 510, 'ﲘ ﲙ ﲚ ﲛ ﲜﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ', 'إِنَّمَا ٱلْحَيَوٰةُ ٱلدُّنْيَا لَعِبٌۭ وَلَهْوٌۭ ۚ وَإِن تُؤْمِنُوا۟ وَتَتَّقُوا۟ يُؤْتِكُمْ أُجُورَكُمْ وَلَا يَسْـَٔلْكُمْ أَمْوَٰلَكُمْ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 47, 510, 'ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ', 'إِن يَسْـَٔلْكُمُوهَا فَيُحْفِكُمْ تَبْخَلُوا۟ وَيُخْرِجْ أَضْغَـٰنَكُمْ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 47, 510, 'ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾﲿ ﳀ ﳁ ﳂ ﳃﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ', 'هَـٰٓأَنتُمْ هَـٰٓؤُلَآءِ تُدْعَوْنَ لِتُنفِقُوا۟ فِى سَبِيلِ ٱللَّهِ فَمِنكُم مَّن يَبْخَلُ ۖ وَمَن يَبْخَلْ فَإِنَّمَا يَبْخَلُ عَن نَّفْسِهِۦ ۚ وَٱللَّهُ ٱلْغَنِىُّ وَأَنتُمُ ٱلْفُقَرَآءُ ۚ وَإِن تَتَوَلَّوْا۟ يَسْتَبْدِلْ قَوْمًا غَيْرَكُمْ ثُمَّ لَا يَكُونُوٓا۟ أَمْثَـٰلَكُم ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 48, 511, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ', 'إِنَّا فَتَحْنَا لَكَ فَتْحًۭا مُّبِينًۭا ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 48, 511, 'ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ', 'لِّيَغْفِرَ لَكَ ٱللَّهُ مَا تَقَدَّمَ مِن ذَنۢبِكَ وَمَا تَأَخَّرَ وَيُتِمَّ نِعْمَتَهُۥ عَلَيْكَ وَيَهْدِيَكَ صِرَٰطًۭا مُّسْتَقِيمًۭا ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 48, 511, 'ﱗ ﱘ ﱙ ﱚ ﱛ', 'وَيَنصُرَكَ ٱللَّهُ نَصْرًا عَزِيزًا ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 48, 511, 'ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦﱧ ﱨ ﱩ ﱪ ﱫﱬ ﱭ ﱮ ﱯ ﱰ ﱱ', 'هُوَ ٱلَّذِىٓ أَنزَلَ ٱلسَّكِينَةَ فِى قُلُوبِ ٱلْمُؤْمِنِينَ لِيَزْدَادُوٓا۟ إِيمَـٰنًۭا مَّعَ إِيمَـٰنِهِمْ ۗ وَلِلَّهِ جُنُودُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۚ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمًۭا ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 48, 511, 'ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ', 'لِّيُدْخِلَ ٱلْمُؤْمِنِينَ وَٱلْمُؤْمِنَـٰتِ جَنَّـٰتٍۢ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَـٰرُ خَـٰلِدِينَ فِيهَا وَيُكَفِّرَ عَنْهُمْ سَيِّـَٔاتِهِمْ ۚ وَكَانَ ذَٰلِكَ عِندَ ٱللَّهِ فَوْزًا عَظِيمًۭا ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 48, 511, 'ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏﲐ ﲑ ﲒ ﲓﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛﲜ ﲝ ﲞ ﲟ', 'وَيُعَذِّبَ ٱلْمُنَـٰفِقِينَ وَٱلْمُنَـٰفِقَـٰتِ وَٱلْمُشْرِكِينَ وَٱلْمُشْرِكَـٰتِ ٱلظَّآنِّينَ بِٱللَّهِ ظَنَّ ٱلسَّوْءِ ۚ عَلَيْهِمْ دَآئِرَةُ ٱلسَّوْءِ ۖ وَغَضِبَ ٱللَّهُ عَلَيْهِمْ وَلَعَنَهُمْ وَأَعَدَّ لَهُمْ جَهَنَّمَ ۖ وَسَآءَتْ مَصِيرًۭا ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 48, 511, 'ﲠ ﲡ ﲢ ﲣﲤ ﲥ ﲦ ﲧ ﲨ ﲩ', 'وَلِلَّهِ جُنُودُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۚ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمًا ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 48, 511, 'ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ', 'إِنَّآ أَرْسَلْنَـٰكَ شَـٰهِدًۭا وَمُبَشِّرًۭا وَنَذِيرًۭا ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 48, 511, 'ﲰ ﲱ ﲲ ﲳ ﲴﲵ ﲶ ﲷ ﲸ ﲹ', 'لِّتُؤْمِنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ وَتُعَزِّرُوهُ وَتُوَقِّرُوهُ وَتُسَبِّحُوهُ بُكْرَةًۭ وَأَصِيلًا ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 48, 512, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ', 'إِنَّ ٱلَّذِينَ يُبَايِعُونَكَ إِنَّمَا يُبَايِعُونَ ٱللَّهَ يَدُ ٱللَّهِ فَوْقَ أَيْدِيهِمْ ۚ فَمَن نَّكَثَ فَإِنَّمَا يَنكُثُ عَلَىٰ نَفْسِهِۦ ۖ وَمَنْ أَوْفَىٰ بِمَا عَـٰهَدَ عَلَيْهُ ٱللَّهَ فَسَيُؤْتِيهِ أَجْرًا عَظِيمًۭا ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 48, 512, 'ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ', 'سَيَقُولُ لَكَ ٱلْمُخَلَّفُونَ مِنَ ٱلْأَعْرَابِ شَغَلَتْنَآ أَمْوَٰلُنَا وَأَهْلُونَا فَٱسْتَغْفِرْ لَنَا ۚ يَقُولُونَ بِأَلْسِنَتِهِم مَّا لَيْسَ فِى قُلُوبِهِمْ ۚ قُلْ فَمَن يَمْلِكُ لَكُم مِّنَ ٱللَّهِ شَيْـًٔا إِنْ أَرَادَ بِكُمْ ضَرًّا أَوْ أَرَادَ بِكُمْ نَفْعًۢا ۚ بَلْ كَانَ ٱللَّهُ بِمَا تَعْمَلُونَ خَبِيرًۢا ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 48, 512, 'ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ', 'بَلْ ظَنَنتُمْ أَن لَّن يَنقَلِبَ ٱلرَّسُولُ وَٱلْمُؤْمِنُونَ إِلَىٰٓ أَهْلِيهِمْ أَبَدًۭا وَزُيِّنَ ذَٰلِكَ فِى قُلُوبِكُمْ وَظَنَنتُمْ ظَنَّ ٱلسَّوْءِ وَكُنتُمْ قَوْمًۢا بُورًۭا ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 48, 512, 'ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ', 'وَمَن لَّمْ يُؤْمِنۢ بِٱللَّهِ وَرَسُولِهِۦ فَإِنَّآ أَعْتَدْنَا لِلْكَـٰفِرِينَ سَعِيرًۭا ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 48, 512, 'ﲥ ﲦ ﲧ ﲨﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯﲰ ﲱ ﲲ ﲳ ﲴ ﲵ', 'وَلِلَّهِ مُلْكُ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۚ يَغْفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُ ۚ وَكَانَ ٱللَّهُ غَفُورًۭا رَّحِيمًۭا ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 48, 512, 'ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾﲿ ﳀ ﳁ ﳂ ﳃ ﳄﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍﳎ ﳏ ﳐ ﳑﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ', 'سَيَقُولُ ٱلْمُخَلَّفُونَ إِذَا ٱنطَلَقْتُمْ إِلَىٰ مَغَانِمَ لِتَأْخُذُوهَا ذَرُونَا نَتَّبِعْكُمْ ۖ يُرِيدُونَ أَن يُبَدِّلُوا۟ كَلَـٰمَ ٱللَّهِ ۚ قُل لَّن تَتَّبِعُونَا كَذَٰلِكُمْ قَالَ ٱللَّهُ مِن قَبْلُ ۖ فَسَيَقُولُونَ بَلْ تَحْسُدُونَنَا ۚ بَلْ كَانُوا۟ لَا يَفْقَهُونَ إِلَّا قَلِيلًۭا ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 48, 513, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ', 'قُل لِّلْمُخَلَّفِينَ مِنَ ٱلْأَعْرَابِ سَتُدْعَوْنَ إِلَىٰ قَوْمٍ أُو۟لِى بَأْسٍۢ شَدِيدٍۢ تُقَـٰتِلُونَهُمْ أَوْ يُسْلِمُونَ ۖ فَإِن تُطِيعُوا۟ يُؤْتِكُمُ ٱللَّهُ أَجْرًا حَسَنًۭا ۖ وَإِن تَتَوَلَّوْا۟ كَمَا تَوَلَّيْتُم مِّن قَبْلُ يُعَذِّبْكُمْ عَذَابًا أَلِيمًۭا ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 48, 513, 'ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ', 'لَّيْسَ عَلَى ٱلْأَعْمَىٰ حَرَجٌۭ وَلَا عَلَى ٱلْأَعْرَجِ حَرَجٌۭ وَلَا عَلَى ٱلْمَرِيضِ حَرَجٌۭ ۗ وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ يُدْخِلْهُ جَنَّـٰتٍۢ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَـٰرُ ۖ وَمَن يَتَوَلَّ يُعَذِّبْهُ عَذَابًا أَلِيمًۭا ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 48, 513, 'ﱾﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ', '۞ لَّقَدْ رَضِىَ ٱللَّهُ عَنِ ٱلْمُؤْمِنِينَ إِذْ يُبَايِعُونَكَ تَحْتَ ٱلشَّجَرَةِ فَعَلِمَ مَا فِى قُلُوبِهِمْ فَأَنزَلَ ٱلسَّكِينَةَ عَلَيْهِمْ وَأَثَـٰبَهُمْ فَتْحًۭا قَرِيبًۭا ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 48, 513, 'ﲓ ﲔ ﲕﲖ ﲗ ﲘ ﲙ ﲚ ﲛ', 'وَمَغَانِمَ كَثِيرَةًۭ يَأْخُذُونَهَا ۗ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمًۭا ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 48, 513, 'ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ', 'وَعَدَكُمُ ٱللَّهُ مَغَانِمَ كَثِيرَةًۭ تَأْخُذُونَهَا فَعَجَّلَ لَكُمْ هَـٰذِهِۦ وَكَفَّ أَيْدِىَ ٱلنَّاسِ عَنكُمْ وَلِتَكُونَ ءَايَةًۭ لِّلْمُؤْمِنِينَ وَيَهْدِيَكُمْ صِرَٰطًۭا مُّسْتَقِيمًۭا ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 48, 513, 'ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ', 'وَأُخْرَىٰ لَمْ تَقْدِرُوا۟ عَلَيْهَا قَدْ أَحَاطَ ٱللَّهُ بِهَا ۚ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَىْءٍۢ قَدِيرًۭا ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 48, 513, 'ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ', 'وَلَوْ قَـٰتَلَكُمُ ٱلَّذِينَ كَفَرُوا۟ لَوَلَّوُا۟ ٱلْأَدْبَـٰرَ ثُمَّ لَا يَجِدُونَ وَلِيًّۭا وَلَا نَصِيرًۭا ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 48, 513, 'ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ', 'سُنَّةَ ٱللَّهِ ٱلَّتِى قَدْ خَلَتْ مِن قَبْلُ ۖ وَلَن تَجِدَ لِسُنَّةِ ٱللَّهِ تَبْدِيلًۭا ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 48, 514, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'وَهُوَ ٱلَّذِى كَفَّ أَيْدِيَهُمْ عَنكُمْ وَأَيْدِيَكُمْ عَنْهُم بِبَطْنِ مَكَّةَ مِنۢ بَعْدِ أَنْ أَظْفَرَكُمْ عَلَيْهِمْ ۚ وَكَانَ ٱللَّهُ بِمَا تَعْمَلُونَ بَصِيرًا ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 48, 514, 'ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ', 'هُمُ ٱلَّذِينَ كَفَرُوا۟ وَصَدُّوكُمْ عَنِ ٱلْمَسْجِدِ ٱلْحَرَامِ وَٱلْهَدْىَ مَعْكُوفًا أَن يَبْلُغَ مَحِلَّهُۥ ۚ وَلَوْلَا رِجَالٌۭ مُّؤْمِنُونَ وَنِسَآءٌۭ مُّؤْمِنَـٰتٌۭ لَّمْ تَعْلَمُوهُمْ أَن تَطَـُٔوهُمْ فَتُصِيبَكُم مِّنْهُم مَّعَرَّةٌۢ بِغَيْرِ عِلْمٍۢ ۖ لِّيُدْخِلَ ٱللَّهُ فِى رَحْمَتِهِۦ مَن يَشَآءُ ۚ لَوْ تَزَيَّلُوا۟ لَعَذَّبْنَا ٱلَّذِينَ كَفَرُوا۟ مِنْهُمْ عَذَابًا أَلِيمًا ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 48, 514, 'ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ', 'إِذْ جَعَلَ ٱلَّذِينَ كَفَرُوا۟ فِى قُلُوبِهِمُ ٱلْحَمِيَّةَ حَمِيَّةَ ٱلْجَـٰهِلِيَّةِ فَأَنزَلَ ٱللَّهُ سَكِينَتَهُۥ عَلَىٰ رَسُولِهِۦ وَعَلَى ٱلْمُؤْمِنِينَ وَأَلْزَمَهُمْ كَلِمَةَ ٱلتَّقْوَىٰ وَكَانُوٓا۟ أَحَقَّ بِهَا وَأَهْلَهَا ۚ وَكَانَ ٱللَّهُ بِكُلِّ شَىْءٍ عَلِيمًۭا ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 48, 514, 'ﲠ ﲡ ﲢ ﲣ ﲤ ﲥﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ', 'لَّقَدْ صَدَقَ ٱللَّهُ رَسُولَهُ ٱلرُّءْيَا بِٱلْحَقِّ ۖ لَتَدْخُلُنَّ ٱلْمَسْجِدَ ٱلْحَرَامَ إِن شَآءَ ٱللَّهُ ءَامِنِينَ مُحَلِّقِينَ رُءُوسَكُمْ وَمُقَصِّرِينَ لَا تَخَافُونَ ۖ فَعَلِمَ مَا لَمْ تَعْلَمُوا۟ فَجَعَلَ مِن دُونِ ذَٰلِكَ فَتْحًۭا قَرِيبًا ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 48, 514, 'ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉﳊ ﳋ ﳌ ﳍ ﳎ', 'هُوَ ٱلَّذِىٓ أَرْسَلَ رَسُولَهُۥ بِٱلْهُدَىٰ وَدِينِ ٱلْحَقِّ لِيُظْهِرَهُۥ عَلَى ٱلدِّينِ كُلِّهِۦ ۚ وَكَفَىٰ بِٱللَّهِ شَهِيدًۭا ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 48, 515, 'ﱁ ﱂ ﱃﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ ﱔﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛﱜ ﱝ ﱞ ﱟ ﱠﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ', 'مُّحَمَّدٌۭ رَّسُولُ ٱللَّهِ ۚ وَٱلَّذِينَ مَعَهُۥٓ أَشِدَّآءُ عَلَى ٱلْكُفَّارِ رُحَمَآءُ بَيْنَهُمْ ۖ تَرَىٰهُمْ رُكَّعًۭا سُجَّدًۭا يَبْتَغُونَ فَضْلًۭا مِّنَ ٱللَّهِ وَرِضْوَٰنًۭا ۖ سِيمَاهُمْ فِى وُجُوهِهِم مِّنْ أَثَرِ ٱلسُّجُودِ ۚ ذَٰلِكَ مَثَلُهُمْ فِى ٱلتَّوْرَىٰةِ ۚ وَمَثَلُهُمْ فِى ٱلْإِنجِيلِ كَزَرْعٍ أَخْرَجَ شَطْـَٔهُۥ فَـَٔازَرَهُۥ فَٱسْتَغْلَظَ فَٱسْتَوَىٰ عَلَىٰ سُوقِهِۦ يُعْجِبُ ٱلزُّرَّاعَ لِيَغِيظَ بِهِمُ ٱلْكُفَّارَ ۗ وَعَدَ ٱللَّهُ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّـٰلِحَـٰتِ مِنْهُم مَّغْفِرَةًۭ وَأَجْرًا عَظِيمًۢا ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 49, 515, 'ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆﲇ ﲈ ﲉﲊ ﲋ ﲌ ﲍ ﲎ ﲏ', 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تُقَدِّمُوا۟ بَيْنَ يَدَىِ ٱللَّهِ وَرَسُولِهِۦ ۖ وَٱتَّقُوا۟ ٱللَّهَ ۚ إِنَّ ٱللَّهَ سَمِيعٌ عَلِيمٌۭ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 49, 515, 'ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ', 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تَرْفَعُوٓا۟ أَصْوَٰتَكُمْ فَوْقَ صَوْتِ ٱلنَّبِىِّ وَلَا تَجْهَرُوا۟ لَهُۥ بِٱلْقَوْلِ كَجَهْرِ بَعْضِكُمْ لِبَعْضٍ أَن تَحْبَطَ أَعْمَـٰلُكُمْ وَأَنتُمْ لَا تَشْعُرُونَ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 49, 515, 'ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳﲴ ﲵ ﲶ ﲷ ﲸ ﲹ', 'إِنَّ ٱلَّذِينَ يَغُضُّونَ أَصْوَٰتَهُمْ عِندَ رَسُولِ ٱللَّهِ أُو۟لَـٰٓئِكَ ٱلَّذِينَ ٱمْتَحَنَ ٱللَّهُ قُلُوبَهُمْ لِلتَّقْوَىٰ ۚ لَهُم مَّغْفِرَةٌۭ وَأَجْرٌ عَظِيمٌ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 49, 515, 'ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ', 'إِنَّ ٱلَّذِينَ يُنَادُونَكَ مِن وَرَآءِ ٱلْحُجُرَٰتِ أَكْثَرُهُمْ لَا يَعْقِلُونَ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 49, 516, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉﱊ ﱋ ﱌ ﱍ ﱎ', 'وَلَوْ أَنَّهُمْ صَبَرُوا۟ حَتَّىٰ تَخْرُجَ إِلَيْهِمْ لَكَانَ خَيْرًۭا لَّهُمْ ۚ وَٱللَّهُ غَفُورٌۭ رَّحِيمٌۭ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 49, 516, 'ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ', 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِن جَآءَكُمْ فَاسِقٌۢ بِنَبَإٍۢ فَتَبَيَّنُوٓا۟ أَن تُصِيبُوا۟ قَوْمًۢا بِجَهَـٰلَةٍۢ فَتُصْبِحُوا۟ عَلَىٰ مَا فَعَلْتُمْ نَـٰدِمِينَ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 49, 516, 'ﱡ ﱢ ﱣ ﱤ ﱥﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺﱻ ﱼ ﱽ ﱾ ﱿ', 'وَٱعْلَمُوٓا۟ أَنَّ فِيكُمْ رَسُولَ ٱللَّهِ ۚ لَوْ يُطِيعُكُمْ فِى كَثِيرٍۢ مِّنَ ٱلْأَمْرِ لَعَنِتُّمْ وَلَـٰكِنَّ ٱللَّهَ حَبَّبَ إِلَيْكُمُ ٱلْإِيمَـٰنَ وَزَيَّنَهُۥ فِى قُلُوبِكُمْ وَكَرَّهَ إِلَيْكُمُ ٱلْكُفْرَ وَٱلْفُسُوقَ وَٱلْعِصْيَانَ ۚ أُو۟لَـٰٓئِكَ هُمُ ٱلرَّٰشِدُونَ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 49, 516, 'ﲀ ﲁ ﲂ ﲃﲄ ﲅ ﲆ ﲇ ﲈ', 'فَضْلًۭا مِّنَ ٱللَّهِ وَنِعْمَةًۭ ۚ وَٱللَّهُ عَلِيمٌ حَكِيمٌۭ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 49, 516, 'ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤﲥ ﲦ ﲧ ﲨ ﲩ ﲪ', 'وَإِن طَآئِفَتَانِ مِنَ ٱلْمُؤْمِنِينَ ٱقْتَتَلُوا۟ فَأَصْلِحُوا۟ بَيْنَهُمَا ۖ فَإِنۢ بَغَتْ إِحْدَىٰهُمَا عَلَى ٱلْأُخْرَىٰ فَقَـٰتِلُوا۟ ٱلَّتِى تَبْغِى حَتَّىٰ تَفِىٓءَ إِلَىٰٓ أَمْرِ ٱللَّهِ ۚ فَإِن فَآءَتْ فَأَصْلِحُوا۟ بَيْنَهُمَا بِٱلْعَدْلِ وَأَقْسِطُوٓا۟ ۖ إِنَّ ٱللَّهَ يُحِبُّ ٱلْمُقْسِطِينَ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 49, 516, 'ﲫ ﲬ ﲭ ﲮ ﲯ ﲰﲱ ﲲ ﲳ ﲴ ﲵ ﲶ', 'إِنَّمَا ٱلْمُؤْمِنُونَ إِخْوَةٌۭ فَأَصْلِحُوا۟ بَيْنَ أَخَوَيْكُمْ ۚ وَٱتَّقُوا۟ ٱللَّهَ لَعَلَّكُمْ تُرْحَمُونَ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 49, 516, 'ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓﳔ ﳕ ﳖ ﳗ ﳘ ﳙﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ ﳡ', 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا يَسْخَرْ قَوْمٌۭ مِّن قَوْمٍ عَسَىٰٓ أَن يَكُونُوا۟ خَيْرًۭا مِّنْهُمْ وَلَا نِسَآءٌۭ مِّن نِّسَآءٍ عَسَىٰٓ أَن يَكُنَّ خَيْرًۭا مِّنْهُنَّ ۖ وَلَا تَلْمِزُوٓا۟ أَنفُسَكُمْ وَلَا تَنَابَزُوا۟ بِٱلْأَلْقَـٰبِ ۖ بِئْسَ ٱلِٱسْمُ ٱلْفُسُوقُ بَعْدَ ٱلْإِيمَـٰنِ ۚ وَمَن لَّمْ يَتُبْ فَأُو۟لَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 49, 517, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛﱜ ﱝ ﱞﱟ ﱠ ﱡ ﱢ ﱣ ﱤ', 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ ٱجْتَنِبُوا۟ كَثِيرًۭا مِّنَ ٱلظَّنِّ إِنَّ بَعْضَ ٱلظَّنِّ إِثْمٌۭ ۖ وَلَا تَجَسَّسُوا۟ وَلَا يَغْتَب بَّعْضُكُم بَعْضًا ۚ أَيُحِبُّ أَحَدُكُمْ أَن يَأْكُلَ لَحْمَ أَخِيهِ مَيْتًۭا فَكَرِهْتُمُوهُ ۚ وَٱتَّقُوا۟ ٱللَّهَ ۚ إِنَّ ٱللَّهَ تَوَّابٌۭ رَّحِيمٌۭ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 49, 517, 'ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯﱰ ﱱ ﱲ ﱳ ﱴ ﱵﱶ ﱷ ﱸ ﱹ ﱺ ﱻ', 'يَـٰٓأَيُّهَا ٱلنَّاسُ إِنَّا خَلَقْنَـٰكُم مِّن ذَكَرٍۢ وَأُنثَىٰ وَجَعَلْنَـٰكُمْ شُعُوبًۭا وَقَبَآئِلَ لِتَعَارَفُوٓا۟ ۚ إِنَّ أَكْرَمَكُمْ عِندَ ٱللَّهِ أَتْقَىٰكُمْ ۚ إِنَّ ٱللَّهَ عَلِيمٌ خَبِيرٌۭ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 49, 517, 'ﱼﱽ ﱾ ﱿﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕﲖ ﲗ ﲘ ﲙ ﲚ ﲛ', '۞ قَالَتِ ٱلْأَعْرَابُ ءَامَنَّا ۖ قُل لَّمْ تُؤْمِنُوا۟ وَلَـٰكِن قُولُوٓا۟ أَسْلَمْنَا وَلَمَّا يَدْخُلِ ٱلْإِيمَـٰنُ فِى قُلُوبِكُمْ ۖ وَإِن تُطِيعُوا۟ ٱللَّهَ وَرَسُولَهُۥ لَا يَلِتْكُم مِّنْ أَعْمَـٰلِكُمْ شَيْـًٔا ۚ إِنَّ ٱللَّهَ غَفُورٌۭ رَّحِيمٌ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 49, 517, 'ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪﲫ ﲬ ﲭ ﲮ ﲯ', 'إِنَّمَا ٱلْمُؤْمِنُونَ ٱلَّذِينَ ءَامَنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ ثُمَّ لَمْ يَرْتَابُوا۟ وَجَـٰهَدُوا۟ بِأَمْوَٰلِهِمْ وَأَنفُسِهِمْ فِى سَبِيلِ ٱللَّهِ ۚ أُو۟لَـٰٓئِكَ هُمُ ٱلصَّـٰدِقُونَ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 49, 517, 'ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻﲼ ﲽ ﲾ ﲿ ﳀ ﳁ', 'قُلْ أَتُعَلِّمُونَ ٱللَّهَ بِدِينِكُمْ وَٱللَّهُ يَعْلَمُ مَا فِى ٱلسَّمَـٰوَٰتِ وَمَا فِى ٱلْأَرْضِ ۚ وَٱللَّهُ بِكُلِّ شَىْءٍ عَلِيمٌۭ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 49, 517, 'ﳂ ﳃ ﳄ ﳅﳆ ﳇ ﳈ ﳉ ﳊ ﳋﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ', 'يَمُنُّونَ عَلَيْكَ أَنْ أَسْلَمُوا۟ ۖ قُل لَّا تَمُنُّوا۟ عَلَىَّ إِسْلَـٰمَكُم ۖ بَلِ ٱللَّهُ يَمُنُّ عَلَيْكُمْ أَنْ هَدَىٰكُمْ لِلْإِيمَـٰنِ إِن كُنتُمْ صَـٰدِقِينَ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 49, 517, 'ﳘ ﳙ ﳚ ﳛ ﳜ ﳝﳞ ﳟ ﳠ ﳡ ﳢ ﳣ', 'إِنَّ ٱللَّهَ يَعْلَمُ غَيْبَ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضِ ۚ وَٱللَّهُ بَصِيرٌۢ بِمَا تَعْمَلُونَ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 50, 518, 'ﱁﱂ ﱃ ﱄ ﱅ', 'قٓ ۚ وَٱلْقُرْءَانِ ٱلْمَجِيدِ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 50, 518, 'ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ', 'بَلْ عَجِبُوٓا۟ أَن جَآءَهُم مُّنذِرٌۭ مِّنْهُمْ فَقَالَ ٱلْكَـٰفِرُونَ هَـٰذَا شَىْءٌ عَجِيبٌ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 50, 518, 'ﱒ ﱓ ﱔ ﱕﱖ ﱗ ﱘ ﱙ ﱚ', 'أَءِذَا مِتْنَا وَكُنَّا تُرَابًۭا ۖ ذَٰلِكَ رَجْعٌۢ بَعِيدٌۭ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 50, 518, 'ﱛ ﱜ ﱝ ﱞ ﱟ ﱠﱡ ﱢ ﱣ ﱤ ﱥ', 'قَدْ عَلِمْنَا مَا تَنقُصُ ٱلْأَرْضُ مِنْهُمْ ۖ وَعِندَنَا كِتَـٰبٌ حَفِيظٌۢ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 50, 518, 'ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ', 'بَلْ كَذَّبُوا۟ بِٱلْحَقِّ لَمَّا جَآءَهُمْ فَهُمْ فِىٓ أَمْرٍۢ مَّرِيجٍ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 50, 518, 'ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ', 'أَفَلَمْ يَنظُرُوٓا۟ إِلَى ٱلسَّمَآءِ فَوْقَهُمْ كَيْفَ بَنَيْنَـٰهَا وَزَيَّنَّـٰهَا وَمَا لَهَا مِن فُرُوجٍۢ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 50, 518, 'ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ', 'وَٱلْأَرْضَ مَدَدْنَـٰهَا وَأَلْقَيْنَا فِيهَا رَوَٰسِىَ وَأَنۢبَتْنَا فِيهَا مِن كُلِّ زَوْجٍۭ بَهِيجٍۢ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 50, 518, 'ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ', 'تَبْصِرَةًۭ وَذِكْرَىٰ لِكُلِّ عَبْدٍۢ مُّنِيبٍۢ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 50, 518, 'ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ', 'وَنَزَّلْنَا مِنَ ٱلسَّمَآءِ مَآءًۭ مُّبَـٰرَكًۭا فَأَنۢبَتْنَا بِهِۦ جَنَّـٰتٍۢ وَحَبَّ ٱلْحَصِيدِ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 50, 518, 'ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ', 'وَٱلنَّخْلَ بَاسِقَـٰتٍۢ لَّهَا طَلْعٌۭ نَّضِيدٌۭ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 50, 518, 'ﲠ ﲡﲢ ﲣ ﲤ ﲥ ﲦﲧ ﲨ ﲩ ﲪ', 'رِّزْقًۭا لِّلْعِبَادِ ۖ وَأَحْيَيْنَا بِهِۦ بَلْدَةًۭ مَّيْتًۭا ۚ كَذَٰلِكَ ٱلْخُرُوجُ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 50, 518, 'ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ', 'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍۢ وَأَصْحَـٰبُ ٱلرَّسِّ وَثَمُودُ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 50, 518, 'ﲳ ﲴ ﲵ ﲶ ﲷ', 'وَعَادٌۭ وَفِرْعَوْنُ وَإِخْوَٰنُ لُوطٍۢ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 50, 518, 'ﲸ ﲹ ﲺ ﲻﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ', 'وَأَصْحَـٰبُ ٱلْأَيْكَةِ وَقَوْمُ تُبَّعٍۢ ۚ كُلٌّۭ كَذَّبَ ٱلرُّسُلَ فَحَقَّ وَعِيدِ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 50, 518, 'ﳃ ﳄ ﳅﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ', 'أَفَعَيِينَا بِٱلْخَلْقِ ٱلْأَوَّلِ ۚ بَلْ هُمْ فِى لَبْسٍۢ مِّنْ خَلْقٍۢ جَدِيدٍۢ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 50, 519, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ', 'وَلَقَدْ خَلَقْنَا ٱلْإِنسَـٰنَ وَنَعْلَمُ مَا تُوَسْوِسُ بِهِۦ نَفْسُهُۥ ۖ وَنَحْنُ أَقْرَبُ إِلَيْهِ مِنْ حَبْلِ ٱلْوَرِيدِ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 50, 519, 'ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ', 'إِذْ يَتَلَقَّى ٱلْمُتَلَقِّيَانِ عَنِ ٱلْيَمِينِ وَعَنِ ٱلشِّمَالِ قَعِيدٌۭ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 50, 519, 'ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ', 'مَّا يَلْفِظُ مِن قَوْلٍ إِلَّا لَدَيْهِ رَقِيبٌ عَتِيدٌۭ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 50, 519, 'ﱣ ﱤ ﱥ ﱦﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ', 'وَجَآءَتْ سَكْرَةُ ٱلْمَوْتِ بِٱلْحَقِّ ۖ ذَٰلِكَ مَا كُنتَ مِنْهُ تَحِيدُ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 50, 519, 'ﱮ ﱯ ﱰﱱ ﱲ ﱳ ﱴ ﱵ', 'وَنُفِخَ فِى ٱلصُّورِ ۚ ذَٰلِكَ يَوْمُ ٱلْوَعِيدِ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 50, 519, 'ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ', 'وَجَآءَتْ كُلُّ نَفْسٍۢ مَّعَهَا سَآئِقٌۭ وَشَهِيدٌۭ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 50, 519, 'ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ', 'لَّقَدْ كُنتَ فِى غَفْلَةٍۢ مِّنْ هَـٰذَا فَكَشَفْنَا عَنكَ غِطَآءَكَ فَبَصَرُكَ ٱلْيَوْمَ حَدِيدٌۭ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 50, 519, 'ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ', 'وَقَالَ قَرِينُهُۥ هَـٰذَا مَا لَدَىَّ عَتِيدٌ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 50, 519, 'ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ', 'أَلْقِيَا فِى جَهَنَّمَ كُلَّ كَفَّارٍ عَنِيدٍۢ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 50, 519, 'ﲘ ﲙ ﲚ ﲛ ﲜ', 'مَّنَّاعٍۢ لِّلْخَيْرِ مُعْتَدٍۢ مُّرِيبٍ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 50, 519, 'ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ', 'ٱلَّذِى جَعَلَ مَعَ ٱللَّهِ إِلَـٰهًا ءَاخَرَ فَأَلْقِيَاهُ فِى ٱلْعَذَابِ ٱلشَّدِيدِ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 50, 519, 'ﲨﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ', '۞ قَالَ قَرِينُهُۥ رَبَّنَا مَآ أَطْغَيْتُهُۥ وَلَـٰكِن كَانَ فِى ضَلَـٰلٍۭ بَعِيدٍۢ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 50, 519, 'ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ', 'قَالَ لَا تَخْتَصِمُوا۟ لَدَىَّ وَقَدْ قَدَّمْتُ إِلَيْكُم بِٱلْوَعِيدِ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 50, 519, 'ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ', 'مَا يُبَدَّلُ ٱلْقَوْلُ لَدَىَّ وَمَآ أَنَا۠ بِظَلَّـٰمٍۢ لِّلْعَبِيدِ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 50, 519, 'ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ', 'يَوْمَ نَقُولُ لِجَهَنَّمَ هَلِ ٱمْتَلَأْتِ وَتَقُولُ هَلْ مِن مَّزِيدٍۢ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 50, 519, 'ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ', 'وَأُزْلِفَتِ ٱلْجَنَّةُ لِلْمُتَّقِينَ غَيْرَ بَعِيدٍ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 50, 519, 'ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ', 'هَـٰذَا مَا تُوعَدُونَ لِكُلِّ أَوَّابٍ حَفِيظٍۢ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 50, 519, 'ﳝ ﳞ ﳟ ﳠ ﳡ ﳢ ﳣ ﳤ', 'مَّنْ خَشِىَ ٱلرَّحْمَـٰنَ بِٱلْغَيْبِ وَجَآءَ بِقَلْبٍۢ مُّنِيبٍ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 50, 519, 'ﳥ ﳦﳧ ﳨ ﳩ ﳪ ﳫ', 'ٱدْخُلُوهَا بِسَلَـٰمٍۢ ۖ ذَٰلِكَ يَوْمُ ٱلْخُلُودِ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 50, 519, 'ﳬ ﳭ ﳮ ﳯ ﳰ ﳱ ﳲ', 'لَهُم مَّا يَشَآءُونَ فِيهَا وَلَدَيْنَا مَزِيدٌۭ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 50, 520, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ', 'وَكَمْ أَهْلَكْنَا قَبْلَهُم مِّن قَرْنٍ هُمْ أَشَدُّ مِنْهُم بَطْشًۭا فَنَقَّبُوا۟ فِى ٱلْبِلَـٰدِ هَلْ مِن مَّحِيصٍ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 50, 520, 'ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ', 'إِنَّ فِى ذَٰلِكَ لَذِكْرَىٰ لِمَن كَانَ لَهُۥ قَلْبٌ أَوْ أَلْقَى ٱلسَّمْعَ وَهُوَ شَهِيدٌۭ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 50, 520, 'ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ', 'وَلَقَدْ خَلَقْنَا ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ وَمَا بَيْنَهُمَا فِى سِتَّةِ أَيَّامٍۢ وَمَا مَسَّنَا مِن لُّغُوبٍۢ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 50, 520, 'ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ', 'فَٱصْبِرْ عَلَىٰ مَا يَقُولُونَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوعِ ٱلشَّمْسِ وَقَبْلَ ٱلْغُرُوبِ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 50, 520, 'ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ', 'وَمِنَ ٱلَّيْلِ فَسَبِّحْهُ وَأَدْبَـٰرَ ٱلسُّجُودِ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 50, 520, 'ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ', 'وَٱسْتَمِعْ يَوْمَ يُنَادِ ٱلْمُنَادِ مِن مَّكَانٍۢ قَرِيبٍۢ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 50, 520, 'ﲈ ﲉ ﲊ ﲋﲌ ﲍ ﲎ ﲏ ﲐ', 'يَوْمَ يَسْمَعُونَ ٱلصَّيْحَةَ بِٱلْحَقِّ ۚ ذَٰلِكَ يَوْمُ ٱلْخُرُوجِ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 50, 520, 'ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ', 'إِنَّا نَحْنُ نُحْىِۦ وَنُمِيتُ وَإِلَيْنَا ٱلْمَصِيرُ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 50, 520, 'ﲘ ﲙ ﲚ ﲛ ﲜﲝ ﲞ ﲟ ﲠ ﲡ ﲢ', 'يَوْمَ تَشَقَّقُ ٱلْأَرْضُ عَنْهُمْ سِرَاعًۭا ۚ ذَٰلِكَ حَشْرٌ عَلَيْنَا يَسِيرٌۭ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 50, 520, 'ﲣ ﲤ ﲥ ﲦﲧ ﲨ ﲩ ﲪ ﲫﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ', 'نَّحْنُ أَعْلَمُ بِمَا يَقُولُونَ ۖ وَمَآ أَنتَ عَلَيْهِم بِجَبَّارٍۢ ۖ فَذَكِّرْ بِٱلْقُرْءَانِ مَن يَخَافُ وَعِيدِ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 51, 520, 'ﲳ ﲴ ﲵ', 'وَٱلذَّٰرِيَـٰتِ ذَرْوًۭا ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 51, 520, 'ﲶ ﲷ ﲸ', 'فَٱلْحَـٰمِلَـٰتِ وِقْرًۭا ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 51, 520, 'ﲹ ﲺ ﲻ', 'فَٱلْجَـٰرِيَـٰتِ يُسْرًۭا ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 51, 520, 'ﲼ ﲽ ﲾ', 'فَٱلْمُقَسِّمَـٰتِ أَمْرًا ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 51, 520, 'ﲿ ﳀ ﳁ ﳂ', 'إِنَّمَا تُوعَدُونَ لَصَادِقٌۭ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 51, 520, 'ﳃ ﳄ ﳅ ﳆ', 'وَإِنَّ ٱلدِّينَ لَوَٰقِعٌۭ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 51, 521, 'ﱁ ﱂ ﱃ ﱄ', 'وَٱلسَّمَآءِ ذَاتِ ٱلْحُبُكِ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 51, 521, 'ﱅ ﱆ ﱇ ﱈ ﱉ', 'إِنَّكُمْ لَفِى قَوْلٍۢ مُّخْتَلِفٍۢ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 51, 521, 'ﱊ ﱋ ﱌ ﱍ ﱎ', 'يُؤْفَكُ عَنْهُ مَنْ أُفِكَ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 51, 521, 'ﱏ ﱐ ﱑ', 'قُتِلَ ٱلْخَرَّٰصُونَ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 51, 521, 'ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ', 'ٱلَّذِينَ هُمْ فِى غَمْرَةٍۢ سَاهُونَ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 51, 521, 'ﱘ ﱙ ﱚ ﱛ ﱜ', 'يَسْـَٔلُونَ أَيَّانَ يَوْمُ ٱلدِّينِ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 51, 521, 'ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ', 'يَوْمَ هُمْ عَلَى ٱلنَّارِ يُفْتَنُونَ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 51, 521, 'ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ', 'ذُوقُوا۟ فِتْنَتَكُمْ هَـٰذَا ٱلَّذِى كُنتُم بِهِۦ تَسْتَعْجِلُونَ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 51, 521, 'ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ', 'إِنَّ ٱلْمُتَّقِينَ فِى جَنَّـٰتٍۢ وَعُيُونٍ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 51, 521, 'ﱱ ﱲ ﱳ ﱴﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ', 'ءَاخِذِينَ مَآ ءَاتَىٰهُمْ رَبُّهُمْ ۚ إِنَّهُمْ كَانُوا۟ قَبْلَ ذَٰلِكَ مُحْسِنِينَ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 51, 521, 'ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ', 'كَانُوا۟ قَلِيلًۭا مِّنَ ٱلَّيْلِ مَا يَهْجَعُونَ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 51, 521, 'ﲃ ﲄ ﲅ ﲆ', 'وَبِٱلْأَسْحَارِ هُمْ يَسْتَغْفِرُونَ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 51, 521, 'ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ', 'وَفِىٓ أَمْوَٰلِهِمْ حَقٌّۭ لِّلسَّآئِلِ وَٱلْمَحْرُومِ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 51, 521, 'ﲍ ﲎ ﲏ ﲐ ﲑ', 'وَفِى ٱلْأَرْضِ ءَايَـٰتٌۭ لِّلْمُوقِنِينَ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 51, 521, 'ﲒ ﲓﲔ ﲕ ﲖ ﲗ', 'وَفِىٓ أَنفُسِكُمْ ۚ أَفَلَا تُبْصِرُونَ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 51, 521, 'ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ', 'وَفِى ٱلسَّمَآءِ رِزْقُكُمْ وَمَا تُوعَدُونَ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 51, 521, 'ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ', 'فَوَرَبِّ ٱلسَّمَآءِ وَٱلْأَرْضِ إِنَّهُۥ لَحَقٌّۭ مِّثْلَ مَآ أَنَّكُمْ تَنطِقُونَ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 51, 521, 'ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ', 'هَلْ أَتَىٰكَ حَدِيثُ ضَيْفِ إِبْرَٰهِيمَ ٱلْمُكْرَمِينَ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 51, 521, 'ﲯ ﲰ ﲱ ﲲ ﲳﲴ ﲵ ﲶ ﲷ ﲸ ﲹ', 'إِذْ دَخَلُوا۟ عَلَيْهِ فَقَالُوا۟ سَلَـٰمًۭا ۖ قَالَ سَلَـٰمٌۭ قَوْمٌۭ مُّنكَرُونَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 51, 521, 'ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ', 'فَرَاغَ إِلَىٰٓ أَهْلِهِۦ فَجَآءَ بِعِجْلٍۢ سَمِينٍۢ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 51, 521, 'ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ', 'فَقَرَّبَهُۥٓ إِلَيْهِمْ قَالَ أَلَا تَأْكُلُونَ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 51, 521, 'ﳇ ﳈ ﳉﳊ ﳋ ﳌ ﳍﳎ ﳏ ﳐ ﳑ ﳒ', 'فَأَوْجَسَ مِنْهُمْ خِيفَةًۭ ۖ قَالُوا۟ لَا تَخَفْ ۖ وَبَشَّرُوهُ بِغُلَـٰمٍ عَلِيمٍۢ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 51, 521, 'ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ', 'فَأَقْبَلَتِ ٱمْرَأَتُهُۥ فِى صَرَّةٍۢ فَصَكَّتْ وَجْهَهَا وَقَالَتْ عَجُوزٌ عَقِيمٌۭ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 51, 521, 'ﳝ ﳞ ﳟ ﳠﳡ ﳢ ﳣ ﳤ ﳥ ﳦ', 'قَالُوا۟ كَذَٰلِكِ قَالَ رَبُّكِ ۖ إِنَّهُۥ هُوَ ٱلْحَكِيمُ ٱلْعَلِيمُ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 51, 522, 'ﱁﱂ ﱃ ﱄ ﱅ ﱆ ﱇ', '۞ قَالَ فَمَا خَطْبُكُمْ أَيُّهَا ٱلْمُرْسَلُونَ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 51, 522, 'ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ', 'قَالُوٓا۟ إِنَّآ أُرْسِلْنَآ إِلَىٰ قَوْمٍۢ مُّجْرِمِينَ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 51, 522, 'ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ', 'لِنُرْسِلَ عَلَيْهِمْ حِجَارَةًۭ مِّن طِينٍۢ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 51, 522, 'ﱕ ﱖ ﱗ ﱘ ﱙ', 'مُّسَوَّمَةً عِندَ رَبِّكَ لِلْمُسْرِفِينَ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 51, 522, 'ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ', 'فَأَخْرَجْنَا مَن كَانَ فِيهَا مِنَ ٱلْمُؤْمِنِينَ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 51, 522, 'ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ', 'فَمَا وَجَدْنَا فِيهَا غَيْرَ بَيْتٍۢ مِّنَ ٱلْمُسْلِمِينَ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 51, 522, 'ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ', 'وَتَرَكْنَا فِيهَآ ءَايَةًۭ لِّلَّذِينَ يَخَافُونَ ٱلْعَذَابَ ٱلْأَلِيمَ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 51, 522, 'ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ', 'وَفِى مُوسَىٰٓ إِذْ أَرْسَلْنَـٰهُ إِلَىٰ فِرْعَوْنَ بِسُلْطَـٰنٍۢ مُّبِينٍۢ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 51, 522, 'ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ', 'فَتَوَلَّىٰ بِرُكْنِهِۦ وَقَالَ سَـٰحِرٌ أَوْ مَجْنُونٌۭ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 51, 522, 'ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ', 'فَأَخَذْنَـٰهُ وَجُنُودَهُۥ فَنَبَذْنَـٰهُمْ فِى ٱلْيَمِّ وَهُوَ مُلِيمٌۭ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 51, 522, 'ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ', 'وَفِى عَادٍ إِذْ أَرْسَلْنَا عَلَيْهِمُ ٱلرِّيحَ ٱلْعَقِيمَ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 51, 522, 'ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ', 'مَا تَذَرُ مِن شَىْءٍ أَتَتْ عَلَيْهِ إِلَّا جَعَلَتْهُ كَٱلرَّمِيمِ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 51, 522, 'ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ', 'وَفِى ثَمُودَ إِذْ قِيلَ لَهُمْ تَمَتَّعُوا۟ حَتَّىٰ حِينٍۢ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 51, 522, 'ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ', 'فَعَتَوْا۟ عَنْ أَمْرِ رَبِّهِمْ فَأَخَذَتْهُمُ ٱلصَّـٰعِقَةُ وَهُمْ يَنظُرُونَ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 51, 522, 'ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ', 'فَمَا ٱسْتَطَـٰعُوا۟ مِن قِيَامٍۢ وَمَا كَانُوا۟ مُنتَصِرِينَ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 51, 522, 'ﲵ ﲶ ﲷ ﲸﲹ ﲺ ﲻ ﲼ ﲽ ﲾ', 'وَقَوْمَ نُوحٍۢ مِّن قَبْلُ ۖ إِنَّهُمْ كَانُوا۟ قَوْمًۭا فَـٰسِقِينَ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 51, 522, 'ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ', 'وَٱلسَّمَآءَ بَنَيْنَـٰهَا بِأَيْي۟دٍۢ وَإِنَّا لَمُوسِعُونَ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 51, 522, 'ﳅ ﳆ ﳇ ﳈ ﳉ', 'وَٱلْأَرْضَ فَرَشْنَـٰهَا فَنِعْمَ ٱلْمَـٰهِدُونَ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 51, 522, 'ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ', 'وَمِن كُلِّ شَىْءٍ خَلَقْنَا زَوْجَيْنِ لَعَلَّكُمْ تَذَكَّرُونَ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 51, 522, 'ﳒ ﳓ ﳔﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ', 'فَفِرُّوٓا۟ إِلَى ٱللَّهِ ۖ إِنِّى لَكُم مِّنْهُ نَذِيرٌۭ مُّبِينٌۭ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 51, 522, 'ﳜ ﳝ ﳞ ﳟ ﳠ ﳡﳢ ﳣ ﳤ ﳥ ﳦ ﳧ ﳨ', 'وَلَا تَجْعَلُوا۟ مَعَ ٱللَّهِ إِلَـٰهًا ءَاخَرَ ۖ إِنِّى لَكُم مِّنْهُ نَذِيرٌۭ مُّبِينٌۭ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 51, 523, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ', 'كَذَٰلِكَ مَآ أَتَى ٱلَّذِينَ مِن قَبْلِهِم مِّن رَّسُولٍ إِلَّا قَالُوا۟ سَاحِرٌ أَوْ مَجْنُونٌ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 51, 523, 'ﱏ ﱐﱑ ﱒ ﱓ ﱔ ﱕ ﱖ', 'أَتَوَاصَوْا۟ بِهِۦ ۚ بَلْ هُمْ قَوْمٌۭ طَاغُونَ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 51, 523, 'ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ', 'فَتَوَلَّ عَنْهُمْ فَمَآ أَنتَ بِمَلُومٍۢ ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (55, 51, 523, 'ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ', 'وَذَكِّرْ فَإِنَّ ٱلذِّكْرَىٰ تَنفَعُ ٱلْمُؤْمِنِينَ ٥٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (56, 51, 523, 'ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ', 'وَمَا خَلَقْتُ ٱلْجِنَّ وَٱلْإِنسَ إِلَّا لِيَعْبُدُونِ ٥٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (57, 51, 523, 'ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ', 'مَآ أُرِيدُ مِنْهُم مِّن رِّزْقٍۢ وَمَآ أُرِيدُ أَن يُطْعِمُونِ ٥٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (58, 51, 523, 'ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ', 'إِنَّ ٱللَّهَ هُوَ ٱلرَّزَّاقُ ذُو ٱلْقُوَّةِ ٱلْمَتِينُ ٥٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (59, 51, 523, 'ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ', 'فَإِنَّ لِلَّذِينَ ظَلَمُوا۟ ذَنُوبًۭا مِّثْلَ ذَنُوبِ أَصْحَـٰبِهِمْ فَلَا يَسْتَعْجِلُونِ ٥٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (60, 51, 523, 'ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ', 'فَوَيْلٌۭ لِّلَّذِينَ كَفَرُوا۟ مِن يَوْمِهِمُ ٱلَّذِى يُوعَدُونَ ٦٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 52, 523, 'ﲎ ﲏ', 'وَٱلطُّورِ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 52, 523, 'ﲐ ﲑ ﲒ', 'وَكِتَـٰبٍۢ مَّسْطُورٍۢ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 52, 523, 'ﲓ ﲔ ﲕ ﲖ', 'فِى رَقٍّۢ مَّنشُورٍۢ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 52, 523, 'ﲗ ﲘ ﲙ', 'وَٱلْبَيْتِ ٱلْمَعْمُورِ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 52, 523, 'ﲚ ﲛ ﲜ', 'وَٱلسَّقْفِ ٱلْمَرْفُوعِ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 52, 523, 'ﲝ ﲞ ﲟ', 'وَٱلْبَحْرِ ٱلْمَسْجُورِ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 52, 523, 'ﲠ ﲡ ﲢ ﲣ ﲤ', 'إِنَّ عَذَابَ رَبِّكَ لَوَٰقِعٌۭ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 52, 523, 'ﲥ ﲦ ﲧ ﲨ ﲩ', 'مَّا لَهُۥ مِن دَافِعٍۢ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 52, 523, 'ﲪ ﲫ ﲬ ﲭ ﲮ', 'يَوْمَ تَمُورُ ٱلسَّمَآءُ مَوْرًۭا ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 52, 523, 'ﲯ ﲰ ﲱ ﲲ', 'وَتَسِيرُ ٱلْجِبَالُ سَيْرًۭا ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 52, 523, 'ﲳ ﲴ ﲵ ﲶ', 'فَوَيْلٌۭ يَوْمَئِذٍۢ لِّلْمُكَذِّبِينَ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 52, 523, 'ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ', 'ٱلَّذِينَ هُمْ فِى خَوْضٍۢ يَلْعَبُونَ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 52, 523, 'ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ', 'يَوْمَ يُدَعُّونَ إِلَىٰ نَارِ جَهَنَّمَ دَعًّا ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 52, 523, 'ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ', 'هَـٰذِهِ ٱلنَّارُ ٱلَّتِى كُنتُم بِهَا تُكَذِّبُونَ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 52, 524, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ', 'أَفَسِحْرٌ هَـٰذَآ أَمْ أَنتُمْ لَا تُبْصِرُونَ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 52, 524, 'ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'ٱصْلَوْهَا فَٱصْبِرُوٓا۟ أَوْ لَا تَصْبِرُوا۟ سَوَآءٌ عَلَيْكُمْ ۖ إِنَّمَا تُجْزَوْنَ مَا كُنتُمْ تَعْمَلُونَ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 52, 524, 'ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ', 'إِنَّ ٱلْمُتَّقِينَ فِى جَنَّـٰتٍۢ وَنَعِيمٍۢ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 52, 524, 'ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ', 'فَـٰكِهِينَ بِمَآ ءَاتَىٰهُمْ رَبُّهُمْ وَوَقَىٰهُمْ رَبُّهُمْ عَذَابَ ٱلْجَحِيمِ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 52, 524, 'ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ', 'كُلُوا۟ وَٱشْرَبُوا۟ هَنِيٓـًٔۢا بِمَا كُنتُمْ تَعْمَلُونَ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 52, 524, 'ﱬ ﱭ ﱮ ﱯﱰ ﱱ ﱲ ﱳ ﱴ', 'مُتَّكِـِٔينَ عَلَىٰ سُرُرٍۢ مَّصْفُوفَةٍۢ ۖ وَزَوَّجْنَـٰهُم بِحُورٍ عِينٍۢ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 52, 524, 'ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ', 'وَٱلَّذِينَ ءَامَنُوا۟ وَٱتَّبَعَتْهُمْ ذُرِّيَّتُهُم بِإِيمَـٰنٍ أَلْحَقْنَا بِهِمْ ذُرِّيَّتَهُمْ وَمَآ أَلَتْنَـٰهُم مِّنْ عَمَلِهِم مِّن شَىْءٍۢ ۚ كُلُّ ٱمْرِئٍۭ بِمَا كَسَبَ رَهِينٌۭ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 52, 524, 'ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ', 'وَأَمْدَدْنَـٰهُم بِفَـٰكِهَةٍۢ وَلَحْمٍۢ مِّمَّا يَشْتَهُونَ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 52, 524, 'ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ', 'يَتَنَـٰزَعُونَ فِيهَا كَأْسًۭا لَّا لَغْوٌۭ فِيهَا وَلَا تَأْثِيمٌۭ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 52, 524, 'ﲙﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ', '۞ وَيَطُوفُ عَلَيْهِمْ غِلْمَانٌۭ لَّهُمْ كَأَنَّهُمْ لُؤْلُؤٌۭ مَّكْنُونٌۭ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 52, 524, 'ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ', 'وَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍۢ يَتَسَآءَلُونَ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 52, 524, 'ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ', 'قَالُوٓا۟ إِنَّا كُنَّا قَبْلُ فِىٓ أَهْلِنَا مُشْفِقِينَ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 52, 524, 'ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ', 'فَمَنَّ ٱللَّهُ عَلَيْنَا وَوَقَىٰنَا عَذَابَ ٱلسَّمُومِ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 52, 524, 'ﲷ ﲸ ﲹ ﲺ ﲻﲼ ﲽ ﲾ ﲿ ﳀ ﳁ', 'إِنَّا كُنَّا مِن قَبْلُ نَدْعُوهُ ۖ إِنَّهُۥ هُوَ ٱلْبَرُّ ٱلرَّحِيمُ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 52, 524, 'ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ', 'فَذَكِّرْ فَمَآ أَنتَ بِنِعْمَتِ رَبِّكَ بِكَاهِنٍۢ وَلَا مَجْنُونٍ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 52, 524, 'ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ', 'أَمْ يَقُولُونَ شَاعِرٌۭ نَّتَرَبَّصُ بِهِۦ رَيْبَ ٱلْمَنُونِ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 52, 524, 'ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ', 'قُلْ تَرَبَّصُوا۟ فَإِنِّى مَعَكُم مِّنَ ٱلْمُتَرَبِّصِينَ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 52, 525, 'ﱁ ﱂ ﱃ ﱄﱅ ﱆ ﱇ ﱈ ﱉ ﱊ', 'أَمْ تَأْمُرُهُمْ أَحْلَـٰمُهُم بِهَـٰذَآ ۚ أَمْ هُمْ قَوْمٌۭ طَاغُونَ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 52, 525, 'ﱋ ﱌ ﱍﱎ ﱏ ﱐ ﱑ ﱒ', 'أَمْ يَقُولُونَ تَقَوَّلَهُۥ ۚ بَل لَّا يُؤْمِنُونَ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 52, 525, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ', 'فَلْيَأْتُوا۟ بِحَدِيثٍۢ مِّثْلِهِۦٓ إِن كَانُوا۟ صَـٰدِقِينَ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 52, 525, 'ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ', 'أَمْ خُلِقُوا۟ مِنْ غَيْرِ شَىْءٍ أَمْ هُمُ ٱلْخَـٰلِقُونَ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 52, 525, 'ﱣ ﱤ ﱥ ﱦﱧ ﱨ ﱩ ﱪ ﱫ', 'أَمْ خَلَقُوا۟ ٱلسَّمَـٰوَٰتِ وَٱلْأَرْضَ ۚ بَل لَّا يُوقِنُونَ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 52, 525, 'ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ', 'أَمْ عِندَهُمْ خَزَآئِنُ رَبِّكَ أَمْ هُمُ ٱلْمُصَۣيْطِرُونَ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 52, 525, 'ﱴ ﱵ ﱶ ﱷ ﱸﱹ ﱺ ﱻ ﱼ ﱽ ﱾ', 'أَمْ لَهُمْ سُلَّمٌۭ يَسْتَمِعُونَ فِيهِ ۖ فَلْيَأْتِ مُسْتَمِعُهُم بِسُلْطَـٰنٍۢ مُّبِينٍ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 52, 525, 'ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ', 'أَمْ لَهُ ٱلْبَنَـٰتُ وَلَكُمُ ٱلْبَنُونَ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 52, 525, 'ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ', 'أَمْ تَسْـَٔلُهُمْ أَجْرًۭا فَهُم مِّن مَّغْرَمٍۢ مُّثْقَلُونَ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 52, 525, 'ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ', 'أَمْ عِندَهُمُ ٱلْغَيْبُ فَهُمْ يَكْتُبُونَ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 52, 525, 'ﲓ ﲔ ﲕﲖ ﲗ ﲘ ﲙ ﲚ ﲛ', 'أَمْ يُرِيدُونَ كَيْدًۭا ۖ فَٱلَّذِينَ كَفَرُوا۟ هُمُ ٱلْمَكِيدُونَ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 52, 525, 'ﲜ ﲝ ﲞ ﲟ ﲠﲡ ﲢ ﲣ ﲤ ﲥ ﲦ', 'أَمْ لَهُمْ إِلَـٰهٌ غَيْرُ ٱللَّهِ ۚ سُبْحَـٰنَ ٱللَّهِ عَمَّا يُشْرِكُونَ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 52, 525, 'ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ', 'وَإِن يَرَوْا۟ كِسْفًۭا مِّنَ ٱلسَّمَآءِ سَاقِطًۭا يَقُولُوا۟ سَحَابٌۭ مَّرْكُومٌۭ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 52, 525, 'ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ', 'فَذَرْهُمْ حَتَّىٰ يُلَـٰقُوا۟ يَوْمَهُمُ ٱلَّذِى فِيهِ يُصْعَقُونَ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 52, 525, 'ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ', 'يَوْمَ لَا يُغْنِى عَنْهُمْ كَيْدُهُمْ شَيْـًۭٔا وَلَا هُمْ يُنصَرُونَ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 52, 525, 'ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ', 'وَإِنَّ لِلَّذِينَ ظَلَمُوا۟ عَذَابًۭا دُونَ ذَٰلِكَ وَلَـٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 52, 525, 'ﳎ ﳏ ﳐ ﳑ ﳒﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ', 'وَٱصْبِرْ لِحُكْمِ رَبِّكَ فَإِنَّكَ بِأَعْيُنِنَا ۖ وَسَبِّحْ بِحَمْدِ رَبِّكَ حِينَ تَقُومُ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 52, 525, 'ﳚ ﳛ ﳜ ﳝ ﳞ ﳟ', 'وَمِنَ ٱلَّيْلِ فَسَبِّحْهُ وَإِدْبَـٰرَ ٱلنُّجُومِ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 53, 526, 'ﱁ ﱂ ﱃ ﱄ', 'وَٱلنَّجْمِ إِذَا هَوَىٰ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 53, 526, 'ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ', 'مَا ضَلَّ صَاحِبُكُمْ وَمَا غَوَىٰ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 53, 526, 'ﱋ ﱌ ﱍ ﱎ ﱏ', 'وَمَا يَنطِقُ عَنِ ٱلْهَوَىٰٓ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 53, 526, 'ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'إِنْ هُوَ إِلَّا وَحْىٌۭ يُوحَىٰ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 53, 526, 'ﱖ ﱗ ﱘ ﱙ', 'عَلَّمَهُۥ شَدِيدُ ٱلْقُوَىٰ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 53, 526, 'ﱚ ﱛ ﱜ ﱝ', 'ذُو مِرَّةٍۢ فَٱسْتَوَىٰ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 53, 526, 'ﱞ ﱟ ﱠ ﱡ', 'وَهُوَ بِٱلْأُفُقِ ٱلْأَعْلَىٰ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 53, 526, 'ﱢ ﱣ ﱤ ﱥ', 'ثُمَّ دَنَا فَتَدَلَّىٰ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 53, 526, 'ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ', 'فَكَانَ قَابَ قَوْسَيْنِ أَوْ أَدْنَىٰ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 53, 526, 'ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ', 'فَأَوْحَىٰٓ إِلَىٰ عَبْدِهِۦ مَآ أَوْحَىٰ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 53, 526, 'ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ', 'مَا كَذَبَ ٱلْفُؤَادُ مَا رَأَىٰٓ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 53, 526, 'ﱸ ﱹ ﱺ ﱻ ﱼ', 'أَفَتُمَـٰرُونَهُۥ عَلَىٰ مَا يَرَىٰ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 53, 526, 'ﱽ ﱾ ﱿ ﲀ ﲁ', 'وَلَقَدْ رَءَاهُ نَزْلَةً أُخْرَىٰ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 53, 526, 'ﲂ ﲃ ﲄ ﲅ', 'عِندَ سِدْرَةِ ٱلْمُنتَهَىٰ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 53, 526, 'ﲆ ﲇ ﲈ ﲉ', 'عِندَهَا جَنَّةُ ٱلْمَأْوَىٰٓ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 53, 526, 'ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ', 'إِذْ يَغْشَى ٱلسِّدْرَةَ مَا يَغْشَىٰ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 53, 526, 'ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ', 'مَا زَاغَ ٱلْبَصَرُ وَمَا طَغَىٰ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 53, 526, 'ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ', 'لَقَدْ رَأَىٰ مِنْ ءَايَـٰتِ رَبِّهِ ٱلْكُبْرَىٰٓ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 53, 526, 'ﲝ ﲞ ﲟ ﲠ', 'أَفَرَءَيْتُمُ ٱللَّـٰتَ وَٱلْعُزَّىٰ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 53, 526, 'ﲡ ﲢ ﲣ ﲤ', 'وَمَنَوٰةَ ٱلثَّالِثَةَ ٱلْأُخْرَىٰٓ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 53, 526, 'ﲥ ﲦ ﲧ ﲨ ﲩ', 'أَلَكُمُ ٱلذَّكَرُ وَلَهُ ٱلْأُنثَىٰ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 53, 526, 'ﲪ ﲫ ﲬ ﲭ ﲮ', 'تِلْكَ إِذًۭا قِسْمَةٌۭ ضِيزَىٰٓ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 53, 526, 'ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ', 'إِنْ هِىَ إِلَّآ أَسْمَآءٌۭ سَمَّيْتُمُوهَآ أَنتُمْ وَءَابَآؤُكُم مَّآ أَنزَلَ ٱللَّهُ بِهَا مِن سُلْطَـٰنٍ ۚ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَمَا تَهْوَى ٱلْأَنفُسُ ۖ وَلَقَدْ جَآءَهُم مِّن رَّبِّهِمُ ٱلْهُدَىٰٓ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 53, 526, 'ﳋ ﳌ ﳍ ﳎ ﳏ', 'أَمْ لِلْإِنسَـٰنِ مَا تَمَنَّىٰ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 53, 526, 'ﳐ ﳑ ﳒ ﳓ', 'فَلِلَّهِ ٱلْـَٔاخِرَةُ وَٱلْأُولَىٰ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 53, 526, 'ﳔﳕ ﳖ ﳗ ﳘ ﳙ ﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ ﳡ ﳢ ﳣ ﳤ ﳥ ﳦ ﳧ', '۞ وَكَم مِّن مَّلَكٍۢ فِى ٱلسَّمَـٰوَٰتِ لَا تُغْنِى شَفَـٰعَتُهُمْ شَيْـًٔا إِلَّا مِنۢ بَعْدِ أَن يَأْذَنَ ٱللَّهُ لِمَن يَشَآءُ وَيَرْضَىٰٓ ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 53, 527, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ', 'إِنَّ ٱلَّذِينَ لَا يُؤْمِنُونَ بِٱلْـَٔاخِرَةِ لَيُسَمُّونَ ٱلْمَلَـٰٓئِكَةَ تَسْمِيَةَ ٱلْأُنثَىٰ ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 53, 527, 'ﱋ ﱌ ﱍ ﱎ ﱏﱐ ﱑ ﱒ ﱓ ﱔﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ', 'وَمَا لَهُم بِهِۦ مِنْ عِلْمٍ ۖ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّ ۖ وَإِنَّ ٱلظَّنَّ لَا يُغْنِى مِنَ ٱلْحَقِّ شَيْـًۭٔا ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 53, 527, 'ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ', 'فَأَعْرِضْ عَن مَّن تَوَلَّىٰ عَن ذِكْرِنَا وَلَمْ يُرِدْ إِلَّا ٱلْحَيَوٰةَ ٱلدُّنْيَا ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 53, 527, 'ﱪ ﱫ ﱬ ﱭﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ', 'ذَٰلِكَ مَبْلَغُهُم مِّنَ ٱلْعِلْمِ ۚ إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَن ضَلَّ عَن سَبِيلِهِۦ وَهُوَ أَعْلَمُ بِمَنِ ٱهْتَدَىٰ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 53, 527, 'ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ', 'وَلِلَّهِ مَا فِى ٱلسَّمَـٰوَٰتِ وَمَا فِى ٱلْأَرْضِ لِيَجْزِىَ ٱلَّذِينَ أَسَـٰٓـُٔوا۟ بِمَا عَمِلُوا۟ وَيَجْزِىَ ٱلَّذِينَ أَحْسَنُوا۟ بِٱلْحُسْنَى ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 53, 527, 'ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓﲔ ﲕ ﲖ ﲗ ﲘﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦﲧ ﲨ ﲩ ﲪﲫ ﲬ ﲭ ﲮ ﲯ ﲰ', 'ٱلَّذِينَ يَجْتَنِبُونَ كَبَـٰٓئِرَ ٱلْإِثْمِ وَٱلْفَوَٰحِشَ إِلَّا ٱللَّمَمَ ۚ إِنَّ رَبَّكَ وَٰسِعُ ٱلْمَغْفِرَةِ ۚ هُوَ أَعْلَمُ بِكُمْ إِذْ أَنشَأَكُم مِّنَ ٱلْأَرْضِ وَإِذْ أَنتُمْ أَجِنَّةٌۭ فِى بُطُونِ أُمَّهَـٰتِكُمْ ۖ فَلَا تُزَكُّوٓا۟ أَنفُسَكُمْ ۖ هُوَ أَعْلَمُ بِمَنِ ٱتَّقَىٰٓ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 53, 527, 'ﲱ ﲲ ﲳ ﲴ', 'أَفَرَءَيْتَ ٱلَّذِى تَوَلَّىٰ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 53, 527, 'ﲵ ﲶ ﲷ ﲸ', 'وَأَعْطَىٰ قَلِيلًۭا وَأَكْدَىٰٓ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 53, 527, 'ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ', 'أَعِندَهُۥ عِلْمُ ٱلْغَيْبِ فَهُوَ يَرَىٰٓ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 53, 527, 'ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ', 'أَمْ لَمْ يُنَبَّأْ بِمَا فِى صُحُفِ مُوسَىٰ ٣٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (37, 53, 527, 'ﳇ ﳈ ﳉ ﳊ', 'وَإِبْرَٰهِيمَ ٱلَّذِى وَفَّىٰٓ ٣٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 53, 527, 'ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ', 'أَلَّا تَزِرُ وَازِرَةٌۭ وِزْرَ أُخْرَىٰ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 53, 527, 'ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ', 'وَأَن لَّيْسَ لِلْإِنسَـٰنِ إِلَّا مَا سَعَىٰ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 53, 527, 'ﳘ ﳙ ﳚ ﳛ ﳜ', 'وَأَنَّ سَعْيَهُۥ سَوْفَ يُرَىٰ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 53, 527, 'ﳝ ﳞ ﳟ ﳠ ﳡ', 'ثُمَّ يُجْزَىٰهُ ٱلْجَزَآءَ ٱلْأَوْفَىٰ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 53, 527, 'ﳢ ﳣ ﳤ ﳥ ﳦ', 'وَأَنَّ إِلَىٰ رَبِّكَ ٱلْمُنتَهَىٰ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 53, 527, 'ﳧ ﳨ ﳩ ﳪ ﳫ', 'وَأَنَّهُۥ هُوَ أَضْحَكَ وَأَبْكَىٰ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 53, 527, 'ﳬ ﳭ ﳮ ﳯ ﳰ', 'وَأَنَّهُۥ هُوَ أَمَاتَ وَأَحْيَا ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (45, 53, 528, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ', 'وَأَنَّهُۥ خَلَقَ ٱلزَّوْجَيْنِ ٱلذَّكَرَ وَٱلْأُنثَىٰ ٤٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (46, 53, 528, 'ﱇ ﱈ ﱉ ﱊ ﱋ', 'مِن نُّطْفَةٍ إِذَا تُمْنَىٰ ٤٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (47, 53, 528, 'ﱌ ﱍ ﱎ ﱏ ﱐ', 'وَأَنَّ عَلَيْهِ ٱلنَّشْأَةَ ٱلْأُخْرَىٰ ٤٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (48, 53, 528, 'ﱑ ﱒ ﱓ ﱔ ﱕ', 'وَأَنَّهُۥ هُوَ أَغْنَىٰ وَأَقْنَىٰ ٤٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (49, 53, 528, 'ﱖ ﱗ ﱘ ﱙ ﱚ', 'وَأَنَّهُۥ هُوَ رَبُّ ٱلشِّعْرَىٰ ٤٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (50, 53, 528, 'ﱛ ﱜ ﱝ ﱞ ﱟ', 'وَأَنَّهُۥٓ أَهْلَكَ عَادًا ٱلْأُولَىٰ ٥٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (51, 53, 528, 'ﱠ ﱡ ﱢ ﱣ', 'وَثَمُودَا۟ فَمَآ أَبْقَىٰ ٥١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (52, 53, 528, 'ﱤ ﱥ ﱦ ﱧﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ', 'وَقَوْمَ نُوحٍۢ مِّن قَبْلُ ۖ إِنَّهُمْ كَانُوا۟ هُمْ أَظْلَمَ وَأَطْغَىٰ ٥٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (53, 53, 528, 'ﱯ ﱰ ﱱ', 'وَٱلْمُؤْتَفِكَةَ أَهْوَىٰ ٥٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (54, 53, 528, 'ﱲ ﱳ ﱴ ﱵ', 'فَغَشَّىٰهَا مَا غَشَّىٰ ٥٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (55, 53, 528, 'ﱶ ﱷ ﱸ ﱹ ﱺ', 'فَبِأَىِّ ءَالَآءِ رَبِّكَ تَتَمَارَىٰ ٥٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (56, 53, 528, 'ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ', 'هَـٰذَا نَذِيرٌۭ مِّنَ ٱلنُّذُرِ ٱلْأُولَىٰٓ ٥٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (57, 53, 528, 'ﲁ ﲂ ﲃ', 'أَزِفَتِ ٱلْـَٔازِفَةُ ٥٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (58, 53, 528, 'ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ', 'لَيْسَ لَهَا مِن دُونِ ٱللَّهِ كَاشِفَةٌ ٥٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (59, 53, 528, 'ﲋ ﲌ ﲍ ﲎ ﲏ', 'أَفَمِنْ هَـٰذَا ٱلْحَدِيثِ تَعْجَبُونَ ٥٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (60, 53, 528, 'ﲐ ﲑ ﲒ ﲓ', 'وَتَضْحَكُونَ وَلَا تَبْكُونَ ٦٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (61, 53, 528, 'ﲔ ﲕ ﲖ', 'وَأَنتُمْ سَـٰمِدُونَ ٦١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (62, 53, 528, 'ﲗﲘ ﲙﲚ ﲛﲜ ﲝ', 'فَٱسْجُدُوا۟ لِلَّهِ وَٱعْبُدُوا۟ ۩ ٦٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 54, 528, 'ﲞ ﲟ ﲠ ﲡ ﲢ', 'ٱقْتَرَبَتِ ٱلسَّاعَةُ وَٱنشَقَّ ٱلْقَمَرُ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 54, 528, 'ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ', 'وَإِن يَرَوْا۟ ءَايَةًۭ يُعْرِضُوا۟ وَيَقُولُوا۟ سِحْرٌۭ مُّسْتَمِرٌّۭ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 54, 528, 'ﲫ ﲬ ﲭﲮ ﲯ ﲰ ﲱ ﲲ', 'وَكَذَّبُوا۟ وَٱتَّبَعُوٓا۟ أَهْوَآءَهُمْ ۚ وَكُلُّ أَمْرٍۢ مُّسْتَقِرٌّۭ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 54, 528, 'ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ', 'وَلَقَدْ جَآءَهُم مِّنَ ٱلْأَنۢبَآءِ مَا فِيهِ مُزْدَجَرٌ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 54, 528, 'ﲻ ﲼﲽ ﲾ ﲿ ﳀ ﳁ', 'حِكْمَةٌۢ بَـٰلِغَةٌۭ ۖ فَمَا تُغْنِ ٱلنُّذُرُ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 54, 528, 'ﳂ ﳃﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ', 'فَتَوَلَّ عَنْهُمْ ۘ يَوْمَ يَدْعُ ٱلدَّاعِ إِلَىٰ شَىْءٍۢ نُّكُرٍ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 54, 529, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ', 'خُشَّعًا أَبْصَـٰرُهُمْ يَخْرُجُونَ مِنَ ٱلْأَجْدَاثِ كَأَنَّهُمْ جَرَادٌۭ مُّنتَشِرٌۭ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 54, 529, 'ﱊ ﱋ ﱌﱍ ﱎ ﱏ ﱐ ﱑ ﱒ ﱓ', 'مُّهْطِعِينَ إِلَى ٱلدَّاعِ ۖ يَقُولُ ٱلْكَـٰفِرُونَ هَـٰذَا يَوْمٌ عَسِرٌۭ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 54, 529, 'ﱔﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ', '۞ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍۢ فَكَذَّبُوا۟ عَبْدَنَا وَقَالُوا۟ مَجْنُونٌۭ وَٱزْدُجِرَ ٩');
