INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (38, 70, 569, 'ﳘ ﳙ ﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ', 'أَيَطْمَعُ كُلُّ ٱمْرِئٍۢ مِّنْهُمْ أَن يُدْخَلَ جَنَّةَ نَعِيمٍۢ ٣٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (39, 70, 569, 'ﳡﳢ ﳣ ﳤ ﳥ ﳦ ﳧ', 'كَلَّآ ۖ إِنَّا خَلَقْنَـٰهُم مِّمَّا يَعْلَمُونَ ٣٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (40, 70, 569, 'ﳨ ﳩ ﳪ ﳫ ﳬ ﳭ ﳮ ﳯ', 'فَلَآ أُقْسِمُ بِرَبِّ ٱلْمَشَـٰرِقِ وَٱلْمَغَـٰرِبِ إِنَّا لَقَـٰدِرُونَ ٤٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (41, 70, 570, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ', 'عَلَىٰٓ أَن نُّبَدِّلَ خَيْرًۭا مِّنْهُمْ وَمَا نَحْنُ بِمَسْبُوقِينَ ٤١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (42, 70, 570, 'ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ', 'فَذَرْهُمْ يَخُوضُوا۟ وَيَلْعَبُوا۟ حَتَّىٰ يُلَـٰقُوا۟ يَوْمَهُمُ ٱلَّذِى يُوعَدُونَ ٤٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (43, 70, 570, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ', 'يَوْمَ يَخْرُجُونَ مِنَ ٱلْأَجْدَاثِ سِرَاعًۭا كَأَنَّهُمْ إِلَىٰ نُصُبٍۢ يُوفِضُونَ ٤٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (44, 70, 570, 'ﱝ ﱞ ﱟ ﱠﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ', 'خَـٰشِعَةً أَبْصَـٰرُهُمْ تَرْهَقُهُمْ ذِلَّةٌۭ ۚ ذَٰلِكَ ٱلْيَوْمُ ٱلَّذِى كَانُوا۟ يُوعَدُونَ ٤٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 71, 570, 'ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ', 'إِنَّآ أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِۦٓ أَنْ أَنذِرْ قَوْمَكَ مِن قَبْلِ أَن يَأْتِيَهُمْ عَذَابٌ أَلِيمٌۭ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 71, 570, 'ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ', 'قَالَ يَـٰقَوْمِ إِنِّى لَكُمْ نَذِيرٌۭ مُّبِينٌ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 71, 570, 'ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ', 'أَنِ ٱعْبُدُوا۟ ٱللَّهَ وَٱتَّقُوهُ وَأَطِيعُونِ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 71, 570, 'ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋﲌ ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓﲔ ﲕ ﲖ ﲗ ﲘ', 'يَغْفِرْ لَكُم مِّن ذُنُوبِكُمْ وَيُؤَخِّرْكُمْ إِلَىٰٓ أَجَلٍۢ مُّسَمًّى ۚ إِنَّ أَجَلَ ٱللَّهِ إِذَا جَآءَ لَا يُؤَخَّرُ ۖ لَوْ كُنتُمْ تَعْلَمُونَ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 71, 570, 'ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ', 'قَالَ رَبِّ إِنِّى دَعَوْتُ قَوْمِى لَيْلًۭا وَنَهَارًۭا ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 71, 570, 'ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ', 'فَلَمْ يَزِدْهُمْ دُعَآءِىٓ إِلَّا فِرَارًۭا ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 71, 570, 'ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ', 'وَإِنِّى كُلَّمَا دَعَوْتُهُمْ لِتَغْفِرَ لَهُمْ جَعَلُوٓا۟ أَصَـٰبِعَهُمْ فِىٓ ءَاذَانِهِمْ وَٱسْتَغْشَوْا۟ ثِيَابَهُمْ وَأَصَرُّوا۟ وَٱسْتَكْبَرُوا۟ ٱسْتِكْبَارًۭا ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 71, 570, 'ﲶ ﲷ ﲸ ﲹ ﲺ', 'ثُمَّ إِنِّى دَعَوْتُهُمْ جِهَارًۭا ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 71, 570, 'ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ', 'ثُمَّ إِنِّىٓ أَعْلَنتُ لَهُمْ وَأَسْرَرْتُ لَهُمْ إِسْرَارًۭا ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 71, 570, 'ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ', 'فَقُلْتُ ٱسْتَغْفِرُوا۟ رَبَّكُمْ إِنَّهُۥ كَانَ غَفَّارًۭا ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 71, 571, 'ﱁ ﱂ ﱃ ﱄ ﱅ', 'يُرْسِلِ ٱلسَّمَآءَ عَلَيْكُم مِّدْرَارًۭا ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 71, 571, 'ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ', 'وَيُمْدِدْكُم بِأَمْوَٰلٍۢ وَبَنِينَ وَيَجْعَل لَّكُمْ جَنَّـٰتٍۢ وَيَجْعَل لَّكُمْ أَنْهَـٰرًۭا ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 71, 571, 'ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ', 'مَّا لَكُمْ لَا تَرْجُونَ لِلَّهِ وَقَارًۭا ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 71, 571, 'ﱗ ﱘ ﱙ ﱚ', 'وَقَدْ خَلَقَكُمْ أَطْوَارًا ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 71, 571, 'ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ', 'أَلَمْ تَرَوْا۟ كَيْفَ خَلَقَ ٱللَّهُ سَبْعَ سَمَـٰوَٰتٍۢ طِبَاقًۭا ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 71, 571, 'ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ', 'وَجَعَلَ ٱلْقَمَرَ فِيهِنَّ نُورًۭا وَجَعَلَ ٱلشَّمْسَ سِرَاجًۭا ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 71, 571, 'ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ', 'وَٱللَّهُ أَنۢبَتَكُم مِّنَ ٱلْأَرْضِ نَبَاتًۭا ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 71, 571, 'ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ', 'ثُمَّ يُعِيدُكُمْ فِيهَا وَيُخْرِجُكُمْ إِخْرَاجًۭا ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 71, 571, 'ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ', 'وَٱللَّهُ جَعَلَ لَكُمُ ٱلْأَرْضَ بِسَاطًۭا ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 71, 571, 'ﱾ ﱿ ﲀ ﲁ ﲂ', 'لِّتَسْلُكُوا۟ مِنْهَا سُبُلًۭا فِجَاجًۭا ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 71, 571, 'ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐ', 'قَالَ نُوحٌۭ رَّبِّ إِنَّهُمْ عَصَوْنِى وَٱتَّبَعُوا۟ مَن لَّمْ يَزِدْهُ مَالُهُۥ وَوَلَدُهُۥٓ إِلَّا خَسَارًۭا ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 71, 571, 'ﲑ ﲒ ﲓ ﲔ', 'وَمَكَرُوا۟ مَكْرًۭا كُبَّارًۭا ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 71, 571, 'ﲕ ﲖ ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ', 'وَقَالُوا۟ لَا تَذَرُنَّ ءَالِهَتَكُمْ وَلَا تَذَرُنَّ وَدًّۭا وَلَا سُوَاعًۭا وَلَا يَغُوثَ وَيَعُوقَ وَنَسْرًۭا ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 71, 571, 'ﲣ ﲤ ﲥﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ', 'وَقَدْ أَضَلُّوا۟ كَثِيرًۭا ۖ وَلَا تَزِدِ ٱلظَّـٰلِمِينَ إِلَّا ضَلَـٰلًۭا ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 71, 571, 'ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ', 'مِّمَّا خَطِيٓـَٔـٰتِهِمْ أُغْرِقُوا۟ فَأُدْخِلُوا۟ نَارًۭا فَلَمْ يَجِدُوا۟ لَهُم مِّن دُونِ ٱللَّهِ أَنصَارًۭا ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 71, 571, 'ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ', 'وَقَالَ نُوحٌۭ رَّبِّ لَا تَذَرْ عَلَى ٱلْأَرْضِ مِنَ ٱلْكَـٰفِرِينَ دَيَّارًا ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 71, 571, 'ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ', 'إِنَّكَ إِن تَذَرْهُمْ يُضِلُّوا۟ عِبَادَكَ وَلَا يَلِدُوٓا۟ إِلَّا فَاجِرًۭا كَفَّارًۭا ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 71, 571, 'ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ', 'رَّبِّ ٱغْفِرْ لِى وَلِوَٰلِدَىَّ وَلِمَن دَخَلَ بَيْتِىَ مُؤْمِنًۭا وَلِلْمُؤْمِنِينَ وَٱلْمُؤْمِنَـٰتِ وَلَا تَزِدِ ٱلظَّـٰلِمِينَ إِلَّا تَبَارًۢا ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 72, 572, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ', 'قُلْ أُوحِىَ إِلَىَّ أَنَّهُ ٱسْتَمَعَ نَفَرٌۭ مِّنَ ٱلْجِنِّ فَقَالُوٓا۟ إِنَّا سَمِعْنَا قُرْءَانًا عَجَبًۭا ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 72, 572, 'ﱏ ﱐ ﱑ ﱒ ﱓﱔ ﱕ ﱖ ﱗ ﱘ ﱙ', 'يَهْدِىٓ إِلَى ٱلرُّشْدِ فَـَٔامَنَّا بِهِۦ ۖ وَلَن نُّشْرِكَ بِرَبِّنَآ أَحَدًۭا ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 72, 572, 'ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ', 'وَأَنَّهُۥ تَعَـٰلَىٰ جَدُّ رَبِّنَا مَا ٱتَّخَذَ صَـٰحِبَةًۭ وَلَا وَلَدًۭا ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 72, 572, 'ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ', 'وَأَنَّهُۥ كَانَ يَقُولُ سَفِيهُنَا عَلَى ٱللَّهِ شَطَطًۭا ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 72, 572, 'ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ', 'وَأَنَّا ظَنَنَّآ أَن لَّن تَقُولَ ٱلْإِنسُ وَٱلْجِنُّ عَلَى ٱللَّهِ كَذِبًۭا ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 72, 572, 'ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ', 'وَأَنَّهُۥ كَانَ رِجَالٌۭ مِّنَ ٱلْإِنسِ يَعُوذُونَ بِرِجَالٍۢ مِّنَ ٱلْجِنِّ فَزَادُوهُمْ رَهَقًۭا ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 72, 572, 'ﲃ ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ', 'وَأَنَّهُمْ ظَنُّوا۟ كَمَا ظَنَنتُمْ أَن لَّن يَبْعَثَ ٱللَّهُ أَحَدًۭا ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 72, 572, 'ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ', 'وَأَنَّا لَمَسْنَا ٱلسَّمَآءَ فَوَجَدْنَـٰهَا مُلِئَتْ حَرَسًۭا شَدِيدًۭا وَشُهُبًۭا ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 72, 572, 'ﲖ ﲗ ﲘ ﲙ ﲚ ﲛﲜ ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ ﲤ', 'وَأَنَّا كُنَّا نَقْعُدُ مِنْهَا مَقَـٰعِدَ لِلسَّمْعِ ۖ فَمَن يَسْتَمِعِ ٱلْـَٔانَ يَجِدْ لَهُۥ شِهَابًۭا رَّصَدًۭا ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 72, 572, 'ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ', 'وَأَنَّا لَا نَدْرِىٓ أَشَرٌّ أُرِيدَ بِمَن فِى ٱلْأَرْضِ أَمْ أَرَادَ بِهِمْ رَبُّهُمْ رَشَدًۭا ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 72, 572, 'ﲳ ﲴ ﲵ ﲶ ﲷ ﲸﲹ ﲺ ﲻ ﲼ ﲽ', 'وَأَنَّا مِنَّا ٱلصَّـٰلِحُونَ وَمِنَّا دُونَ ذَٰلِكَ ۖ كُنَّا طَرَآئِقَ قِدَدًۭا ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 72, 572, 'ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ', 'وَأَنَّا ظَنَنَّآ أَن لَّن نُّعْجِزَ ٱللَّهَ فِى ٱلْأَرْضِ وَلَن نُّعْجِزَهُۥ هَرَبًۭا ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 72, 572, 'ﳊ ﳋ ﳌ ﳍ ﳎ ﳏﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ', 'وَأَنَّا لَمَّا سَمِعْنَا ٱلْهُدَىٰٓ ءَامَنَّا بِهِۦ ۖ فَمَن يُؤْمِنۢ بِرَبِّهِۦ فَلَا يَخَافُ بَخْسًۭا وَلَا رَهَقًۭا ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 72, 573, 'ﱁ ﱂ ﱃ ﱄ ﱅﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ', 'وَأَنَّا مِنَّا ٱلْمُسْلِمُونَ وَمِنَّا ٱلْقَـٰسِطُونَ ۖ فَمَنْ أَسْلَمَ فَأُو۟لَـٰٓئِكَ تَحَرَّوْا۟ رَشَدًۭا ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 72, 573, 'ﱍ ﱎ ﱏ ﱐ ﱑ ﱒ', 'وَأَمَّا ٱلْقَـٰسِطُونَ فَكَانُوا۟ لِجَهَنَّمَ حَطَبًۭا ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 72, 573, 'ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ', 'وَأَلَّوِ ٱسْتَقَـٰمُوا۟ عَلَى ٱلطَّرِيقَةِ لَأَسْقَيْنَـٰهُم مَّآءً غَدَقًۭا ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 72, 573, 'ﱛ ﱜﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ', 'لِّنَفْتِنَهُمْ فِيهِ ۚ وَمَن يُعْرِضْ عَن ذِكْرِ رَبِّهِۦ يَسْلُكْهُ عَذَابًۭا صَعَدًۭا ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 72, 573, 'ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ', 'وَأَنَّ ٱلْمَسَـٰجِدَ لِلَّهِ فَلَا تَدْعُوا۟ مَعَ ٱللَّهِ أَحَدًۭا ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 72, 573, 'ﱰ ﱱ ﱲ ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ', 'وَأَنَّهُۥ لَمَّا قَامَ عَبْدُ ٱللَّهِ يَدْعُوهُ كَادُوا۟ يَكُونُونَ عَلَيْهِ لِبَدًۭا ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 72, 573, 'ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ', 'قُلْ إِنَّمَآ أَدْعُوا۟ رَبِّى وَلَآ أُشْرِكُ بِهِۦٓ أَحَدًۭا ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 72, 573, 'ﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ', 'قُلْ إِنِّى لَآ أَمْلِكُ لَكُمْ ضَرًّۭا وَلَا رَشَدًۭا ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 72, 573, 'ﲍ ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ ﲗ ﲘ ﲙ', 'قُلْ إِنِّى لَن يُجِيرَنِى مِنَ ٱللَّهِ أَحَدٌۭ وَلَنْ أَجِدَ مِن دُونِهِۦ مُلْتَحَدًا ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 72, 573, 'ﲚ ﲛ ﲜ ﲝ ﲞﲟ ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ', 'إِلَّا بَلَـٰغًۭا مِّنَ ٱللَّهِ وَرِسَـٰلَـٰتِهِۦ ۚ وَمَن يَعْصِ ٱللَّهَ وَرَسُولَهُۥ فَإِنَّ لَهُۥ نَارَ جَهَنَّمَ خَـٰلِدِينَ فِيهَآ أَبَدًا ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 72, 573, 'ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ ﲵ ﲶ ﲷ', 'حَتَّىٰٓ إِذَا رَأَوْا۟ مَا يُوعَدُونَ فَسَيَعْلَمُونَ مَنْ أَضْعَفُ نَاصِرًۭا وَأَقَلُّ عَدَدًۭا ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 72, 573, 'ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ ﳂ ﳃ', 'قُلْ إِنْ أَدْرِىٓ أَقَرِيبٌۭ مَّا تُوعَدُونَ أَمْ يَجْعَلُ لَهُۥ رَبِّىٓ أَمَدًا ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 72, 573, 'ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ ﳋ', 'عَـٰلِمُ ٱلْغَيْبِ فَلَا يُظْهِرُ عَلَىٰ غَيْبِهِۦٓ أَحَدًا ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 72, 573, 'ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ', 'إِلَّا مَنِ ٱرْتَضَىٰ مِن رَّسُولٍۢ فَإِنَّهُۥ يَسْلُكُ مِنۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِۦ رَصَدًۭا ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 72, 573, 'ﳚ ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ ﳡ ﳢ ﳣ ﳤ ﳥ ﳦ ﳧ', 'لِّيَعْلَمَ أَن قَدْ أَبْلَغُوا۟ رِسَـٰلَـٰتِ رَبِّهِمْ وَأَحَاطَ بِمَا لَدَيْهِمْ وَأَحْصَىٰ كُلَّ شَىْءٍ عَدَدًۢا ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 73, 574, 'ﱁ ﱂ ﱃ', 'يَـٰٓأَيُّهَا ٱلْمُزَّمِّلُ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 73, 574, 'ﱄ ﱅ ﱆ ﱇ ﱈ', 'قُمِ ٱلَّيْلَ إِلَّا قَلِيلًۭا ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 73, 574, 'ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ', 'نِّصْفَهُۥٓ أَوِ ٱنقُصْ مِنْهُ قَلِيلًا ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 73, 574, 'ﱏ ﱐ ﱑ ﱒ ﱓ ﱔ ﱕ', 'أَوْ زِدْ عَلَيْهِ وَرَتِّلِ ٱلْقُرْءَانَ تَرْتِيلًا ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 73, 574, 'ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ', 'إِنَّا سَنُلْقِى عَلَيْكَ قَوْلًۭا ثَقِيلًا ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 73, 574, 'ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ ﱤ', 'إِنَّ نَاشِئَةَ ٱلَّيْلِ هِىَ أَشَدُّ وَطْـًۭٔا وَأَقْوَمُ قِيلًا ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 73, 574, 'ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ', 'إِنَّ لَكَ فِى ٱلنَّهَارِ سَبْحًۭا طَوِيلًۭا ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 73, 574, 'ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ', 'وَٱذْكُرِ ٱسْمَ رَبِّكَ وَتَبَتَّلْ إِلَيْهِ تَبْتِيلًۭا ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 73, 574, 'ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ ﱻ ﱼ', 'رَّبُّ ٱلْمَشْرِقِ وَٱلْمَغْرِبِ لَآ إِلَـٰهَ إِلَّا هُوَ فَٱتَّخِذْهُ وَكِيلًۭا ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 73, 574, 'ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃ ﲄ', 'وَٱصْبِرْ عَلَىٰ مَا يَقُولُونَ وَٱهْجُرْهُمْ هَجْرًۭا جَمِيلًۭا ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 73, 574, 'ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ', 'وَذَرْنِى وَٱلْمُكَذِّبِينَ أُو۟لِى ٱلنَّعْمَةِ وَمَهِّلْهُمْ قَلِيلًا ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 73, 574, 'ﲌ ﲍ ﲎ ﲏ ﲐ', 'إِنَّ لَدَيْنَآ أَنكَالًۭا وَجَحِيمًۭا ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 73, 574, 'ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ', 'وَطَعَامًۭا ذَا غُصَّةٍۢ وَعَذَابًا أَلِيمًۭا ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 73, 574, 'ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ ﲝ ﲞ ﲟ', 'يَوْمَ تَرْجُفُ ٱلْأَرْضُ وَٱلْجِبَالُ وَكَانَتِ ٱلْجِبَالُ كَثِيبًۭا مَّهِيلًا ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 73, 574, 'ﲠ ﲡ ﲢ ﲣ ﲤ ﲥ ﲦ ﲧ ﲨ ﲩ ﲪ ﲫ', 'إِنَّآ أَرْسَلْنَآ إِلَيْكُمْ رَسُولًۭا شَـٰهِدًا عَلَيْكُمْ كَمَآ أَرْسَلْنَآ إِلَىٰ فِرْعَوْنَ رَسُولًۭا ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 73, 574, 'ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ', 'فَعَصَىٰ فِرْعَوْنُ ٱلرَّسُولَ فَأَخَذْنَـٰهُ أَخْذًۭا وَبِيلًۭا ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 73, 574, 'ﲳ ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ', 'فَكَيْفَ تَتَّقُونَ إِن كَفَرْتُمْ يَوْمًۭا يَجْعَلُ ٱلْوِلْدَٰنَ شِيبًا ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 73, 574, 'ﲼ ﲽ ﲾﲿ ﳀ ﳁ ﳂ ﳃ', 'ٱلسَّمَآءُ مُنفَطِرٌۢ بِهِۦ ۚ كَانَ وَعْدُهُۥ مَفْعُولًا ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 73, 574, 'ﳄ ﳅ ﳆﳇ ﳈ ﳉ ﳊ ﳋ ﳌ ﳍ ﳎ', 'إِنَّ هَـٰذِهِۦ تَذْكِرَةٌۭ ۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِيلًا ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 73, 575, 'ﱁﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐﱑ ﱒ ﱓ ﱔ ﱕﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜﱝ ﱞ ﱟ ﱠ ﱡ ﱢﱣ ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ ﱵﱶ ﱷ ﱸ ﱹ ﱺﱻ ﱼ ﱽ ﱾ ﱿ ﲀ ﲁ ﲂ ﲃﲄ ﲅ ﲆ ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ ﲎ ﲏ ﲐﲑ ﲒ ﲓﲔ ﲕ ﲖ ﲗ ﲘ ﲙ', '۞ إِنَّ رَبَّكَ يَعْلَمُ أَنَّكَ تَقُومُ أَدْنَىٰ مِن ثُلُثَىِ ٱلَّيْلِ وَنِصْفَهُۥ وَثُلُثَهُۥ وَطَآئِفَةٌۭ مِّنَ ٱلَّذِينَ مَعَكَ ۚ وَٱللَّهُ يُقَدِّرُ ٱلَّيْلَ وَٱلنَّهَارَ ۚ عَلِمَ أَن لَّن تُحْصُوهُ فَتَابَ عَلَيْكُمْ ۖ فَٱقْرَءُوا۟ مَا تَيَسَّرَ مِنَ ٱلْقُرْءَانِ ۚ عَلِمَ أَن سَيَكُونُ مِنكُم مَّرْضَىٰ ۙ وَءَاخَرُونَ يَضْرِبُونَ فِى ٱلْأَرْضِ يَبْتَغُونَ مِن فَضْلِ ٱللَّهِ ۙ وَءَاخَرُونَ يُقَـٰتِلُونَ فِى سَبِيلِ ٱللَّهِ ۖ فَٱقْرَءُوا۟ مَا تَيَسَّرَ مِنْهُ ۚ وَأَقِيمُوا۟ ٱلصَّلَوٰةَ وَءَاتُوا۟ ٱلزَّكَوٰةَ وَأَقْرِضُوا۟ ٱللَّهَ قَرْضًا حَسَنًۭا ۚ وَمَا تُقَدِّمُوا۟ لِأَنفُسِكُم مِّنْ خَيْرٍۢ تَجِدُوهُ عِندَ ٱللَّهِ هُوَ خَيْرًۭا وَأَعْظَمَ أَجْرًۭا ۚ وَٱسْتَغْفِرُوا۟ ٱللَّهَ ۖ إِنَّ ٱللَّهَ غَفُورٌۭ رَّحِيمٌۢ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 74, 575, 'ﲚ ﲛ ﲜ', 'يَـٰٓأَيُّهَا ٱلْمُدَّثِّرُ ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 74, 575, 'ﲝ ﲞ ﲟ', 'قُمْ فَأَنذِرْ ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 74, 575, 'ﲠ ﲡ ﲢ', 'وَرَبَّكَ فَكَبِّرْ ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 74, 575, 'ﲣ ﲤ ﲥ', 'وَثِيَابَكَ فَطَهِّرْ ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 74, 575, 'ﲦ ﲧ ﲨ', 'وَٱلرُّجْزَ فَٱهْجُرْ ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 74, 575, 'ﲩ ﲪ ﲫ ﲬ', 'وَلَا تَمْنُن تَسْتَكْثِرُ ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 74, 575, 'ﲭ ﲮ ﲯ', 'وَلِرَبِّكَ فَٱصْبِرْ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 74, 575, 'ﲰ ﲱ ﲲ ﲳ ﲴ', 'فَإِذَا نُقِرَ فِى ٱلنَّاقُورِ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 74, 575, 'ﲵ ﲶ ﲷ ﲸ ﲹ', 'فَذَٰلِكَ يَوْمَئِذٍۢ يَوْمٌ عَسِيرٌ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 74, 575, 'ﲺ ﲻ ﲼ ﲽ ﲾ', 'عَلَى ٱلْكَـٰفِرِينَ غَيْرُ يَسِيرٍۢ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 74, 575, 'ﲿ ﳀ ﳁ ﳂ ﳃ', 'ذَرْنِى وَمَنْ خَلَقْتُ وَحِيدًۭا ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 74, 575, 'ﳄ ﳅ ﳆ ﳇ ﳈ', 'وَجَعَلْتُ لَهُۥ مَالًۭا مَّمْدُودًۭا ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 74, 575, 'ﳉ ﳊ ﳋ', 'وَبَنِينَ شُهُودًۭا ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 74, 575, 'ﳌ ﳍ ﳎ ﳏ', 'وَمَهَّدتُّ لَهُۥ تَمْهِيدًۭا ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 74, 575, 'ﳐ ﳑ ﳒ ﳓ ﳔ', 'ثُمَّ يَطْمَعُ أَنْ أَزِيدَ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 74, 575, 'ﳕﳖ ﳗ ﳘ ﳙ ﳚ ﳛ', 'كَلَّآ ۖ إِنَّهُۥ كَانَ لِـَٔايَـٰتِنَا عَنِيدًۭا ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 74, 575, 'ﳜ ﳝ ﳞ', 'سَأُرْهِقُهُۥ صَعُودًا ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 74, 575, 'ﳟ ﳠ ﳡ ﳢ', 'إِنَّهُۥ فَكَّرَ وَقَدَّرَ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 74, 576, 'ﱁ ﱂ ﱃ ﱄ', 'فَقُتِلَ كَيْفَ قَدَّرَ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 74, 576, 'ﱅ ﱆ ﱇ ﱈ ﱉ', 'ثُمَّ قُتِلَ كَيْفَ قَدَّرَ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 74, 576, 'ﱊ ﱋ ﱌ', 'ثُمَّ نَظَرَ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 74, 576, 'ﱍ ﱎ ﱏ ﱐ', 'ثُمَّ عَبَسَ وَبَسَرَ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 74, 576, 'ﱑ ﱒ ﱓ ﱔ', 'ثُمَّ أَدْبَرَ وَٱسْتَكْبَرَ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 74, 576, 'ﱕ ﱖ ﱗ ﱘ ﱙ ﱚ ﱛ', 'فَقَالَ إِنْ هَـٰذَآ إِلَّا سِحْرٌۭ يُؤْثَرُ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 74, 576, 'ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ', 'إِنْ هَـٰذَآ إِلَّا قَوْلُ ٱلْبَشَرِ ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 74, 576, 'ﱢ ﱣ ﱤ', 'سَأُصْلِيهِ سَقَرَ ٢٦');
