INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 76, 578, 'ﲷ ﲸ ﲹ ﲺ ﲻ ﲼ ﲽ ﲾ ﲿ ﳀ ﳁ', 'إِنَّا خَلَقْنَا ٱلْإِنسَـٰنَ مِن نُّطْفَةٍ أَمْشَاجٍۢ نَّبْتَلِيهِ فَجَعَلْنَـٰهُ سَمِيعًۢا بَصِيرًا ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 76, 578, 'ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ', 'إِنَّا هَدَيْنَـٰهُ ٱلسَّبِيلَ إِمَّا شَاكِرًۭا وَإِمَّا كَفُورًا ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 76, 578, 'ﳊ ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ', 'إِنَّآ أَعْتَدْنَا لِلْكَـٰفِرِينَ سَلَـٰسِلَا۟ وَأَغْلَـٰلًۭا وَسَعِيرًا ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 76, 578, 'ﳑ ﳒ ﳓ ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ', 'إِنَّ ٱلْأَبْرَارَ يَشْرَبُونَ مِن كَأْسٍۢ كَانَ مِزَاجُهَا كَافُورًا ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 76, 579, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ', 'عَيْنًۭا يَشْرَبُ بِهَا عِبَادُ ٱللَّهِ يُفَجِّرُونَهَا تَفْجِيرًۭا ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 76, 579, 'ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ', 'يُوفُونَ بِٱلنَّذْرِ وَيَخَافُونَ يَوْمًۭا كَانَ شَرُّهُۥ مُسْتَطِيرًۭا ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 76, 579, 'ﱑ ﱒ ﱓ ﱔ ﱕ ﱖ ﱗ ﱘ', 'وَيُطْعِمُونَ ٱلطَّعَامَ عَلَىٰ حُبِّهِۦ مِسْكِينًۭا وَيَتِيمًۭا وَأَسِيرًا ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 76, 579, 'ﱙ ﱚ ﱛ ﱜ ﱝ ﱞ ﱟ ﱠ ﱡ ﱢ ﱣ', 'إِنَّمَا نُطْعِمُكُمْ لِوَجْهِ ٱللَّهِ لَا نُرِيدُ مِنكُمْ جَزَآءًۭ وَلَا شُكُورًا ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 76, 579, 'ﱤ ﱥ ﱦ ﱧ ﱨ ﱩ ﱪ ﱫ', 'إِنَّا نَخَافُ مِن رَّبِّنَا يَوْمًا عَبُوسًۭا قَمْطَرِيرًۭا ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 76, 579, 'ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ', 'فَوَقَىٰهُمُ ٱللَّهُ شَرَّ ذَٰلِكَ ٱلْيَوْمِ وَلَقَّىٰهُمْ نَضْرَةًۭ وَسُرُورًۭا ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 76, 579, 'ﱵ ﱶ ﱷ ﱸ ﱹ ﱺ', 'وَجَزَىٰهُم بِمَا صَبَرُوا۟ جَنَّةًۭ وَحَرِيرًۭا ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 76, 579, 'ﱻ ﱼ ﱽ ﱾﱿ ﲀ ﲁ ﲂ ﲃ ﲄ ﲅ ﲆ', 'مُّتَّكِـِٔينَ فِيهَا عَلَى ٱلْأَرَآئِكِ ۖ لَا يَرَوْنَ فِيهَا شَمْسًۭا وَلَا زَمْهَرِيرًۭا ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 76, 579, 'ﲇ ﲈ ﲉ ﲊ ﲋ ﲌ ﲍ', 'وَدَانِيَةً عَلَيْهِمْ ظِلَـٰلُهَا وَذُلِّلَتْ قُطُوفُهَا تَذْلِيلًۭا ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 76, 579, 'ﲎ ﲏ ﲐ ﲑ ﲒ ﲓ ﲔ ﲕ ﲖ', 'وَيُطَافُ عَلَيْهِم بِـَٔانِيَةٍۢ مِّن فِضَّةٍۢ وَأَكْوَابٍۢ كَانَتْ قَوَارِيرَا۠ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 76, 579, 'ﲗ ﲘ ﲙ ﲚ ﲛ ﲜ', 'قَوَارِيرَا۟ مِن فِضَّةٍۢ قَدَّرُوهَا تَقْدِيرًۭا ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 76, 579, 'ﲝ ﲞ ﲟ ﲠ ﲡ ﲢ ﲣ', 'وَيُسْقَوْنَ فِيهَا كَأْسًۭا كَانَ مِزَاجُهَا زَنجَبِيلًا ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 76, 579, 'ﲤ ﲥ ﲦ ﲧ ﲨ', 'عَيْنًۭا فِيهَا تُسَمَّىٰ سَلْسَبِيلًۭا ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 76, 579, 'ﲩﲪ ﲫ ﲬ ﲭ ﲮ ﲯ ﲰ ﲱ ﲲ ﲳ', '۞ وَيَطُوفُ عَلَيْهِمْ وِلْدَٰنٌۭ مُّخَلَّدُونَ إِذَا رَأَيْتَهُمْ حَسِبْتَهُمْ لُؤْلُؤًۭا مَّنثُورًۭا ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 76, 579, 'ﲴ ﲵ ﲶ ﲷ ﲸ ﲹ ﲺ ﲻ', 'وَإِذَا رَأَيْتَ ثَمَّ رَأَيْتَ نَعِيمًۭا وَمُلْكًۭا كَبِيرًا ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 76, 579, 'ﲼ ﲽ ﲾ ﲿ ﳀﳁ ﳂ ﳃ ﳄ ﳅ ﳆ ﳇ ﳈ ﳉ ﳊ', 'عَـٰلِيَهُمْ ثِيَابُ سُندُسٍ خُضْرٌۭ وَإِسْتَبْرَقٌۭ ۖ وَحُلُّوٓا۟ أَسَاوِرَ مِن فِضَّةٍۢ وَسَقَىٰهُمْ رَبُّهُمْ شَرَابًۭا طَهُورًا ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 76, 579, 'ﳋ ﳌ ﳍ ﳎ ﳏ ﳐ ﳑ ﳒ ﳓ', 'إِنَّ هَـٰذَا كَانَ لَكُمْ جَزَآءًۭ وَكَانَ سَعْيُكُم مَّشْكُورًا ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 76, 579, 'ﳔ ﳕ ﳖ ﳗ ﳘ ﳙ ﳚ', 'إِنَّا نَحْنُ نَزَّلْنَا عَلَيْكَ ٱلْقُرْءَانَ تَنزِيلًۭا ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 76, 579, 'ﳛ ﳜ ﳝ ﳞ ﳟ ﳠ ﳡ ﳢ ﳣ ﳤ', 'فَٱصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تُطِعْ مِنْهُمْ ءَاثِمًا أَوْ كَفُورًۭا ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 76, 579, 'ﳥ ﳦ ﳧ ﳨ ﳩ ﳪ', 'وَٱذْكُرِ ٱسْمَ رَبِّكَ بُكْرَةًۭ وَأَصِيلًۭا ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 76, 580, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ ﱇ ﱈ', 'وَمِنَ ٱلَّيْلِ فَٱسْجُدْ لَهُۥ وَسَبِّحْهُ لَيْلًۭا طَوِيلًا ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 76, 580, 'ﱉ ﱊ ﱋ ﱌ ﱍ ﱎ ﱏ ﱐ ﱑ', 'إِنَّ هَـٰٓؤُلَآءِ يُحِبُّونَ ٱلْعَاجِلَةَ وَيَذَرُونَ وَرَآءَهُمْ يَوْمًۭا ثَقِيلًۭا ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 76, 580, 'ﱒ ﱓ ﱔ ﱕﱖ ﱗ ﱘ ﱙ ﱚ ﱛ ﱜ', 'نَّحْنُ خَلَقْنَـٰهُمْ وَشَدَدْنَآ أَسْرَهُمْ ۖ وَإِذَا شِئْنَا بَدَّلْنَآ أَمْثَـٰلَهُمْ تَبْدِيلًا ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 76, 580, 'ﱝ ﱞ ﱟﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ', 'إِنَّ هَـٰذِهِۦ تَذْكِرَةٌۭ ۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِيلًۭا ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 76, 580, 'ﱨ ﱩ ﱪ ﱫ ﱬ ﱭﱮ ﱯ ﱰ ﱱ ﱲ ﱳ ﱴ', 'وَمَا تَشَآءُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُ ۚ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمًۭا ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 76, 580, 'ﱵ ﱶ ﱷ ﱸ ﱹﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ', 'يُدْخِلُ مَن يَشَآءُ فِى رَحْمَتِهِۦ ۚ وَٱلظَّـٰلِمِينَ أَعَدَّ لَهُمْ عَذَابًا أَلِيمًۢا ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (1, 77, 580, 'ﲁ ﲂ ﲃ', 'وَٱلْمُرْسَلَـٰتِ عُرْفًۭا ١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (2, 77, 580, 'ﲄ ﲅ ﲆ', 'فَٱلْعَـٰصِفَـٰتِ عَصْفًۭا ٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (3, 77, 580, 'ﲇ ﲈ ﲉ', 'وَٱلنَّـٰشِرَٰتِ نَشْرًۭا ٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (4, 77, 580, 'ﲊ ﲋ ﲌ', 'فَٱلْفَـٰرِقَـٰتِ فَرْقًۭا ٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (5, 77, 580, 'ﲍ ﲎ ﲏ', 'فَٱلْمُلْقِيَـٰتِ ذِكْرًا ٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (6, 77, 580, 'ﲐ ﲑ ﲒ ﲓ', 'عُذْرًا أَوْ نُذْرًا ٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (7, 77, 580, 'ﲔ ﲕ ﲖ ﲗ', 'إِنَّمَا تُوعَدُونَ لَوَٰقِعٌۭ ٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (8, 77, 580, 'ﲘ ﲙ ﲚ ﲛ', 'فَإِذَا ٱلنُّجُومُ طُمِسَتْ ٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (9, 77, 580, 'ﲜ ﲝ ﲞ ﲟ', 'وَإِذَا ٱلسَّمَآءُ فُرِجَتْ ٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (10, 77, 580, 'ﲠ ﲡ ﲢ ﲣ', 'وَإِذَا ٱلْجِبَالُ نُسِفَتْ ١٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (11, 77, 580, 'ﲤ ﲥ ﲦ ﲧ', 'وَإِذَا ٱلرُّسُلُ أُقِّتَتْ ١١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (12, 77, 580, 'ﲨ ﲩ ﲪ ﲫ', 'لِأَىِّ يَوْمٍ أُجِّلَتْ ١٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (13, 77, 580, 'ﲬ ﲭ ﲮ', 'لِيَوْمِ ٱلْفَصْلِ ١٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (14, 77, 580, 'ﲯ ﲰ ﲱ ﲲ ﲳ ﲴ', 'وَمَآ أَدْرَىٰكَ مَا يَوْمُ ٱلْفَصْلِ ١٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (15, 77, 580, 'ﲵ ﲶ ﲷ ﲸ', 'وَيْلٌۭ يَوْمَئِذٍۢ لِّلْمُكَذِّبِينَ ١٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (16, 77, 580, 'ﲹ ﲺ ﲻ ﲼ', 'أَلَمْ نُهْلِكِ ٱلْأَوَّلِينَ ١٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (17, 77, 580, 'ﲽ ﲾ ﲿ ﳀ', 'ثُمَّ نُتْبِعُهُمُ ٱلْـَٔاخِرِينَ ١٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (18, 77, 580, 'ﳁ ﳂ ﳃ ﳄ', 'كَذَٰلِكَ نَفْعَلُ بِٱلْمُجْرِمِينَ ١٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (19, 77, 580, 'ﳅ ﳆ ﳇ ﳈ', 'وَيْلٌۭ يَوْمَئِذٍۢ لِّلْمُكَذِّبِينَ ١٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (20, 77, 581, 'ﱁ ﱂ ﱃ ﱄ ﱅ ﱆ', 'أَلَمْ نَخْلُقكُّم مِّن مَّآءٍۢ مَّهِينٍۢ ٢٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (21, 77, 581, 'ﱇ ﱈ ﱉ ﱊ ﱋ', 'فَجَعَلْنَـٰهُ فِى قَرَارٍۢ مَّكِينٍ ٢١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (22, 77, 581, 'ﱌ ﱍ ﱎ ﱏ', 'إِلَىٰ قَدَرٍۢ مَّعْلُومٍۢ ٢٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (23, 77, 581, 'ﱐ ﱑ ﱒ ﱓ', 'فَقَدَرْنَا فَنِعْمَ ٱلْقَـٰدِرُونَ ٢٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (24, 77, 581, 'ﱔ ﱕ ﱖ ﱗ', 'وَيْلٌۭ يَوْمَئِذٍۢ لِّلْمُكَذِّبِينَ ٢٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (25, 77, 581, 'ﱘ ﱙ ﱚ ﱛ ﱜ', 'أَلَمْ نَجْعَلِ ٱلْأَرْضَ كِفَاتًا ٢٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (26, 77, 581, 'ﱝ ﱞ ﱟ', 'أَحْيَآءًۭ وَأَمْوَٰتًۭا ٢٦');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (27, 77, 581, 'ﱠ ﱡ ﱢ ﱣ ﱤ ﱥ ﱦ ﱧ', 'وَجَعَلْنَا فِيهَا رَوَٰسِىَ شَـٰمِخَـٰتٍۢ وَأَسْقَيْنَـٰكُم مَّآءًۭ فُرَاتًۭا ٢٧');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (28, 77, 581, 'ﱨ ﱩ ﱪ ﱫ', 'وَيْلٌۭ يَوْمَئِذٍۢ لِّلْمُكَذِّبِينَ ٢٨');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (29, 77, 581, 'ﱬ ﱭ ﱮ ﱯ ﱰ ﱱ ﱲ', 'ٱنطَلِقُوٓا۟ إِلَىٰ مَا كُنتُم بِهِۦ تُكَذِّبُونَ ٢٩');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (30, 77, 581, 'ﱳ ﱴ ﱵ ﱶ ﱷ ﱸ ﱹ', 'ٱنطَلِقُوٓا۟ إِلَىٰ ظِلٍّۢ ذِى ثَلَـٰثِ شُعَبٍۢ ٣٠');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (31, 77, 581, 'ﱺ ﱻ ﱼ ﱽ ﱾ ﱿ ﲀ', 'لَّا ظَلِيلٍۢ وَلَا يُغْنِى مِنَ ٱللَّهَبِ ٣١');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (32, 77, 581, 'ﲁ ﲂ ﲃ ﲄ ﲅ', 'إِنَّهَا تَرْمِى بِشَرَرٍۢ كَٱلْقَصْرِ ٣٢');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (33, 77, 581, 'ﲆ ﲇ ﲈ ﲉ', 'كَأَنَّهُۥ جِمَـٰلَتٌۭ صُفْرٌۭ ٣٣');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (34, 77, 581, 'ﲊ ﲋ ﲌ ﲍ', 'وَيْلٌۭ يَوْمَئِذٍۢ لِّلْمُكَذِّبِينَ ٣٤');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (35, 77, 581, 'ﲎ ﲏ ﲐ ﲑ ﲒ', 'هَـٰذَا يَوْمُ لَا يَنطِقُونَ ٣٥');
INSERT INTO AYAHS (ayah, surah, page, glyphs, text) VALUES (36, 77, 581, 'ﲓ ﲔ ﲕ ﲖ ﲗ', 'وَلَا يُؤْذَنُ لَهُمْ فَيَعْتَذِرُونَ ٣٦');
